INSERT INTO `tbl_library` VALUES ('YBZNRz', 'BbiEvi', '2', '2013-2018年中国电力产业发展规划与投资战略部署专题研究报告', null, '<p style=\"font-family:\'\';background-color:#FFFFFF;\">\r\n	<span style=\"line-height:2;\">第一章 中国电力行业发展环境分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第一节 电力行业统计标准&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、行业统计口径&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、行业统计方法&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、行业数据种类&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 电力行业政策环境分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、行业管理体制&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、行业相关政策动向&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）综合性宏观政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）电源布局、电网规划政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）电价改革政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）新能源政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）环保政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（6）电力监管政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（7）行政审批政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（8）地方政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、政策未来发展趋向&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三节 电力行业社会环境分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、火电行业节能减排分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）火电行业节能减排背景&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）火电行业节能减排现状&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）火电行业节能减排趋势&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、水电行业对生态影响分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、核泄漏对行业影响分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二章 2011-2012年中国电力行业运营与供需形势分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第一节 2011年中国电力生产行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、2011年电力生产行业经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）2011年电力生产行业经营效益分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）2011年电力生产行业盈利能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）2011年电力生产行业运营能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）2011年电力生产行业偿债能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）2011年电力生产行业发展能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、2011年电力生产行业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）2011年电力生产行业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）2011年不同规模企业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）2011年不同性质企业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）2011年不同地区企业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、2012年电力生产行业运营状况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）2012年产业规模分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）2012年资本/劳动密集度分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）2012年电力生产行业产销分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）2012年成本费用结构分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）2012年电力生产行业盈亏分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 2011-2012年中国电力供应行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、2011年电力供应行业经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）2011年电力供应行业经营效益分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）2011年电力供应行业盈利能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）2011年电力供应行业运营能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）2011年电力供应行业偿债能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）2011年电力供应行业发展能力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、2011年电力供应行业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）2011年电力供应行业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）2011年不同规模企业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）2011年不同性质企业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）2011年不同地区企业经济指标分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、2012年电力供应行业运营状况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）2012年产业规模分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）2012年资本/劳动密集度分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）2012年电力供应行业产销分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）2012年成本费用结构分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）2012年电力供应行业盈亏分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三节 中国电力行业供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、电力供应分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）电力装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）电力发电规模&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）发电设备利用小时数&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、电力消费分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）全社会用电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）各产业用电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）城乡居民生活用电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）高耗能行业用电情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、电力输送分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）全国供电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）全国售电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）全国线损电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、电力供需现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）电力供需形势现状&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）电力供需形势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）当前电力供需需关注的问题分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三章 2012年中国重点地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第一节 华北地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、华北地区电力行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、华北地区电力供应与消费&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）华北地区电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）华北地区电力消费情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、华北地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 华东地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、华东地区电力行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、华东地区电力供应与消费&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）华东地区电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）华东地区电力消费情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、华东地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三节 华中地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、华中地区电力行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、华中地区电力供应与消费&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）华中地区电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）华中地区电力消费情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、华中地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第四节 东北地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、东北地区电力行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、东北地区电力供应与消费&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）东北地区电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）东北地区电力消费情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、东北地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第五节 西北地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、西北地区电力行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、西北地区电力供应与消费&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）西北地区电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）西北地区电力消费情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、西北地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第六节 南方地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、南方地区电力行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、南方地区电力供应与消费&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）南方地区电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）南方地区电力消费情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、南方地区电力供需形势现状与趋势预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第四章 2012年中国电力子行业电力供应与运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第一节 2012年火电行业电力供应与运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、火电行业投资建设分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、火电行业电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）火电行业装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）火电行业累计装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）火电行业新增装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）火电行业发电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）火电设备利用小时数&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、火电行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）火电行业规模分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）火电行业生产情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）火电行业需求情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）火电行业供求平衡情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）火电行业财务运营情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、火电行业上网电价分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、火电行业发展趋势与前景&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 2012年水电行业电力供应与运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、水电行业开发潜力分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、水电行业投资建设分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、水电行业电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）水电行业装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）水电行业累计装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）水电行业新增装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）水电行业发电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）水电设备利用小时数&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、水电行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）水电行业规模分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）水电行业生产情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）水电行业需求情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）水电行业供求平衡情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）水电行业财务运营情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、水电行业上网电价分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">六、水电行业发展趋势与前景&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三节 2012年核电行业电力供应与运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、核电行业投资建设分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、核电行业电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）核电行业装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）核电行业累计装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）核电行业新增装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）核电行业发电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）核电设备利用小时数&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、核电行业运营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）核电行业规模分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）核电行业生产情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）核电行业需求情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）核电行业供求平衡情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）核电行业财务运营情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、核电行业上网电价分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、核电行业发展趋势与前景&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第四节 2012年风电行业电力供应分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、风能资源储量及其分布&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、风电行业投资建设分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、风电行业电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）风电行业装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）风电行业累计装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）风电行业新增装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）风电行业发电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）风电设备利用小时数&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、风电行业并网问题分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、风电行业上网电价分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">六、风电行业发展趋势与前景&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第五节 2012年光伏发电行业电力供应分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、光伏发电行业投资建设分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、光伏发电行业电力供应情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）光伏发电行业装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）光伏发电行业累计装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）光伏发电行业新增装机容量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）光伏发电行业发电量&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、光伏发电行业面临问题分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、光伏发电行业上网电价分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、光伏发电行业发展趋势与前景&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第五章 2012年中国电力行业矛盾分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第一节 电力与煤炭矛盾分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、煤炭行业发展分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）煤炭产量及分布情况&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）煤炭价格走势分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、当前煤电矛盾分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、造成煤电矛盾的原因分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、缓解煤电矛盾的建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）改进煤炭订货方式&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）完善煤电价格联动机制&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）改进和完善政府宏观调控及市场监管&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）适度鼓励引导煤电联营&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、解决煤电矛盾的根本途径&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）尽快修改完善电力相关法律法规&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）加快建立现代企业制度&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）建立社会诚信体系&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 电力结构性矛盾分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、电源结构矛盾分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）电源结构现状及存在问题&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）电源结构现状分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）电源结构存在问题&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）影响电源结构的因素分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）电源结构调整的目标与方向&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）电源结构调整的建议和策略&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）电源结构调整的政策建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）电源结构优化的主要策略&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、电源建设与电网建设不协调&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）电网行业发展现状&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）电源与电网矛盾分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）造成电源与电网矛盾原因分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）解决电源与电网矛盾的建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第六章 2012年中国电力行业主要企业经营分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第一节 中国华能集团公司经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、企业发展简况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、企业经营业务分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、企业产业分布分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、企业电力生产分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业装机容量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业发电量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业电源结构分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、企业经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业资产总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业营业收入分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业利润总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">六、企业经营优劣势分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">七、企业发展战略分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">八、企业最新发展动向分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 中国大唐集团公司经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、企业发展简况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、企业经营业务分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、企业产业分布分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、企业电力生产分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业装机容量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业发电量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业电源结构分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、企业经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业资产总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业营业收入分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业利润总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">六、企业经营优劣势分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">七、企业发展战略分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">八、企业最新发展动向分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三节 中国国电集团公司经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、企业发展简况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、企业经营业务分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、企业产业分布分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、企业电力生产分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业装机容量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业发电量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业电源结构分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、企业经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业资产总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业营业收入分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业利润总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">六、企业经营优劣势分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">七、企业发展战略分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">八、企业最新发展动向分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第四节 中国华电集团公司经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、企业发展简况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、企业经营业务分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、企业产业分布分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、企业电力生产分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业装机容量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业发电量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业电源结构分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、企业经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业资产总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业营业收入分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业利润总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">六、企业经营优劣势分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">七、企业发展战略分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">八、企业最新发展动向分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第五节 中国电力投资集团公司经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、企业发展简况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、企业经营业务分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、企业产业分布分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、企业电力生产分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业装机容量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业发电量分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业电源结构分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">五、企业经营情况分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1、企业资产总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2、企业营业收入分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">3、企业利润总额分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">六、企业经营优劣势分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">七、企业发展战略分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">八、企业最新发展动向分析</span>\r\n</p>\r\n<p style=\"font-family:\'\';background-color:#FFFFFF;\">\r\n	<span style=\"line-height:2;\">第七章 2013-2018年中国电力行业投资与前景预测分析</span><br />\r\n<span style=\"line-height:2;\">第一节 中国电力行业投资特性分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、电力行业进入壁垒分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、电力行业盈利模式分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、电力行业盈利因素分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 2013-2018年中国电力行业投资现状分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、电源工程投资现状分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）电源工程投资规模&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）电源工程投资结构&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、电网工程投资现状分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）电网工程投资规模&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）电网细分领域投资分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">1）输电环节投资分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">2）变电环节投资分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三节 2013-2018年中国电力行业投资前景预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、电力行业投资驱动因素&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、电力行业投资前景预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）电源工程投资前景预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）电网工程投资前景预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第四节 2013-2018年中国电力行业市场规模预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、电力行业市场规模预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、子行业市场规模预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）火电行业市场规模预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）水电行业市场规模预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）核电行业市场规模预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）新能源发电行业市场规模预测&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第八章 2013-2018年中国电力行业授信风险及机会分析&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第一节 电力行业环境风险分析及提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、国际环境对行业影响及风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、宏观环境对行业影响及风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、央行货币及银行业调控政策&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第二节 电力行业政策分析及提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、产业政策影响及风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、环保政策影响及风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、能源规划影响及风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第三节 电力行业市场风险及提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、市场供需风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、市场价格风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、行业竞争风险提示&nbsp;</span><br />\r\n<span style=\"line-height:2;\">第四节 电力行业授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">一、总体授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">二、区域授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）区域发展特点及总结&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）区域市场授信建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">三、子行业授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（1）火电行业授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（2）水电行业授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（3）核电行业授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（4）风电行业授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">（5）光伏发电行业授信机会及建议&nbsp;</span><br />\r\n<span style=\"line-height:2;\">四、企业授信机会及建议</span>\r\n</p>\r\n<p style=\"font-family:\'\';background-color:#FFFFFF;\">\r\n	<span style=\"line-height:2;\">图表目录：（部分）</span><br />\r\n<span style=\"line-height:2;\">图表：2007-2012年全国累计用电量及增速（单位：亿千瓦时，%）</span><br />\r\n<span style=\"line-height:2;\">图表：2007-2012年电力装机容量及增长情况（单位：万千瓦，%）</span><br />\r\n<span style=\"line-height:2;\">图表：电力行业结构图</span><br />\r\n<span style=\"line-height:2;\">图表：2004-2012年电力行业工业总产值及占GDP比重（单位：亿元，%）</span><br />\r\n<span style=\"line-height:2;\">图表：预测全球每年平均炎热天数（左图）