INSERT INTO `tbl_library` VALUES ('rUNRjq', '7ZRfUz', '2', '2012-2017年中国电网储能行业市场调查与投资前景分析报告', null, '<p style=\"font-family:arial, helvetica, clean, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第一章 电网储能概述&nbsp;14</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第一节 电网储能定义&nbsp;14</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第二节 电网储能行业发展历程&nbsp;14</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第三节 电网储能分类情况&nbsp;14</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第四节 电网储能产业链分析&nbsp;18</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、产业链模型介绍&nbsp;18</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、电网储能产业链模型分析&nbsp;21</span>\r\n</p>\r\n<p style=\"font-family:arial, helvetica, clean, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第二章 2011年中国电网储能行业发展环境分析&nbsp;22</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第一节 2011年中国经济环境分析&nbsp;22</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、宏观经济&nbsp;22</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、工业形势&nbsp;23</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">三、固定资产投资&nbsp;28</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第二节 2011年中国电网储能行业发展政策环境分析&nbsp;32</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、行业政策影响分析&nbsp;32</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、相关行业标准分析&nbsp;33</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第三节 2011年中国电网储能行业发展社会环境分析&nbsp;34</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、居民消费水平分析&nbsp;34</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、工业发展形势分析&nbsp;36</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第三节 国外主要生产工艺简介&nbsp;37</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第四节 国内主要生产方法&nbsp;38</span>\r\n</p>\r\n<p style=\"font-family:arial, helvetica, clean, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第三章 中国电网储能生产现状分析&nbsp;39</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第一节 电网储能行业总体规模&nbsp;39</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第二节 电网储能产能概况&nbsp;40</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、2009-2011年产能分析&nbsp;40</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、2012-2017年产能预测&nbsp;40</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第三节 电网储能市场容量概况&nbsp;41</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、 2009-2011年市场容量分析&nbsp;41</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、 产能配置与产能利用率调查&nbsp;41</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">三、2012-2017年市场容量预测&nbsp;42</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第四节 电网储能产业的生命周期分析&nbsp;43</span>\r\n</p>\r\n<p style=\"font-family:arial, helvetica, clean, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第四章 电网储能国内产品价格走势及影响因素分析&nbsp;46</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第一节 2010年国内产品价格回顾&nbsp;46</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第二节 2011年国内产品市场价格及评述&nbsp;46</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第三节 国内产品价格影响因素分析&nbsp;46</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第四节 2012-2017年国内产品未来价格走势预测&nbsp;48</span>\r\n</p>\r\n<p style=\"font-family:arial, helvetica, clean, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第五章 2010年我国电网储能行业发展现状分析&nbsp;49</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第一节 我国电网储能行业发展现状&nbsp;49</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、电网储能行业品牌发展现状&nbsp;49</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、电网储能行业需求市场现状&nbsp;49</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">三、电网储能市消费结构分析&nbsp;49</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第二节 中国电网储能产品技术分析&nbsp;50</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、电网储能产品主要生产技术&nbsp;50</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、电网储能产品市场的新技术进展&nbsp;50</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">三、2012-2017年电网储能产品技术趋势&nbsp;50</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第三节 中国电网储能行业存在的问题&nbsp;51</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一、电网储能产品市场存在的主要问题&nbsp;51</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">二、国内电网储能产品市场的三大瓶颈&nbsp;51</span><br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">三、电网储能产品解决问题的对策&nbsp;52</span>\r\n</p>\r\n<p style=\"font-family:arial, helvetica, clean, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<br />\r\n<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">第六章&nbsp; 2012-2017年年中国电网储能行业投资概况&nbsp;54</span><br />\r\n<span style=\"font-famil