INSERT INTO `tbl_library` VALUES ('YZBrAj', 'qYziUj', '2', '粉煤灰综合利用管理办法', null, '<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">《粉煤灰综合利用管理办法》 2013年第19号令&nbsp;</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\"><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国国家发展和改革委员会</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国科学技术部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国工业和信息化部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国财政部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国国土资源部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国环境保护部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国住房和城乡建设部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">中华人民共和国交通运输部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">国家税务总局</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">国家质量监督检验检疫总局</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">令</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">第19号</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">为规范和引导粉煤灰综合利用行为，促进粉煤灰综合利用健康发展，我们对《粉煤灰综合利用管理办法》进行了修订，现予发布，自2013年3月1日起施行。1994年1月原国家经贸委等六部门发布施行的《粉煤灰综合利用管理办法》（国经贸节[1994]14号）同时废止。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">国家发展和改革委员会主任：张 平</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">科　　技　　部　　部　长：万 钢</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">工 业 和 信 息 化 部部长：苗 圩</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">财　　政　　部　　部　长：谢旭人</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">国　土　资　源　部　部长：徐绍史</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">环　境　保　护　部　部长：周生贤</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">住 房 城 乡 建 设 部部长：姜伟新</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">交　通　运　输　部　部长：杨传堂</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">税　务　总　局　　局　长：肖 捷</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">质　检　总　局　　局　长：支树平</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\">2013年1月5日</span> \r\n</p>\r\n<p align=\"left\">\r\n	<a href=\"http://www.nea.gov.cn/132111505_11n.pdf\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\"></span></a><a href=\"/userfiles/file/20130624/20130624142209_766.pdf\" target=\"_blank\">附件：《粉煤灰综合利用管理办法》</a> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:12:40', '980', '1', '1');
INSERT INTO `tbl_library` VALUES ('e2Izeq', 'qYziUj', '2', '可再生能源电价附加有关会计处理规定', null, '<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" class=\"ke-zeroborder\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p style=\"text-align:center;\" align=\"center\">\r\n					<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">关于印发《可再生能源电价附加</span></strong> \r\n				</p>\r\n				<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n					<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">有关会计处理规定》的通知</span></strong> \r\n				</p>\r\n				<p style=\"text-align:center;\" align=\"center\">\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">财会[2012]24号</span> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国务院有关部委、有关直属机构，各省、自治区、直辖市、计划单列市财政厅（局），新疆生产建设兵团财务局，财政部驻各省、自治区、直辖市、计划单列市财政监察专员办事处：</span> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">为促进我国可再生能源的开发利用，根据《中华人民共和国可再生能源法》、《财政部 国家发展改革委 \r\n国家能源局关于印发〈可再生能源电价附加补助资金管理暂行办法〉的通知》（财建〔2012〕102号）等相关规定，我们制定了《可再生能源电价附加有关会计处理规定》，请布置本地区相关企业执行。执行中有何问题，请及时反馈我部。</span> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><a href=\"http://www.nea.gov.cn/132096146_11n.pdf\" target=\"_blank\"></a><a href=\"/userfiles/file/20130624/20130624142623_471.pdf\" target=\"_blank\">附件：可再生能源电价附加有关会计处理规定</a></span> \r\n				</p>\r\n				<p style=\"text-align:right;\" align=\"right\">\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">财 政 部</span> \r\n				</p>\r\n				<p style=\"text-align:right;\" align=\"right\">\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2012年12月27日</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', null, '2013-08-23 16:13:02', '950', '1', '1');
INSERT INTO `tbl_library` VALUES ('fMNZfu', 'qYziUj', '2', '关于扩大脱硝电价政策试点范围有关问题的通知', null, '<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家发展改革委关于扩大脱硝电价政策</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">试点范围有关问题的通知</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">发改价格[2012]4095号</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各省、自治区、直辖市发展改革委、物价局，国家电网公司、南方电网公司、内蒙古电力公司，华能、大唐、华电、国电、中电投、神华集团公司：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">为加快燃煤机组脱硝设施建设，提高发电企业脱硝积极性，减少氮氧化物排放，促进环境保护，决定进一步加大脱硝电价政策试行力度。现将有关事项通知如下：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">一、扩大脱硝电价试点范围。自2013年1月1日起，将脱硝电价试点范围由现行14个省（自治区、直辖市）的部分燃煤发电机组，扩大为全国所有燃煤发电机组。燃煤发电机组安装脱硝设施、具备在线监测功能且运行正常的，持国家或省级环保部门出具的脱硝设施验收合格文件，报省级价格主管部门审核后，执行脱硝电价。脱硝电价标准为每千瓦时8厘钱。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">二、脱硝电价资金暂由电网企业垫付。发电企业执行脱硝电价后，电网企业增加的购电资金暂由其垫付，今后择机在销售电价中予以解决。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">三、加强对脱硝电价政策执行的监管。各省、自治区、直辖市价格主管部门要及时对已安装脱硝设施的燃煤机组执行脱硝电价，调动发电企业脱硝积极性；会同有关部门加强对发电企业脱硝设施运行情况的监管，督促发电企业提高脱硝效率。同时，要注意总结脱硝电价政策实施情况，妥善处理出现的问题，并及时向我委报告。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家发展改革委</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2012年12月28日</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:13:35', '940', '1', '1');
INSERT INTO `tbl_library` VALUES ('6V3q2a', 'qYziUj', '2', '可再生能源电价附加资金补助目录（第三批）', null, '<p>\r\n	<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" class=\"ke-zeroborder\">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">关于公布可再生能源电价附加资金补助目录（第三批）的通知</span></strong> \r\n					</p>\r\n					<p style=\"text-align:center;\" align=\"center\">\r\n						<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">财建[2012]1067号</span> \r\n					</p>\r\n					<p>\r\n						<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">有关省、自治区、直辖市财政厅（局）、发展改革委、能源局、物价局，国家电网公司、中国南方电网有限责任公司、内蒙古自治区电力有限责任公司：</span> \r\n					</p>\r\n					<p>\r\n						<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">根据《财政部 国家发展改革委 \r\n国家能源局关于印发&lt;可再生能源电价附加补助资金管理暂行办法&gt;的通知》（财建〔2012〕102号）要求，财政部、国家发展改革委、国家能源局将符合条件的项目列入可再生能源电价附加资金补助目录,现予以公布。</span> \r\n					</p>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">财政部 国家发展改革委 国家能源局</span> \r\n					</p>\r\n					<p style=\"text-align:right;\" align=\"right\">\r\n						<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2012年12月20日</span> \r\n					</p>\r\n					<p>\r\n						<a href=\"http://jjs.mof.gov.cn/zhengwuxinxi/tongzhigonggao/201301/P020130107360592338196.xls\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span></a><a href=\"/userfiles/file/20130624/20130624143332_813.xls\" target=\"_blank\">附件：可再生能源电价附加资金补助目录（第三批）</a> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:13:53', '910', '1', '1');
INSERT INTO `tbl_library` VALUES ('AFZFFr', 'qYziUj', '2', '加强煤矿建设安全管理规定的通知', null, '<p style=\"text-align:center;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家安全监管总局 国家煤矿安监局</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家发展改革委 国家能源局 住房城乡建设部</span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">关于印发加强煤矿建设安全管理规定的通知</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">安监总煤监〔2012〕153号</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各产煤省、自治区、直辖市及新疆生产建设兵团发展改革委、能源局、煤炭行业管理部门、安全监管局、煤矿安全监管部门、住房城乡建设行政主管部门，各省级煤矿安全监察局，司法部直属煤矿管理局，有关中央企业：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">近年来，国家为保障能源供应安全，出台了一系列政策措施，加快大型煤炭基地建设，对小煤矿实施整合技改、兼并重组，淘汰落后产能，大力促进煤炭工业产业结构调整，提高了煤炭生产集约化程度和生产力水平，保障了对国民经济发展的煤炭供应。但在快速发展过程中也出现了部分煤矿建设项目违反建设程序、违背工程建设规律，前期工作不到位、工程设计不规范、建设施工安全问题突出、安全生产主体责任不落实，部分建设项目安全监管职责不清、监管体系不完善、监管力度亟待加强等问题。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">为认真贯彻落实《国务院关于进一步加强企业安全生产工作的通知》（国发〔2010〕23号）和《国务院关于坚持科学发展安全发展促进安全生产形势持续稳定好转的意见》(国发〔2011〕40号)精神，国家安全监管总局、国家煤矿安监局、国家发展改革委、国家能源局、住房城乡建设部研究制定了《加强煤矿建设安全管理规定》（以下简称《规定》）。现印发给你们，请认真贯彻执行。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">《规定》自发布之日起执行，其他现行文件内容与《规定》不一致的，依照《规定》执行。</span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家安全监管总局</span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家煤矿安监局</span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家发展改革委</span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家能源局</span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">住房城乡建设部</span> \r\n</p>\r\n<p style=\"text-align:right;\" align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2012年12月21日</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">加强煤矿建设安全管理规定</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">为加强煤矿建设安全管理，规范煤矿建设程序，保障煤矿建设项目安全生产，促进全国煤矿安全生产形势持续稳定好转，现作以下规定：</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 一、落实煤矿建设单位安全生产管理主体责任，强化建设单位安全管理职责</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（一）明确建设项目安全管理主体责任。建设单位法定代表人（实际控制人或主要负责人）对建设项目安全全面负责，是建设项目安全管理的第一责任人，上级集团公司对建设项目安全承担领导责任。建设单位必须落实安全生产管理主体责任，履行安全管理职责。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二）建立建设项目统一管理机制。煤矿建设、施工、监理和设计单位应当建立信息畅通、优势互补、统一指挥、反应灵敏、控制有效的安全管理机制，建设单位必须对煤矿建设项目进行统一协调管理，建立安全、技术、工程管理机构，建立健全项目建设期间领导带班下井等安全管理制度，按专业配足安全、技术人员，组织项目施工准备工作，加强对建设项目施工的监督管理。建设单位应当与施工､监理等单位签订安全管理协议,明确各方的安全管理责任,并指定专职安全管理人员进行安全检查与协调。建设单位要指派安全管理人员现场跟班管理，及时发现和处理建设过程中的安全隐患。建设单位每半年要向省级煤炭行业管理部门和煤矿安全监察机构报送1次工程进展情况。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（三）规范建设项目招投标。建设单位应按照法律、行政法规和项目核准文件等要求，做好项目勘察、设计、施工、监理以及重要设备、材料等采购活动的招投标工作，不得规避招标、虚假招标、限制或者排斥潜在招标人、非法转包或违规分包，不得压低涉及安全的工程造价，并接受有关部门的监督。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（四）加强地质勘查工作。地质勘查单位必须取得与地质勘查工程相适应的资质，并对提供的勘查报告真实性、准确性负责。建设单位应当向设计、施工、监理等单位提供与建设工程项目有关的原始资料，地质勘查报告必须经过评审，达到相关规范要求，确保资源储量、构造、瓦斯、水文、煤层自燃倾向性、煤尘爆炸危险性等开采条件满足煤矿设计、施工和监理的需要，并经国土资源主管部门备案。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">对于资源整合项目，建设单位要向设计、施工、监理等单位提供水文地质补充勘探资料，重点查清采空区分布区域、面积及积水、积气等情况。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（五）做好煤层突出危险性评估工作。新建矿井在可行性研究阶段，应当对矿井内采掘工程可能揭露的所有平均厚度在0.3米以上的煤层进行突出危险性评估。煤层突出危险性应当由具有突出危险性鉴定资质的单位、甲级资质的安全评价机构，或政府有关部门牵头组织相关专家进行评估，评估报告结论应当适用全矿井开采范围。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（六）规范建设项目设计报批程序。建设单位应根据批准的煤矿建设规模，委托具有相应资质的设计单位编制设计，同一项目的初步设计与安全设施设计应当由同一设计单位编制。初步设计应当按照批准的安全设施设计进行修改完善和报批。煤矿建设项目施工过程中瓦斯、煤层自燃倾向性、煤尘爆炸危险性、水文地质类型等条件发生变化，原设计的开拓方式、开采工艺，提升、运输、通风等主要生产系统以及首采区、首采工作面布置等需要变更，或施工过程中发现设计存在重大缺陷、影响安全施工，需要修改设计的，应当立即停止施工，委托原设计单位对初步设计和安全设施设计进行修改，报原批准部门审批。其中，涉及项目核准文件所规定的建设规模、重大技术方案等有关内容调整的，还应当事先以书面形式向原核准部门报批。矿井瓦斯等级､水文地质类型升级，应当重新考核施工单位的资质和业绩，确保施工单位具有相应的资质和能力｡</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（七）编制建设项目施工组织设计。施工组织设计应当由建设单位（或项目总承包单位）负责组织编制，并经设计、施工、监理等相关单位会审。施工组织设计中必须有保障安全施工的措施，确定的矿井一期、二期、三期工程施工顺序，应科学合理，符合有关规定。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（八）严格建设项目开工管理。建设单位要严格执行煤矿建设项目开工标准，按规定取得开工手续，不具备开工条件、未取得开工手续的项目，不得以任何名义进行井筒开挖（含冻结）和剥离土（岩）开挖等主体工程施工。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（九）做好建设项目联合试运转工作。煤矿建设项目达到国家规定的条件后方可申请联合试运转。大中型煤矿建设项目联合试运转按规定报省级煤炭行业管理部门或投资主管部门批准,小型煤矿建设项目联合试运转按规定报市(地)级煤炭行业管理部门或投资主管部门批准。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">建设单位必须统筹安排联合试运转与竣工验收工作,联合试运转的时间一般为1至6个月；有特殊情况需要延期的，必须按规定经省级煤炭行业管理部门或投资主管部门批准，但联合试运转总时间最长不得超过12个月。未提交联合试运转报告,或者联合试运转没有达到预期目标和效果的,不得申请竣工验收｡超过批准的联合试运转期限的，必须立即停止联合试运转，严禁以联合试运转名义组织生产。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十）建立建设项目应急救援机制。建设单位应当严格落实安全生产应急管理责任,编制完善应急预案,按规定建立救援队伍或签订救援协议,配备必要的应急救援物资、装备和设施;加强管理人员及全体从业人员的教育培训,通过定期演练,使作业和施救人员掌握相关应急预案内容,具备应急处置能力｡</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 二、落实煤矿建设项目设计规范要求，强化设计管理</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十一）规范建设项目设计工作。工程咨询、设计单位必须按照国家有关标准、规定等编制可行性研究报告、项目申请报告、初步设计和安全设施设计，不得承担与其资质等级不符的设计编制任务，也不得承担未按规定查明资源储量、瓦斯、水文、地质等安全开采条件的煤矿建设项目的设计编制任务。承接高瓦斯、煤与瓦斯突出、水文地质类型复杂和极复杂矿井建设项目设计的设计单位，必须具有相应的煤矿设计业绩。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十二）强化建设项目现场服务。对已开工的建设项目，设计单位必须委派相关专业人员常驻施工现场，加强与施工和建设单位沟通交流，及时协助解决相关问题。</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">三、落实煤矿建设项目施工单位安全施工主体责任，强化施工现场安全管理</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十三）落实煤矿建设项目施工单位安全施工主体责任。施工单位对煤矿建设施工安全承担主体责任，上级管理单位承担领导责任。施工单位必须设置安全生产管理机构，明确安全、技术负责人，加强对施工项目部及施工过程的安全管理，按照批准的设计组织施工，并对工程质量负责。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十四）强化施工项目部管理。施工项目部必须建立健全安全生产责任制，制定项目部领导和管理人员现场带（跟）班等安全生产规章制度、各工种操作规程、应急救援预案。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">施工项目部必须配备满足施工安全需要的装备，专职安全生产管理人员，以及矿建、机电、通风、地测等工程技术人员和特种作业人员。项目部负责人、专职安全生产管理人员、特种作业人员必须具备相应的安全生产知识和管理能力，并持证上岗。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十五）严格按资质等级范围承建工程。施工单位必须依法取得政府主管部门颁发的建筑业企业资质和安全生产许可证，并严格按资质等级许可的范围承建相应规模的煤矿建设项目，严禁转包、违法分包工程和挂靠资质施工。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">承接高瓦斯、煤与瓦斯突出、水文地质类型复杂和极复杂的矿井，立井井深大于600米、斜井垂深大于200米或斜井长度大于1000米工程的施工单位，必须具有一级及以上矿山工程施工总承包资质，同时具有相应的煤矿建设工程施工业绩。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十六）保证建设项目安全投入，强化隐患排查治理。施工单位应当建立安全生产费用提取和使用管理制度，按照国家规定足额提取和使用安全费用，满足安全施工需要。提取的安全费用列入工程造价，在竞标时，不得删减，列入标外管理。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">施工单位要建立事故隐患排查治理制度，加强现场安全管理，及时排查并消除事故隐患。发现重大事故隐患的，应当立即停止施工、从危险区域撤出作业人员并及时报告建设单位。</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">四、落实煤矿建设项目监理单位安全责任，强化工程监理</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十七）明确监理单位安全责任。项目监理单位必须依法取得相应资质并在相应资质等级范围内承揽业务。监理单位对煤矿施工质量和安全等承担监理责任，应当公正、独立、自主地开展监理工作，配备与建设项目监理工作相适应的足够数量的监理工程师和监理人员（不得少于4人）及监理设备。要明确项目监理机构人员的分工和岗位职责，制定和落实项目监理规划、实施细则，严格监理程序。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十八）严格履行监理职责。监理单位要按照建设工程监理合同、监理规范等规定，严格审查施工组织设计中的安全技术措施或专项施工方案是否符合工程建设强制性标准，并监督落实，开展工程质量现场巡视检查和工程情况监理。对存在事故隐患的，要及时下达整改指令并报告建设单位；对存在重大事故隐患的，应当要求立即停止施工，并监督施工单位予以排除，待隐患消除后方可恢复施工。现场监理人员应当认真填写监理日志并做好监理报告。</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">五、落实煤矿建设项目安全监管责任，强化监督管理</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（十九）严厉打击非法违法建设行为。各有关部门和机构要加强协调配合，在政府统一领导下，加强联合执法，加大对超层越界、未批先建、批小建大等非法、违法、违规建设行为的打击力度。对不符合条件擅自开工的，必须责令其停止施工；对设计、施工、监理单位资质资格不符合要求的，应当及时予以处理；对抢工期、赶进度的，要责令其整改；对施工过程中存在事故隐患的，应当责令其整改或停止施工；对存在重大事故隐患冒险组织施工的，要责令其停止施工，并依法查处；对以整合、技改名义违规组织生产，以及故意拖延工期、未在规定期限内实施改造的矿井，要依法予以关闭。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二十）切实加强煤炭地质勘查管理。各级国土资源主管部门要切实加强对煤炭地质勘查工作的管理，提高勘查地质报告质量。要进一步规范地质勘查实施方案管理，加强勘查实施方案审查，加强煤炭和煤层气综合勘查，并监督实施。勘查工作完成后，要对大中型储量规模的勘查项目开展野外工程验收。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二十一）严格煤矿地质勘查报告评审备案和矿区范围划定。评审单位要严格按照有关规范开展煤矿地质勘查报告评审工作。地质勘查报告达不到国家规范要求的，不予通过评审、备案。勘查程度达不到现行规范要求的，国土资源主管部门不得为其划定矿区范围。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二十二）严格各类资质管理。国土资源主管部门要严格地质勘查资质管理。住房城乡建设行政主管部门会同煤炭行业管理等部门制定完善煤矿建设相关资质管理办法，并加强日常监督管理。煤炭行业管理等部门要对开展煤矿设计、施工、监理业务的单位实施动态管理，发现能力、业绩不符合相应资质要求或存在违法违规行为的，应当向颁证部门提出处理建议，由颁证部门依法严肃处理。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二十三）加强施工企业安全生产许可管理工作。住房城乡建设行政主管部门向煤矿施工企业颁发安全生产许可证时，应当有煤炭行业管理部门、煤矿安全监察机构的初审同意意见。煤炭行业管理部门和煤矿安全监管监察部门发现煤矿施工企业达不到安全生产条件的，应当责令其停止施工，并向住房城乡建设行政主管部门提出处理建议，由其依据有关规定进行处理。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二十四）加强施工单位信息管理。施工单位应当将施工资质、项目部负责人、安全负责人、技术负责人等资格证书及中标文件等资料报送工程所在地省级煤炭行业管理部门备案，施工项目部负责人等发生变化时，应当及时书面报告。煤炭行业管理部门必须核实同一项目施工单位数量及资质，施工项目部经理及安全管理人员资格，以及从事施工的专业技术人员、特种作业人员、一般施工人员培训等情况，并加强日常监督检查。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二十五）加强隐患排查治理的监督检查。煤矿安全监管部门要建立完善煤矿建设项目安全隐患排查治理监督检查制度，发现重大安全隐患的，要下达整改指令书，并建立信息管理台账，逐级报告，实行挂牌督办。对整改不力的企业，要向社会公告，并依法处罚。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二十六）强化煤矿建设项目安全监察工作。各级煤矿安全监察机构要加强对地方政府煤矿安全监管工作的监督检查和指导；加大煤矿建设项目安全监察力度，发现非法、违法建设的，要依法查处；要依法组织开展煤矿建设安全事故调查处理工作，依法追究有关责任单位和责任人的责任。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:14:10', '900', '1', '1');
INSERT INTO `tbl_library` VALUES ('riq6ju', 'qYziUj', '2', '解除发电用煤临时价格干预措施', null, '<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" class=\"ke-zeroborder\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p style=\"text-align:center;\" align=\"center\">\r\n					<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家发展改革委关于解除发电用煤临时</span></strong> \r\n				</p>\r\n				<p style=\"text-align:center;font-size:18pt;\" align=\"center\">\r\n					<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">价格干预措施的通知</span></strong> \r\n				</p>\r\n				<p style=\"text-align:center;\" align=\"center\">\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">发改价格[2012]3956号</span> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各省、自治区、直辖市发展改革委、物价局、经贸委（经信委），中国煤炭工业协会，神华集团公司、中煤能源集团公司，华能、大唐、华电、国电、中电投集团公司：</span> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">为进一步深化煤炭市场化改革，充分发挥市场配置资源的基础性作用，根据当前发电用煤（以下简称“电煤”）供需形势和价格变化情况，决定解除自2012年1月1日起实施的电煤临时价格干预措施。现将有关事项通知如下：</span> \r\n				</p>\r\n				<p>\r\n					<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 一、解除电煤临时价格干预措施</span></strong> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">鉴于当前电煤供需逐步趋缓、电煤价格在全国范围内基本稳定，根据《中华人民共和国价格法》第三十二条规定，决定从2013年1月1日起，解除对电煤的临时价格干预措施，即取消《国家发展改革委关于加强发电用煤价格调控的通知》（发改电[2011]299号）中对合同电煤价格涨幅和市场交易电煤最高限价的有关规定，电煤由供需双方自主协商定价。</span> \r\n				</p>\r\n				<p>\r\n					<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 二、进一步做好煤炭价格监测工作</span></strong> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">解除对电煤临时价格干预措施后，各省级价格主管部门要进一步加强煤炭价格监测工作，特别是电煤价格监测，进一步完善电煤生产经营情况监测制度，建立电煤价格监测、预警制度。山西、内蒙古、陕西等主要产煤省（区）价格主管部门要按月监测主要煤炭生产企业的电煤结算价格、产量、热值、成本等情况。若电煤价格出现非正常变化，要及时向我委报告。</span> \r\n				</p>\r\n				<p>\r\n					<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 三、切实加强电煤市场监管</span></strong> \r\n				</p>\r\n				<p>\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各省级价格主管部门要继续加强对电煤市场的监管，切实做好对煤炭生产、销售过程中各类违规设立的涉煤基金和收费项目的清理整顿工作，并开展监督检查。地方各级政府不得采取行政手段不正当干预企业煤炭供销等经营活动。严禁企业之间达成价格垄断协议控制煤价；不得采取降低热值、降低煤质、以次充好等手段变相涨价；不得哄抬煤炭价格。对煤炭经营中的价格违法行为，各级价格主管部门将依法予以严肃查处。</span> \r\n				</p>\r\n				<p style=\"text-align:center;\" align=\"center\">\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">国家发展改革委</span> \r\n				</p>\r\n				<p style=\"text-align:center;\" align=\"center\">\r\n					<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2012年12月18日</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', null, '2013-08-23 16:14:22', '890', '1', '1');
INSERT INTO `tbl_library` VALUES ('J73Afq', 'aqQrmy', '2', '北京市贯彻落实《国家鼓励的资源综合利用认定管理办法》实施细则', null, '<p style=\"text-align:center;\">\r\n	关于印发《北京市贯彻落实&lt;国家鼓励的资源综合利用认定管理办法&gt;实施细则》（2012年修订）的通知\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	发文单位：北京市发展和改革委员会\r\n</div>\r\n<div style=\"text-align:center;\">\r\n	文号：京发改〔2012〕2024号\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"left\">\r\n	各区县发展改革委、财政局、国税局、地税局，各相关单位：\r\n</p>\r\n<p align=\"left\">\r\n	　　为贯彻落实国家发展改革委、财政部、国家税务总局《关于印发〈国家鼓励的资源综合利用认定管理办法〉的通知》（发改环资〔2006〕1864号），进一步加强对资源综合利用认定的管理，提高资源产出率，促进经济社会可持续发展，我们对2008年制定的《北京市贯彻落实&lt;国家鼓励的资源综合利用认定管理办法&gt;实施细则》（京发改〔2008〕1101号）进行了修订，现将修订后的《北京市贯彻落实&lt;国家鼓励的资源综合利用认定管理办法&gt;实施细则》印发给你们，请遵照执行。\r\n</p>\r\n<p align=\"left\">\r\n	　　特此通知。\r\n</p>\r\n<p align=\"right\">\r\n	　　北京市发展和改革委员会\r\n</p>\r\n<p align=\"right\">\r\n	　　北京市财政局\r\n</p>\r\n<p align=\"right\">\r\n	　　北京市国家税务局\r\n</p>\r\n<p align=\"right\">\r\n	　　北京市地方税务局\r\n</p>\r\n<p align=\"right\">\r\n	　　2012年11月1日\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<a href=\"/userfiles/file/20130624/20130624144646_30.doc\" target=\"_blank\">附件：北京市贯彻落实《国家鼓励的资源综合利用认定管理办法》实施细则</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<!--End -->', null, '2013-08-23 16:18:32', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('N7vMJf', 'aqQrmy', '2', '北京市节能监测机构管理实施细则（试行）', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">北京市发展和改革委员会 北京市财政局关于印发北京市节能监测管理暂行办法的通知</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">发文单位：北京市发展和改革委员会 北京市财政局</span> \r\n</div>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">文号：京发改规〔2013〕1号</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各有关单位：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为规范节能监测工作，切实发挥其对节能工作的支撑保障作用，根据《中华人民共和国节约能源法》和《北京市实施&lt;中华人民共和国节约能源法&gt;办法》，借鉴国内其它地区积极建设完善节能监测体系的有关经验，结合本市实际情况，我们研究制定了《北京市节能监测管理暂行办法》，已经市政府同意，现印发给你们，请认真贯彻执行。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　特此通知。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　北京市发展和改革委员会 北京市财政局</span> \r\n</p>\r\n<p align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　2013年3月1日</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（联系人：资环处 运动； 联系电话：66415588-1131）</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">北京市节能监测管理暂行办法</span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第一章 总则</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第一条　为加强节能管理，规范节能监测工作，根据《中华人民共和国节约能源法》和《北京市实施〈中华人民共和国节约能源法〉办法》及相关法律、法规和规章，结合本市实际，制定本办法。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第二条　本办法所称节能监测，是指节能主管部门委托节能监测机构，依据国家及本市节能有关法律、法规、标准和要求，对用能单位的主要用能系统及用能设备能耗情况进行监督检测，对能源管理和利用状况进行综合评价的活动。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第三条　本办法适用于本市行政区域内用能单位和通过市节能主管部门遴选的节能监测机构（以下简称“节能监测机构”）。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第四条 节能监测遵循管理与服务相结合的原则。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第二章 监测工作组织与实施</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第五条　北京市发展和改革委员会（以下统称“市发展改革委”）作为本市节能主管部门，负责管理、指导和组织全市节能监测工作。主要职责包括：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）依据节能有关法律、法规，制定节能监测政策、规章，组织编制节能监测技术标准和规范；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）遴选节能监测机构，并对节能监测机构实行动态管理；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）制定监测计划，下达监测任务，委托节能监测机构实施节能监测；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）通报节能监测结果；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）依法对违反有关法律、法规和标准的用能单位进行处理。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第六条 用能单位应配合节能监测工作，根据节能监测要求提供相关资料和必要的工作条件。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第七条 节能监测机构职责：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）贯彻执行国家、本市有关节能的政策、法规和技术标准，参与制定地方监测方法、标准和技术规范；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）受节能主管部门委托，对用能单位进行节能监督检测和综合评价；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）根据节能监测数据和综合评价结果，对用能单位提出整改建议。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第八条 节能监测的主要内容：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）对用能单位能源利用和能源管理情况进行检查；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）依据技术标准和规范对主要用能设备能耗参数进行测试；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）对用能单位主要用能系统运行状况进行测试分析；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）开展在线监控服务平台数据核查；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）开展节能监测综合评价；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（六）开展法律法规规定的或各级节能主管部门及行业主管部门根据工作需要开展的其他节能监测事项。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第九条 节能监测包括周期监测和专项监测。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　周期监测是指市节能主管部门委托节能监测机构对指定的重点用能单位开展的周期性节能监测活动，原则上2年为一个周期。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　专项监测是指市节能主管部门会同有关行业主管部门，根据相关规划、年度节能计划及工作需要，对重点领域用能单位开展的不定期监测。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十条 市节能主管部门会同市行业主管部门及区（县）节能主管部门制定并发布年度节能监测计划，委托节能监测机构开展相关工作。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十一条 \r\n节能监测机构接受委托后，制定具体实施方案，于现场监测前5个工作日通知被监测单位及所在区（县）节能主管部门。节能监测人员实施现场监测时，应当出示有效证明。现场监测结束后10个工作日内出具节能监测报告（一式4份）报市节能主管部门，由市节能主管部门抄送市行业主管部门、区（县）节能主管部门和被监测单位。节能监测报告应包括用能设备能耗测试结果、能源管理和利用状况综合评价、整改建议等内容。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　节能监测机构完成一个季度的节能监测任务后10个工作日内向市节能主管部门提交节能监测季度综合分析报告（一式4份）。综合分析报告应包括用能单位用能情况分析、节能潜力分析、存在的问题和建议等内容。市节能主管部门每季度向市行业主管部门、区（县）节能主管部门通报节能监测总体情况。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十二条 各区（县）节能主管部门可参照本办法，组织对本区域内指定的重点用能单位开展节能监测工作。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十三条 市、区（县）节能主管部门组织开展的节能监测工作相关经费由同级财政安排，分别列入市、区（县）节能主管部门预算。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第三章 监测结果处理</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十四条 \r\n对节能监测中发现违反《中华人民共和国节约能源法》、《北京市实施〈中华人民共和国节约能源法〉办法》有关规定的用能单位，由市节能主管部门依法进行处理。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第四章 节能监测机构管理</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十五条 节能监测机构由市节能主管部门面向社会公开征集和遴选，并委托市级节能专业机构进行日常管理。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十六条 市节能主管部门对节能监测机构实行定期考评，考评不合格的取消其节能监测机构资格。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十七条 节能监测机构在从事节能监测工作中存在徇私舞弊、违法违规行为的，由相关部门追究法律责任。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第五章 附则</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十八条 本办法由市发展改革委负责解释。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十九条 本办法有效期5年，自发布之日起30日后施行。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　附件：北京市节能监测机构管理实施细则（试行）</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　附件</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">北京市节能监测机构管理实施细则（试行）</span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第一条 为加强节能监测机构管理，保障节能监测工作质量和效果，切实发挥节能监测对节能工作的基础支撑作用，制定本细则。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第二条 节能监测机构，是指具有相应专业资质和技术服务能力，由市节能主管部门面向社会公开征集和遴选的专业机构。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第三条 市节能主管部门遴选、管理节能监测机构，适用本细则。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第四条 市级节能专业机构受市节能主管部门委托，协助组织实施节能监测工作。主要职责是：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）协助市节能主管部门组织对节能监测机构的遴选和业务培训，对节能监测机构提供业务指导；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）协助市节能主管部门制订年度节能监测计划；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）协助市节能主管部门对节能监测机构进行日常管理，对节能监测过程进行监督，对节能监测机构进行考核评价；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）负责汇总、分析全市节能监测数据，编制年度总结分析报告；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）协助市节能主管部门组织节能监测相关规范、标准的起草、制订。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第五条 节能监测机构应符合以下基本条件：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）在本市登记注册，具有独立法人资格或法人授权资格；　</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）取得国家或市级计量认证资质并覆盖常规用能设备节能监测或能效测试项目，计量认证证书在有效期内；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）具备相应的专业技术人员，其中技术负责人应具有高级工程师以上（含）职称；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）具备常规用能设备测试仪器，且仪器设备均检定合格；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）熟悉国家和本市节能相关的法律、法规及技术标准、规范，熟悉重点行业主要生产工艺和流程，熟悉相关节能技术产品，能够独立开展节能监测工作和编制节能监测报告；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（六）具有良好的商业信誉和健全的财务会计制度，具有完善的质量管理体系，具备为政府节能管理部门提供公正、高效服务的能力。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第六条 节能监测机构按照公开、公平、公正和竞争的原则，面向社会公开征集。机构自愿申请，专家评审，由市节能主管部门审定。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第七条 \r\n节能监测机构接受委托任务后，应制定具体实施方案。在实施现场监测前5个工作日，应通知被监测单位和区（县）节能主管部门。现场监测任务结束后，节能监测机构应在10个工作日内出具节能监测报告报送市节能主管部门。节能监测报告应包括用能设备能耗测试结果、能源管理和利用状况综合评价、整改建议等内容。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　节能监测机构完成一个季度的监测工作后10个工作日内向市节能主管部门提交节能监测季度综合分析报告。综合分析报告应包括用能单位用能情况分析、节能潜力分析、存在的问题与改进建议等内容。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第八条 节能监测机构接受市、区（县）节能主管部门委托实施节能监测，应当遵守下列规定：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）严格执行有关节能法律、法规、规章和节能监测技术规程、标准和工作程序，保证节能监测结果的科学性、准确性和客观性；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）按时完成各项节能监测任务，按时提交节能监测报告和综合分析报告，并对报告的真实性负责；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）不得泄露被监测单位的商业秘密；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）不得向被监测单位收取费用；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）法律、法规、规章等规定的其他内容。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第九条 \r\n市节能主管部门对节能监测机构实行动态管理。每年初，市节能主管部门根据上年度工作质量、业绩等对节能监测机构进行考核评价。存在下列情形之一的，取消其节能监测机构资格：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）考核评价结果为“不合格”等级的；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）泄露被监测单位商业秘密等被用能单位举报并经查证属实的；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）违规向被监测单位收取费用的；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）出具虚假节能监测报告的；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）不能按时完成节能监测任务并提交报告、年度内2次出现有明显缺陷报告、或提交的报告达不到节能主管部门要求的。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十条 节能监测机构在节能监测工作中，存在徇私舞弊、违法违规行为的，由有关部门追究其法律责任。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十一条 节能监测工作经费由节能主管部门按有关规定向同级财政申请，纳入同级财政预算安排。</span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<!--End -->', null, '2013-08-23 16:18:47', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('IBneYj', 'aqQrmy', '2', '北京创造·战略性新兴产业创业投资引导基金管理暂行办法', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">关于印发北京创造战略性新兴产业创业投资引导基金管理暂行办法的通知</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">发文单位：北京市发展和改革委员会</span> \r\n</div>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">文</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">号：京发改[2012]694号</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各有关单位：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为加快培育和发展战略性新兴产业，转变政府投入方式，完善产业发展环境，根据国家创业投资基金工作安排及相关管理办法规定，我们制定了《北京创造·战略性新兴产业创业投资引导基金管理暂行办法》，经报市政府同意，现印发给你们，请遵照执行。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　特此通知。</span> \r\n</p>\r\n<p align=\"right\">\r\n	<br />\r\n</p>\r\n<p align=\"right\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　二〇一二年五月十二日</span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span><a href=\"/userfiles/file/20130624/20130624145435_297.doc\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">附件：北京创造·战略性新兴产业创业投资引导基金管理暂行办法</span></a> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<!--End -->', null, '2013-08-23 16:19:03', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('uQB3iq', 'aqQrmy', '2', '关于促进上海新能源产业发展的若干规定', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">市政府办公厅转发《关于促进上海新能源产业发</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">展的若干规定》</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">沪府办发〔2009〕54</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">号</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span><span style=\"font-family:SimSun;line-height:2;\">各区、县人民政府，市政府各委、办、局：</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　市发展改革委、市经济信息化委制订的《关于促进上海新能源产业发展的若干规定》已经市政府同意，现转发给你们，请按照执行。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　上海市人民政府办公厅 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　二○○九年十二月七日</span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">关于促进上海新能源产业发展的若干规定</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为贯彻落实《可再生能源法》，市委、市政府《关于进一步推进科技创新加快高新技术产业化的若干意见》和市政府《关于加快推进上海高新技术产业化的实施意见》，优化上海新能源产业的创新和发展环境，增强上海新能源产业的创新能力和产业竞争力，推动上海新能源产业成为支撑和拉动经济发展的重点领域，根据有关法律、法规及政策，特制定关于促进上海新能源产业发展的若干规定如下： </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　一、确定重点支持领域和对象 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　本规定适用于在本市进行工商注册、税务登记，并从事核电、风电、太阳能发电、整体煤气化联合循环发电（IGCC）和智能电网等领域研发、生产和应用的企业和机构。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　二、明确发展和应用原则 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　根据上海的资源、产业、科技、成本等实际情况，上海在新能源产业发展和装备制造上，坚持“立足长远、统筹规划、高端制造、差别竞争”的原则；在新能源开发应用上，坚持“引领水平、体现示范、促进制造、逐步推广”的原则。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　三、积极支持新能源装备制造业发展 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）制订首台（套）政策。支持符合条件的中资及中资控股风机制造企业根据《风力发电设备产业化专项资金管理暂行办法》，申请国家首台（套）政策资金补助。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　根据《上海市企业自主创新专项资金管理办法》，对经认定的首台（套）风力发电、核电、IGCC、薄膜太阳能电池和智能电网等关键装备，给予资金支持。对个别特重大项目，经市政府批准后，补贴标准可不受最高补贴金额的限制。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）设立专项资金。本市设立的自主创新和高新技术产业发展重大项目专项资金，重点支持企业实施新能源领域的高新技术产业发展重大项目、重大技术装备研制项目、重要共性关键技术研发项目和公共服务平台项目。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　对高新技术产业化重大项目，专项资金的支持比例一般不超过项目新增总投资的10%；对重大技术装备研制项目、重要共性关键技术研发项目和公共服务平台项目，专项资金的支持比例一般不超过项目新增总投资的30%。以上具体通过资本金、贷款贴息、投资补助等方式给予支持。对特别重大的项目，专项资金支持比例需超过上述比例上限或采取特殊支持方式的，另行报市政府同意后确定。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）鼓励技术创新。新能源企业经认定的拥有自主知识产权的高新技术成果转化项目，按有关规定，由本市高新技术成果转化专项资金予以研发支持。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　对新能源企业为提高核心竞争力单纯引进技术和生产母机等装备的项目，或收购兼并境外拥有核心技术的企业和研发机构且获得相对控股权的项目，按本市有关规定给予资助。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　支持和鼓励新能源企业为吸收和创新的技术申请国内外专利。对获得外国发明专利权的，由本市知识产权部门给予每件一个国家最高不超过30000元的专利申请费资助（最多不超过3个国家）；对企业申请国内专利的，按专利申请费实际缴纳费用资助；获国内发明专利权的，对发明专利的实审费、授权费及授权后第二年、第三年的年费，按实际缴纳费用资助。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）给予税收政策支持。支持符合条件的新能源企业申报认定国家高新技术企业、技术先进型服务企业。被认定为国家高新技术企业，减按15%的税率征收企业所得税。自2009年1月1日起至2013年12月31日止，对符合条件的技术先进型服务企业，减按15%的税率征收企业所得税。技术先进型服务企业职工教育经费按不超过企业工资总额8%的比例，据实在企业所得税税前扣除；对技术先进型服务企业离岸服务外包业务收入，免征营业税。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　新能源企业为开发新技术、新产品、新工艺发生的研究开发费用，未形成无形资产计入当期损益的，在按规定据实扣除的基础上，按研究开发费用的50%加计扣除；形成无形资产的，按无形资产成本的150%摊销。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　对新能源企业和个人从事技术转让、技术开发业务和与之相关的技术咨询、技术服务业务所取得的收入，免征营业税。符合条件的新能源产品的技术转让，在一个纳税年度内，技术转让所得不超过500万元的部分，免征企业所得税；超过500万元的部分，减半征收企业所得税。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　对新能源企业投资符合《当前国家重点鼓励发展的产业、产品和技术目录》和《外商投资产业指导目录》鼓励类的项目，在投资总额内进口的自用设备，以及按合同随设备进口的技术及配套件、备件，除列入《国内投资项目不予免税的进口商品目录》和《外商投资项目不予免税的进口商品目录》的商品外，免征关税。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）引进高端人才。积极创造条件，引入国内外优秀的行业领军人才和技术团队，重点实施高层次海外人才“千人计划”。引进的人员可根据相关规定优先解决上海户籍、上海市居住证。每年组织评选认定，加大对领军人才和高层次人才的资助力度。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　从事新能源高新技术转化项目的海外留学生在沪取得的工薪收入，在计算个人应纳所得税时，可按规定加计扣除。新能源高新技术企业和科研院所等用人单位聘用的外籍专家，其薪金可列支成本。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　建立产学研合作机制，通过“校企合作”等方式，加强新能源领域人才培养力度。支持高校和科研机构建设新能源领域的研究基地和创新平台，支持有条件的高等院校设立新能源相关学科和专业。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　新能源领域的国有独资高新技术企业在实施公司制改制时，可按规定将国有净资产增值中不高于35%的部分作为股份，奖励有贡献的企业骨干人员。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（六）加大金融支持力度。根据国家有关要求，设立地方政府创业投资引导基金，借助财政资金的杠杆效应，引导社会资金增加对新能源领域创业企业的投资。探索设立专注于新能源领域的专业创业投资企业，逐步完善有利于创业投资企业投资新能源产业的配套机制。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　积极支持符合条件的新能源企业通过境内外资本市场融资。鼓励新能源企业加大体制机制创新力度，充分利用现有金融资源，通过战略重组、兼并收购等多种方式，进一步转换经营机制、提升产业能级。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　鼓励和引导银行业金融机构进一步加大对新能源企业的信贷支持和金融服务力度，加强新能源产业重大项目的信息沟通，有效降低新能源企业的融资成本，提高信贷审批效率，逐步建立金融支持新能源产业发展的有效管理机制。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（七）吸引设立企业总部。鼓励跨国新能源企业在上海设立地区总部，并按《上海市鼓励跨国公司设立地区总部的规定》给予资助与奖励等政策优惠。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　制定吸引国内企业设立地区总部和研发中心政策，对符合条件在本市新注册及新迁入的内资新能源企业地区总部和研发中心，给予资金支持等政策优惠。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（八）建设产业基地。支持具备条件的区县建设新能源产业基地，鼓励基地完善公共基础设施，建设研发、中试、咨询等公共服务平台。市、区县政府对基地公共建设和重点项目，给予资金、土地、人才等方面的支持，区县政府可因地制宜地，制定本区鼓励新能源产业发展的具体实施意见和办法。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（九）加强国际交流与合作。加强与国际权威检测认证机构合作，建立上海国家级太阳能光伏、风机设备等检测认证中心。鼓励新能源企业开展产品的国际注册与营销，支持企业通过IEC、UL、TUV、GL等国际权威认证。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　构建上海新能源技术交流合作平台、标准化平台和培训平台。发挥中介服务等机构、企事业单位的作用，支持举办新能源技术和产品展览、展示和研讨，推介新能源技术和产品，支持发展新能源产业技术咨询、工程设计、项目管理等服务。支持新能源企业制订相关标准，并根据《上海市标准化推进专项资金管理办法》的相关规定给予资助。支持新能源企业与国外机构合作，组织技术、管理、服务等多领域培训。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　四、加大新能源开发应用的力度 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）组织编制发展规划。鼓励风电、太阳能光伏发电等新能源开发利用，不断提高新能源在能源消费结构中的比重。到2012年，规划本市风力发电装机达500兆瓦，重点建设“三岛”（崇明、长兴、横沙）陆上风电基地和“两海”（临港、奉贤海域）海上风电基地，太阳能光伏发电装机达50兆瓦。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）支持应用项目建设。将新能源重点项目纳入“绿色通道”，在规划、土地指标、环境保护、基础设施配套等方面，优先予以支持。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　在确保防汛安全等前提下，支持风力发电等新能源项目利用具备条件的近海和沿海（江）滩涂、海塘内青坎等开发建设。需要用地的风电开发利用项目，以实际占用土地面积征用土地，按规定程序办理建设用地手续。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　鼓励符合安装条件的公共建筑、工业建筑等安装大型并网太阳能光伏发电系统。其中，政府投资的公共建筑，应在可行性研究报告中论证安装光伏发电系统的可行性，具备安装条件的，必须进行一体化设计；市建设交通、住宅管理部门结合建筑节能工作，每年安排一批项目。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　加快配套电网建设。对经市发展改革部门核准的新能源发电项目，其配套电网项目纳入本市电网建设计划。市电网企业要结合本市智能电网示范工程，加快电网建设与改造，积极提供并网的条件，确保新能源发电全额上网。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　结合太阳能与建筑一体化（BIPV）开发利用，制定相关技术标准和规程。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）给予电价政策支持。根据国家发展改革委制定的《可再生能源发电价格和费用分摊管理试行办法》，积极帮助企业向国家争取最优惠的核准电价。在国家核定电价前，采取临时电价等措施予以扶持。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　积极向国家发展改革委争取电价附加政策设立新能源发电扶持专项资金，研究符合上海实际的新能源发电上网指导价机制。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　对新能源发电项目的上网电价低于发电成本的差额，通过绿色电力资金消化，不足部分，从新能源发电扶持专项资金中支出。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　进一步加强宣传,倡导重点用能单位、居民用户购买“绿色电力”，建立绿色电力资金。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）积极争取落实国家及本市财政资金补贴。根据《太阳能光电建筑应用财政补助资金管理暂行办法》、《关于实施金太阳示范工程的通知》以及《上海市可再生能源和新能源发展专项资金扶持办法》、《上海市建筑节能项目专项扶持管理办法》等，支持符合条件的新能源项目申请中央和本市财政专项资金补助。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）支持应用新技术、新产品。对在本市生产的拥有专利技术的新能源产品或经国家和上海市认定的新能源产品，凡符合相关规定的，支持列入《上海市自主创新产品目录》。对列入《上海市自主创新产品目录》的新能源产品，鼓励财政性资金优先采购；政府支持新建和改造公共建筑时，也予以优先采购。建设一批用于本地新能源产品示范的“试验风电场”、“试验光伏电站”等项目。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　本规定由市发展改革委、市经济信息化委会同有关部门解释。市有关部门可根据本规定和工作需要，制定实施细则。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　本规定自印发之日起执行。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　上海市发展和改革委员会 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　上海市经济和信息化委员会 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　二○○九年十一月十一日&nbsp;</span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span> \r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:19:20', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('3Yz2uy', 'aqQrmy', '2', '天津市扩大脱硝电价试点范围', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">关于扩大我市脱硝电价试点范围的通知</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:left;\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">市电力公司、华北电网有限公司，有关燃煤发电企业：&nbsp;</span> \r\n</div>\r\n<span id=\"zoom\"> \r\n<div style=\"text-align:left;\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">　　根据《国家发展改革委关于扩大脱硝电价试点范围有关问题的通知》（发改价格[2012]4095号），为促进燃煤机组脱硝设施建设，提高发电企业脱硝积极性，结合我市实际，现将扩大脱硝电价试点范围的有关事项通知如下：&nbsp;</span> \r\n</div>\r\n<span style=\"font-family:SimSun;line-height:2;\"> \r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:2;\">　　一、按照国家规定，决定从2013年1月1日起，将本市脱硝电价试点范围由现行三个发电企业的五台燃煤机组，扩大为所有的燃煤发电机组。&nbsp;</span> \r\n</div>\r\n</span><span style=\"font-family:SimSun;line-height:2;\"> \r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:2;\">　　二、凡燃煤发电机组脱硝设施运行正常、具备在线监测功能，持环保部门验收合格文件报我委审核后，执行脱硝电价。脱硝电价标准为每千瓦时8厘钱。&nbsp;</span> \r\n</div>\r\n</span><span style=\"font-family:SimSun;line-height:2;\"> \r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:2;\">　　三、发电企业执行脱硝电价后，市电力公司、华北电网有限公司由此增加的购电资金，国家将择机在销售电价中予以解决。&nbsp;</span> \r\n</div>\r\n</span><span style=\"font-family:SimSun;line-height:2;\"> \r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:2;\">　　四、各燃煤发电企业，要严格按照国家和本市要求运行脱硝设施，提高机组投运率、减少氮氧化物排放浓度等指标，稳定烟气脱硝在线监测系统，建立脱硝设施运行台帐。&nbsp;</span> \r\n</div>\r\n</span><span style=\"font-family:SimSun;line-height:2;\"> \r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:2;\">　　环保部门要定期督察燃煤机组脱硝设施运行情况，进一步细化脱硝设施考核指标。价格主管部门要加强对脱硝电价的监督检查，依法查处价格违法行为。&nbsp;</span> \r\n</div>\r\n</span> \r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:SimSun;line-height:2;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-family:SimSun;line-height:2;\">天津市发改委</span><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span id=\"zoom\"><span style=\"font-family:SimSun;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family:SimSun;line-height:2;\">2013年1</span><span style=\"font-family:SimSun;line-height:2;\"></span><span style=\"font-family:SimSun;line-height:2;\">月11日 &nbsp;</span></span><span style=\"font-family:SimSun;line-height:2;\"></span> \r\n</p>\r\n<p style=\"text-align:right;\">\r\n	<br />\r\n</p>\r\n</span> \r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:20:06', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('ArYbUb', 'aqQrmy', '2', '江苏省新兴产业创业投资引导基金评审暂行办法', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">于印发《江苏省新兴产业创业投资引导基金评审暂行</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">办法》的通知 苏发改规发〔2011〕8号</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"Custom_UnionStyle\">\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各市、县发展改革委，各有关单位：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为了做好省级新兴产业创业投资引导基金的评审工作，根据《国务院办公厅转发发展改革委等部门关于创业投资引导基金规范设立与运作指导意见的通知》（国办发〔2008〕116号）和省政府办公厅转发省财政厅、省发展改革委《江苏省新兴产业创业投资引导基金管理办法》（苏政办发〔2010〕153号），我委研究制定了《江苏省新兴产业创业投资引导基金评审暂行办法》，经委主任办公会讨论通过，现予以印发。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　附件：江苏省新兴产业创业投资引导基金评审暂行办法</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　                                                                                                                                 \r\n江苏省发展和改革委员会</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　 </span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">二○一一年七月二十九日</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">江苏省新兴产业创业投资引导基金评审暂行办法</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第一条　为了规范省新兴产业创业投资引导基金的评审工作，确保决策的民主性和科学性，根据《国务院办公厅转发发展改革委等部门关于创业投资引导基金规范设立与运作指导意见的通知》（国办发〔2008〕116号）和《江苏省新兴产业创业投资引导基金管理办法》（苏政办发〔2010〕153号）等有关规定，制定本办法。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第二条　江苏省新兴产业创业投资引导基金（以下简称“引导基金”）是由省政府设立的政策性引导基金，旨在发挥财政资金的杠杆效应，增加创业投资资本供给，有效引导创业投资企业投向新兴产业，培育壮大战略性新兴产业规模，推进全省经济结构调整和产业升级。引导基金存续期开展的各项评审工作适用本办法。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第三条　评审工作遵循公开、公平、公正的原则。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第四条　根据《江苏省新兴产业创业投资引导基金管理办法》，省发展改革委牵头设立江苏省新兴产业创业投资引导基金评审委员会（以下简称“评审委员会”）。评审委员会的主要职能为：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）建立引导基金评审专家库；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）受理市、县（开发区）发展改革和财政部门的申请方案、受理创业投资企业或创业投资管理机构的申请方案，并进行合规性初选；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）根据需要开展相关调查；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）组织专家进行独立评审；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）向引导基金管理委员会报送专家评审意见。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第五条　引导基金评审专家库由政府有关部门、资深创投行业专家、行业自律组织代表、高校或相关研究机构的专家组成。评审专家应具备以下基本条件：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）熟悉新兴产业发展规划，对申请合作的创业投资企业拟投资的产业领域具有较为深入的了解；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）熟悉创业投资领域及相关市场情况，有较为丰富的实际工作和管理经验；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）在行业内有一定的权威性和代表性；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）在社会上有较好的信誉；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（五）与申报单位无直接或间接的利害关系。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第六条　评审委员会与省新兴产业创业投资引导基金管理委员会办公室（以下简称“管委会办公室”）共同制定引导基金年度申报指南。评审委员会根据申报指南明确的申报时间和扶持方式，确定评审时间和评审重点。评审委员会对申报材料的完整性、合规性进行初步审查，对进入初选名单的创投机构和市县（开发区）级引导基金的申请材料出具初审意见。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第七条　评审委员会可以根据需要组织或委托专业机构，对初步审查合格的合作方案进行现场调查。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第八条　专家评审由每5名专家组成评审小组。评审小组成员从评审专家库中随机抽取，应至少包含3名省内外创投行业专家及高校或相关研究机构的专家。评审专家根据申报材料、初审意见和调查报告对申请扶持方案进行独立打分，形成专家评审意见。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第九条　引导基金运作方式包括与市县（开发区）合作、阶段参股、跟进投资、风险补助和投资保障。根据申请省级引导基金扶持的不同方式，设立相应评审要点。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十条　市县（开发区）申请与省级引导基金合作设立政策性引导基金，其评审要点为：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）拟合作设立引导基金的募资规模、条件和时间；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）拟合作设立引导基金的投资方向、阶段和地域。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）拟合作设立引导基金的组织构架、投资决策、风险控制机制和管理机构；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）申请合作的市县（开发区）新兴产业和创业投资发展情况，申请合作方案的可行性。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十一条　阶段参股是指创业投资企业或管理机构申请引导基金进行参股，吸引社会资本共同发起设立创业投资企业，并在约定期限内退出，其评审要点为：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）创业投资企业或管理机构的募资能力，包括已管理资金的规模，拟合作设立创业投资企业的资金募集情况；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）创业投资企业或管理机构的治理结构，包括决策机制、激励机制及风险管理机制；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）创业投资企业或管理机构的团队管理能力，具有至少三名从事创业投资或相关业务五年以上的专业管理人员和核心业务人员，具有较好的创业投资管理业绩；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）拟设立基金的投资计划、项目储备情况和提供增值服务的能力等；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十二条　跟进投资是指创业投资企业选定投资符合相关要求的创业企业，可以申请引导基金按适当股权比例共同投资，其评审要点为：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）申请引导基金跟进投资的理由和金额等；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）申请跟进投资的创业投资企业财务情况、资产情况等；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）申请跟进投资的创业企业情况，包括创业企业上年度的财务情况和资产情况；创业投资企业编制的《投资建议书》或《可行性研究报告》、创业投资企业与创业企业或其股东签订的《投资意向书》等。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十三条　风险补助是指创业投资企业对初创期创业企业投资，发生损失后可以申请风险补助，其评审要点为：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（一）申请引导基金风险补偿的理由和金额（引导基金按照最高不超过创业投资企业实际投资额的5%给予风险补助，补助金额最高不超过500万元人民币）；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（二）创业投资企业的基本情况，包括财务情况、税收缴纳情况和资产情况；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（三）创业投资企业投资情况，包括创业投资企业已投资全部项目情况，对初创期企业的投资协议书等；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　（四）申请风险补偿项目情况，包括被投资创业企业符合初创期条件的证明文件、创业企业年度财务情况、资产情况、以清算或协议方式退出被投资企业的证明材料等。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十四条　引导基金用于投资保障资助资金的来源为引导基金投资收益，其评审要求另行规制定。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十五条　参加评审工作的专家应当客观、公正地履行职责，严格遵守保密规定。一旦发现评审专家有违反规定的行为，取消其专家资格。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十六条　经批准同意与省级引导基金合作的市县（开发区）引导基金，应参照省级引导基金评审办法，建立专家评审制度。市县（开发区）与省合作成立的政策性引导基金评审通过的扶持方案应同时向省引导基金管委会办公室和评审委员会报备。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　第十七条　本办法自</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">二○一一</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">年九月一日起施行。</span> \r\n	</p>\r\n</div>\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span> \r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:20:27', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('3e2iIj', 'aqQrmy', '2', '山东省加快培育和发展战略性新兴产业', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">山东省人民政府关于加</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">快培育和发展战略性新兴产业的实施意见</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">鲁政发〔２０11〕15号</span> \r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各市人民政府，各县（市、区）人民政府，省政府各部门、各直属机构，各大企业，各高等院校：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为贯彻落实《国务院关于加快培育和发展战略性新兴产业的决定》（国发〔2010〕32号），推进我省战略性新兴产业加快发展，现制定如下实施意见：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　一、提高认识，切实增强培育和发展战略性新兴产业的紧迫感</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　1培育和发展战略性新兴产业是加快转变经济发展方式的必然选择。培育和发展战略性新兴产业是顺应世界科技革命和结构升级潮流的客观要求；是提高产业核心竞争力，抢占发展制高点的迫切需要；是建设资源节约型、环境友好型社会，促进可持续发展的重大举措；是推进产业结构优化升级、提高经济增长质量和效益，再造山东发展新优势的战略选择；是建设经济文化强省，全面实现小康社会奋斗目标的必由之路。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　二、统一思想，明确培育和发展战略性新兴产业的总体要求</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　2指导思想。以邓小平理论和“三个代表”重要思想为指导，深入贯彻落实科学发展观，顺应国际科技革命和产业升级的新趋势，把握我省经济社会发展阶段的新特征，围绕加快转变发展方式、调整优化经济结构新目标，实施高端高质高效新战略，突出发展新材料、新一代信息技术、新医药、新能源和海洋开发等重点产业，加强人才队伍建设，完善创新平台载体，深化体制机制改革，强化对外开放合作，加大政策资金扶持，营造良好发展环境，优化资源要素配置，促进高技术产业化，培植具有自主知识产权的特色产业链和产业集群，扩大高端市场份额，带动相关产业发展，推动全省经济社会在更高层次上实现又好又快发展。 \r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　3基本原则。加快培育和发展战略性新兴产业，必须坚持统筹规划，科学布局；重点突破，区域带动；科技先导，创新驱动；产业集聚，链条延伸；政府引导，市场运作；整体推进，配套联动。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　4发展目标。到2015年，全省战略性新兴产业增加值占地区生产总值的比重达到10%,对经济发展和产业结构升级的带动作用显著增强。到2020年，全省战略性新兴产业增加值占地区生产总值的比重力争达到20%，成为带动经济增长、产业升级和劳动就业的重要力量。其中新材料、新医药、新能源和节能环保、海洋开发和高端装备制造产业成长为全省国民经济的支柱产业，新一代信息技术、生物产业成长为全省国民经济的先导产业；科技创新整体水平居国内前列，培植一批具有国际影响力的大企业和极具产业竞争力的中小企业，成为我国战略性新兴产业创新和生产的重要基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　三、突出重点，推动战略性新兴产业快速健康发展</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　5新材料产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——高性能纤维。充分发挥我省高性能纤维材料的技术优势和产业基础，突破关键技术，开发成套装备，生产高端制品，打造碳纤维、芳纶、超高分子量聚乙烯纤维、玻璃纤维四大产业链，满足航空航天、高端装备制造等领域的需求，建设我国重要的高性能纤维研发和生产基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——特种高分子材料。重点开发TDI、新一代MDI及HDI等异氰酸酯生产工艺及成套生产设备，加快实现聚氨酯主要原料尤其是高附加值异氰酸酯的产业化，拉长产业链，打造以万华工业园为重要载体的聚氨酯研发生产基地。重点开发含氟聚合物和膜材料，加快系列产品产业化，推广氯碱离子膜的应用，推进燃料电池膜产业化，拉长产业链，带动相关产业的发展，打造以东岳为龙头的含氟聚合物及膜材料特色产业链。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——特种金属材料。积极开发新型合金功能材料，加快镁铝合金、硅铝键合丝、高性能电子铜箔、环氧玻璃布基覆铜板、集成电路用低弧度金丝、纳米金等新型材料的技术开发和产业化，优化产品结构，提高产品附加值，培植高性能金属材料产业集群。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——精品钢新型材料。重点发展高强度轿车用钢、高档电力用钢和工模具钢、高速铁路用钢、高等级造船及海洋工程用钢等关键钢材新品种，开展百万千瓦火电及核电用特厚钢板和高压锅炉管、25万千伏安以上变压器用高磁感低铁损取向硅钢等技术攻关，促进建筑钢材升级换代，着力打造精品钢材产业基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——功能陶瓷。加强信息功能材料、纳米尺度薄膜与粉体材料、能源与生物功能材料、磁性电子材料等陶瓷材料的技术开发，积极推进无铅、无镉以及纳米基瓷料等节能环保陶瓷的创新和产业化，建设我国重要的功能陶瓷研发生产基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　6新一代信息技术产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——通信网络设备与产品。加快建设宽带、泛在、融合、安全的信息网络基础设施，支持新一代移动通信、下一代互联网核心设备和智能终端的研发及产业化，加快推进三网融合，促进物联网、云计算的研发和示范应用。开展量子通信技术研发，突破量子通信网络系统集成关键技术，开发远距离、高速率量子通信实用化核心技术，形成具有自主知识产权的技术及产品，加快其在政府、金融等领域的推广应用。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——集成电路。以集成电路设计为突破口，以济南国家集成电路设计产业化基地为载体，以通信、计算机、工业控制、信息安全和应用电子产品为依托，培育一批具有自主创新能力的集成电路设计企业，开发一批具有自主知识产权的集成电路产品，积极发展集成电路制造、封装、测试和相关产业，形成配套完善的集成电路产业链。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——高端计算机。重点支持高性能计算、云计算、面向微处理器的计算机体系结构、嵌入式和高可信计算等产品的研发，加快发展高端容错、工业控制和高性能计算机；研发高效能、高可靠性、高安全性服务器，巩固发展我省高端服务器的领先地位；开发计算能力千万亿次、存储容量为PB级的高性能计算和存储环境，建设高性能计算中心；支持具有自主知识产权的信息安全产品研发及产业化。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——新型显示。超前布局新型平板显示产业，搭建OLED \r\n(有机电致发光二极管)关键技术开发和工程化平台，以AMOLED（有源矩阵有机发光二极管）为重点，突破AMOLED基板、真空镀膜、薄膜封装等共性技术，支持AMOLED面板产业化；集中力量突破数字视频压缩编解码芯片、数字视频处理芯片等关键技术；重点发展液晶电视、OLED电视、激光电视、三维电视等数字电视。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——高端软件。加快研发工业软件、中间件和基础软件，积极发展嵌入式操作系统、嵌入式软件开发平台等核心软件；积极开展基于物联网、云计算环境下的新型软件业态和关键技术研究；加快研发网络通信、信息安全、数字音视频、智能控制、汽车电子等重点领域嵌入式软件；建设软件和服务外包产业基地，努力提升我省软件产业的规模和水平。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　7新能源和节能环保产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——太阳能。发挥骨干企业优势，加大自主创新力度，加快突破光热利用、系统集成等一批关键技术，掌握一批先进工艺，开发推广一批光热利用和光伏发电新产品，打造太阳能热利用和光伏发电两大产业链，建设济南、德州两大太阳能产业基地，打造潍坊、东营、威海、菏泽太阳能产业集群。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——风能。鼓励实施风能资源规模化开发，加快推进大型风电装备的研发和产业化，重点发展大容量风电机组、发电机，以及机械传动、运行控制等子系统，形成从材料、叶片、塔筒、主轴、控制系统、发电机到风电机组的产业链，建设风电装备制造基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——核能。积极研发新一代核能技术和先进反应堆，加快海阳、荣成核电站建设，发挥山东核电、烟台台海玛努尔等骨干企业的优势，开展重大技术攻关，加强系统设计和设备研发制造，培育具有自主知识产权的核电装备品牌，着力打造烟台、威海两个核电装备制造基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——生物质能。重点发展以废弃油脂等为原料的液体燃料，以秸秆等为原料的生物质燃气和成型燃料；加快突破生物沼气利用关键技术，建设一批沼气综合利用示范工程，鼓励在规模化养殖场、工业有机废水处理和城市污水处理厂等有条件的地方，建设沼气发电站。大力支持生物质锅炉、物化转换成套装备、沼气发电机组等生物质能装备的研发制造。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——智能电网。加快适应新能源发展的智能电网建设，推进关键技术和装备的突破和应用，支持电网企业进行智能化电网改造，实施新能源和智能电网融合工程，实现新能源发电的安全、可靠、稳定运行。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——新能源汽车。支持新能源汽车和关键零部件的研发制造，尽快突破电池阳极材料、电池隔膜及以锂离子电池为主的大容量电池产业化技术，重点发展纯电动和油电混合型公交客车、电动载货车、多功能特种车、电动轿车等新能源汽车，加大新能源汽车的示范和推广力度，建设北汽福田、中通等新能源汽车整车基地，带动汽车零部件及相关产业的发展。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——节能技术及装备。加快突破一批重点领域节能关键技术，围绕重大节能工程，大力推广高效节能技术及装备；开发推广余热余能利用、能量系统优化、建筑节能、节能机电、半导体照明等节能技术及产品，带动能效整体水平提高。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——环保技术及装备。鼓励研究开发“三废”综合治理关键技术和装备，加大先进环保技术装备及产品示范推广力度。实施工业废弃物综合利用工程，对钢铁、发电、焦炭、化工、造纸、印染、发酵等重点行业的环保设施进行技术改造，促进污水、废气以及固体排放物的回收处理和综合利用。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——循环经济关键技术及装备。围绕减量化、再利用、资源化，重点在化工、建材、冶金、有色等行业实施一批循环利用示范工程，积极推广工业余热、工业废水、高炉煤气、废旧设备等资源的循环利用新技术，示范推广一批循环经济关键装备，提高企业的资源利用效率。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　8新医药和生物产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——新医药。以原料药、化学创新药、生物技术药、疫苗与诊断试剂、现代中药、海洋药物和新型医疗器械等领域为重点，加快实施一批技术含量高、市场急需的重点项目，加快建设山东国家创新药物孵化基地和综合性新药开发技术大平台，加快建设鲁中（济南、淄博、潍坊）、鲁南（济宁、枣庄、临沂、菏泽）、半岛（青岛、烟台、威海）新医药产业聚集区，推动医药产业上规模、上水平，做大做强我省医药产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——生物育种。利用现代生物技术，加快培育一批高产优质抗逆的小麦、玉米、棉花、花生、马铃薯等重要粮棉作物良种，一批优良果蔬品种，一批优良畜禽品种和一批名优特稀的鱼虾贝参等优良水产品种，大力实施良种工程，发展现代种业，建设全国重要的生物育种基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——生物制造。充分发挥农副产品资源优势，应用优良菌种选育、生物发酵、在线控制、高纯度过滤、新型节能环保等先进技术，努力实现循环经济和清洁生产，着力培植玉米、小麦、大豆、海洋副产物等深加工产业链，开发生产系列食品、化学品和新型生物材料制品，加快发展生物酶工程产业，实现生物制造大省到强省的跨越。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　9海洋开发产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——海洋开发重大基础设施。加快建设海洋科学综合考察船、国家深海基地等重大科学工程和青岛海洋科学与技术国家实验室（筹）、中集烟台海洋工程研究院等一批创新平台，把青岛建设成为我国海洋科技开发和资源勘察基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——海水健康养殖。综合运用海水增养殖和疾病控制新技术，建设海水健康养殖基地和海洋牧场，推动我省传统渔业向以资源节约、环境友好、生态和谐、产品安全为标志的现代渔业转变。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——海洋精细化工。加快发展以海水淡化、海水提取溴、镁等为主的海水综合利用工程，生产溴系阻燃剂、医药中间体、染料中间体等深加工产品，完善海洋化工产业链，建成我国重要的海洋精细化工产业基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——海洋工程技术及装备。重点开发海洋焊接、建筑、防腐、污染清除等工程技术和专用材料，加快发展海洋油气开发装备、临港机械装备、海水淡化装备、海洋电力装备、海洋勘测海底布缆装备、海洋仪器装备等产业，建设国家海洋设备检测中心，实施以海洋水文预报、海况预测预报、海底通信及现代海洋观测为主的“数字海洋”工程，把胶东半岛打造成具有国际竞争力的海洋装备制造业基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——高端船舶制造。重点发展以重载集装箱船、大吨位客货滚装船、豪华游艇、邮轮等为主的特种船舶和船用配套产品，把半岛蓝色经济区建设成为特种船舶制造基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——海底资源勘探开发。重点发展深（远）海环境监测、资源勘查技术与装备，深海运载和作业技术与装备成果的应用；推进深海生物基因资源利用技术开发及产业化；开发多金属结核、结壳、热液硫化物开采技术和装备；形成具备深（远）海空间利用技术的集成与服务能力的国家深海开发基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——海洋可再生能源利用。积极开展潮汐能、波浪能、海流能、海洋风能区划及发电技术集成创新和转化应用。重点发展万千瓦级潮汐电站、百千瓦级波浪和海流能机组技术及相关设备的产业化，鼓励开发温差能综合海上生存空间系统，推广应用海洋生物质能技术，建设海洋生物质能开发利用试验基地，提高能源保障能力。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　10高端装备制造产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——数控加工装备。重点发展二次开发功能的开放式数控控制系统、嵌入式软件、高档数控加工刀具、车铣钻镗磨等多种工艺复合的加工中心、工业机器人等数控加工装备，提升智能化控制装备水平和核心竞争力。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——轨道交通设备。依托青岛高速列车国家高新技术产业化基地、北车济南轨道交通公司，突破空气动力学分析、牵引传动与制动、运行控制、轨道结构、关键材料及部件等关键技术，开发新一代高速列车、轻轨、地铁等轨道交通装备，带动相关产业发展。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——通用航空装备。抓住低空空域管理改革机遇，研发制造飞机整机、关键配套零部件和地面装备等重点产品，突破一批关键技术，建设特色鲜明的通用航空装备研发、制造和服务基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　——石油装备。重点发展新型石油勘探、钻井、采油、集输等系列产品和深海半潜式钻井平台等大型化、深海化、专业化油气装备，建设高端石油装备制造业基地。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　四、强化科技创新，提升产业核心竞争力</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　11加强产业关键核心技术研究。准确把握世界新技术发展趋势，围绕战略性新兴产业发展的重大需求，发挥政府各类科技专项的引领作用，立足自主创新，鼓励技术引进、消化、吸收、再创新，集中力量突破一批支撑战略性新兴产业发展的关键共性技术，形成一批具有自主知识产权的技术和标准，提升产业技术水平。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　12加快科技成果产业化。完善科技成果产业化机制，建立健全科研机构、高等院校的创新成果发布制度和技术转移机构，加快建设科技成果中试基地、孵化基地，促进技术转移和扩散；设立战略性新兴产业发展专项，积极推进产业化示范工程，缩短成果转化周期，加快产业化进程。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　13加强创新平台建设。围绕战略性新兴产业发展需求，依托优势技术领域，支持企业围绕原始创新、集成创新和引进消化吸收再创新，加强与国内外知名大型企业、高等院校、科研机构的合作，建设一批重点实验室、工程实验室、工程（技术）研究中心、企业技术中心等国家级和省级创新平台，以企业为主体建设一批院士工作站和博士后工作站。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　14强化企业技术创新主体地位。加强财税政策引导，激励企业增加研发投入，不断提高高新技术企业研发投入占销售收入的比重，力争到2015年国有大中型企业、高新技术企业研发投入占销售收入的比重分别达到3%和5%以上；对面向应用、具有明确市场前景的政府科技计划项目，建立由骨干企业牵头组织、科研机构和高等院校共同参与实施的有效机制，引导和支持创新要素向企业聚集。发展一批由企业主导，科研机构、高等院校积极参与的产业技术创新联盟；制定有利于战略性新兴产业企业增加研发投入的政策，提高企业核心竞争力。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　15建设产业创新支撑体系。发挥知识密集型服务业支撑作用，加强服务体系建设，创新管理模式和运营机制，大力发展研发服务、设计服务、信息服务、创业服务、风险投资服务、技术交易、知识产权和科技成果转化等高技术服务业，加快培育新型服务业态，为产业创新提供强有力支撑。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　16加快落实知识产权战略。支持知识产权创造和运用，强化专利、商标、版权等知识产权的保护和管理，依法保护科技人员的合法权益，鼓励企业建立专利联盟。完善高等院校和科研机构知识产权转移转化的利益保障和实现机制，建立高效的知识产权评估交易机制，加大对具有重大社会效益创新成果的奖励力度。鼓励我省企业参加各类标准制定，对参与或牵头制定战略性新兴产业国内和国际标准的，在安排科技专项资金时优先给予支持。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　17加强人才队伍建设。树立人才优先发展的战略理念，实行高层次人才无障碍引进等政策，以战略性新兴产业重大工程和项目为载体，实施 \r\n“千人计划”、“万人计划”和“泰山学者”等一系列高层次人才培养和凝聚工程，重点引进一批省外和海外的创新创业领军人才，尽快形成山东战略新兴产业的人才高地。加强人才培养和培训，调整优化人才培养方向和专业设置，加强企业在职人员的培训力度，鼓励企业与高等院校、科研院所的人才共建，促进创新型、应用型、复合型、技能型人才的培养。创新人才选拔和使用机制，引导企业建立科学合理的薪酬制度和完善的人才激励政策，鼓励企业实行股权、期权、技术入股等多种形式的激励机制，促进各类人才向战略新兴产业集聚。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　18加快创新型省份建设。抓好济南、青岛、烟台创建国家级创新型城市试点，带动全省创新发展。加大科技投入，加快科技成果转化，加强自主创新，到2020年，力争全社会研发投入占地区生产总值的比重达到25%以上，科技进步贡献率达到60％以上，对外技术依存度降低到30％以下，发明专利年度授权量和国际科学论文被引用数均进入全国前5位，基本建成创新型省份。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　五、深化开放合作,拓宽发展新空间 </span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　19强化国际科技合作与交流。把握经济全球化的新特点，积极探索国际合作新模式，拓宽交流新渠道，在更高层次上参与国际合作，尽快掌握关键核心技术，提升我省自主发展能力与核心竞争力。完善合作机制，深化中德、中乌、中俄、中法、中白、中古等科技合作，支持我省高等院校、科研院所与国外开展多种形式的合作；积极吸引跨国公司、国外研究机构和高等院校到我省以独资、合资、合作等形式设立研发机构；支持符合条件的外资企业与内资企业、研究机构合作申请国家、省级科研课题和重大专项；鼓励海外人才携科技成果到我省进行产业化；吸引具有高层次的学术带头人和国际知名专家到我省兼职。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　20 \r\n提高利用外资的产业技术水平。拓宽利用外资渠道，加大招商引资力度，引导更多的外资投向战略性新兴产业。鼓励外商到我省设立创业投资企业，着力引进世界500强和国际知名企业到我省投资创业，设立研发机构；鼓励外资企业到我省设立合作基地和产业园区，促进产业集聚，带动相关产业发展。依托济南、青岛、烟台等城市，大力发展软件、医药等服务外包产业。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　21开展全方位的国内合作与交流。加强与中国科学院、中国工程院、北京大学、清华大学等科研机构和重点高等院校的合作，积极推动国家级科研院所在我省设立研发机构和分中心，加快筹建中科院山东分院。鼓励国内各研发机构申报和参与山东省重大科研和工程项目，促进省外科研机构、企业与省内有关机构合作，加强与长江三角洲、珠江三角洲、环渤海和北京、上海、南京、武汉、西安等地区和城市的对接与合作。 \r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　22支持企业跨国经营。鼓励我省企业到发达国家和地区，通过境外收购、设立研发机构等多种形式，充分利用国际创新平台，共享人才、信息、技术等各类资源，运用先进的研发手段，不断开发先进技术和产品，培育国际品牌，为国内企业跨越发展提供支撑。支持我省高技术企业到境外开发战略资源，设立海外生产基地，建立全球营销网络，拓宽发展空间，提高我省战略性新兴产业出口产品的国际市场份额。组织企业联合走出去，参与国家境外经贸合作区建设。建立健全我省拟境外投资企业信息库，定期发布相关信息，为省内企业境外投资和开展合作提供服务。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　六、完善政策措施，为产业发展提供有力支撑</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　23进一步加大财政支持力度。省级财政进一步优化现有专项资金支出结构，每年支持战略性新兴产业的资金不少于10亿元，重点支持关键共性技术研发、产业化示范工程、创新能力建设、产业链和产业基地培育等。各地也要结合实际，进一步加大支持力度，形成上下配套联动的资金投入机制。认真落实增值税转型、所得税减免、进口设备免税、研发费用加计扣除等现有税收优惠政策。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　24引导金融机构加大对战略性新兴产业的信贷支持。引导商业银行调整优化信贷结构，积极推进知识产权质押融资、产业链融资等新模式。每年筛选确定一批省级项目、重点产业链和重点产业基地，搞好与金融机构的对接，争取更多的信贷资金扶持。积极争取政策性银行的扶持，鼓励政策性信贷资金向战略性新兴产业倾斜。进一步健全信用担保体系，引导设立一批专业担保公司，扩大担保资金总量，为战略性新兴产业发展融资提供担保服务。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　25扩大直接融资规模。加快战略性新兴产业企业境内外上市步伐，支持自主创新能力强、成长性好的企业到主板、中小企业板或创业板上市，鼓励符合条件的企业到境外上市。选择一批信用等级高、还本付息能力强的企业发行企业债券、公司债券、短期融资券和中期票据等；推动产业链上下游企业或区域聚集企业发行企业集合债和集合票据。鼓励非上市企业充分利用场外交易市场进行股权交易，完善发展全省统一的股权场外交易市场，推动一批高风险、高收益、尚不具备上市条件的战略性新兴产业企业在场外交易市场挂牌融资。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　26加快推动创业投资发展。设立一批战略性新兴产业创投基金，特别是积极争取设立中央财政参股的战略性新兴产业创投基金。鼓励有条件的市、县设立地方财政参股的创投基金。鼓励黄河三角洲产业投资基金、山东半岛蓝色经济发展投资基金等产业投资基金向战略性新兴产业倾斜。吸引央企、民企、外资等各类社会资本在我省设立创投基金，助推我省战略性新兴产业加快发展。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　27实行优惠的土地政策。各级政府应优先向战略性新兴产业项目供地，对属于优先发展产业且用地集约的战略性新兴产业项目，在确定土地出让底价时，可按不低于所在地土地等别相对应工业用地出让最低价标准的70%执行。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　28优化战略性新兴产业发展布局。深入实施重点区域带动战略，支持山东半岛蓝色经济区、黄河三角洲高效生态经济区、省会城市群经济圈和鲁南经济带，发挥优质资源富集地带的优势，尽快建成国内一流的战略性新兴产业聚集区。以高新技术开发区、经济开发区、可持续发展实验区、出口加工区、保税区、高技术产业基地等为载体，引导战略性新兴产业投资项目向园区集中，避免盲目布局、低水平重复建设。培植一批优势突出、特色鲜明、技术先进、配套发展的产业集群，打造一批创新能力强、带动作用大、经济效益好的高端产业链条，促进战略性新兴产业聚集式发展。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　29积极拓展新兴产业市场空间。制定全省战略性新兴产业产品推广应用计划，有序开展新产品应用试点示范。发挥政府采购的拉动作用，调整政府采购目录，优先采购战略性新兴产业新产品。及时发布国际市场需求变化信息，引导企业主动规避贸易壁垒、汇率波动等风险。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　30进一步优化发展软环境。加快政府职能转变，推进作风和效能建设，强化服务意识，提高服务效率，对列入战略性新兴产业规划的重点企业和重点项目提供“一站式”服务。及时总结宣传好典型、好经验、好做法，介绍国家和省扶持战略新兴产业发展的相关政策和规定，营造良好的社会氛围，创造良好的市场环境。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　七、推进体制机制创新，增强发展活力</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　31建立企业主动追求创新的内在机制。深化国有企业改革，积极引进高技术的战略投资者，采取股权转让、增资扩股、合资合作等多种形式进行资产重组，优化股权结构，建立现代企业制度，增强企业活力。引导国有资本向重点战略性新兴产业集中，向科技、人才、品牌等优势企业集聚。放宽市场准入，改善融资条件，搞好配套服务，加快发展创新型民营企业。鼓励民营企业参与国有大型企业和垄断行业企业的改组改造、并购联合，使民营科技企业成为发展战略性新兴产业的重要力量。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　32创新产学研合作机制。扶持应用型科研院所转换机制，主动与企业建立紧密的经济、科技合作关系，兴办研发机构成果转化基地，采取技术入股等方式，联合兴办创新型企业，形成资源共享、技术共创、风险共担、合作共赢的新机制。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　33推进综合配套改革。深化行政审批、财税、投融资、土地等重点领域改革，创新政府管理模式，为战略性新兴产业发展提供强有力的保障。加快市场体系建设，促进生产要素合理流动和资源优化配置。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　八、加强统筹协调，为战略性新兴产业发展提供组织保障 </span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　34加强组织协调。建立由省发展改革委牵头的战略性新兴产业发展部门协调机制,形成合力，统筹推进。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　35建立科学决策机制。成立山东省战略性新兴产业专家咨询委员会，提高决策的科学性、针对性和有效性。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　36强化规划引导。组织编制战略性新兴产业发展规划及专项规划，加强与相关规划和政策的衔接，提高规划的科学性、指导性和可操作性。加强政策引导，合理配置人才、资金、土地等各类要素，发挥比较优势，提高综合效益。 \r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　37建立考核制度。尽快建立反映战略性新兴产业发展变化的统计指标体系，定期检查，及时督促，确保省政府提出的政策措施、任务目标落到实处。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　各级人民政府和省直有关部门要抓紧制定具体措施，切实推动战略性新兴产业快速健康发展。 </span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">二○一一年四月二十五日</span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<!--<$[标题]>end-->', null, '2013-08-23 16:21:30', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('qeQbUn', 'aqQrmy', '2', '福建重点推进305个海洋经济项目 ', null, '<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp; &nbsp; 近日，省发展改革委、省海洋经济办印发福建省海洋经济重大项目建设实施方案，确定重点推进305个海洋经济重大项目建设，其中在建项目183个，总投资1638.85亿元;前期项目122个，预计投资2079.75亿元。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　据悉，我省重点推进的海洋经济重大项目涉及的领域包括海洋工程装备和高端船舶修造、邮轮游艇、海洋生物医药、海水淡化与综合利用、海洋可再生能源、现代海洋渔业、海洋旅游和文化创意、航运物流、海洋产业园区、海洋科研平台、渔港建设、港口泊位建设。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　方案明确了2013年项目建设的工作目标及主要推进措施，提出全年完成投资241.68亿元，建成或部分建成74个项目，重点推进58个项目前期工作取得实质性进展。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:22:41', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('BnIjeu', 'aqQrmy', '2', '湖北省政府重大投资项目公示试行办法', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">湖北省发展和改革委员会关于印发《湖北省政府重大投资项目公示试行办法》的通知</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">鄂发改投资[2012]1321号</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div align=\"left\">\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各市、州、直管市、神农架林区发展改革委，省直有关部门：</span> \r\n	</p>\r\n	<p>\r\n		<span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 为贯彻落实</span><a href=\"http://www.sdpc.gov.cn/zcfb/zcfbtz/2010tz/t20110117_391052.htm\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><strong>《国家发改委关于实行政府重大投资项目公示工作的指导意见》</strong></span></a><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（发改投资[2010]3131号）和投资体制改革精神，切实做好我省政府重大投资项目公示工作，省发展改革委制定了《湖北省政府重大投资项目公示试行办法》，经委主任办公会议审议通过，现印发给你们，请结合实际，遵照执行。</span></span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 附件：《湖北省政府重大投资项目公示试行办法》</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p align=\"right\">\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">二〇一二年十一月十三日</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span></span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span></span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">湖北省政府重大投资项目公示试行办法</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第一条  \r\n为了进一步规范政府投资行为，加大政府投资项目审批决策的透明度、社会参与度和公众监督力度，提高政府投资项目决策的科学化、民主化水平，根据</span><a href=\"http://www.sdpc.gov.cn/gdzctz/tzfg/t20051010_44895.htm\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><strong>《国务院关于投资体制改革的决定》</strong></span></a><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">(国发[2004]20号）和</span><a href=\"http://www.sdpc.gov.cn/zcfb/zcfbtz/2010tz/t20110117_391052.htm\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><strong>《国家发展改革委关于实行政府重大投资项目公示工作的指导意见》</strong></span></a><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">(发改投资[2010]3131号），结合我省工作实际，制定本办法。</span></span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第二条  \r\n本办法所称政府投资项目，是指由湖北省发展和改革委员会（以下简称省发展改革委）审批或经省发展改革委审核上报国家发展和改革委员会审批项目建议书、可行性研究报告或初步设计报告，且使用国家或省本级财政预算内资金、中央预算内专项资金（国债）、纳入省财政预算管理的各类专项建设资金，以及其它财政性投资资金的建设项目。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第三条  \r\n省发展改革委在政府投资项目中选择有关重大项目开展公示试点工作。符合下列情况的重大项目，应当优先选择：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （一）对经济和社会发展、生态环境保护、资源开发利用有重大影响的公益性项目和基础设施建设项目；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （二）与社会公共利益密切相关、社会关注程度较高的公益性项目和基础设施建设项目。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 对符合上述条款规定，但涉及国家安全、国家秘密、商业秘密以及按照法律法规的有关规定不适宜公开的项目不进行公示。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第四条  根据</span><a href=\"http://www.hubei.gov.cn/zwgk/zfxxgk/zfxxgkgd/200810/t20081009_80573.shtml\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><strong>《湖北省政府信息公开规定》</strong></span></a><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">有关要求，加强保密审查，公开的项目信息不得危及国家安全、公共安全、经济安全和社会稳定。</span></span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第五条  列入公示的项目，通过省发展改革委门户网站</span><a href=\"http://www.hbjw.gov.cn/\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （www.hbfgw.gov.cn</span></a><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">）、项目建设所在地政府主要党报和项目选址地点周边张贴等方式向社会公开项目的有关内容。公示主要内容包括：</span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （一）项目名称；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （二）项目的申报单位和建设单位；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （三）项目建设地点；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （四）项目建设目标及功能；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （五）项目建设规模及主要建设内容；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （六）项目估算总投资及资金来源；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （七）负责审批项目单位（具体到处室）及联系方式；</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> （八）其他需要公示的内容。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第六条  \r\n对于拟公示的政府重大投资项目，省发展改革委在审批项目建议书前，或批复项目建议书后、上报可行性研究报告前进行公示，公示期限为10个工作日；对项目建议书和可行性研究报告合并审批的项目，在审批可行性研究报告（代项目建议书）前进行公示，公示期限为10个工作日。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第七条  \r\n公示期满后，省发展改革委应及时汇总、归纳征集到的意见和建议。对重要的意见和建议，可以采取走访、座谈等方式，进一步了解情况，征求有关方面意见，形成主要意见和建议，并及时告知项目申报单位和建设单位，要求申报单位和建设单位认真研究，充分吸收和采纳。对涉及环保、土地、城乡规划以及其他部门等方面的主要意见和建议，同时送交相关部门，供相关部门在进行有关管理工作时参考。征求意见后，要采取多种方式，向提出意见和建议的单位和个人反馈相关信息。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第八条  \r\n对征集到的主要意见和建议，项目申报单位和建设单位在编制可行性研究报告时应当认真研究。项目申报单位应当在申报项目可行性研究报告中说明对公示意见和建议的采纳情况，未予采纳的要说明理由。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第九条  \r\n省发展改革委在委托有资质的工程咨询单位评估项目可行性研究报告时，应要求咨询评估机构对主要意见和建议的采纳情况作出评议。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第十条  \r\n省发展改革委在审批项目可行性研究报告时，应当充分研究分析征集到的主要意见和建议，以及项目单位的说明和咨询评估机构的评议意见，并可要求项目申报单位依据公示意见和建议对项目建设内容进行相应调整和修改完善。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第十一条  \r\n省发展改革委在批复项目可行性研究报告以后，应当将主要意见和建议的采纳情况和未予采纳的理由在原公示网站或相关媒体上公布。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第十二条  \r\n各市、州、直管市、神农架林区，县（市、区）发展改革部门可以参照本办法制定本区域政府重大投资项目公示暂行办法。及时总结试点经验，在实践中不断改进完善。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第十三条  \r\n有关部门及其工作人员，要按照本办法的要求，并严格遵守相关法律法规，认真履行职责，依法依规开展公示工作。对不按规定开展公示工作，或违反公示程序要求，造成不良影响和后果的，将依法依纪实行责任追究。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第十四条  本办法由省发展改革委负责解释。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 第十五条  本办法自印发之日起施行。</span> \r\n	</p>\r\n	<p>\r\n		<span></span> \r\n	</p>\r\n	<p>\r\n		<span><strong><a href=\"http://www.hbjw.gov.cn/publish/20121122/20121122445376.shtml\" target=\"_blank\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">湖北省发展和改革委员会关于印发《湖北省政府重大投资项目公示试行办法》的通知</span></a></strong></span> \r\n	</p>\r\n</div>\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span> \r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-23 16:24:13', '0', '1', '1');
