INSERT INTO `tbl_library` VALUES ('6nMvAn', 'buUJBr', '2', '中华人民共和国公司法', null, '<a href=\"/userfiles/file/20130624/20130624164703_385.doc\" target=\"_blank\">中华人民共和国公司法-2011年最新公司法全文</a>', null, '2013-06-24 16:47:07', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('ymy2ye', 'buUJBr', '2', '中华人民共和国科学技术进步法', null, '<p style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（1993年7月2日第八届全国人民代表大会常务委员会 第二次会议通过中华人民共和国主席令第四号公布）</span>\r\n</p>\r\n<p style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">目&nbsp; 录</span></b>\r\n</p>\r\n<p style=\"font-family:\'\';font-size:medium;\">\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.13.htm#01\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一章 总 则</span></a></b>\r\n</p>\r\n<p style=\"font-family:\'\';font-size:medium;\">\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.13.htm#02\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二章 科学技术与经济建设和社会发展</span></a></b>\r\n</p>\r\n<p style=\"font-family:\'\';font-size:medium;\">\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.13.htm#03\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三章 高技术研究和高技术产业</span></a></b>\r\n</p>\r\n<p align=\"center\" style=\"font-family:\'\';font-size:medium;\">\r\n	<br />\r\n<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一章 总 则</span></b>\r\n</p>\r\n<p style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;为了促进科学技术进步，在社会主义现代化建设中优先发展科学技术，发挥科学技术第一生产力的作用，推动科学技术为经济建设服务，根据宪法，制定本法。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家实行经济建设和社会发展依靠科学技术，科学技术工作面向经济建设和社会发展的基本方针。</span>\r\n</p>\r\n<p style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 国家保障科学研究的自由，鼓励科学探索和技术创新，使科学技术达到世界先进水平。 国家和全社会尊重知识、尊重人才，尊重科学技术工作者的创造性劳动，保护知识产权。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家根据科学技术进步和社会主义市场经济的需要，改革和完善科学技术体制，建立科学技术与经济有效结合的机制。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第五条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励科学研究和技术开发，推广应用科学技术成果，改造传统产业，发展高技术产业，以及应用科学技术为经济建设和社会发展服务的活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第六条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家普及科学技术知识，提高全体公民的科学文化水平。 国家鼓励机关、企业事业组织、社会团体和公民参与和支持科学技术进步活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第七条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国务院制定科学技术发展规划，确定科学技术的重大项目、与科学技术密切相关的重大项目，保障科学技术进步与经济建设和社会发展相协调。 制定科学技术发展规划和重大政策，确定科学技术的重大项目、与科学技术密切相关的重大项目，应当充分听取科学技术工作者的意见，实行科学决策的原则。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第八条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国务院科学技术政策部门，负责全国科学技术工作的宏观管理和统筹协调。国务院其他有关行政部门，依照国务院规定的职责范围负责有关的科学技术进步工作。 地方各级人民政府应当采取有效措施，推进科学技术进步。 国家帮助少数民族地区、边远贫困地区加速发展科学技术事业。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第九条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;中华人民共和国政府积极发展同外国政府、国际组织之间的科学技术合作与交流，鼓励研究开发机构、高等院校、社会团体和科学技术工作者与国外科学技术界建立多种形式的合作关系。</span>\r\n</p>\r\n<p align=\"right\" style=\"font-family:\'\';font-size:medium;\">\r\n	<b><a href=\"http://www.jincao.com/fa/09/law09.13.htm#00\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">返回</span></a></b>\r\n</p>\r\n<p align=\"center\" style=\"font-family:\'\';font-size:medium;\">\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二章 科学技术与经济建设和社会发展</span></b>\r\n</p>\r\n<p align=\"left\" style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励研究开发新技术、新产品、新材料、新工艺，开展合理化建议、技术改进和技术协作活动，不断提高产品质量，提高劳动生产率和经济效益，发展社会生产力。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十一条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家选择对经济建设具有重大意义的项目，组织科学研究和技术开发，加速科学技术成果在生产领域中的推广应用。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十二条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家建立和发展技术市场，推动科学技术成果的商品化。技术贸易活动应当遵循自愿平等、互利有偿和诚实信用的原则。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十三条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家依靠科学技术进步，推动经济建设和社会发展，控制人口增长，提高人口素质，合理开发和利用资源，防御自然灾害，保护生活环境和生态环境。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十四条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家依靠科学技术进步，振兴农村经济，促进农业科学技术成果的推广应用，发展高产、优质、高效的现代化农业。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十五条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;县级以上地方各级人民政府应当采取措施，保障农业科学技术研究开发机构和示范推广机构有权自主管理和使用试验基地和生产资料，进行农业新品种、新技术的研究开发、试验和推广。 农业科学技术成果的应用和推广，依照有关法律的规定实行有偿服务或者无偿服务。&nbsp;</span>\r\n</p>\r\n<p align=\"left\" style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十六条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;地方各级人民政府应当鼓励和支持农村群众性科学技术组织的发展，对种植业、林业、畜牧业、渔业等各业提供产前、产中、产后综合配套的社会化科学技术服务。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十七条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家依靠科学技术进步，发展工业、交通运输、邮电通信、地质勘查、建筑安装和商业等行业，提高经济效益和社会效益。</span>\r\n</p>\r\n<p align=\"left\" style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十八条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励企业建立和完善技术开发机构，鼓励企业与研究开发机构、高等院校联合和协作，增强研究开发、中间试验和工业性试验能力。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十九条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;企业应当根据国际、国内市场的需求，进行技术改造和设备更新，提高科学管理水平，吸收和开发新技术，增强市场竞争能力。 企业进行技术改造和从国外引进先进技术和设备，应当经过咨询论证，贯彻国家的产业政策和技术政策。 企业采用新技术开发生产新产品的，可以依照国家的规定享受优惠待遇。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家依靠科学技术进步，发展国防科学技术事业，促进国防现代化建设，增强国防实力。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十一条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励运用先进的科学技术，促进教育、文化、卫生、体育等各项事业的发展。</span>\r\n</p>\r\n<p align=\"right\" style=\"font-family:\'\';font-size:medium;\">\r\n	<b><a href=\"http://www.jincao.com/fa/09/law09.13.htm#00\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">返回</span></a></b>\r\n</p>\r\n<p align=\"center\" style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三章 高技术研究和高技术产业</span></b>\r\n</p>\r\n<p align=\"left\" style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十二条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家推进高技术的研究，发挥高技术在科学技术进步中的先导作用；扶持、促进</span>\r\n</p>\r\n<p align=\"left\" style=\"font-family:\'\';font-size:medium;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">高技术产业的形成和发展，运用高技术改造传统产业，发挥高技术产业在经济建设中的作用。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十三条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国务院科学技术行政部门和其他有关行政部门，在全国范围内组织科学技术力量实施高技术研究，推广高技术研究成果。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十四条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;经国务院批准，选择具备条件的地区建立高新技术产业开发区。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十五条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;对在高新技术产业开发区和高新技术产业开发区外从事高技术产品开发、生产的企业和研究开发机构，实行国家规定的优惠政策，具体办法由国务院规定。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十六条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励和引导从事高技术产品开发、生产和经营的企业建立符合国际规范的管理制度，生产符合国际标准的高技术产品，参与国际市场竞争，推进高技术产业的国际化。 第四章 基础研究和应用基础研究</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十七条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家保障基础研究和应用基础研究持续、稳定的发展、加强科学技术进步的基础。 基础研究和应用基础研究经费在研究开发经费总额中应当占有适当比例。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十八条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国务院科学技术行政部门对学科前沿和经济、社会发展中的重大基础性科学研究课题，应当有计划地组织实施。 研究开发机构、高等院校及其他企业事业组织和公民可以自主选择课题，从事基础研究和应用基础研究。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十九条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家建立自然科学基金，按照专家评议、择优支持的原则，资助基础研究和应用基础研究。 国家支持优秀青年的科学研究活动，在自然科学基金中设立青年科学基金。</span>\r\n</p>\r\n<p align=\"right\" style=\"font-family:\'\';font-size:medium;\">\r\n	<b><a href=\"http://www.jincao.com/fa/09/law09.13.htm#00\">返回</a></b>\r\n</p>', null, '2013-06-24 16:51:49', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('euANNv', 'buUJBr', '2', '中华人民共和国科技成果转化法', null, '<p align=\"left\">\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">目&nbsp; 录</span></b>\r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.14.htm#01\"><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一章 总 则</span></b></a>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><b><a href=\"http://www.jincao.com/fa/09/law09.14.htm#02\"><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二章 组 织 实 施</span></a></b>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"01\"></a><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一章 总 则</span></b>\r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;为了促进科技成果转化为现实生产力，规范科技成果转化活动，加速科学技术进步，推动经济建设和社会发展，制定本法。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;本法所称成果转化，是指为提高生产力水平而对科学研究与技术开发所产生的具有实用价值的科技成果所进行的后续试验、开发、应用、推广直至形成新产品、新工艺、新材料，发展新产业等活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;科技成果转化活动应当有利于提高经济效益、社会效益和保护环境与资源，有利于促进经济建设、社会发展和国防建设。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 科技成果转化活动应当遵循自愿、互利、公平、诚实信用的原则，依法或者依照合同的约定，享受利益，承担风险。科技成果转化中的知识产权受法律保护。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 科技成果转化活动应当遵守法律，维护国家利益，不得损害社会公共利益。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国务院科学技术行政部门、计划部门、经济综合管理部门和其他有关行政部门依照国务院规定的职责范围，管理、指导和协调科技成果转化工作。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 地方各级人民政府负责管理、指导和协调本行政区域内的科技成果转化工作。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<a href=\"http://www.jincao.com/fa/09/law09.14.htm#00\"><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">返回</span></b></a>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"02\"></a><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二章 组 织 实 施</span></b>\r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第五条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国务院和地方各级人民政府应当将科技成果的转化纳入国民经济和社会发展计划，并组织协调实施有关科技成果的转化。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第六条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国务院有关部门和省、自治区、直辖市人民政府定期发布科技成果目录和重点科技成果转化项目指南，优先安排和支持下列项目的实施：</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; (一)明显提高产业技术水平和经济效益的；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; (二)形成产业规模，具有国际经济竞争能力的；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; (三)合理开发和利用资源、节约能源、降低消耗以及防治环境污染的；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; (四)促进高产、优质、高效农业和农村经济发展的；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; (五)加速少数民族地区、边远贫困地区社会经济发展的。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第七条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家通过制定政策措施，提倡和鼓励采用先进技术、工艺和装备，不断改进、限制使用或者淘汰落后技术、工艺和装备。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第八条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;各级人民政府组织实施的重点科技成果转化项目，可以由有关部门组织采用公开招标的方式实施转化。有关部门应当对中标单位提供招标时确定的资助或者其他条件。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第九条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;科技成果持有者可以采用下列方式进行科技成果转化。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （一）自行投资实施转化；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （二）向他人转让该科技成果；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （三）许可他人合作该科技成果；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （四）以该科技成果作为合作条件，与他人共同实施转化；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （五）以该科技成果作价投资，折算股份或者出资比例。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;企业为采用新技术、新工艺、新材料和生产新产品，可以自行发布信息或者委托技术交易中介机构征集其所需的科技成果，或者征寻科技成果转化的合作者。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十一条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;企业依法有权独立或者与境内外企业、事业单位和其他合作者联合实施科技成果转化。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 企业可以通过公平竞争，独立或者与其他单位联合承担政府组织实施的科技研究开发和科技成果转化项目。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十二条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励研究开发机构、高等院校等事业单位与生产企业相结合，实施科技成果转化。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 研究开发机构、高等院校等事业单位，可以参与政府有关部门或者企业实施科技成果转化的招标投标活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十三条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励农业科研机构、农业试验示范单位独立或者与其他单位合作实施农业科技成果转化。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 农业科研机构为推进其科技成果转化，可以依法经营其独立研究开发或者与其他单位合作研究开发并经过审定的优良品种。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十四条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家设立的研究开发机构、高等院校所取得的具有实用价值的职务科技成果，本单位未能适时地实施转化的，科技成果完成人和参加人在不变更职务科技成果权属的前提下，可以根据与本单位的协议进行该项科技成果的转化，并享有协议规定的权益。该单位对上述科技成果转化活动应当予以支持。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 科技成果完成人或者课题负责人，不得阻碍职务科技成果的转化，不得将职务科技成果及其技术资料和数据占为己有，侵犯单位的合法权益。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十五条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;科技成果完成单位、科技成果转化实施单位和科技成果转化投资单位，就科技成果的后续试验、开发、应用和生产经营进行合作，应当签订合同，约定各方享有的权利和承担的风险。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十六条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;科技成果转化活动中对科技成果进行检测和价值评估，必须遵循公正、客观的原则，不得提供虚假的检测结果或者评估证明。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 国家设立的研究开发机构、高等院校和国有企业与中国境外的企业、其他组织或者个人合作进行科技成果转化活动，必须按照国家有关规定对科技成果的价值进行评估。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">科技成果转化中的对外合作，涉及国家秘密事项的，依法按照规定的程序事先经过批准。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十七条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;依法设立的从事技术交易的场所或者机构，可以进行下列推动科技成果转化的活动：</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （一）介绍和推荐先进、成熟、实用的科技成果；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （二）提供科技成果转化需要的经济信息、技术信息、环境信息和其他有关信息；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （三）进行技术贸易活动；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （四）为科技成果转化提供其他咨询服务。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十八条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;在技术交易中从事代理或者居间等有偿服务的中介机构，须按照国家有关规定领取营业执照；在该机构中从事经纪业务的人员，须按照国家有关规定取得资格证书。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十九条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;国家鼓励企业、事业单位和农村科技经济合作组织进行中间试验、工业性试验、农业试验示范以及其他技术创新和技术服务活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 从事科技成果转化的中间试验基地、工业性试验基地、农业试验示范基地以及其他技术创新技术服务机构可以进行下列活动：</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （一）对新产品、新工艺进行中间试验和工业性试验；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （二）面向社会进行地区或者行业科技成果系统化、工程化的配套开发和技术创新；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （三）为中小企业、乡镇企业、农村科技经济合作组织提供技术和技术服务；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （四）为转化高技术成果、创办相关企业提供综合配套服务。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">前款所列基地和机构的基本建设经国务院有关部门和省、自治区、直辖市人民政府批准，纳入国家或者地方有关规划。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十条</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp;科技成果转化的试验产品，按照国家有关试销产品的规定，经有关部门批准，可以在核定的试销期内试销。试产、试销上述产品应当符合。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<a href=\"http://www.jincao.com/fa/09/law09.14.htm#00\"><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">返回</span></b></a>\r\n</p>', null, '2013-06-24 16:52:48', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('6FJfye', 'buUJBr', '2', '中华人民共和国电力法', null, '<p align=\"center\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">(1995年12月28日第八届全国人民代表大会常务委员会第十七次会议通过)</span>\r\n</p>\r\n<p align=\"center\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp; 目&nbsp; 录</span></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#01\"><span style=\"font-family:SimSun;line-height:2;\">第一章 总 则</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#02\"><span style=\"font-family:SimSun;line-height:2;\">第二章 电力建设</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#03\"><span style=\"font-family:SimSun;line-height:2;\">第三章 电力生产与电网管理</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#04\"><span style=\"font-family:SimSun;line-height:2;\">第四章 电力供应与使用</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#05\"><span style=\"font-family:SimSun;line-height:2;\">第五章 电价与电费</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#06\"><span style=\"font-family:SimSun;line-height:2;\">第六章 农村电力建设和农业用电</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#07\"><span style=\"font-family:SimSun;line-height:2;\">第七章 电力设施保护</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#08\"><span style=\"font-family:SimSun;line-height:2;\">第八章 监督检查</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#09\"><span style=\"font-family:SimSun;line-height:2;\">第九章 法律责任</span></a></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;</span><a href=\"http://www.jincao.com/fa/09/law09.19.htm#10\"><span style=\"font-family:SimSun;line-height:2;\">第十章 附 则</span></a></b>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"01\"></a><span style=\"font-family:SimSun;line-height:2;\">第一章 总 则</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;为了保障和促进电力事业的发展，维护电力投资者、经营者和使用者的合法权益，保障电力安全运行，制定本法。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;本法适用于中华人民共和国境内的电力建设、生产、供应和使用活动。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力事业应当适应国民经济和社会发展的需要，适当超前发展。国家鼓励、引导国内外的经济组织和个人依法投资开发电源，兴办电力生产企业。 电力事业投资，实行谁投资、谁收益的原则。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力设施受国家保护。 禁止任何单位和个人危害电力设施安全或者非法侵占、使用电能。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力建设、生产、供应和使用应当依法保护环境，采用新技术，减少有害物质排放，防治污染和其他公害。 国家鼓励和支持利用可再生能源和清洁能源发电。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国务院电力管理部门负责全国电力事业的监督管理。国务院有关部门在各自的职责范围内负责电力事业的监督管理。 县级以上地方人民政府经济综合主管部门是本行政区域内的电力管理部门，负责 电力事业的监督管理。县级以上地方人民政府有关部门在各自的职责范围内负责电力 事业的监督管理。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第七条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力建设企业、电力生产企业、电网经营企业依法实行自主经营、自负盈亏，并接受电力管理部门的监督。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第八条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家帮助和扶持少数民族地区、边远地区和贫困地区发展电力事业。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第九条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家鼓励在电力建设、生产、供应和使用过程中，采用先进的科学技术和管理方法，对在研究、开发、采用先进的科学技术和管理方法等方面作出显著成绩 的单位和个人给予奖励。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span><span style=\"font-family:SimSun;line-height:2;\">　</span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"02\"></a><span style=\"font-family:SimSun;line-height:2;\">第二章 电力建设</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力发展规划应当根据国民经济和社会发展的需要制定，并纳入国民经济和社会发展计划。 电力发展规划，应当体现合理利用能源、电源与电网配套发展、提高经济效益和 有利于环境保护的原则。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;城市电网的建设与改造规划，应当纳入城市总体规划。城市人民政府应当按照规划，安排变电设施用地、输电线路走廊和电缆通道。 任何单位和个人不得非法占用变电设施用地、输电线路走廊和电缆通道。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家通过制定有关政策，支持、促进电力建设。 地方人民政府应当根据电力发展规划，因地制宜，采取多种措施开发电源，发展电 力建设。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力投资者对其投资形成的电力，享有法定权益。并网运行的，电力投资者有优先使用权；未并网的自备电厂，电力投资者自行支配使用。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力建设项目应当符合电力发展规划，符合国家电力产业政策。 电力建设项目不得使用国家明令淘汰的电力设备和技术。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;输变电工程、调度通信自动化工程等电网配套工程和环境保护工程，应当与发电工程项目同时设计、同时建设、同时验收、同时投入使用。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十六条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力建设项目使用土地，应当依照有关法律、行政法规的规定办理；依法征用土地的，应当依法支付土地补偿费和安置补偿费，做好迁移居民的安置工作 。 电力建设应当贯彻切实保护耕地、节约利用土地的原则。 地方人民政府对电力事业依法使用土地和迁移居民，应当予以支持和协助。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十七条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;地方人民政府应当支持电力企业为发电工程建设勘探水源和依法取水 、用水。电力企业应当节约用水。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"03\"></a><span style=\"font-family:SimSun;line-height:2;\">第三章 电力生产与电网管理</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十八条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力生产与电网运行应当遵循安全、优质、经济的原则。 电网运行应当连续、稳定，保证供电可靠性。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第十九条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力企业应当加强安全生产管理，坚持安全第一、预防为主的方针， 建立、健全安全生产责任制度。 电力企业应当对电力设施定期进行检修和维护，保证其正常运行。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;发电燃料供应企业、运输企业和电力生产企业应当依照国务院有关规定或者合同约定供应、运输和接卸燃料。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电网运行实行统一调度、分级管理。任何单位和个人不得非法干预电网调度。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家提倡电力生产企业与电网、电网与电网并网运行。具有独立法人资格的电力生产企业要求将生产的电力并网运行的，电网经营企业应当接受。 并网运行必须符合国家标准或者电力行业标准。 并网双方应当按照统一调度、分级管理和平等互利、协商一致的原则，签订并网 协议，确定双方的权利和义务；并网双方达不成协议的，由省级以上电力管理部门协 调决定。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电网调度管理办法，由国务院依照本法的规定制定。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"04\"></a><span style=\"font-family:SimSun;line-height:2;\">第四章 电力供应与使用</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家对电力供应和使用，实行安全用电、节约用电、计划用电的管理原则。 电力供应与使用办法由国务院依照本法的规定制定。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;供电企业在批准的供电营业区内向用户供电。 供电营业区的划分，应当考虑电网的结构和供电合理性等因素。一个供电营业区内 只设立一个供电营业机构。 省、自治区、直辖市范围内的供电营业区的设立、变更，由供电企业提出申请， 经省、自治区、直辖市人民政府电力管理部门会同同级有关部门审查批准后，由省、 自治区、直辖市人民政府电力管理部门发给《供电营业许可证》。跨省、自治区、直 辖市的供电营业区的设立、变更，由国务院电力管理部门审查批准并发给《供电营业 许可证》。供电营业机构持《供电营业许可证》向工商行政管理部门申请领取营业执 照，方可营业。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十六条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;供电营业区内的供电营业机构，对本营业区内的用户有按照国家规定供电的义务；不得违反国家规定对其营业区内申请用电的单位和个人拒绝供电。 申请新装用电、临时用电、增加用电容量、变更用电和终止用电，应当依照规定 的程序办理手续。 供电企业应当在其营业场所公告用电的程序、制度和收费标准，并提供用户须知 资料。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十七条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力供应与使用双方应当根据平等自愿、协商一致的原则，按照国务院制定的电力供应与使用办法签订供用电合同，确定双方的权利和义务。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十八条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;供电企业应当保证供给用户的供电质量符合国家标准。对公用供电设施引起的供电质量问题，应当及时处理。 用户对供电质量有特殊要求的，供电企业应当根据其必要性和电网的可能，提供 相应的电力。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第二十九条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;供电企业在发电、供电系统正常的情况下，应当连续向用户供电，不得中断。因供电设施检修、依法限电或者用户违法用电等原因，需要中断供电时， 供电企业应当按照国家有关规定事先通知用户。 用户对供电企业中断供电有异议的，可以向电力管理部门投诉；受理投诉的电力 管理部门应当依法处理。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;因抢险救灾需要紧急供电时，供电企业必须尽速安排供电，所需供电工程费用和应付电费依照国家有关规定执行。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;用户应当安装用电计量装置。用户使用的电力电量，以计量检定机构依法认可的用电计量装置的记录为准。 用户受电装置的设计、施工安装和运行管理，应当符合国家标准或者电力行业标 准。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;用户用电不得危害供电、用电安全和扰乱供电、用电秩序。 对危害供电、用电安全和扰乱供电、用电秩序的，供电企业有权制止。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;供电企业应当按照国家核准的电价和用电计量装置的记录，向用户计收电费。 供电企业查电人员和抄表收费人员进入用户，进行用电安全检查或者抄表收费时 ，应当出示有关证件。 用户应当按照国家核准的电价和用电计量装置的记录，按时交纳电费；对供电企 业查电人员和抄表收费人员依法履行职责，应当提供方便。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;供电企业和用户应当遵守国家有关规定，采取有效措施，做好安全用电、节约用电和计划用电工作。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"05\"></a><span style=\"font-family:SimSun;line-height:2;\">第五章 电价与电费</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;本法所称电价，是指电力生产企业的上网电价、电网间的互供电价 、电网销售电价。 电价实行统一政策，统一定价原则，分级管理。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十六条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;制定电价，应当合理补偿成本，合理确定收益，依法计入税金，坚 持公平负担，促进电力建设。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十七条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;上网电价实行同网同质同价。具体办法和实施步骤由国务院规定。 电力生产企业有特殊情况需另行制定上网电价的，具体办法由国务院规定。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十八条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;跨省、自治区、直辖市电网和省级电网内的上网电价，由电力生产 企业和电网经营企业协商提出方案，报国务院物价行政主管部门核准。 独立电网内的上网电价，由电力生产企业和电网经营企业协商提出方案，报有管 理权的物价行政主管部门核准。 地方投资的电力生产企业所生产的电力，属于在省内各地区形成独立电网的或者 自发自用的，其电价可以由省、自治区、直辖市人民政府管理。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第三十九条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;跨省、自治区、直辖市电网和独立电网之间、省级电网和独立电网 之间的互供电价，由双方协商提出方案，报国务院物价行政主管部门或者其授权的部 门核准。 独立电网与独立电网之间的互供电价，由双方协商提出方案，报有管理权的物价 行政主管部门核准。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;跨省、自治区、直辖市电网和省级电网的销售电价，由电网经营企业 提出方案，报国务院物价行政主管部门或者其授权的部门核准。 独立电网的销售电价，由电网经营企业提出方案，报有管理权的物价行政主管部 门核准。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家实行分类电价和分时电价。分类标准和分时办法由国务院确定 。 对同一电网内的同一电压等级、同一用电类别的用户，执行相同的电价标准。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;用户用电增容收费标准，由国务院物价行政主管部门会同国务院电力管理部门制定。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;任何单位不得超越电价管理权限制定电价。供电企业不得擅自变更电价。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;禁止任何单位和个人在电费中加收其他费用；但是，法律、行政法规另有规定的，按照规定执行。 地方集资办电在电费中加收费用的，由省、自治区、直辖市人民政府依照国务院 有关规定制定办法。 禁止供电企业在收取电费时，代收其他费用。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电价的管理办法，由国务院依照本法的规定制定。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"06\"></a><span style=\"font-family:SimSun;line-height:2;\">第六章 农村电力建设和农业用电</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十六条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;省、自治区、直辖市人民政府应当制定农村电气化发展规划，并将其纳入当地电力发展规划及国民经济和社会发展计划。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十七条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家对农村电气化实行优惠政策，对少数民族地区、边远地区和贫困地区的农村电力建设给予重点扶持。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十八条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;国家提倡农村开发水能资源，建设中、小型水电站，促进农村电气化。 国家鼓励和支持农村利用太阳能、风能、地热能、生物质能和其他能源进行农村 电源建设，增加农村电力供应。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第四十九条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;县级以上地方人民政府及其经济综合主管部门在安排用电指标时， 应当保证农业和农村用电的适当比例，优先保证农村排涝、抗旱和农业季节性生产用 电。 电力企业应当执行前款的用电安排，不得减少农业和农村用电指标。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;农业用电价格按照保本、微利的原则确定。 农民生活用电与当地城镇居民生活用电应当逐步实行相同的电价。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;农业和农村用电管理办法，由国务院依照本法的规定制定。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"07\"></a><span style=\"font-family:SimSun;line-height:2;\">第七章 电力设施保护</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;任何单位和个人不得危害发电设施、变电设施和电力线路设施及其 有关辅助设施。 在电力设施周围进行爆破及其他可能危及电力设施安全的作业的，应当按照国务 院有关电力设施保护的规定，经批准并采取确保电力设施安全的措施后，方可进行作 业。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力管理部门应当按照国务院有关电力设施保护的规定，对电力设 施保护区设立标志。 任何单位和个人不得在依法划定的电力设施保护区内修建可能危及电力设施安全 的建筑物、构筑物，不得种植可能危及电力设施安全的植物，不得堆放可能危及电力 设施安全的物品。 在依法划定电力设施保护区前已经种植的植物妨碍电力设施安全的，应当修剪或 者砍伐。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;任何单位和个人需要在依法划定的电力设施保护区内进行可能危及 电力设施安全的作业时，应当经电力管理部门批准并采取安全措施后，方可进行作业 。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力设施与公用工程、绿化工程和其他工程在新建、改建或者扩建 中相互妨碍时，有关单位应当按照国家有关规定协商，达成协议后方可施工。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"08\"></a><span style=\"font-family:SimSun;line-height:2;\">第八章 监督检查</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十六条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力管理部门依法对电力企业和用户执行电力法律、行政法规的情 况进行监督检查。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十七条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力管理部门根据工作需要，可以配备电力监督检查人员。 电力监督检查人员应当公正廉洁，秉公执法，熟悉电力法律、法规，掌握有关电力 专业技术。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十八条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力监督检查人员进行监督检查时，有权向电力企业或者用户了解 有关执行电力法律、行政法规的情况，查阅有关资料，并有权进入现场进行检查。 电力企业和用户对执行监督检查任务的电力监督检查人员应当提供方便。 电力监督检查人员进行监督检查时，应当出示证件。</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"09\"></a><span style=\"font-family:SimSun;line-height:2;\">第九章 法律责任</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第五十九条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力企业或者用户违反供用电合同，给对方造成损失的，应当依法 承担赔偿责任。 电力企业违反本法第二十八条、第二十九条第一款的规定，未保证供电质量或者 未事先通知用户中断供电，给用户造成损失的，应当依法承担赔偿责任。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;因电力运行事故给用户或者第三人造成损害的，电力企业应当依法承 担赔偿责任。 电力运行事故由下列原因之一造成的，电力企业不承担赔偿责任：</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （一）不可抗力；</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （二）用户自身的过错。 因用户或者第三人的过错给电力企业或者其他用户造成损害的，该用户或者第三 人应当依法承担赔偿责任。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第十一条第二款的规定，非法占用变电设施用地、输电线 路走廊或者电缆通道的，由县级以上地方人民政府责令限期改正；逾期不改正的，强 制清除障碍。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第十四条规定，电力建设项目不符合电力发展规划、产业 政策的，由电力管理部门责令停止建设。 违反本法第十四条规定，电力建设项目使用国家明令淘汰的电力设备和技术的， 由电力管理部门责令停止使用，没收国家明令淘汰的电力设备，并处五万元以下的罚 款。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第二十五条规定，未经许可，从事供电或者变更供电营业 区的，由电力管理部门责令改正，没收违法所得，可以并处违法所得五倍以下的罚款 。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第二十六条、第二十九条规定，拒绝供电或者中断供电的 ，由电力管理部门责令改正，给予警告；情节严重的，对有关主管人员和直接责任人 员给予行政处分。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第三十二条规定，危害供电、用电安全或者扰乱供电、用 电秩序的，由电力管理部门责令改正，给予警告；情节严重或者拒绝改正的，可以中 止供电，可以并处五万元以下的罚款。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十六条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第三十三条、第四十三条、第四十四条规定，未按照国家 核准的电价和用电计量装置的记录向用户计收电费、超越权限制定电价或者在电费中 加收其他费用的，由物价行政主管部门给予警告，责令返还违法收取的费用，可以并 处违法收取费用五倍以下的罚款；情节严重的，对有关主管人员和直接责任人员给予 行政处分。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十七条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第四十九条第二款规定，减少农业和农村用电指标的，由 电力管理部门责令改正；情节严重的，对有关主管人员和直接责任人员给予行政处分 ；造成损失的，责令赔偿损失。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十八条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第五十二条第二款和第五十四条规定，未经批准或者未采 取安全措施在电力设施周围或者在依法划定的电力设施保护区内进行作业，危及电力 设施安全的，由电力管理部门责令停止作业、恢复原状并赔偿损失。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第六十九条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;违反本法第五十三条规定，在依法划定的电力设施保护区内修建建 筑物、构筑物或者种植植物、堆放物品，危及电力设施安全的，由当地人民政府责令 强制拆除、砍伐或者清除。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第七十条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;有下列行为之一，应当给予治安管理处罚的，由公安机关依照治安管 理处罚条例的有关规定予以处罚；构成犯罪的，依法追究刑事责任：</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （一）阻碍电力建设或者电力设施抢修，致使电力建设或者电力设施抢修不能正 常进行的；</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （二）扰乱电力生产企业、变电所、电力调度机构和供电企业的秩序，致使生产 、工作和营业不能正常进行的；</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （三）殴打、公然侮辱履行职务的查电人员或者抄表收费人员的；</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; （四）拒绝、阻碍电力监督检查人员依法执行职务的。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第七十一条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;盗窃电能的，由电力管理部门责令停止违法行为，追缴电费并处应交电费五倍以下的罚款；构成犯罪的，依照刑法第一百五十一条或者第一百五十二条 的规定追究刑事责任。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第七十二条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;盗窃电力设施或者以其他方法破坏电力设施，危害公共安全的，依照刑法第一百零九条或者第一百一十条的规定追究刑事责任。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第七十三条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力管理部门的工作人员滥用职权、玩忽职守、徇私舞弊，构成犯 罪的，依法追究刑事责任；尚不构成犯罪的，依法给予行政处分。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第七十四条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;电力企业职工违反规章制度、违章调度或者不服从调度指令，造成 重大事故的，比照刑法第一百一十四条的规定追究刑事责任。 电力企业职工故意延误电力设施抢修或者抢险救灾供电，造成严重后果的，比照刑法第一百一十四条的规定追究刑事责任。 电力企业的管理人员和查电人员、抄表收费人员勒索用户、以电谋私，构成犯罪 的，依法追究刑事责任；尚不构成犯罪的，依法给予行政处分。 　</span>\r\n</p>\r\n<p align=\"center\">\r\n	<b><a name=\"10\"></a><span style=\"font-family:SimSun;line-height:2;\">第十章 附 则</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><b><span style=\"font-family:SimSun;line-height:2;\">第七十五条</span></b><span style=\"font-family:SimSun;line-height:2;\">&nbsp;本法自1996年4月1日起施行。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"9p\" style=\"font-family:SimSun;line-height:2;\"><b>附：刑法有关条款 　</b></span>\r\n</p>\r\n<p align=\"left\">\r\n	<span class=\"9p\" style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 第一百零九条 破坏电力、煤气或者其他易燃易爆设备，危害公共安全，尚未造成 严重后果的，处三年以上十年以下有期徒刑。</span>\r\n</p>\r\n<p align=\"left\">\r\n	<span class=\"9p\" style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 第一百一十条 破坏交通工具、交通设备、电力煤气设备、易燃易爆设备造成严 重后果的，处十年以上有期徒刑、无期徒刑或者死刑。 过失犯前款罪的，处七年以下有期徒刑或者拘役。</span>\r\n</p>\r\n<p align=\"left\">\r\n	<span class=\"9p\" style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 第一百一十四条 工厂、矿山、林场、建筑企业或者其他企业、事业单位的职工 ，由于不服管理、违反规章制度，或者强令工人违章冒险作业，因而发生重大伤亡事 故，造成严重后果的，处三年以下有期徒刑或者拘役；情节特别恶劣的，处三年以上 七年以下有期徒刑。</span>\r\n</p>\r\n<p align=\"left\">\r\n	<span class=\"9p\" style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 第一百五十一条 盗窃、诈骗、抢夺公私财物数额较大的，处五年以下有期徒刑 、拘役或者管制。</span>\r\n</p>\r\n<p align=\"left\">\r\n	<span class=\"9p\" style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;&nbsp; 第一百五十二条 惯窃、惯骗或者盗窃、诈骗、抢夺公私财物数额巨大的，处五 年以上十年以下有期徒刑；情节特别严重的，处十年以上有期徒刑或者无期徒刑，可以并处没收财产</span>\r\n</p>\r\n<p align=\"right\">\r\n	<span class=\"9p\"><b><a href=\"http://www.jincao.com/fa/09/law09.19.htm#00\"><span style=\"font-family:SimSun;line-height:2;\">返 回</span></a></b></span>\r\n</p>', null, '2013-06-24 16:55:54', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('nyeuiu', 'ZrIzaa', '2', '北京市关于加强节能工作决定的意见', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">北京市人民政府关于贯彻落实国务院关于加强节能工作决定的意见(京政发[2007]3号)</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">发文单位：北京市政府</span> \r\n</div>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">文号：京政发[20</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">07]3号</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各区、县人民政府，市政府各委、办、局，各市属机构： </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为进一步加强本市安全生产工作，维护首都安全和稳定，保障人民群众的生命财产安全，现就贯彻落实《国务院关于进一步加强安全生产工作的决定》（国发[2004]2号）提出如下意见。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　一、提高认识，明确指导思想和奋斗目标 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　各地区、各部门、各单位要从全面贯彻落实“三个代表”重要思想，切实维护最广大人民群众根本利益的高度，充分认识安全生产工作的重要性和紧迫性，坚持以人为本，牢固树立人民群众的身体健康和生命安全高于一切和安全生产“责任重于泰山”的思想观念，牢固树立首都安全意识，坚持“安全第一、预防为主”的方针，坚持科学的发展观，进一步加强政府对安全生产工作的领导，落实安全生产综合监管、行业（领域）监管、行业（领域）管理和属地监管责任，落实生产经营单位安全生产的主体责任，不断完善和全面落实安全生产管理的各项制度和措施，求真务实，齐抓共管，加强对各类生产经营单位安全生产的监督检查，加大事故隐患查处和治理力度，形成安全生产监管和执法触及企业核心利益机制，严防重、特大事故发生，促进安全生产与经济社会同步协调发展，确保首都的安全和稳定。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　到2008年，健全完善适应首都经济社会发展的安全生产监管体系，全市安全生产状况继续保持稳定并进一步好转，工矿企业和建筑施工企业生产安全事故死亡人数、煤矿百万吨死亡率、道路交通运输万车死亡率等指标有较大幅度下降，初步形成完善的安全生产法规体系和规范的安全生产法治秩序，为成功举办第29届奥运会创造良好的环境。到2015年，全市安全生产状况实现根本性好转，亿元国内生产总值死亡率、10万人死亡率等指标达到或者接近世界中等发达国家水平。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　二、加强领导，抓好各级安全生产监管机构和执法队伍建设 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　各级政府必须依照《中华人民共和国安全生产法》的规定，加强对本地区安全生产工作的领导。各级政府主要领导人和政府有关部门正职负责人对本地区、本部门安全生产工作负全面领导责任；各级政府其他领导人和政府有关部门其他负责人在分管范围内对安全生产工作负领导责任。要建立健全领导干部安全生产责任制，加强监督，严格考核。各级政府要把安全生产工作列入重要议事日程，每季度组织召开一次安全生产例会，分析本地区安全生产形势，研究安全生产的重大问题，部署安全生产工作，检查安全生产管理责任落实情况。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　各级政府的安全生产委员会统一组织和指导协调本地区安全生产工作。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　各区县政府都要健全安全生产监管机构，建立安全生产专业执法队伍，落实编制、人员和经费，为开展安全生产监管和执法工作提供必要的条件，保障安全生产监管机构和执法队伍有效履行职责。街道办事处、乡镇政府也要明确安全生产监管工作机构或人员，具体负责本辖区安全生产工作。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　三、落实安全生产综合监管、行业（领域）监管（管理）和属地监管责任 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　市和区、县安全生产监督管理部门对本行政区域内安全生产工作实施综合监督管理，指导、协调和监督政府有关部门履行安全生产监督和管理职责。组织实施本地区安全生产规划，组织安全生产联合检查，指导和督促重大事故隐患治理工作，组织制定和管理本地区生产安全事故应急救援预案，依法组织生产安全事故调查处理。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　公安、建设、质量技术监督、国土资源、煤炭、国防科技工业等负有安全生产监督管理职责的政府有关部门，依照有关法律、法规规定，分别对消防、道路交通、建筑施工、特种设备、煤矿、非煤矿山、民用爆破器材生产等方面的安全生产工作实施监督管理；其他政府部门依照有关法律、法规、规章和《北京市人民政府关于印发市政府工作部门安全监管（管理）职责的通知》（京政发[2004]22号）确定的职责，负责有关行业（领域）的安全生产管理工作。制定安全生产质量工作标准并组织实施，掌握重大事故隐患情况，督促和指导隐患治理工作，广泛深入开展安全宣传教育活动。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　各区县政府以及街道办事处、乡镇政府要对本地区的安全生产工作全面负起责任，高度重视并切实加强安全生产管理工作，层层建立责任制，全面落实属地监管责任。要进一步明确本地区安全生产综合监管部门和行业（领域）监管（管理）部门的职责，加强对本地区安全生产的综合监管和行业（领域）监管（管理）；要加大安全生产监督检查和执法工作力度，加强对本地区重大事故隐患治理工作的组织、指导、协调和监督。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　四、完善目标管理，加强监督考核 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　按照国家下达的年度安全生产各项控制指标和考核要求，结合本市实际情况，市安全生产委员会将年度安全生产控制指标分解下达，由相关部门和区县政府分别承担安全生产控制指标及其监管责任。各责任单位要强化各项控制措施，确保不突破控制指标。各级安全生产委员会负责安全生产控制指标的管理和考核工作。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　认真落实各级安全生产责任。各级政府要建立安全生产综合监管、行业（领域）监管（管理）和属地监管责任制，安全生产委员会负责制定年度考核目标和考核办法，以“折子工程”方式明确完成标准和时限，分解落实到责任单位和责任人。安全生产委员会负责定期检查目标责任落实情况，督促落实不力的责任单位和责任人抓紧改进，年终对责任制完成情况进行全面评价和考核，向本级政府报告。因工作不力，造成突破年度安全生产控制指标和目标责任未落实的，要严厉追究责任单位和责任人的责任。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　五、强化管理，落实生产经营单位安全生产主体责任 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　生产经营单位是安全生产责任主体，主要负责人必须履行法定的安全生产职责，对本单位的安全生产工作全面负责。在生产经营活动中，必须保证对本单位职工的安全教育到位、安全管理机构和人员到位、安全工作标准到位、安全管理机制到位和处罚到位。各类生产经营单位要全面贯彻落实安全生产的法律法规，建立健全安全生产各项规章制度，完善安全生产各项防范措施，保证安全生产的必要投入，依靠科技进步，积极采用安全性能可靠的新技术、新工艺、新设备和新材料，不断改进安全生产条件，提高安全生产管理水平，加强对职工安全生产意识教育和安全生产技能教育，及时消除生产经营活动中的事故隐患，确保安全生产。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　生产经营单位举办大型社会活动，应当制定符合规定要求的活动方案和突发事件的应急预案，并按照国家和本市有关规定履行审批手续。活动举办期间，举办单位应当落实各项安全措施，保证活动场所的设备、设施安全运转，配备足够的工作人员维持现场秩序，必要时可以申请公安机关协助。在人员相对聚集时，举办单位应当采取控制和疏散措施，确保参加活动的人数在安全条件允许的范围内。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　各类生产经营单位必须依法设置安全生产管理机构或者配备专、兼职安全生产管理人员。煤矿的专职安全生产管理人员不得少于3人，占从业人员总数的比例不得低于的2.5%。建筑施工单位专职安全生产管理人员的配备按国家有关规定执行。非煤矿山、城市轨道交通运营单位和危险物品的生产、经营、储存单位以及从业人员超过300人的其他生产经营单位，专职安全生产管理人员的数量不得低于从业人员总数的1%。出租摊位在100个以上的小商品批发市场、建材市场、家具市场、集贸市场等经营单位，也要设立安全生产管理机构或配备专职安全生产管理人员。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　六、消除事故隐患，防范重、特大生产安全事故发生 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　生产经营单位是事故隐患治理和消除的责任主体，要及时发现存在的事故隐患，立即进行治理并予以消除。对不能立即消除的隐患，要有确保安全的防范措施，要有落实责任、人员和资金的治理方案，限期予以消除。对重大事故隐患，要向所在的区县政府和行业（领域）监管（管理）部门报告。生产经营单位未履行法律、法规规定职责，造成重大事故隐患的，对责任单位和责任人要依法给予处罚。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　建立重大生产安全事故隐患管理制度，明确安全生产综合监管、行业（领域）监管（管理）和属地监管责任。各级政府要了解本地区生产经营单位存在的重大事故隐患情况，组织、指导、协调、监督隐患治理工作，对难以及时消除的事故隐患要逐级报告。行业（领域）监管（管理）部门要了解本行业（领域）的重大事故隐患情况，督促和指导生产经营单位积极消除事故隐患，每半年要向同级安全生产委员会报告一次重大事故隐患治理和消除情况。安全生产综合监管部门对本地区重大事故隐患进行综合分析和评价，提出事故隐患控制和消除的指导意见，组织、协调和督促、检查重大事故隐患治理工作。政府有关部门不履行或者不正确履行有关职责，使重大事故隐患不能及时消除或者造成重大事故隐患的，要追究有关责任单位和责任人的责任。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　七、实施安全生产许可制度，强化安全生产行政执法 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　政府部门在履行行政许可职责中，要把安全生产作为一项重要内容，关口前移，从源头上制止不具备安全生产条件的企业进入市场。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　依照《安全生产许可证条例》的规定，负责矿山企业、建筑施工企业和危险化学品、烟花爆竹、民用爆破器材生产企业安全生产许可的部门，要结合本市实际，制定安全生产许可的具体办法，在规定时限内向已经进行生产且具备安全生产条件的企业颁发安全生产许可证。开办企业必须具备法律、行政法规和国家标准或者行业标准规定的安全生产条件，依法向政府有关部门申请、办理安全生产许可证，持证生产经营。新建、改建、扩建项目的安全设施必须与主体工程同时设计、同时施工、同时投入生产和使用（简称“三同时”），对未通过“三同时”审查的建设项目，有关部门不予办理行政许可手续，企业不准开工投产。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　负有安全生产监管职责的部门要增强执法意识，认真履行监管职责，加强对生产经营单位特别是容易发生重大生产安全事故单位的监督检查。对不依法履行安全生产职责、存在事故隐患又不采取坚决治理措施、发生生产安全事故或者造成其他严重后果的生产经营单位及其主要负责人，要依法给予责令停产停业整顿、降低资质、暂扣或吊销有关证照、撤职、降级等处罚，记入企业信用信息系统，触及其核心利益。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　八、继续深化安全生产专项整治工作 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　继续深化矿山、道路交通运输、危险化学品、民用爆破器材和烟花爆竹、人员密集场所消防安全等方面的安全生产专项整治。各级政府要进一步加强对专项整治工作的领导，紧密结合本地区实际，突出重点，有的放矢，确保取得实效。负有安全生产监管职责的部门具体组织和指导专项整治工作，各部门要积极配合，通力协作，搞好联合执法，提高专项整治工作的效率。各级安全生产委员会负责监督协调专项整治工作。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　九、全面开展安全质量标准化活动 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　开展安全质量标准化活动，提高生产经营单位安全生产管理水平。工矿、商贸、交通运输、建筑施工、旅游、餐饮、娱乐等行业（领域）监管（管理）部门要制定本行业（领域）的安全生产质量工作标准，细化生产经营流程各环节、各岗位的安全生产质量工作标准。2004年内，完成本行业（领域）和生产经营单位迫切需要的安全生产质量工作标准的制定工作。安全生产质量工作标准要根据实际情况及时修改和完善。各类生产经营单位要建立各环节、各岗位的安全生产质量责任制，生产经营活动和行为必须符合安全生产有关法律法规和安全生产技术规范要求，做到规范化和标准化。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　十、建立生产安全应急救援体系 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　建立全市生产安全应急救援指挥中心，充分利用现有的应急救援资源，建设具有快速反应能力的专业化救援队伍，提高救援装备水平，增强生产安全事故的抢险救援能力。建立矿山、危险化学品生产安全救援基地，组建建筑、危险化学品应急救援队伍。按照《北京市特大生产安全事故应急救援预案》、《北京市危险化学品事故应急救援预案》要求，定期组织开展工程抢险、人员疏散、现场医疗救治、紧急处置等应急救援演练和培训。各行业（领域）监管（管理）部门、区县政府以及生产经营单位也要制定生产安全事故应急救援预案。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　十一、建立并实施安全生产投入保障和风险抵押金制度 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　本市对矿山、建筑施工、危险化学品、烟花爆竹、民用爆破器材等高危行业生产企业实行提取安全费用制度，先在煤炭生产企业和建筑施工企业试行。煤炭生产企业安全费用的提取按财政部、发展改革委、国家煤矿安全监察局印发的《煤炭生产安全费用提取和使用管理办法》（财建[2004]119号）有关规定执行；建筑施工单位按不低于建筑施工产值6‰的比例提取安全费用。企业安全费用由企业自行提取，专户储存，专项用于安全生产。具体办法由市安全生产监督局会同市财政局、市国税局、市地税局、市发展改革委、市建委等有关部门研究制定，报市政府批准后实施。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　生产经营单位必须认真执行工伤保险制度，依法参加工伤保险，及时为从业人员交纳保险费。同时，依据《中华人民共和国安全生产法》等有关法律法规，向受到生产安全事故伤害的员工或家属支付赔偿金。进一步提高企业生产安全事故伤亡赔偿标准，建立企业负责人自觉保障安全投入，努力减少事故的机制。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为强化生产经营单位的安全生产责任，本市对矿山、道路交通运输、建筑施工、危险化学品、烟花爆竹等生产经营单位，实行风险抵押金制度，收取一定数额的安全生产风险抵押金，用于生产经营单位发生生产安全事故后的事故抢险救灾和善后处理。风险抵押金制度待国家具体办法出台后实施。 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　十二、加强安全生产宣传教育 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　坚持正确的舆论导向，大力宣传国家安全生产法律法规、方针政策和加强安全生产工作的重大举措，宣传安全生产工作的先进典型和经验；加强安全文化建设，普及各种安全常识及自我保护、紧急救助知识，提高社会公众的安全意识，增强市民安全防范和紧急避险的能力。认真组织开展“安全生产月”、“安康杯竞赛”等活动，加强对生产经营单位主要负责人、安全生产管理人员、特种作业人员的培训和考核。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　建立安全生产新闻发布制度。定期向全社会公布安全生产形势，增加安全生产工作的透明度。对重大事故隐患和严重忽视安全生产、导致重特大事故的典型案例，坚决给予曝光。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　市安全生产委员会组织有关部门，每两年对区县政府主要领导人进行一次安全生产教育；各区县安全生产委员会组织有关部门，每两年对街道办事处、乡镇政府主要领导人进行一次安全生产教育；市安全生产监督局负责每年对安全生产执法人员进行一次安全生产教育。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　市、区县政府每年安排一定的经费，专项用于安全生产宣传教育和法律法规规定的考核。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　二00四年七月七日 </span><br />\r\n<br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">发布部门：北京市政府　发布日期：2004年07月07日　实施日期：2004年07月07日　(地方法规)&nbsp;</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<!--End -->', null, '2013-06-25 15:25:50', '1000', '1', '1');
INSERT INTO `tbl_library` VALUES ('FVfm6v', 'ZrIzaa', '2', '内蒙古自治区水库移民工作稽察办法（试行）', null, '<p style=\"text-align:center;\">\r\n	<span style=\"line-height:2;\">内蒙古自治区发展和改革委员会关于印发《内蒙古自治区水库移民工作稽察办法》（试行）的通知</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:2;\">内发改规范字[2013]4号</span> \r\n</p>\r\n<p>\r\n	<span class=\"zt2\"> </span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;line-height:2;\">各盟市移民管理机构：</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">为贯彻落实《国家发展改革委办公厅关于加强水库移民后期扶持政策实施监督检查工作的通知》（发改办[2012]1131号）精神，加强全区水库移民工作的监督管理，发挥稽察监督功能，建立水库移民工作稽察制度，依照水利部水库移民开发局印发的《水库移民后期扶持政策实施稽察办法》（移稽察[2012]67号），我委研究制定了《内蒙古自治区水库移民工作稽察办法》（试行），现印发你们，请遵照执行。</span> \r\n</p>\r\n<p>\r\n	<span> </span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">附件：《内蒙古自治区水库移民工作稽察办法》（试行）</span> \r\n</p>\r\n<p>\r\n	<span> </span> \r\n</p>\r\n<p>\r\n	<span> </span> \r\n</p>\r\n<p>\r\n	<span> </span> \r\n</p>\r\n<p style=\"text-indent:216pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　 \r\n内蒙古自治区发展和改革委员会</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;line-height:2;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;&nbsp;2013年3月28日</span> \r\n</p>\r\n<p align=\"justify\">\r\n	<span></span><span style=\"font-size:12px;line-height:2;\">附件：</span> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">内蒙古自治区水库移民工作稽察办法</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">（试行）</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span> </span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第一章 总 则</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第一条 为加强全区水库移民工作的监督管理，根据《大中型水利水电工程建设征地补偿和移民安置条例》（国务院令第471号）、《国务院关于完善大中型水库移民后期扶持政策的意见》（国发[2006]17号）、《国家发展改革委员会办公厅关于加强水库移民后期扶持政策实施监督检查工作的通知》（发改农经[2012]1131号）、水利部水库移民开发局印发的《水库移民后期扶持政策实施稽察办法》等规定，结合自治区实际，制定本办法。</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二条 水库移民稽察是对国家和自治区水库移民政策贯彻落实情况、移民安置规划、移民后期扶持规划、库区和移民安置区经济社会发展规划的实施、以及水库移民后期扶持效果等进行监督检查。</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三条 水库移民稽察工作对自治区人民政府水库移民管理机构负责；稽察工作接受国家水库移民管理机构的指导和监督检查。</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四条 水库移民稽察工作坚持依法办事、客观公正、实事求是、突出重点、服务移民的原则。</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第五条 各盟市、旗县人民政府有关部门、移民管理机构，各相关单位和个人，应积极配合稽察工作。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二章 稽察工作组织管理</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第六条 自治区发展改革委员会负责全区水库移民稽察工作的领导和协调；自治区大中型水利水电工程移民安置工作办公室（简称自治区移民办）负责水库移民稽察工作的组织实施，其主要职责是:</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）贯彻落实国家有关水库移民工作监督检查政策法规，研究制定全区水库移民稽察工作规章制度；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）制订自治区年度水库移民稽察工作计划；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）组织开展水库移民稽察工作，印发整改意见通知书；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）督促落实国家和自治区水库移民稽察整改意见；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（五）负责本级水库移民稽察专家管理；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（六）上报全区水库移民稽察工作情况，收集汇总和发布水库移民稽察工作信息；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（七）完成国家水库移民管理机构交办的各项稽察任务。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第七条 各盟市移民管理机构按照第六条的内容，协助自治区移民办开展工作。</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第八条 水库移民稽察对象为盟市和旗县两级移民管理工作，重点是以旗县为被稽察单位。水库移民稽察分为常规稽察和专项稽察两种工作内容。</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第九条 稽察工作一般每年开展1-2次，形成定期报告制度。自治区移民办于每年元月份向部际联席会议办公室报告上年度全区水库移民稽察工作情况，同时抄送自治区厅际联席会议有关单位。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十条 水库移民稽察采取稽察专家组的方式开展工作。稽察组一般由组长和若干名专家组成，组长由自治区移民办派员或委派专家担任。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十一条 水库移民稽察组组长主要职责：</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）拟定稽察组工作方案；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）全面负责稽察组承担的工作；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）客观公正评价被稽察对象；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）提出整改意见和建议；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（五）完成稽察报告，并对报告负责。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十二条  建立自治区水库移民稽察专家库，水库移民稽察专家由自治区移民办聘任，聘期一年，可以连聘。由稽察专家库随机抽取专家，组成专家组开展工作。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十三条 稽察专家一般应具备以下条件：</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）熟悉国家有关水库移民法规、政策、制度；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）具有较强的综合分析和判断能力；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）具备相关专业高级职称，有较丰富的水库移民工作经验或项目管理、财务管理等方面的知识和经验；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）坚持原则、清正廉洁、公平公正、忠于职守；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（五）身体健康，能适应较长时间出差和基层艰苦工作。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三章 稽察工作内容</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十四条 水库移民后期扶持政策实施保障机制建立与运行情况。</span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">按照国家和自治区水库移民法规、政策规定和要求，制定相应的地方配套文件，包括水库移民后期扶持政策实施方案、人口核定登记工作实施细则、扶持方式确定意见、资金管理办法、项目管理办法、监督检查和责任追究办法以及宣传提纲等。配套文件应符合国家政策，满足政策实施需要。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十五条 水库移民资金落实和使用情况。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">移民安置资金、后期扶持资金（包括后期扶持直补资金、后期扶持结余资金、应急补助资金、库区基金），应按照项目投资计划、年度资金计划、移民人口核定、批准的规划、设计和实施方案及时足额兑现和到位。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">资金预算报批、年度资金计划下达和变更，应符合国家和自治区相关程序和规定，资金拨付应符合时间要求。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">年度计划的执行应符合规定和要求，资金使用和管理应严格执行国家和自治区相关政策法规和规定，资金使用合规合理，资金管理制度健全完善。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十六条 水库移民人口核定动态管理情况。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">水库移民人口核定应符合国家有关规定，移民人口核定登记的成果应履行上级审核和审定程序，新建水库移民人口核定登记应经过国家规定程序审定，直补人口应按照规定实行动态管理。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十七条 水库移民相关规划实施情况。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">水库移民安置规划、后期扶持规划、库区和移民安置区经济社会发展规划及其他专项规划，编制依据、编制程序和方法、规划时限、规划目标任务、规划内容深度应遵循国家和自治区有关规定和要求，规划重点内容和总投资规模应符合当地实际，规划报批和变更应履行相应程序，规划编报和审批主体应符合国家规定，规划实施保障措施应明确有力，规划实施效果应基本达到目标要求。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十八条 水库移民后期扶持项目建设情况。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">水库移民后期扶持项目建设应严格履行国家和自治区相关固定资产投资项目管理规定和程序，按照批准的内容、地点、规模和起止年限建设，项目重大变更应按审批权限履行变更报批程序；项目法人单位和管理人员应符合规定要求；设计、施工、监理单位具备相应资质；项目管理实行法人制、合同制、招投标制、监理制，项目决策和实施接受移民群众监督；项目资金实行专人、专户、专帐管理；项目建成按时竣工验收交付，项目档案资料齐备完整。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第十九条 水库移民后期扶持政策实施效果。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">移民群众收入水平提高情况，移民群众生产生活条件改善情况，移民及相关群众合法权益保障情况，库区和移民安置区基础设施建设、经济发展和社会稳定情况。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十条 专项稽察内容根据实际情况确定。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四章 稽察程序与方式</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十一条 自治区移民办定期组织对全区水库移民工作进行稽察。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十二条 开展稽察工作一般应事先书面通知被稽察地区移民管理机构、相关部门及被稽察单位。特殊情况也可采取事先不通知的方式进行。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十三条 稽察工作一般程序</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）发出稽察通知；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）被稽察单位按要求准备资料；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）稽察组制定工作方案；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）听取被稽察单位汇报；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（五）现场调查及抽样检测；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（六）综合分析评价，编写稽察报告初稿；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（七）与当地移民管理机构和被稽察单位交换意见；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（八）完成稽察报告；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（九）报经主管部门审核同意后印发稽察整改意见。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十四条 稽察工作方案主要包括工作任务、时间安排、人员分工、稽察重点、确定样本、核查事项等内容。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十五条 现场调查主要采取听取有关单位汇报、核实基础材料有关数据、现场查勘实物量完成进度和质量、向移民群众和相关人员了解情况等方式进行。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十六条 稽察工作中发现重大、紧急情况，组长应立即向稽察组织单位报告。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第五章 稽察报告及整改意见</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十七条 稽察报告是稽察工作的成果，应按照相关规定和要求撰写，按时提交。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十八条 稽察报告主要内容：</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）稽察工作概况；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）被稽察单位基本情况；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）稽察内容及评价；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）存在的主要问题；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（五）处理意见和建议。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">专项稽察报告内容根据专项稽察工作具体任务和要求确定。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第二十九条 稽察组织单位依据稽察报告，认真研究拟订稽察整改意见和建议，报经主管部门审核同意，向被稽察单位印发整改意见和建议。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十条 被稽察单位应按照稽察整改意见和建议，认真组织整改，并在规定时间内将整改结果报告稽察组织单位。稽察组织单位将适时对整改情况进行复核。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十一条 对在稽察中发现违反有关水库移民政策法规的，稽察组织单位根据情况轻重可以提出以下单项或多项处理意见：</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）责令限期整改；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）通报批评；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）暂停项目审批和资金拨付，减少资金投入；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）建议有关部门对相关责任单位予以处罚，追究有关责任人的责任。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第六章 稽察人员管理</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十二条 稽察人员开展稽察工作受法律保护，有关单位和人员不得拒绝、阻碍稽察人员执行公务。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十三条 稽察人员开展稽察工作有以下权利：</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）向当地移民管理机构和有关部门、移民安置和后期扶持各类规划实施主体、相关单位和人员调查了解情况，查阅相关文件资料；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）要求被稽察单位、项目法人及参建单位提供与建设项目有关的文件、资料、合同、数据、账簿、凭证、报表，依法复制、录音、拍摄有关的证词证据；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）进入与稽察事项有关的场所或地点，进行查验、核实、取证、质询等工作。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十四条 稽察人员开展稽察工作应履行以下义务：</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）依法行使职责，坚持原则、秉公办事、自觉维护国家利益和移民群众合法权益；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）深入现场，客观公正、实事求是地反映情况和问题，认真完成稽察任务；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）自觉遵守党和国家廉洁自律有关规定；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）保守国家机密和被稽察单位的商业秘密；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（五）为举报人保密；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（六）在规定时限内对稽察情况和处理意见进行保密。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十五条 稽察人员不得参与和干涉被稽察单位的任何具体工作，不得接受被稽察单位馈赠，不得在被稽察单位报销费用，不得参加被稽察单位安排的娱乐、旅游活动，不得利用工作之便为自己和亲友及他人谋取私利。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十六条 稽察人员有下列情况之一的应当回避：</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）在被稽察单位或项目任（兼）职；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）曾参与过被稽察单位和项目的相关工作；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）与被稽察单位主要负责人或项目负责人有直系或旁系亲属关系；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）具有可能影响公正执行公务的其他关系。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十七条 稽察人员违反第三十四条、第三十五条规定，严重失职，对重大问题隐匿不报，编造虚假稽察报告，干扰、损害被稽察单位和项目的正常工作，利用工作之便谋取私利等，将解除聘任，情节严重的应建议有关部门给予党纪政纪处分，直至依法追究法律责任。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第七章 被稽察单位责任与义务</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十八条 被稽察单位的合法权益应当受到尊重和保护。被稽察单位对稽察结论有异议的，可以向稽察组织单位或其上级单位反映。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第三十九条 被稽察单位对稽察人员在稽察工作中出现的违反有关规定的不当行为，应当向稽察组织单位反映。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四十条 被稽察单位应当如实向稽察人员提供相关文件、合同、报表等资料，报告移民管理工作中的重大事项，不得拒绝、隐匿、伪报。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四十一条 被稽察单位和有关人员有下列行为之一的，对主要负责人和直接责任人，应建议其上级机关依法给予行政处分，直至依法追究法律责任。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（一）拒绝、阻碍稽察人员依法履行职责；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（二）拒绝、无故拖延向稽察人员提供本办法规定的有关资料和情况；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（三）隐匿、伪报有关资料；</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">（四）影响公正稽察的其他行为。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第八章 稽察工作保障措施</span> \r\n</p>\r\n<p style=\"text-indent:32.3pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四十二条 自治区各级发展改革、财政、水利、国土等有关部门，应积极支持和配合水库移民稽察工作，及时提供相关文件资料，保障稽察工作协调进行。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四十三条 自治区移民管理机构应明确稽察工作职能设置，整合力量，充实工作人员；稽察工作经费申请和使用，严格执行《大中型水库移民后期扶持政策实施工作专项补助经费使用管理暂行办法》，确保稽察工作顺利开展。</span> \r\n</p>\r\n<p style=\"text-align:center;\" align=\"center\">\r\n	<span style=\"font-size:12px;line-height:2;\">第九章 附 则</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四十四条 本办法由自治区大中型水利水电工程移民工作办公室负责解释。</span> \r\n</p>\r\n<p style=\"text-indent:32.25pt;\">\r\n	<span style=\"font-size:12px;line-height:2;\">第四十五条 本办法自印发之日起施行。</span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:SimSun;\"></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;\"></span> \r\n</p>', null, '2013-06-25 15:26:04', '990', '1', '1');
INSERT INTO `tbl_library` VALUES ('FN3Qza', 'ZrIzaa', '2', '石家庄市供热用热条例', null, '<a href=\"/userfiles/file/20130625/20130625135350_666.doc\" target=\"_blank\">石家庄市供热用热条例</a>', null, '2013-06-25 15:26:11', '980', '1', '1');
INSERT INTO `tbl_library` VALUES ('YFZJFn', 'ZrIzaa', '2', '黑龙江省科学技术普及条例 ', null, '<div align=\"left\">\r\n	<div style=\"text-align:center;\">\r\n		<span style=\"font-family:SimSun;line-height:2;\">黑龙江省第十届人民代表大会</span> \r\n	</div>\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> \r\n	<div style=\"text-align:center;\">\r\n		<span style=\"line-height:2;\">常务委员会公告</span> \r\n	</div>\r\n</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> \r\n	<div style=\"text-align:center;\">\r\n		<span style=\"line-height:2;\">第47号</span> \r\n	</div>\r\n</span> \r\n</div>\r\n<div align=\"left\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">《黑龙江省科学技术普及条例》已由黑龙江省第十届人民代表大会常务委员会第十七次会议于2005年10月17日通过，现予公布，自2006年1月1日起施行。</span> \r\n</div>\r\n<div align=\"left\">\r\n</div>\r\n<div style=\"margin-left:215.5pt;\" align=\"left\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 黑龙江省人民代表大会常务委员会 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;2005年10月17日</span> \r\n</div>\r\n<div align=\"center\">\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">黑龙江省科学技术普及条例</span></b> \r\n</div>\r\n<div align=\"left\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（２００５年１０月１７日黑龙江省第十届人民代表大会常务委员会第十七次会议通过）</span> \r\n</div>\r\n<div align=\"left\">\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一章    总   则</span></b><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第一条 为加强科学技术普及工作，提高公民的科学文化素质，推动全省经济发展和社会进步，根据《中华人民共和国科学技术普及法》及有关法律、行政法规，制定本条例。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二条 本条例所称科学技术普及（以下简称科普），是指用公众易于理解、接受和参与的方式，宣传科学技术知识、倡导科学方法、传播科学思想、弘扬科学精神的活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三条 在本省行政区域内的国家机关、社会团体、企业事业单位、居民委员会、村民委员会及其他组织和公民从事科普活动，适用本条例。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四条 科普工作是公益事业,是科学技术工作的重要组成部分，是科教兴省的一项战略任务。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">科普工作的对象是全体公民，重点是青少年、农民和工人。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第五条 科普工作应当坚持科学态度，反对迷信和反科学、伪科学行为。任何组织和个人不得以科普为名传播不健康、不文明的行为方式、进行其他有损社会公共利益的活动，不得将违背科学原则和科学精神的主张和观点，作为科普知识传播和推广。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二章    \r\n组织和管理</span><br />\r\n</b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第六条 县级以上人民政府应当加强对科普工作的领导，将科普工作作为社会主义物质文明、政治文明和精神文明的重要组成部分，将科普事业纳入本地区国民经济、社会发展计划和科学技术发展规划，作为创建文明单位的考核内容。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第七条 县级以上人民政府应当建立由政府主管领导负责，科学技术行政部门和其他有关部门以及社会团体参加的科普工作联席会议制度。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第八条 科普工作联席会议负责协调本地区的科普工作，履行下列职责：</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（一）审议科普工作总体规划； </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二）研究确定科普事业发展的重大政策、措施；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（三）审议科普工作年度工作要点；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（四）促进各部门及全社会支持并参与科普工作；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（五）协调本地区和部门间的科普工作；</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">科普工作联席会议办公室设在县级以上科学技术行政部门。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第九条 县级以上科学技术行政部门是科普工作的主管部门，负责制定科普工作总体规划，实行政策引导，进行监督检查，并组织实施本条例。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">县级以上人民政府其他有关行政部门应当制定并实施本部门的科普工作计划，并根据各自的职责，做好相关的科普工作。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十条 科学技术协会应当发挥科普工作的主要社会力量的作用，参与制定本地区科普工作的总体规划和实施计划，为政府科普工作决策提供建议。充分利用并发挥其科普网络和组织优势，加强对所属团体和农村专业技术协会（研究会）科普工作的组织管理与业务指导，鼓励并支持科技人员进行科普研究和科普创作；组织开展经常性、群众性、社会性的科普工作。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　第三章    \r\n青少年、农民和工人科普</span><br />\r\n</b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十一条 县级以上教育行政部门应当发挥中小学校开展青少年科普教育的主渠道作用，将科普教育纳入素质教育内容，并安排一定的教学时数，在校本教材和乡土教材中适当增加科普内容。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十二条 各类学校及其他教育机构应当制定科普教育活动方案，组织学生参加科普兴趣小组、科技创新大赛、具有科普内容的夏令营、冬令营和小发明、小创造等活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十三条 中小学校应当根据需要组织学生参观科技馆、博物馆等科普教育基地。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">具备开放条件的国家和省重点实验室应当定期向青少年免费开放。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十四条 中小学校应当设立专职或者兼职科普辅导教师，提供科普读物，保证必要的讲座时间，并定期组织培训。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十五条 科技、教育、新闻出版等有关部门和科学技术协会应当有针对性地向青少年推荐科普课外读物，组织有关专家编写适合青少年特点的科普课外读物，并向青少年优惠提供。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">图书、电子、音像出版部门应当将青少年科普读物纳入出版计划，保证每年制作和出版一定数量的符合青少年特点的科普图书、电子和音像制品。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十六条 县级以上农业行政部门应当会同有关单位，根据农村实际情况，制定并组织实施农村科普工作计划。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">社会各界应当采取多种形式，参与并支持农村科普工作。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十七条 每年的农历正月十六为农民科技节。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">县级以上人民政府应当在农民科技节期间，组织开展农民科技竞赛、举办农业科技讲座等活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十八条 乡（镇）人民政府、村民委员会应当发挥乡（镇）科普组织的作用，根据当地经济与社会发展的需要，围绕科学生产、文明生活，开展科普活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">乡（镇）、村文化站（室）、广播站应当宣传科学、文明的生产和生活方式。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">农村各类经济组织和农村专业技术协会应当利用自身优势，为农民提供科技咨询、技术推广、技术指导等服务。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第十九条 县级以上农业行政部门及科学技术协会应当组织农业院校、农业科研院所、农业技术推广机构及其他涉农单位，开展面向农民的农业科技培训，为农民提供科技信息服务，并会同有关部门适时开展进城务工农民的技能培训。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十条 县级以上农业行政部门及科学技术协会应当定期组织乡（镇）农业技术干部培训，扶持并发挥重点科技示范户、种养殖大户等的引导和带动作用。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十一条 县级以上人民政府文化、科技、卫生和农业等有关部门每年应当开展以文化、科技、卫生和农业等为主要内容的下乡活动，并落实经费，制定奖励、考评等相关制度。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">文化、科技和农业等有关部门应当每年根据农民需要，组织捐赠科普图书，放映科技电影，演出文艺节目，举办农村实用技术讲座、咨询、培训等活动，并协助建设农村文化、科普图书室。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">医疗卫生单位应当每年组织医务人员向农民开展医疗保健咨询等服务，宣传疾病防治、优生优育、健康保健等知识。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十二条 科学技术协会应当协助政府组织科技人员开展科普之冬活动，并组织有关单位开展科普大集和科普村镇建设等活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十三条 县级以上工会和产业工会组织应当组织职工开展以提高技术水平和创新能力为重点的科普宣传教育活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十四条 各级工会组织应当利用现有的文化宫（馆）、职工干部培训学院（校）、技术交流馆等场所开展职工技术创新、技能培训、技术推广等科普活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十五条 企业应当结合技术改造、新产品开发、新技术和新工艺推广应用，组织职工技能培训，普及与生产经营、职业卫生、安全防护等有关的科学技术知识，提高生产技能。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十六条 有条件的企业可以创办行业博物馆、科技馆，建立科普组织，开展科普活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四章    \r\n社会责任</span></b><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十七条 普及科学技术是全社会的共同责任，社会各界应当参与和支持，提倡公民参加科普活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">鼓励各类社会组织和个人兴办科普事业。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十八条 县级以上人事行政部门应当将科普教育纳入国家工作人员、专业技术人员继续教育培训内容。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各级干部教育培训机构应当根据培训对象的需要，开设现代科技基础知识课程或者举办科技知识专题讲座。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第二十九条 各级领导干部应当每年参加一次以上的现代科技知识、科学思想和科学方法的科普学习讲座。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十条 工会、共青团和妇联等社会团体应当根据科普工作规划和计划，并结合职工、青少年和妇女的特点开展科普活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十一条 广播电台、电视台和综合性互联网站应当开办固定科普宣传栏目，制作科普节目，在科技活动周和重大科普活动期间应当免费播放科普公益性广告。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">综合类和自然科学类报纸、期刊应当开设科普专栏、专版，宣传科普知识。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">影视生产、发行和放映单位应当制作、发行、放映科普影视作品。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十二条 科研机构、高等院校应当组织科技工作者和教师、学生开展科普活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">具备开放条件的科研机构、高等院校应当向公众开放具有科普功能的陈列室、生产车间、实验基地、实验室和其他场地、设施。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十三条 省重点科技攻关项目和重大产业化项目，项目承担单位或者项目负责人应当根据项目特点，开展相关科普宣传。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">鼓励省科学技术奖的申报人提交相关的科普成果。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">具备开放条件的省重大建设和技改项目完成后，应当提供向公众开放、通俗易懂的科普宣传模型和展示等。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十四条 科技馆、博物馆、图书馆、气象站、公园、医院、商场、车站、影剧院、体育场馆等公共场所的经营管理单位，应当结合自身的业务和特点，面向公众开展经常性的科普宣传。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十五条 大型洽谈会、博览会、重大体育赛事等大型活动的承办单位，应当利用场馆和设施设置相关的科普宣传内容。 </span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十六条 居民委员会应当利用所在地的科技、教育、文化、卫生等资源，定期向居民开办环境保护、医疗卫生、婚育知识、公共安全、节约等贴近百姓生活的科普讲座，并设置固定科普画廊、科普宣传板，开展创建科普文明社区活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十七条 鼓励大学生、专家、学者和专业技术人员参与各种科普志愿活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十八条 每年五月的第三周为全省科技活动周。社会各界应当根据全省科技活动周的主题开展科普活动。</span> \r\n</div>\r\n<p>\r\n	<b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第五章    保障措施</span></b><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第三十九条 各级人民政府应当将科普经费列入同级财政预算，并随着国民经济和社会事业的发展逐步增加对科普经费的投入。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各级人民政府有关部门应当安排一定的科普经费。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">提倡社会团体和企业事业单位安排一定的经费用于开展科普活动。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">科普经费和社会组织、个人资助科普事业的财产，必须用于科普事业，任何单位或者个人不得克扣、截留、挪用。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十条 省级人民政府、设区的市及有条件的县（市）人民政府应当将科普场馆、设施建设纳入城乡建设规划和基本建设计划，并加强对现有科普场馆、设施的利用、维修和改造。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">县级以上人民政府应当利用现有的科技、教育、文化等设施和场所，做好科普工作。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十一条 利用政府投资建设或者享受政策优惠的博物馆、科技馆、青年宫、少年宫（中心）、文化宫、妇女儿童活动中心等场馆不得擅自改作他用。对青少年、六十周岁以上老年人及残疾人应当免费或者优惠开放；科技活动周期间及重大科普宣传日应当免费向社会开放；经费困难的，同级财政应当予以补贴，使其正常运行。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十二条 鼓励和支持省外、境外博物馆、科技馆到我省布展、巡展；向青少年、六十周岁以上老年人及残疾人免费或者优惠开放的，按照国家有关规定，可以享受优惠政策。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十三条 获得县级以上科普奖项或者具有科普创作成果的，在专业技术职务任职资格评审中，同等条件下予以优先。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十四条 县级以上人民政府应当加强科普组织和科普队伍建设，建立、健全科普组织，提高科普工作者待遇，改善科普工作者条件，依法维护科普组织和科普工作者的合法权益，鼓励科普组织和科普工作者自主开展科普活动，依法兴办科普事业。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十五条 县级以上人民政府、科学技术协会和有关单位应当对在科普工作中作出突出贡献的组织和个人给予表彰和奖励。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第六章    \r\n法律责任</span><br />\r\n</b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十六条 违反本条例规定，未履行法定职责的，由上级行政机关或者有关部门予以通报批评，并责令改正。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十七条 以科普为名传播不健康、不文明的行为方式、进行其他有损社会公共利益的活动，扰乱社会秩序或者骗取财物的，由有关主管部门给予批评教育；违反治安管理规定的，由公安机关依法给予治安管理处罚；构成犯罪的，依法追究刑事责任。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十八条 违反本条例规定，克扣、截留、挪用科普财政经费或者贪污、挪用捐赠款物的，由有关主管部门责令限期归还，对负有责任的主管人员和其他直接责任人员依法给予行政处分；构成犯罪的，依法追究刑事责任。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第四十九条 擅自将政府投资建设的科普场所改为他用妨碍开展科普活动的，由上级主管部门责令限期改正；情节严重的，对直接负责的主管人员和其他直接责任人员依法给予行政处分。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第五十条 国家工作人员在科普工作中，玩忽职守、滥用职权、徇私舞弊的，依法给予行政处分；构成犯罪的，依法追究刑事责任。</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　　　　　　　　　　　　　</span><b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第七章    \r\n附 则</span></b><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　</span><br />\r\n<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">第五十一条 本条例自2006年1月1日起施行。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-25 15:26:19', '970', '1', '1');
INSERT INTO `tbl_library` VALUES ('qMNfqq', 'ZrIzaa', '2', '江苏省工程实验室  建设项目管理办法（试行） ', null, '<p>\r\n	<br />\r\n</p>\r\n<p align=\"center\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">江苏省发展和改革委员会文件</span></b> \r\n</p>\r\n<p align=\"center\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">苏发改规发〔2010〕2号</span> \r\n</p>\r\n<p align=\"center\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">省发展改革委关于印发《江苏省工程实验室</span></b> \r\n</p>\r\n<p align=\"center\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">建设项目管理办法（试行）》的通知</span></b> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">各市发展改革委，省有关部门：</span> \r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">现将《江苏省工程实验室建设项目管理办法（试行）》印发给你们。请按通知要求，认真组织开展本地、本部门工程实验室建设项目的建设工作。</span> \r\n</p>\r\n<p style=\"text-indent:238pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">二○一○年七月一日</span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">附件一：</span></b> \r\n</p>\r\n<p>\r\n	<b> </b> \r\n</p>\r\n<p align=\"center\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">江苏省工程实验室建设项目管理办法（试行）</span></b> \r\n</p>\r\n<p align=\"center\">\r\n	<b> </b> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第一条为了贯彻落实科学发展观，促进产业技术进步，加快发展战略性新兴产业，增强产业自主创新能力，加强和规范本省工程实验室建设项目管理，根据《国家工程实验室管理办法（试行）》（国家发展改革委第54号令），结合本省实际，制定本办法。</span> \r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第二条本办法所称江苏省工程实验室，是指为提高本省产业自主创新能力和核心竞争力，开发关键技术装备，强化对国家、省重点工程和项目的技术支撑，依托企业、科研院所或高校设立的研究开发平台。</span> \r\n</p>\r\n<p style=\"text-align:center;text-indent:30pt;\" align=\"center\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第三条江苏省工程实验室的主要任务：开展重点产业核心技术</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">攻关和关键工艺试验研究，研制重大装备样机及其关键部件，开展产业技术标准的研究，培养工程技术创新人才，促进重大科技成果的转化和应用，为行业、企业提供技术服务。</span> \r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第四条江苏省工程实验室的建设原则：</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（一）坚持关键技术供给方向，为重大工程建设和重点产业发展提供技术支撑；</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（二）坚持高水平、专业化，具有显著的专业技术特色、突出的产业技术优势和高水平的创新团队；</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（三）坚持政府引导，整合创新资源，面向市场；</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（四） 坚持体制、机制创新，探索具有行业特点和竞争力的组织形式和运行机制。</span> \r\n</p>\r\n<p style=\"text-indent:30pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第五条 \r\n在江苏省工程实验室项目建设中，省发展改革委主要负责：</span> \r\n</p>\r\n<p style=\"text-indent:30pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（一）发布工程实验室重点建设领域，提出相关政策措施，指导江苏省工程实验室的建设和发展；</span> \r\n</p>\r\n<p style=\"text-indent:30pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（二）受理江苏省工程实验室建设项目申请，组织有关方面对申报材料进行评审；</span> \r\n</p>\r\n<p style=\"text-indent:29.8pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（三）对符合条件的江苏省工程实验室予以批复。</span> \r\n</p>\r\n<p style=\"text-indent:30pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第六条在江苏省工程实验室项目建设中，省相关部门、各省辖市发展改革委（以下简称为“申报部门”）主要负责：</span> \r\n</p>\r\n<p style=\"text-indent:-18pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">(一)    组织本地区或所属单位按要求申报江苏省工程实验室建设</span> \r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">项目；</span> \r\n</p>\r\n<p style=\"text-indent:6pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">(二)    对有关申报材料进行初步审查；</span> \r\n</p>\r\n<p style=\"text-indent:6pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">(三)    对江苏省工程实验室运行进行管理，组织开展江苏省工程</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">实验室的验收和运行评价等工作。</span> \r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第七条 建设单位主要负责：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（一）按照有关批准文件要求，落实江苏省工程实验室建设与运行的人员、设备、场地等相关支撑条件，筹措江苏省工程实验室的建设和运行经费，保障江苏省工程实验室的顺利建设；</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（二）按照有关批准文件要求，完成江苏省工程实验室的建设任务；</span> \r\n</p>\r\n<p style=\"text-indent:14pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（三）按照有关要求，向省发展改革委及申报部门报送项目建设及运行情况。</span> \r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第八条申请江苏省工程实验室建设项目应符合以下基本条件：</span> \r\n</p>\r\n<p style=\"text-indent:-66pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（一）      申请单位应具有从事相关领域研发的创新团队，具有在相</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">应行业领域工程化研发的带头人，带头人原则上应为企业法人或工程化科研团队领军人才；</span> \r\n</p>\r\n<p style=\"text-indent:-66pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（二）              申请单位应具有相关先进的研发试验设施，拥有一批能够</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">带动产业发展的高水平研发成果和技术储备；</span> \r\n</p>\r\n<p style=\"text-indent:-66pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（三）申请建设的江苏省工程实验室定位明确、发展思路清晰，</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">任务和目标合理，管理体制和运行机制规范；</span> \r\n</p>\r\n<p style=\"text-indent:17.9pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（四）符合国家和省相关要求。</span> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第九条申报部门对有关申报材料及建设进行初步审查后，向省发展改革委报送申请报告及附件材料，省发展改革委在组织评审基础上，对符合条件的江苏省工程实验室项目进行批准建设。</span> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十条 \r\n江苏省工程实验室建设项目完成后，建设单位可向申报部门提出验收申请，省发展改革委可委托申报部门按相关规定组织验收。</span> \r\n</p>\r\n<p style=\"text-indent:28pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十一条江苏省工程实验室实行运行情况年报制度。建设单位应于每年12月底之前将年度运行总结报告上报负责申报部门，年度报告主要包括科研基础设施与条件运行情况、人才队伍建设情况、技术研发和转移的重大进展情况以及其它相关情况和建议等，申报部门审查、汇总后，于次年1月底之前上报省发展改革委。</span> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十二条 \r\n省发展改革委定期组织各申报部门开展省级工程实验室评价工作，必要时，将委托有关专家或专业机构定期对江苏省工程实验室运行情况进行评价，并公布评价结果。</span> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十三条评价的主要内容：完成重大任务和重点工程相关研发工作的情况；获得自主知识产权技术成果以及对产业发展的支撑带动作用；研发试验设施建设和利用情况；产学研合作以及人才队伍建设情况；建设单位对江苏省工程实验室的保障作用；批准文件中明确的有关要求。</span> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十四条 江苏省工程实验室名称、建设单位、建设内容等如发生变化，需由申报部门报省发展改革委审核批准。</span> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十五条江苏省工程实验室建设和运行中出现下列情况的，视情节轻重做出责令其限期整改或撤消命名的处理：</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（一）擅自改变项目建设目标、规模、主要内容；</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（二）有重大工程质量、安全、环境等问题，造成较大经济损失和较坏社会影响；</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（三）有重大弄虚作假、伪造或瞒报行为；</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">（四）出现造成严重后果的其他情况。</span><b> </b> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十六条  江苏省工程实验室统一命名为“江苏省XXX工程实验室”。</span> \r\n</p>\r\n<p style=\"text-indent:27pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">第十七条 本办法自２０10年７月 \r\n日起施行。各地、各部门可结合本地实际情况制定相应的管理办法。</span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">附件二：江苏省工程实验室建设项目封面格式</span></b> \r\n</p>\r\n<p align=\"center\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">江苏省工程实验室建设项目申请报告</span></b> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">项目名称：江苏省XXX工程实验室</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">建设单位：</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">申报部门：</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">申报时间</span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:SimSun;line-height:2;font-size:12px;\">附件三：江苏省工程实验室建设项目申请报告编制大纲</span></b> \r\n</p>\r\n<p style=\"text-indent:14pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">一、 项目摘要</span> \r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">二、项目建设的依据、背景与意义</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">三、技术发展与应用前景分析</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;font-size:12px;\"> 1、国内外技术状况与发展趋势预测分析</span> \r\n</p>\r\n<p style=\"text-indent:14pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">2、技术发展的比较（包括本单位技术水平优势和劣势、关键技术突破点）</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">四、重点领域、目标与任务</span> \r\n</p>\r\n<p style=\"text-indent:22pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">1、重点领域</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">2、近期和中期目标</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">3、主要功能与任务</span> \r\n</p>\r\n<p style=\"text-indent:31.8pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">五、组织机构、管理与运行机制</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">1、建设单位概况</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">2、省级工程实验室的机构设置与职责</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">3、主要技术带头人、管理人员概况及技术团队情况</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">4、运行和管理机制</span> \r\n</p>\r\n<p style=\"text-indent:26.9pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">六、建设方案</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;font-size:12px;\"> 1、建设规模</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">2、建设内容（包括技术方案、设备方案和工程方案及其合理性）</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">3、建设周期</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">4、建设地点</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">七、节能及环境影响</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">1、节能分析</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">2、环境影响评价</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">八、投资估算及资金筹措方案</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;font-size:12px;\"> 1、项目总投资估算</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;font-size:12px;\"> 2、项目资金筹措方案及其落实情况</span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">九、项目经济和社会效益分析</span> \r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">1、初步经济效益分析</span> \r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">2、社会效益分析</span> \r\n</p>\r\n<p align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">十、其它需说明的问题</span><br />\r\n<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">十一、相关文件所要求的附件、附图、附表</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;font-size:12px;\">十二、建设单位地址、联系人、联系电话及手机、传真</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-25 15:26:34', '960', '1', '1');
INSERT INTO `tbl_library` VALUES ('nuIZj2', 'ZrIzaa', '2', '浙江省重大产业项目申报办法（试行）', null, '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">省发改委、省国土资源</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">厅关于印发《浙江省重大产业项目申报办法（试行）》的通知</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">文号：</span></strong><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> 浙发改投资〔2012〕1239号</span> \r\n</div>\r\n<div id=\"zoom\">\r\n	<!--<$[信息内容]>begin--><!--ZJEG_RSS.content.begin-->\r\n	<div>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">各市、县（市、区）政府，省级有关单位：</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　为贯彻落实《浙江省政府办公厅关于建立我省重大产业项目用地保障机制的意见》（浙政办发〔2012〕84号）文件精神，省发改委、省国土厅制订了《浙江省重大产业项目申报办法（试行）》，现印发给你们，请遵照执行。</span> \r\n		</p>\r\n		<p align=\"center\">\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">浙江省发展和改革委员会　　　浙江省国土资源厅</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2012年10月9日</span> \r\n		</p>\r\n		<p align=\"center\">\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">《浙江省重大产业项目申报办法（试行）》</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　根据《浙江省政府办公厅关于建立我省重大产业项目用地保障机制的意见》（浙政办发〔2012〕84号）文件精神，特制订省重大产业项目申报办法（试行）。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　一、申报条件和标准</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　1、符合国家产业政策、体现我省“四大国家战略”以及规划布局要求，属于产业结构调整指导目录或外商投资产业指导目录的鼓励类项目。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　2、项目总投资、投资强度、容积率、单位用地销售收入、单位用地税收、产值能耗等指标符合省重大产业申报标准（见下表）。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　3、项目已完成备案、核准或出具项目服务联系单，且满足环保要求。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　4、项目用地符合所在的县（市、区）土地利用总体规划、能够实现耕地占补平衡、落实标准农田补划，且能在年内完成建设用地组件报批、农用地转用批准后半年内供地。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">　　具体标准如下：</span> \r\n		</p>\r\n		<p align=\"center\">\r\n			<br />\r\n		</p>\r\n		<p align=\"center\">\r\n			<br />\r\n		</p>\r\n		<p align=\"center\">\r\n			<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">省重大产业项目申报标准</span> \r\n		</p>\r\n		<div align=\"center\">\r\n			<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\" width=\"104%\">\r\n				<tbody>\r\n					<tr>\r\n						<td rowspan=\"2\" width=\"24%\" colspan=\"2\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">行业分类</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"35%\" colspan=\"3\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">约束性指标</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"33%\" colspan=\"3\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">预期性指标</span> \r\n							</p>\r\n						</td>\r\n						<td rowspan=\"2\" width=\"6%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">备注</span> \r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td width=\"13%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">总投资(亿元)</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"11%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">投资强度(万元/亩)</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"11%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">容积率</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"12%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">单位用地销售收入(万元/亩)</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"10%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">单位用地税收</span> \r\n							</p>\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">(万元/亩)</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"11%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">产值能耗(吨标煤/万元)</span> \r\n							</p>\r\n						</td>\r\n					</tr>\r\n				</tbody>\r\n				<tbody>\r\n					<tr>\r\n						<td rowspan=\"2\" width=\"16%\">\r\n							<p>\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">1、装备制造</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"7%\">\r\n							<p>\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">领域</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"69%\" colspan=\"6\">\r\n							<p>\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">重大成套设备、数控机床、现代仪器仪表、新型纺织机械、高性能轻工机械、新能源关键设备、轨道交通及民用飞机配套设备、工程机械、环保设备、机械基础件和特色产品等装备制造业项目。</span> \r\n							</p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td width=\"7%\">\r\n							<p>\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">指标</span> \r\n							</p>\r\n							<p>\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">要求</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"13%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">≥6</span> \r\n							</p>\r\n						</td>\r\n						<td width=\"11%\">\r\n							<p align=\"center\">\r\n								<span style=\"font-