INSERT INTO `tbl_library` VALUES ('eu2aeq', 'ZrIzaa', '2', '阅读须知', null, '<p>\r\n	<span style=\"font-family:SimSun;line-height:2.5;\">&nbsp; &nbsp; 如需阅读总院购置的专有资料及相关保密资料，请和华电科研总院 综合管理部 联系索取阅读密码，并请做好有关保密工作，如若发现向外传播保密资料（有意或无意），将根据情节轻重和损失大小，保留对传播人员追究相关法律责任及连带经济责任的权利。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"font-family:SimSun;line-height:2.5;\">联系人：张锋</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"font-family:SimSun;line-height:2.5;\">联系电话：010-5196 6662；18911803600</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"font-family:SimSun;line-height:2.5;\">传真：010-5239 2444</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"font-family:SimSun;line-height:2.5;\">E-mail：feng-zhang@chdi.ac.cn；</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"font-family:SimSun;line-height:2.5;\">&nbsp; &nbsp; &nbsp; &nbsp; zhangfengvipchina@163.com</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"line-height:2.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"line-height:2.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"line-height:2.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"line-height:2.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span style=\"line-height:2.5;\"><br />\r\n</