INSERT INTO `tbl_library` VALUES ('e2mEje', 'nEraQv', '2', '中国能源发展报告2011（馆藏纸质版）', null, '<p>\r\n	<span style=\"background-color:#FEFEFE;font-family:SimSun;line-height:2;\">&nbsp; &nbsp;&nbsp;</span><span style=\"background-color:#FEFEFE;color:#333333;font-family:SimSun;line-height:2;\">总院小图书馆备有纸质图书。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;经济科学出版社; 第1版 (2011年7月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Report%20on%20China%27s%20Energy%20Development%20for%202011&search-alias=books\">Report on China\'s Energy Development for 2011</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;211页\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787514107180\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787514107180\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24.8 x 17.2 x 1.4 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>440 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;经济科学出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	这本《中国能源发展报告(2011)》由刘铁男主编，全书分为十章，内容包括：中国能源发展状况与展望，十二五”能源发展基本思路，电力，煤炭，石油天然气，燃料，可再生能源，能源科技装备，能源国际合作，能源法规建设。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	齐心协力 求实创新 开创农村能源工作新局面<br />\r\n第一章 中国能源发展状况与展望<br />\r\n一、2010年能源供需形势及特点<br />\r\n二、2010年中国能源发展特点<br />\r\n三、2011年中国能源发展展望<br />\r\n第二章 “十二五”能源发展基本思路<br />\r\n一、“十一五”能源发展情况<br />\r\n二、能源发展面临的形势<br />\r\n三、“十二五”能源发展基本思路<br />\r\n四、重大工程和项目<br />\r\n五、重大政策措施<br />\r\n第三章 电力<br />\r\n一、电力工业发展现状<br />\r\n二、存在的主要问题<br />\r\n三、2011年电力工业发展展望<br />\r\n四、解决电力发展问题的政策建议<br />\r\n第四章 煤炭<br />\r\n一、煤炭工业发展<br />\r\n二、煤炭市场运行<br />\r\n三、煤炭与相关产业<br />\r\n四、面临的形势及发展思路<br />\r\n第五章 石油天然气<br />\r\n一、石油勘探与开发<br />\r\n二、石油、天然气管道储运设施和石油储备<br />\r\n三、天然气供应<br />\r\n四、对外合作<br />\r\n第六章 燃料<br />\r\n一、炼油行业<br />\r\n二、煤制燃料<br />\r\n三、生物燃料<br />\r\n第七章 可再生能源<br />\r\n一、概述<br />\r\n二、水电<br />\r\n三、风电<br />\r\n四、太阳能<br />\r\n五、生物质能<br />\r\n六、农村能源<br />\r\n七、地热能<br />\r\n八、海洋能<br />\r\n第八章 能源科技装备<br />\r\n一、能源科技和节能<br />\r\n二、能源重大装备<br />\r\n三、能源节约和科技装备发展思路<br />\r\n第九章 能源国际合作<br />\r\n一、国际能源形势<br />\r\n二、2010年国际能源合作成效<br />\r\n三、2011年国际能源合作展望<br />\r\n第十章 能源法规建设<br />\r\n一、“十一五”能源法规建设取得的成就<br />\r\n二、“十二五”能源法规建设的总体思路、工作目标和2011年能源立法工作展望<br />\r\n附录<br />\r\n年中国能源大事记<br />\r\n年能源统计数据<br />\r\n国际能源机构(IEA)对世界能源形势的分析与展望\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:50:19', '8', '1', '1');
INSERT INTO `tbl_library` VALUES ('bUJfQb', 'qUfmYb', '2', '中国能源中长期(2030、2050)发展战略研究:综合卷', null, '<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《中国能源中长期(2030、2050)发展战略研究:综合卷》：中国工程院重大咨询项目。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	前言<br />\r\n提要<br />\r\n第一章 我国能源中长期发展面临的形势和主要制约条件<br />\r\n第一节 世界能源供需平衡趋紧，传统能源发展模式难以持续<br />\r\n第二节 我国能源需求增长过快，资源制约日益凸显<br />\r\n第三节 能源经济效率低，建设两型社会步履艰难<br />\r\n第四节 国内生态环境制约因素不断加强<br />\r\n第五节 应对全球气候变化，形成实质性碳排放约束<br />\r\n第六节 优化能源结构步履维艰，面临多种挑战<br />\r\n第七节 能源供应的对外依存度逐步提高，能源安全问题不容忽视<br />\r\n第二章 我国各种主要能源的供应能力和发展潜力<br />\r\n第一节 我国煤炭的供应能力<br />\r\n第二节 我国石油资源的供应能力<br />\r\n第三节 我国天然气的供应能力<br />\r\n第四节 我国水电资源的供应能力<br />\r\n第五节 我国核电能源的供应能力<br />\r\n第六节 我国风能发电的供应能力<br />\r\n第七节 我国太阳能发电的供应能力<br />\r\n第八节 我国生物质能的供应能力<br />\r\n第九节 其他可再生能源的供应能力<br />\r\n第十节 小结<br />\r\n第三章 我国中长期科学合理能源需求分析<br />\r\n第一节 实践科学发展观，改变能源消费增长模式<br />\r\n第二节 展望我国未来经济社会发展<br />\r\n第三节 用较低能源增长实现中长期宏伟发展目标<br />\r\n第四章 我国能源中长期(2020．2030、2050)发展战略思路与目标<br />\r\n第一节 我国能源中长期发展战略的指导原则<br />\r\n第二节 我国中长期(2030～2050年)能源战略目标<br />\r\n第五章 2030年前能源战略重点及路线图<br />\r\n第一节 形成节能优先体制机制，切实控制能源消费增长速度和消费总量<br />\r\n第二节 基础能源战略调整重点<br />\r\n第三节 重点发展绿色支柱能源<br />\r\n第四节 支持可再生能源规模化发展，培养新的绿色支柱能源<br />\r\n第五节 建设高效、经济、安全(智能)电网和电力系统，积极发展储能技术<br />\r\n第六节 优化能源开发布局，实现气候与环境友好型能源发展<br />\r\n第六章 2030～2050年能源战略重点及路线图<br />\r\n第一节 完善和提高充分体现建设两型社会的节能优先能源发展体系<br />\r\n第二节 继续壮大清洁能源支柱，实现能源结构多元化、清洁化调整<br />\r\n第七章 能源发展战略的科技支撑<br />\r\n第一节 科技是支撑能源战略顺利实现的根本保证<br />\r\n第二节 能源终端利用领域<br />\r\n第三节 煤炭生产与利用技术领域<br />\r\n第四节 油气资源勘探与开发利用技术<br />\r\n第五节 先进核电技术<br />\r\n第六节 可再生能源利用技术<br />\r\n第七节 新型电力技术<br />\r\n第八节 面向未来的基础性技术<br />\r\n第九节 “十二五”时期重大科技支撑工程<br />\r\n第八章 能源发展战略政策措施与体制保障\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-29 22:55:57', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('Y3A7Jn', 'qUfmYb', '2', '中国至2050年能源科技发展路线图', null, '<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		《中国至2050年能源科技发展路线图》：迎接新科技革命挑战，支持科学与持续发展。\r\n		<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n		</div>\r\n	</div>\r\n	<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n		目录\r\n	</h3>\r\n	<p>\r\n		总序<br />\r\n总前言<br />\r\n前言<br />\r\n摘要<br />\r\n第一章 中国至2050年能源科技发展路线图的构架及编制原则<br />\r\n第一节 能源科技发展路线图的基本构架和现状<br />\r\n第二节 能源科技发展路线图编制的基本原则和流程<br />\r\n第二章 中国至2050年能源发展趋势和关键问题<br />\r\n第一节 中国现代化进程与能源发展<br />\r\n第二节 中国能源需求发展趋势<br />\r\n第三节 中国能源发展关键问题<br />\r\n第三章 中国至2050年能源发展的重要科技问题和技术方向<br />\r\n第一节 中国能源发展的重要科技问题<br />\r\n第二节 中国能源发展的重要技术方向<br />\r\n第四章 中国至2050年能源发展重要技术方向路线图<br />\r\n第一节 高效非化石燃料地面交通技术<br />\r\n第二节 煤炭的洁净和高附加值利用技术<br />\r\n第三节 电网安全稳定技术<br />\r\n第四节 生物质液体燃料和原材料技术<br />\r\n第五节 可再生能源规模化发电技术<br />\r\n第六节 深层地热工程化技术<br />\r\n第七节 氢能利用技术<br />\r\n第八节 天然气水合物开发与利用技术<br />\r\n第九节 新型核电与核废料处理技术<br />\r\n第十节 具有潜在发展前景的能源技术<br />\r\n第五章 中国至年创新能源科技发展总体部署<br />\r\n第一节 中国创新能源科技发展总体部署<br />\r\n第二节 中国能源技术应用时序<br />\r\n第六章 “中国至年能源科技发展路线图”实施保障体系<br />\r\n参考文献\r\n	</p>\r\n	<p>\r\n		后记\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-29 22:56:07', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('7Bv6Vj', 'nEraQv', '2', '能源蓝皮书:中国能源发展报告2012', null, '<div>\r\n	<span><span style=\"line-height:19px;\"><b><br />\r\n</b></span></span> \r\n</div>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;社会科学文献出版社; 第1版 (2012年7月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Annual%20Report%20on%20China%27s%20Energy%20Development%20(2012)&search-alias=books\">Annual Report on China\'s Energy Development (2012)</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;350页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7509735009, 9787509735008\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787509735008\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.6 x 16.6 x 2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>780 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;社会科学文献出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span><span style=\"line-height:19px;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:19px;\"> </span></span> \r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	崔民选编著的《中国能源发展报告(2012)》立足客观翔实的数据，紧密结合国内外经济形势的变化，对当前中国能源各行业的运行态势进行科学分析；针对合同能源管理、能源资本化、能源利益平台构建、能源运输体系建设等热点问题进行深入探讨并提出切实可行的对策和建议。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	Ⅰ　总报告<br />\r\n1　加快推进我国能源结构战略性调整崔民选吕瑞贤&nbsp;<br />\r\n一　2011年能源运行形势&nbsp;<br />\r\n二　2011年能源行业重大事件盘点&nbsp;<br />\r\n三　加快推进能源结构战略性调整&nbsp;<br />\r\nⅡ　行业运行篇<br />\r\n2　依托行业融合，增强煤炭行业可持续发展能力——陈彦斌　任民娟&nbsp;<br />\r\n3　石化行业迈入转型升级关键时期——杨博钦霍小龙&nbsp;<br />\r\n4　天然气：新定价机制确定，产业链进入高速发展——王军生　刘文&nbsp;<br />\r\n5　抓住机遇，加快推进电力市场化改革——戴颖杰赵乐&nbsp;<br />\r\n6　新能源：重要性日益显现——陈义和周博&nbsp;<br />\r\nⅢ　热点专题篇<br />\r\n7　能源资本化：能源产业未来发展的重要推手——张存萍　史萌&nbsp;<br />\r\n8　合同能源管理：迈入发展关键时期——张景顺于潇王敬敬&nbsp;<br />\r\n9　推进国际合作，建设国家能源利益重要平台——李治姚淑敏&nbsp;<br />\r\n10　优化能源区域布局，建设现代能源运输体系——李智礼乔赫&nbsp;<br />\r\n11　后记\r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:51:20', '7', '1', '1');
INSERT INTO `tbl_library` VALUES ('E7ja2m', 'qUfmYb', '2', '全球能源大趋势', null, '<div>\r\n	<span><span style=\"line-height:19px;\"><b><br />\r\n</b></span></span> \r\n</div>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;机械工业出版社; 第1版 (2012年9月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=The%20Powers%20That%20Be%3AGlobal%20Energy%20for%20the%20Twenty-Fist%20Century%20and%20Beyond&search-alias=books\">The Powers That Be:Global Energy for the Twenty-Fist Century and Beyond</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E7%BB%8F%E6%B5%8E%E5%89%8D%E6%B2%BF&search-alias=books\">经济前沿</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;316页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7111393414, 9787111393412\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787111393412\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24 x 17 x 2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>540 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;机械工业出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span><span style=\"line-height:19px;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:19px;\"> </span></span>\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《全球能源大趋势》是一本重要的能源战略参考。美国的能源战略前瞻，能源主导的六大条件，全球能源安全、能源博弈、能源开发。在每个案例中，新燃料都是由于原有燃料的供应不足才逐渐被人们所接受。供给、需求和成本等市场力量至关重要，但是需求的力量驱动着一切。技术也非常重要：煤炭是被蒸汽机的活塞举上了王座，石油的加冕则少不了内燃机的功劳。在每个案例中，能源优势催生了一批批的发明、应用和燃料，带来了新的产品、新的产业、新的交通工具、新的生活方式以及人类向现代社会的不断迈进。因此，概括起来就是：两次资源的短缺和“不可持续”、两次经济困境以及两种发动机改造了我们的世界。或者，这种关系还可以进一步简化：短缺﹢社会经济不稳定﹢技术→新的选择。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	作者简介\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	作者：（美国）斯科特 L.蒙哥马利 译者：宋阳 姜文波<br />\r\n斯科特 L.蒙哥马利，顾问地质学家和独立学者，著有The Chicago Guide to Communicating Science和Science in Translation。\r\n</div>\r\n<p>\r\n	<span><span style=\"line-height:19px;\"><br />\r\n</span></span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-29 23:37:59', '5', '1', '1');
INSERT INTO `tbl_library` VALUES ('A3mMbi', 'qUfmYb', '2', '能源重塑世界', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 《能源重塑世界(套装上下册)》内容简介：能源是全球政治和经济变革的驱动力，在未来能源的博弈中，石油和天然气还能走多远？新能源的开发到底需要多少投入才能闪亮登场？能源能否拯救低迷的全球经济？</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 耶金在其上部宏伟巨著《石油大博弈》（又名《石油风云》）之后，笔不耕辍，沉淀20年研究成果，再度推出这部影响世界的著作——《能源重塑世界》，掀起新一轮能源经济热潮。他以可读性极高的叙事体将地缘政治、能源安全、气候变化等严肃话题贯穿于一个个精彩的故事之中。从北京拥挤的街道，到里海海滨，到中东冲突，到美国国会山和硅谷，作者告诉我们未来变化的内在决策和选择，将一个被能源变革后的全新的世界格局娓娓道来。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 美国的致密油、巴西的盐下石油和加拿大的油砂，这些非常规石油的革命性商业开采将对世界地缘政治产生深远的影响。这将意味着美洲将迎来能源自给的春天，中东等地区的石油市场将从西半球转向东半球——亚洲市场，而中国无疑会是最大的买主。在新的世界石油市场中，中国将扮演越来越重要的角色。与此同时，页岩气的商业性开采使天然气价格跌跌不休，廉价的天然气是否会将新能源“挤出局”？新的油气资源的发现，彻底打破了“石油枯竭论”的谎言。《能源重塑世界(套装上下册)》将明确地告诉你支撑全球经济高速发展的石油到底还能用多久。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp;&nbsp;海上石油资源和运输通道的争夺日益白热化，中国的飞速发展是否已经严重地威胁到美国的利益？围绕石油地缘政治的竞争是否会导致中国和美国的冲突？耶金先生在《能源重塑世界(套装上下册)》中给出了答案。从少数科学家的质疑，到二氧化碳造成的温室效应，到我们这个时代亟待解决的问题，耶金以独特视角提出了气候变化的趋势。耶金带我们观察了可再生能源的重生，探讨了风能、太阳能和生物燃料的各自独特性。最后，他向我们展示了所有这些不同的能源形式将如何推动电动汽车的回归，而一些人则认为电动汽车回归对于全球经济增长很有必要。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 在《能源重塑世界(套装上下册)》中，耶金向读者展示了一系列非同寻常的人物角色和令人印象深刻的故事，据此，读者可以看到未来几十年我们在构筑强大而灵活的能源安全体系时所需遵循的原则。虽然能源的地位比较基础，但是未来却需要我们对能源问题发出全球性追问，而这种追问也正在真正重塑我们的世界。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《能源重塑世界(套装上下册)》围绕三个基本问题展开：快速发展的世界是否会有足够的能源（如果是，成本如何？需要怎样的技术？）；如何确保世界赖以维系的能源系统的安全；对环境的担忧，包括对气候变化的担忧会对能源的未来产生怎样的影响？而能源的发展对于环境又会有怎样的影响？</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 关于第一个问题，人们对能源枯竭的担忧由来已久。但事实证明，地球的资源禀赋远没有里科夫所担忧的那么匮乏。与此同时，可再生能源也建立了比较稳固的资源基础。我们仍然生活在里科夫口中的“化石燃料时代”。今天，石油、煤炭和天然气占据了世界能源总量的80%以上。但是，由于世界经济规模已经变得如此庞大，即便当今的能源供应要比当初想象的丰富得多，我们在确保未来能源供应上面临的挑战也要远远大于凯尔文甚至里科夫所处的时代。我们是否拥有足够的能源来满足今天全球70万亿美元的经济总量需求，甚至满足20年后大约130万亿美元的世界经济规模？换句话说，当全球的汽车从目前的10亿辆增加到超过20亿辆，我们的石油资源还够用吗？目前，发达国家人均石油消费量是14桶/年，而发展中国家的人均石油消费量只有3桶/年。世界将如何应对几十亿人的石油消费从3桶/年增至6桶/年的变化？</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 《能源重塑世界(套装上下册)》的第二个主题是安全。安全问题源自于各种风险和脆弱性因素，例如能源供应的中断和能源危机,等等。自第二次世界大战以来，许多危机都曾经扰乱能源供应，而且往往令人措手不及。下一场危机将来自哪里？可能会来自于被称为“丑恶新世界”的脆弱网络世界。能源的生产和运送系统十分复杂，是最重要的“重要基础设施”之一，因此该系统的数字控制体系就成了网络攻击的主要目标。关闭这一电子系统造成的不止是停电，它可以让整个社会陷入瘫痪。谈到能源供应安全，分析总离不开波斯湾地区。该地区拥有世界60％的常规石油储量。伊朗的核计划可能会打破该地区的势力平衡；恐怖分子网络将目标瞄准该地区庞大的能源基础设施，并企图推翻该地区政权，制造政治动荡，推高油价从而让西方“破产”。此外，这里的年轻人缺乏教育和工作机遇，各种愿望远未得到满足，对政府的不满引发的社会动荡也困扰着这个地区。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp;&nbsp;除了这些，世界还面临许多其他风险和危机。我们必须居安思危，未雨绸缪，确保自己有能力作出反应。只有这样，才不至于在危机发生之后，像日本政府在福岛核事故报告当中所说的那样，被迫承认“时刻准备着”还是“不够的”。</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 《能源重塑世界(套装上下册)》的第三个主题是环境。在这一方面，人们已经做出了很大努力来应对传统的污染问题。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;石油工业出版社; 第1版 (2012年9月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=The%20Quest&search-alias=books\">The Quest</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;660页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7502192255, 9787502192259\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787502192259\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>25.1 x 17.8 x 3.3 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>1.2 Kg\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;石油工业出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《能源重塑世界(套装上下册)》的作者为普利策奖获得者、畅销书作家、全球能源经济权威专家丹尼尔•耶金，沉淀20年后王者归来，推出最新力作《能源重塑世界》。揭示了影响世界经济的能源的发展客观规律，指出了未来世界能源格局的变化趋势，以及中国在世界能源新格局中的地位、作用和影响力。它是一部关于能源经济的鸿篇巨制。<br />\r\n丹尼尔•耶金的新作，它以可读性极高的叙事体将地缘政治、能源安全、气候变化、可持续发展等严肃话题贯穿于一个个精彩的故事中，无论是政策制定者、能源公司管理层、经济和能源专家学者，以及普通大众读者都值得一读。<br />\r\n《能源重塑世界(套装上下册)》受到中国石油天然气集团公司总经理周吉平、中共中央对外联络部副部长艾平、博鳌亚洲论坛原秘书长龙永图、微软创始人比尔•盖茨、美国前国务卿亨利•基辛格、美国前财政部长劳伦斯•萨默斯，《乔布斯传》作者沃尔特・艾萨克森等业界知名人士、专家学者的强烈推荐。<br />\r\n《能源重塑世界(套装上下册)》受到《金融时报》、《华尔街日报》、《经济学人》、《纽约时报》《今日美国》、《洛杉矶时报》等媒体的热捧。<br />\r\n《能源重塑世界(套装上下册)》的核心观点：<br />\r\n1.我们的能源不仅要满足今天全球70万亿美元的经济总量的要求，还要能够带动20年后大约130万亿美元的世界经济规模。我们能做得到吗？<br />\r\n2.石油资源能够满足从目前全球10亿辆汽车到未来20亿辆汽车的能耗需求吗？<br />\r\n3.美国的致密油、加拿大的油砂以及巴西的盐下石油等非常规油气的革命性开发，将对世界能源格局和地缘政治产生深远影响。<br />\r\n4.南海问题是否会导致中美战争的爆发？<br />\r\n5.在核能等新能源的使用又面临严峻的挑战的时候，页岩气的商业性开采使天然气价格不断下挫，天然气的价格优势是否会将新能源挤出局？<br />\r\n6.在新的能源格局中，中国将扮演越来越重要的角色。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	名人推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《能源重塑世界》一书以极其宽广的视角为我们展现了一幅气势恢宏的画卷，对每一位试图了解当今世界政治经济发展格局，把握世界能源发展趋势与中国能源的未来，分析气候变化对全球能源局势的影响以及重新认识新能源发展战略的读者均具有很好的参考价值。相信大家阅读完本书后，会对发生在全球范围内的重大事件有一个更全面、更清晰的认识！<br />\r\n——周吉平，中国石油天然气集团公司总经理<br />\r\n耶金先生出版的《能源重塑世界》一书，超越石油工业，将视野扩展到整个能源行业；超越石油的探明储量和供应，深入研究能源安全的方方面面；超越能源对狭义经济发展的基础作用，放眼气候变化等涉及人类经济社会可持续发展的根本问题。依然不变的是丰富材料的把握、深层关系的揭示和讲故事的高超本领。希望国内有更多的专家不仅致力于重大课题的研究，而且努力创作引人入胜的、能够走入畅销书排行磅的“非虚构”类图书。这将有助于增进我国读者对世界的了解，提高全民族的文化素质，从而有助于振兴中华的历史进程。<br />\r\n——艾平，中共中央对外联络部副部长<br />\r\n作为世界能源领域最具影响力的专家，丹尼尔•耶金先生在《能源重塑世界》一书中，不仅对全球能源格局的变化及其对世界经济的影响进行了深入的剖析，而且对中国在新的世界能源格局中扮演的角色给予了独到的阐述。尽管关于能源经济类的作品已出版了许多，但是这本书仍令人耳目一新。<br />\r\n——龙永图，原博鳌亚洲论坛秘书长，原外经贸部首席谈判代表、副部长<br />\r\n最近我阅读了丹尼尔•耶金的新书《能源重塑世界》，对于塑造当今世界的能源需求、供应和价格的复杂因素，这本书是一个极有价值的向导。这是一本杰作，我愿将其隆重地推荐给任何对能源感兴趣的人。<br />\r\n——比尔•盖茨，美国微软公司创始人<br />\r\n“这本书探讨了最重要的全球性问题，对于能源的可持续发展做了深入的思索，堪称这方面的终极之作。作为我们这个时代一位杰出的能源专家，丹尼尔・耶金将人类对能源安全和环境问题的关注融合在了一起，不仅对当前我们的能源体系进行了解释，还对未来合理可行的能源发展路径进行了分析。想要理解未来经济走向和生活方式的变化，你绝对不能错过阅读该书。”<br />\r\n——沃尔特・艾萨克森，美国著名传记作家，《乔布斯传》作者，阿斯本非盈利协会主席<br />\r\n“作为世界最富经验和最具影响力的全球能源权威之一，丹尼尔・耶金很可能会使此书成为探讨能源这个最复杂、最重要话题且范围涉及科学、历史和经济三个方面的终极之作。作为大师之作，该书内容阐释清晰，讨论了我们这个时代最为重大的问题之一：能源问题，而该问题也将会对国际政治、国际经济和世界各国产生强大的影响，对于全球所有政策制定者来说，本书都应该成为他们的基本必读书。”<br />\r\n——亨利・基辛格，美国著名外交家，国际问题专家，美国前国务卿<br />\r\n作者以广阔的视野和睿智的思想，以能源尤其是以油气为主线、以经济为主题，以全球重大事件为背景，叙述评说能源的历史现状和未来。<br />\r\n本书纵观全球能源与经济发展变化、回望过去、立足现在、展望未来，内容丰富、观点鲜明、见解独到、思想深刻，是一本可读、有趣、富有幻想的好书，对于了解和研究全球能源与经济及相关技术具有开阔视野的指导意义。<br />\r\n——吴贵辉，国家能源局总工程师<br />\r\n第一时间拜读丹尼尔•耶金先生的《能源重塑世界》，深为书中表达的思想所感染。耶金先生在著作中，以较大篇幅论述了电力问题，其独特见解给我们以新的启迪，对破解中国电力发展中的结构调整、技术进步、市场机制、低碳减排等难题提供了“钥匙”，具有非常重要的参考价值。<br />\r\n——谭荣尧，中国国家电力监管委员会总监、研究员<br />\r\n丹尼尔•耶金先生的《能源重塑世界》是他继《石油大博弈》之后的又一力作。本书总结了能源变革的一系列重大事件，及其对全球经济发展的影响。的确，能源需要变革，而变革势必面临诸多问题与挑战，需要人们在反思和借鉴的基础上去探索一条发展之路。希望本书可以带给执著于这种伟大探索的读者有益的启迪！<br />\r\n——李俊峰，国家应对气候变化战略研究中心和国际合作中心主任<br />\r\n未来20年全球经济规模将从今天的70万亿美元增加到130万亿美元，全球能源消费可能增长35%至40%，这些新增消费将主要出现在发展中国家，如何满足这种需求，需要构建什么样的能源结构才能避免出现能源危机？丹尼尔•耶金博士的巨著《能源重塑世界》给我们提供了重要的思考维度。中国已成为世界第二大能源消费国，石油对外依存度快速提升，能源问题不容忽视，能源如何重塑全球经济，我们已无法只做旁观者。<br />\r\n——卢永真，国务院国资委研究中心副主任<br />\r\n这是一幅关于全球能源问题的气势宏大的全景画。特别是对冷战结束以来石油产业深刻而激烈的变革进行了全面的描述和分析，令人印象深刻！<br />\r\n——植玉林，中国北方工业公司党委书记、副总裁<br />\r\n丹尼尔•耶金的新作《伟大的探索：能源重塑全球经济》以经济全球化和世界能源变革为背景，就世界石油形势、石油枯竭论、电力燃料、低碳减排、新能源、汽车燃料等热点和焦点问题，进行了全面、深入的论述。特别是围绕能源供应、能源安全、能源环境三个问题，提出了独到见解，内容翔实、观点深刻、文字生动，是一部解读世界能源体系和全球经济格局的力作，非常值得学习和思考。<br />\r\n——张运洲，国家电网能源研究院院长<br />\r\n丹尼尔•耶金的每一部著作都是引人入胜的，他的新作《能源重塑世界》站在历史的高度，将能源、安全、科技、金融、经济与政治的重大事件有机地结合了起来，以他广博深厚的知识积淀，鲜明的方向感和极强的讲故事的能力，把近20年世界演进波澜壮阔的画面，鲜活而不失厚重，深刻而不失活泼地展现在了我们的面前。一拿到样书我就急不可待地翻阅了起来，而且是开卷难收，有时会读到下半夜，即使是在伦敦奥运期间，面对电视直播该书也没有被放下。<br />\r\n——陈卫东，中海油能源研究院首席研究员<br />\r\n《能源重塑世界》，是丹尼尔•耶金先生继《石油大博弈》和《制高点》后又一杰作。耶金先生是我们这个时代最富经验、最具影响力的能源专家。他以独特视角探寻后危机时代国际能源、安全、环境这一重大课题，高瞻远瞩分析国际能源地缘政治与地缘经济大博弈与大变局，栩栩如生描绘全球能源转型、能源创新与技术革新的美好远景。本人曾有幸与耶金先生几次深谈能源安全问题，非常欣赏书中“地球资源禀赋丰富”、“化石燃料时代”远未结束、“创新是解决能源问题的关键”及“居安思危，未雨绸缪”等高见。如果你想成为成功的能源专家、企业家、战略家，隆重推荐这是一本必读之书。<br />\r\n——陈凤英，中国现代国际关系研究院世界经济研究所所长／研究员<br />\r\n该书牢牢抓住了这个风险多变的世界所面临的能源与安全这个重大课题。围绕该课题，丹尼尔・耶金明确了该课题所涉及的关键问题、展示了解决这些问题的紧迫性并给我们指出了这些问题的可选择解决方案。他深入的专业视角和生动的叙事风格使得该书在不失吸引力的同时又将人类面临的重要问题展现在读者面前。该书有助于我们找到一条通往更加安全和健康的能源未来之路。<br />\r\n——参议员理查德・卢格，美国参议院外交关系委员会前主席<br />\r\n丹尼尔・耶金的《石油大博弈》就是一本宏伟的杰作。而在本书当中，耶金则进一步拓宽了视野，将能源写作范围覆盖到了包括石油在内的所有能源形式。任何想要理解21世纪经济或政治局势的人都应该阅读此书。<br />\r\n——劳伦斯・萨默斯，美国前财政部长，哈佛大学查尔斯・威廉・艾略特讲席教授<br />\r\n就像在较早前的全球性能源政治叙事性作品《石油大博弈》所采取的宏大写作风格一样，丹尼尔・耶金在本书中对于能源科学、能源经济和能源地缘政治的描述同样是恢弘磅礴，权威气象十足。和任何时候一样，他的写作依旧清晰且充满智慧，而他选择的能源这个写作主题则可以说是恰逢其时。<br />\r\n——史蒂夫・科尔，《本•拉登》和《战争幽灵》的作者，新美国基金研究会主席<br />\r\n每个想要了解能源世界、气候变化如何影响能源决策以及如何规划未来能源发展之路的人都确有必要阅读这本出自美国杰出能源专家之手的作品。<br />\r\n——弗雷德・克虏伯，美国环境保护基金会主席<br />\r\n本书以独特的视角深刻而清晰的阐释了我们这个时代最重要的一些问题。<br />\r\n——弗雷德里克・史密斯，联邦快递公司主席和首席执行官<br />\r\n对于清洁能源的不懈寻找反映出我们这代人所面临的日益清晰的挑战。丹尼尔・耶金以高超的技巧，将经济和地缘政治力量与复杂的能源生产和气候变化科学联系起来。通过对大量问题的阐释，本书为我们走向一个可持续发展的能源未来提供了一个清晰的指导。”<br />\r\n——苏珊・霍克菲尔德，麻省理工学院校长<br />\r\n任何想获悉21世纪经济和政治问题的人都应该阅读这本书。<br />\r\n——劳伦斯•萨姆斯，哈佛大学教授<br />\r\n耶金先生随着《石油大博弈》的续集回到了人们的视线，它被称作：《能源重塑世界》。或者，可以说，这是本更棒的书。这本书透彻，公正，与时俱进。《能源重塑世界》将是CEO、自然资源保护论者、立法者、将军、间谍、技术怪才、恐怖小说作者等的必读书籍。本书是一本雄心勃勃的百科全书，它拒绝简单的摘要。<br />\r\n——德怀特•加纳，《纽约时报》\r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-29 23:36:42', '8', '1', '1');
INSERT INTO `tbl_library` VALUES ('VvEbyq', 'qUfmYb', '2', '大国能源的未来', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 《大国能源的未来》内容简介：未来的文明取决于足够的能源持续可用。 但是，面对能源的限制，我们应该怎么办？《大国能源的未来》对可使用能源日益枯竭的事实和我们能源需求的不断增长，作者做出了客观评估，包括石油，天然气，煤炭，水电，核电，风能，太阳能，海洋动力和生物燃料。 接下来，探讨能源分配制度和如何改进能源的使用方式。最后，转向新能源的解决方案，提供了一个现实，科学和经济上可行的可持续发展战略。关注全球能源的未来可以改善世界各地人民的生活质量。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;电子工业出版社; 第1版 (2012年3月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Powering%20the%20Future%3AA%20Scientist%27s%20Guide%20to%20Energy%20Independence&search-alias=books\">Powering the Future:A Scientist\'s Guide to Energy Independence</a> \r\n	</li>\r\n	<li>\r\n		<b>精装:</b>&nbsp;337页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;32\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787121149085\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787121149085\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>21.4 x 15.4 x 2.2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>422 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;电子工业出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《大国能源的未来》编辑推荐：《纽约时报》称他为“世界领先的环境研究者之一”；2007年（剑桥大学）阿斯特年度讲师奖得主；国际林业领域杰出贡献费诺奖得主；可持续发展米切尔国际奖得主如何解决我们的能源问题7如何使我们能够做出理，性的、符合经济和环保的决定？追溯到最为可靠的事实和信息，毫无个人偏见地分析核对后，发现一些令人惊奇的结果。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	“终于！对我们所面临的能源选项的全部范围的权威性的、客观的、科学的分析著作问世了。公众所获取的大部分信息的特色是充斥着大量噱头、偏见及虚构，波特金此书克服了这些，给我们提供了对支撑美国及全世界的能源的来源、成本、环境影响及潜力的清晰评估。这本书是明晰的、经过很好的调查研究的和写得出色的书，对决策制定者和选民们，此书都应是必读读物。”<br />\r\n　　——李•塔尔博特，美国弗吉尼亚州费尔法克斯乔治梅森大学环境科学和政策教授，他是世界银行一些联合国机构及众多国家政府的资深环境顾问<br />\r\n“此书是对美国的能源未来的分析一波特金展示出，我们可以在不牺牲我们生活质量的情况下拥有一个安全的、可靠的、稳定的能源未来，但要实现这些并非没有重大的变革。这将要求能源效率的显著提高，以及在新的能源来源组合中对可再生能源的倚重大大提高——但这是可实现的和能承受的。你或许不赞同他所青睐的2050年能源来源分配方案，但他将使你思考，同时他列出的能源项目建议为美国的联邦领导人提供了一张有价值的路线图。”<br />\r\n　　——杰瑞•R•舒贝尔，美国加州长验证太平洋水族馆总裁兼首席执行官<br />\r\n“《大国能源的未来》是有关能源供应的最佳的入门读物。波特金考察了每种形式的能源并探讨了我们远离化石燃料的各种选项。他运用科学的方法并抑制了个人偏见，追随能源数据轨迹达到合乎逻辑的结论……该书充满了举例、插图以及数据。”<br />\r\n　　——马修•索贝尔，美国俄亥俄州克利夫兰凯西储大学教授<br />\r\n“如果你打算阅读一本能给你提供对应对全球气候变化的现有的选项、其益处和代价，以及所必须进行的政治变革，提高对可再生能源来源的利用，以及更有效率地使用我们现有的不可再生能源来源的全面的、现实的评估的著作，那就是这本书了。”<br />\r\n　　——丹•塔洛克，美国伊利诺伊州芝加哥肯特法学院杰出法学教授兼能源和环境法项目主任\r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-29 23:37:02', '7', '1', '1');
INSERT INTO `tbl_library` VALUES ('aqMjy2', 'qUfmYb', '2', '能源独立之路', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《能源独立之路》没有把笔墨放在对目前能源格局的过多探讨上，而着重探求解决美国能源问题的方法及道路，揭露了能源独立言论背后的错误期盼，解释了为什么能源独立观点对选民如此具有吸引力，为什么类似于风能和太阳能的可再生技术无法提供美国所需的足够能源。《能源独立之路》调查细致、论证充分。任何对能源业务、石油峰值或全球经济对能源的依赖性感兴趣的人都有必要阅读《能源独立之路》。人们都在谈论“能源独立”，但是，这是否真的可行？这能否实现？在这本具有争议的书中，布莱斯系统性地驳斥了夸夸其谈的用清洁能源代替石油的言辞，恳请美国人从经济的角度思考，掌握一点热力学知识，合理评估成本和潜在收益以及合理利用现有资源。作者提出制定有意义的能源政策必须考虑所有经济，政治和军事的因素，参与全球合作而不是对全球社会发号施令。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;清华大学出版社; 第1版 (2010年2月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Gusher%20of%20Lies%3AThe%20Dangerous&search-alias=books\">Gusher of Lies:The Dangerous</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;219页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787302215783, 7302215782\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787302215783\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23 x 16.6 x 2.2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>358 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;清华大学出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	能源对于美国和全世界都太重要了，《能源独立之路》探求解决美国能源问题的方法及道路，解析石油及新能源、替代能源的作用，提出解决能源问题的具有现实性且鞭辟入里的观点，对中国的能源问题极具启示。<br />\r\n似乎每个美国人都想要能源独立。小布什想要，奥巴马也一样。政客们说，此事“关系到国家安全”。《世界是乎的》作者弗里德曼坚称，美国需要在自己周围构筑一道“能源独立的墙”。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	在这部优秀的能源问题著作中，布莱斯打破了美国人所追求的能源独立的梦想，指明了解决能源问题的根本之道。<br />\r\n　　——《纽约邮报》<br />\r\n布莱斯对能源独立的观念进行了猛烈的抨击。 他以严谨的认证逐步触及问题的核心……幸运的是，他认为，在隧道的尽头有一丝光亮的希望。<br />\r\n　　——《纽约时报》<br />\r\n作者用大量的无可辩驳的事实与数据提供了解决国家能源问题的另一种观点。<br />\r\n　　——《图书馆期刊》&nbsp;<br />\r\n布莱斯全面而具有说服力地驳斥了能源独立的说法……我们要解决的真正问题是：我们如何处理能源的相互依赖问题？<br />\r\n　　——Kirkus评论&nbsp;<br />\r\n“布赖斯清晰地揭露了全球能源的事实，也极大地激起了读者的兴趣。”<br />\r\n　　——《福布斯》\r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-29 23:37:19', '6', '1', '1');
INSERT INTO `tbl_library` VALUES ('3yUNZf', 'qUfmYb', '2', '决战新能源:一场影响国家兴衰的产业革命', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">对于环境的预见是可怕的，并且时间很紧迫。但《决战新能源:一场影响国家兴衰的产业革命》并没有到此为止。克鲁普长期担任环境保护协会主席的职务。在《决战新能源:一场影响国家兴衰的产业革命》中，他提出了令人激动和鼓舞的战斗号召：我们可以解决全球变暖的问题。并且在解决这一问题的过程中，我们还将建立新兴的工业、提供新的就业机会并创造21世纪的新财富。&nbsp;</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">读者将会在书中遇到正在重新利用能源并重塑能源使用方式的大胆的创新者和投资者。在他们中，有在整个夏天利用来自温泉的能源维护他的冰晶旅馆的具有开拓精神的经理人；有将来自燃煤发电厂的烟囱废气送入海藻、再将这些废气变成燃料的动力工程师；以及一个印第安人部落，两千年以来，他们一直是太平洋波涛最为汹涌的水域的渔民，而他们现在正靠自己的力量收获着海浪的巨大能量。</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">这些创业者对改写世界上最大的交易并拯救地球充满了信心，《决战新能源:一场影响国家兴衰的产业革命》向人们传达了开发新能源、保护地球环境，并实现企业与社会的可持续发展的观念。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">\r\n	<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		<li>\r\n			<b>出版社:</b>&nbsp;东方出版社; 第1版 (2010年1月1日)\r\n		</li>\r\n		<li>\r\n			<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=%E5%85%A8%E7%90%83%E7%BB%8F%E9%AA%8C%E4%B8%8E%E4%B8%AD%E5%9B%BD%E5%B4%9B%E8%B5%B7%E4%B9%A6%E7%B3%BB&amp;search-alias=books\">全球经验与中国崛起书系</a>\r\n		</li>\r\n		<li>\r\n			<b>平装:</b>&nbsp;194页\r\n		</li>\r\n		<li>\r\n			<b>语种：</b>&nbsp;简体中文\r\n		</li>\r\n		<li>\r\n			<b>开本:</b>&nbsp;16\r\n		</li>\r\n		<li>\r\n			<b>ISBN:</b>&nbsp;9787506037389\r\n		</li>\r\n		<li>\r\n			<b>条形码:</b>&nbsp;9787506037389\r\n		</li>\r\n		<li>\r\n			<b>商品尺寸:&nbsp;</b>23.8 x 16.8 x 1.6 cm\r\n		</li>\r\n		<li>\r\n			<b>商品重量:&nbsp;</b>381 g\r\n		</li>\r\n		<li>\r\n			<b>品牌:</b>&nbsp;东方音像电子出版社（图书）\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n	</p>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		《决战新能源:一场影响国家兴衰的产业革命》：<br />\r\n这是一本对未来充满思考和憧憬的书，这是一本堪称本年度最具社会责任感和人文情怀的书。哥本哈根无法平息所有争论。<br />\r\n美国总统环境顾问弗雷德·克鲁普解读低碳经济<br />\r\n第三次世界大战不是炮与火的战争而是全球新能源战争<br />\r\n一场新的工业革命已经开始<br />\r\n它将彻底改变世界<br />\r\n和我们的生活方式<br />\r\n这场依托于<br />\r\n资本和技术密集型行业的革命<br />\r\n肯定将为21世纪创造最大的财富<br />\r\n保护我们的地球不要毁灭\r\n		<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n		</div>\r\n	</div>\r\n	<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n		作者简介\r\n	</h3>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		作者：(美国)弗雷德·克鲁普(Fred Krupp) (美国)米丽亚姆·霍恩(Miriam Horn) 译者：陈茂云 朱红路 王轶春 等<br />\r\n弗雷德·克鲁普，美国环境保护协会主席，曾任职于美国总统比尔·克林顿和小布什的总统顾问委员会，倡导利用市场力量达成宏大的环境目标。他的成就众多，包括加强《清洁空气法案》，以及因应气候变化行动，促成历史上最大的收购——得克萨斯公用设施公司（TXU）的收购案。<br />\r\n米丽亚姆·霍恩，现为美国环境保护协会的工作人员，曾为美国林务局工作，并为众多报纸和杂志撰稿，包括《名利场》和《纽约时报》。<br />\r\n译者简介：<br />\r\n陈茂云，男，法学硕士，现为律师：<br />\r\n朱红路，男，华北电力大学工学博士在读；<br />\r\n王轶春，女，工学硕士，现为自由译者：<br />\r\n王轶南，女，理学博士，现从事科研工作。\r\n	</div>\r\n	<p>\r\n		<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n	</p>\r\n<br />\r\n</span>\r\n</p>', null, '2013-06-29 23:06:26', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('YvyuYf', 'qUfmYb', '2', '人类能源史:危机与希望', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《人类能源史:危机与希望》是一部篇幅紧凑、妙趣横生、内容全面的权威著作。当今社会，能源已成为任何人都必须关注和面对的问题。然而，我们绝大多数人对于能源的了解仅限于“危机”、“短缺”和“战争”这类字眼，如果要再进一步地探讨，恐怕我们就才尽技穷了。</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">作为一名著名的环境历史学者，作者梳理了隐藏于当代重要政策问题背后的人类百万年以来的历史，从更宽广的历史脉络入手，深度解读人们使用能源的状况，期望激发我们对能源问题的深思。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中国青年出版社; 第1版 (2009年1月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Children%20of%20the%20Sun%3A%20A%20History%20of%20Humanity%27s%20Unappeasable%20Appetite%20for%20Energy&search-alias=books\">Children of the Sun: A History of Humanity\'s Unappeasable Appetite for Energy</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;207页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;32\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787500686095, 7500686099\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787500686095\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>20.6 x 14.6 x 1.8 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>240 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;北京中青文文化传媒有限公司\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	为了生存，这是我们每个地球人都应该接受的教育！<br />\r\n《人类能源史:危机与希望》著名环境史大师的撼世之作！<br />\r\n全新的角度，别样的历史！<br />\r\n洗练而优美的文笔，充满真知灼见，不但使历史浅显易懂，更让人心向往之。<br />\r\n作者以巧妙的方式重新展现人类文明史。本书没有讲到国王、皇后、独裁者以及其他政治力量，只讲到自然界中真正的力量：肌力、马力、煤炭、石油等等。作者为那些试图探究当代社会能源恐惧症的人提供了珍贵的背景资料。<br />\r\n　　——比尔·麦吉本（Bill McKibben）著名人类生态学者，美国著名环保主义理论家，《自然的终结》（The End of Nature）一书作者\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	作者以巧妙的方式重新展现人类文明史。本书没有讲到国王、皇后、独裁者以及其他政治力量，只讲到自然界中真正的力量：肌力、马力、煤炭、石油等等。作者为那些试图探究当代社会能源恐惧症的人提供了珍贵的背景资料。<br />\r\n　　——比尔·麦吉本（Bill McKibben）著名人类生态学者，美国著名环保主义理论家，《自然的终结》（The End of Nature）一书作者\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-29 23:38:08', '4', '1', '1');
INSERT INTO `tbl_library` VALUES ('ZfAjIn', 'qUfmYb', '2', '新能源与分布式发电技术', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《新能源与分布式发电技术》共10章，依据能源领域发展的新趋势，系统地介绍了各类能源及其利用技术，如太阳能、风能、潮汐能、互补发电与综合利用、分布式发电技术等，此外，还涵盖了天文、地理、文学、历史等学科的相关知识。书中包含翔实的数据、大量的图片、诸多实例和生动的故事，让读者对新能源与分布式发电技术有直观和感性的认识，精心设计的各类小栏目更会让读者有耳目一新的感觉，能够提高读者的科学技能和人文素养，堪称新能源与分布式发电技术领域的“百科全书”。</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《新能源与分布式发电技术》主要作为普通高校教材，适用于电气专业、新能源专业的本科教学。也可供对新能源与分布式发电技术有兴趣的其他大学生选修，或供希望了解新能源与分布式发电技术的普通读者参考。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">\r\n	<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		<li>\r\n			<b>出版社:</b>&nbsp;北京大学出版社; 第1版 (2010年9月1日)\r\n		</li>\r\n		<li>\r\n			<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=21%E4%B8%96%E7%BA%AA%E5%85%A8%E5%9B%BD%E6%9C%AC%E7%A7%91%E9%99%A2%E6%A0%A1%E7%94%B5%E6%B0%94%E4%BF%A1%E6%81%AF%E7%B1%BB%E5%88%9B%E6%96%B0%E5%9E%8B%E5%BA%94%E7%94%A8%E4%BA%BA%E6%89%8D%E5%9F%B9%E5%85%BB%E8%A7%84%E5%88%92%E6%95%99%E6%9D%90&amp;search-alias=books\">21世纪全国本科院校电气信息类创新型应用人才培养规划教材</a>\r\n		</li>\r\n		<li>\r\n			<b>平装:</b>&nbsp;271页\r\n		</li>\r\n		<li>\r\n			<b>语种：</b>&nbsp;简体中文\r\n		</li>\r\n		<li>\r\n			<b>开本:</b>&nbsp;16\r\n		</li>\r\n		<li>\r\n			<b>ISBN:</b>&nbsp;9787301176771, 7301176775\r\n		</li>\r\n		<li>\r\n			<b>条形码:</b>&nbsp;9787301176771\r\n		</li>\r\n		<li>\r\n			<b>商品尺寸:&nbsp;</b>25.6 x 18.4 x 1.2 cm\r\n		</li>\r\n		<li>\r\n			<b>商品重量:&nbsp;</b>440 g\r\n		</li>\r\n		<li>\r\n			<b>品牌:</b>&nbsp;北京大学出版社\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n	</p>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		《新能源与分布式发电技术》：21世纪全国本科院校电气信息类创新型应用人才培养规划教材\r\n		<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n		</div>\r\n	</div>\r\n	<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n		目录\r\n	</h3>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		第1章 能源概述<br />\r\n1.1 能源利用的历史<br />\r\n1.2 能源的概念<br />\r\n1.2.1 资源和能源的概念<br />\r\n1.2.2 能源的分类<br />\r\n1.2.3 能源的品质<br />\r\n1.3 能源与环境问题<br />\r\n1.3.1 常规能源的环境影响<br />\r\n1.3.2 世界能源与环境问题<br />\r\n1.4 新能源发展战略<br />\r\n1.4.1 欧美的新能源发展政策与规划<br />\r\n1.4.2 我国的新能源发展政策与规划<br />\r\n习题<br />\r\n第2章 太阳能及其利用<br />\r\n2.1 太阳能利用的历史<br />\r\n2.2 太阳能资源及其分布<br />\r\n2.2.1 太阳能概述<br />\r\n2.2.2 世界太阳能资源分布<br />\r\n2.2.3 我国太阳能资源分布<br />\r\n2.2.4 太阳能资源的特点<br />\r\n2.3 太阳能的利用方式<br />\r\n2.3.1 太阳能热利用<br />\r\n2.3.2 太阳能发电<br />\r\n2.3.3 光化学转换<br />\r\n2.4 太阳能直接热利用<br />\r\n2.4.1 集热器类型<br />\r\n2.4.2 太阳灶和热水器<br />\r\n2.4.3 太阳能空调<br />\r\n2.4.4 太阳池<br />\r\n2.5 太阳能热发电<br />\r\n2.5.1 太阳能热发电系统的构成<br />\r\n2.5.2 太阳能热发电系统的基本类型<br />\r\n2.6 太阳能光伏发电<br />\r\n2.6.1 光伏效应与光伏材料<br />\r\n2.6.2 光伏电池<br />\r\n2.6.3 光伏发电系统<br />\r\n2.6.4 光伏发电的特点<br />\r\n2.7 太阳能光伏发电的发展<br />\r\n2.7.1 光伏发电技术的发展历史<br />\r\n2.7.2 光伏发电产业的发展状况<br />\r\n2.7.3 我国光伏发电行业的发展状况<br />\r\n习题<br />\r\n第3章 风能与风力发电<br />\r\n3.1 风能利用的历史<br />\r\n3.1.1 世界风能利用历史<br />\r\n3.1.2 中国风能利用历史<br />\r\n3.2 风和风资源<br />\r\n3.2.1 风的形成<br />\r\n3.2.2 风的描述<br />\r\n3.2.3 世界风能资源<br />\r\n3.2.4 我国风能资源<br />\r\n3.3 风力机的种类<br />\r\n3.3.1 水平轴风力机<br />\r\n3.3.2 垂直轴风力机<br />\r\n3.4 水平轴风力机的结构和原理<br />\r\n3.4.1 水平轴风力机的基本结构<br />\r\n3.4.2 水平轴风力机的原理<br />\r\n3.4.3 风力机的功率调节方式<br />\r\n3.5 风力发电机组<br />\r\n3.5.1 风力发电机组及其构成<br />\r\n3.5.2 风力发电机<br />\r\n3.5.3 传动和控制机构<br />\r\n3.5.4 塔架和机舱<br />\r\n3.6 风电场<br />\r\n3.6.1 风电场的概念<br />\r\n3.6.2 风力发电的特点<br />\r\n3.7 风力发电的发展<br />\r\n3.7.1 风力发电的发展历史<br />\r\n3.7.2 世界风电的发展状况<br />\r\n3.7.3 我国风电的发展状况<br />\r\n3.7.4 风电的发展前景<br />\r\n习题<br />\r\n第4章 潮汐能与潮汐发电<br />\r\n4.1 人类对潮汐的认识和利用<br />\r\n4.1.1 人类对潮汐的认识<br />\r\n4.1.2 人类对潮汐的早期利用<br />\r\n4.2 潮汐能资源<br />\r\n4.2.1 潮汐的描述和分类<br />\r\n4.2.2 潮汐能资源及其分布<br />\r\n4.3 潮汐发电原理和电站构成<br />\r\n4.3.1 潮汐发电的原理<br />\r\n4.3.2 潮汐电站的结构<br />\r\n4.3.3 潮汐电站的水轮发电机组<br />\r\n4.4 潮汐电站的类型<br />\r\n4.4.1 单库单向潮汐电站<br />\r\n4.4.2 单库双向潮汐电站<br />\r\n4.4.3 双库连续发电潮汐电站<br />\r\n4.5 潮汐发电的特点<br />\r\n4.5.1 潮汐发电的优点<br />\r\n4.5.2 潮汐发电的不足<br />\r\n4.6 潮汐发电的发展<br />\r\n4.6.1 世界潮汐发电的发展<br />\r\n4.6.2 我国潮汐发电的发展<br />\r\n习题<br />\r\n第5章 海洋能多种发电技术<br />\r\n5.1 海洋的概念<br />\r\n5.1.1 人类对海洋的认识<br />\r\n5.1.2 海和洋<br />\r\n5.1.3 海洋资源<br />\r\n5.2 海洋能资源<br />\r\n5.2.1 世界海洋能资源<br />\r\n5.2.2 我国海洋能资源<br />\r\n5.2.3 海洋能的特点<br />\r\n5.3 波浪发电<br />\r\n5.3.1 波浪的成因和类型<br />\r\n5.3.2 波浪能资源的分布和特点<br />\r\n5.3.3 波浪发电装置的基本构成<br />\r\n5.3.4 波浪能的转换方式<br />\r\n5.3.5 波浪能装置的安装模式<br />\r\n5.3.6 典型的波浪能发电装置<br />\r\n5.3.7 代表性波浪能发电项目<br />\r\n5.3.8 波浪发电的发展<br />\r\n5.4 海流发电<br />\r\n5.4.1 海流和海流能<br />\r\n5.4.2 海流发电的发展状况<br />\r\n5.4.3 海流发电的原理<br />\r\n5.5 温差发电<br />\r\n5.5.1 海水的温差和温差能<br />\r\n5.5.2 温差发电的原理<br />\r\n5.5.3 温差发电的发展<br />\r\n5.6 盐差发电<br />\r\n5.6.1 海洋的盐差和盐差能<br />\r\n5.6.2 渗透和渗透压<br />\r\n5.6.3 盐差能发电的方法<br />\r\n5.6.4 盐差发电的发展状况<br />\r\n5.7 海洋能发电的发展方向和前景<br />\r\n习题<br />\r\n第6章 地热能及其利用<br />\r\n6.1 地热资源的形成<br />\r\n6.1.1 地球的构造和热量来源<br />\r\n6.1.2 地热资源的概念<br />\r\n6.2 地热资源的类型<br />\r\n6.2.1 地热资源的存在形态<br />\r\n6.2.2 地热田<br />\r\n6.3 地热能资源及其分布<br />\r\n6.3.1 地热能的蕴藏量<br />\r\n6.3.2 世界地热资源分布<br />\r\n6.3.3 我国的地热资源<br />\r\n6.4 地热能利用的发展<br />\r\n6.4.1 世界地热能直接利用<br />\r\n6.4.2 我国地热能直接利用<br />\r\n6.4.3 世界地热发电的发展<br />\r\n6.4.4 我国地热发电的发展<br />\r\n6.5 地热能的一般利用<br />\r\n6.5.1 地热能的利用方式<br />\r\n6.5.2 地热用于供暖<br />\r\n6.5.3 地热用于农业和养殖业<br />\r\n6.5.4 温泉洗浴和医疗<br />\r\n6.6 地热发电<br />\r\n6.6.1 地热发电的原理<br />\r\n6.6.2 蒸汽型地热发电系统<br />\r\n6.6.3 热水型地热发电系统<br />\r\n6.6.4 联合循环地热发电系统<br />\r\n6.6.5 干热岩地热发电系统<br />\r\n6.6.6 各国地热发电的发展情况<br />\r\n习题<br />\r\n第7章 生物质能及其利用<br />\r\n7.1 生物质和生物质能<br />\r\n7.1.1 生物质的概念<br />\r\n7.1.2 生物质的来源<br />\r\n7.1.3 生物质能及其特点<br />\r\n7.1.4 我国的生物质资源<br />\r\n7.2 生物质能利用概述<br />\r\n7.2.1 生物质能利用的历史<br />\r\n7.2.2 生物质能利用的形式<br />\r\n7.3 生物质燃料<br />\r\n7.3.1 固体生物质燃料<br />\r\n7.3.2 气体生物质燃料<br />\r\n7.3.3 液体生物质燃料<br />\r\n7.4 生物质能发电简介<br />\r\n7.4.1 生物质能发电的基本原理<br />\r\n7.4.2 生物质能发电的特点<br />\r\n7.4.3 生物质能发电的发展状况<br />\r\n7.5 生物质能发电技术<br />\r\n7.5.1 直接燃烧发电<br />\r\n7.5.2 沼气发电<br />\r\n7.5.3 垃圾发电<br />\r\n7.5.4 生物质燃气发电<br />\r\n7.6 典型的能源植物<br />\r\n7.6.1 薪炭树种<br />\r\n7.6.2 石油树<br />\r\n7.6.3 巨藻<br />\r\n习题<br />\r\n第8章 氯能和燃料电池<br />\r\n8.1 氢和氢能概述<br />\r\n8.1.1 氢和氢能简介<br />\r\n8.1.2 氢能及其利用方式<br />\r\n8.1.3 氢能的应用历史<br />\r\n8.2 氢的制取<br />\r\n8.2.1 化石燃料制氢<br />\r\n8.2.2 水分解制氢<br />\r\n8.2.3 生物制氢<br />\r\n8.2.4 太阳能制氢<br />\r\n8.2.5 制氢方式总结<br />\r\n8.3 氢的储存<br />\r\n8.3.1 对储氢系统的要求<br />\r\n8.3.2 氢气的储存<br />\r\n8.3.3 液氢储存<br />\r\n8.3.4 固体金属氢化物储存<br />\r\n8.3.5 研究中的储氢新方法<br />\r\n8.4 燃料电池概述<br />\r\n8.4.1 燃料电池的发展历史<br />\r\n8.4.2 燃料电池的基本原理<br />\r\n8.4.3 燃料电池系统的构成<br />\r\n8.4.4 燃料电池发电的特点<br />\r\n8.5 燃料电池的类型<br />\r\n8.5.1 燃料电池的分类<br />\r\n8.5.2 各种类型的燃料电池<br />\r\n8.6 燃料电池的应用领域<br />\r\n习题<br />\r\n第9章 互补发电与综合利用<br />\r\n9.1 互补发电的概念和特点<br />\r\n9.1.1 互补发电的概念<br />\r\n9.1.2 互补发电的特点<br />\r\n9.2 风能一太阳能互补发电<br />\r\n9.2.1 风一光互补的基础<br />\r\n9.2.2 风一光互补发电系统的结构和配置<br />\r\n9.2.3 风一光互补发电系统的应用<br />\r\n9.3 其他互补发电系统<br />\r\n9.3.1 风能一水力互补发电<br />\r\n9.3.2 风电或光伏一柴油机互补应用<br />\r\n9.3.3 微型燃气轮机一燃料电池互补发电<br />\r\n9.4 能源的综合开发利用<br />\r\n9.4.1 冷热电联产<br />\r\n9.4.2 太阳能房<br />\r\n9.4.3 综合型潮汐电站<br />\r\n9.4.4 地热能的综合利用<br />\r\n9.4.5 海洋温差发电的综合开发<br />\r\n习题<br />\r\n第10章 分布式发电技术<br />\r\n10.1 分布式发电的概念<br />\r\n10.1.1 分布式发电简介<br />\r\n10.1.2 分布式发电的特点<br />\r\n10.1.3 分布式发电的适用场合<br />\r\n10.2 分布式电源<br />\r\n10.2.1 新能源分布式电源<br />\r\n10.2.2 微型燃气轮机<br />\r\n10.3 分布式供电系统和微电网<br />\r\n10.3.1 分布式供电系统<br />\r\n10.3.2 微电网<br />\r\n10.4 分布式发电系统的储能装置<br />\r\n10.4.1 常用的储能技术<br />\r\n10.4.2 储能装置在分布式系统中的作用<br />\r\n10.5 分布式发电的发展应用<br />\r\n10.5.1 发展分布式发电的意义<br />\r\n10.5.2 分布式发电的发展状况<br />\r\n习题<br />\r\n附录<br />\r\n附录A 能源的计量<br />\r\n附录B 数量等级<br />\r\n附录C 常规能源发电技术<br />\r\n附录D 国外著名新能源研究机构<br />\r\n参考答案<br />\r\n参考文献\r\n	</div>\r\n	<p>\r\n		<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n	</p>\r\n<br />\r\n</span>\r\n</p>', null, '2013-06-29 23:10:44', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('F7Zf6z', 'qUfmYb', '2', '世界资源真相和你想的不一样', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《世界资源真相和你想的不一样》主要内容简介：在通货膨胀、纸币贬值已是大势所趋的情况下，全球经济将逐渐进入资源为王的时代。资源就是财富，稀缺资源就是最珍贵的财富。《世界资源真相和你想的不一样》以前所未有的宏观视野介绍全球资源局势，陈述各种可靠资料，并以一目了然的地图标示法，说明蕴藏量、消费量，让所有读者瞬间得到纵观全局的投资视野。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">\r\n	<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		<li>\r\n			<b>出版社:</b>&nbsp;新世界出版社; 第1版 (2010年12月1日)\r\n		</li>\r\n		<li>\r\n			<b>平装:</b>&nbsp;218页\r\n		</li>\r\n		<li>\r\n			<b>语种：</b>&nbsp;简体中文\r\n		</li>\r\n		<li>\r\n			<b>开本:</b>&nbsp;16\r\n		</li>\r\n		<li>\r\n			<b>ISBN:</b>&nbsp;7510414318, 9787510414312\r\n		</li>\r\n		<li>\r\n			<b>条形码:</b>&nbsp;9787510414312\r\n		</li>\r\n		<li>\r\n			<b>商品尺寸:&nbsp;</b>23.8 x 16.4 x 1.8 cm\r\n		</li>\r\n		<li>\r\n			<b>商品重量:&nbsp;</b>358 g\r\n		</li>\r\n		<li>\r\n			<b>品牌:</b>&nbsp;北京卓文天语文化有限公司\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n	</p>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		《世界资源真相和你想的不一样》：<br />\r\n全球资源的供需究竟有多吃紧：<br />\r\n目前全球资源剩下多少?掌握在哪些国家手上?<br />\r\n哪些国家、哪些产业正在大量消耗“我们的”资源?<br />\r\n某些国家消耗资源的同时，会不会产生挤压效应，影响中国的供给源?<br />\r\n明白资源的连锁效应。原来世界足这样运转的：<br />\r\n为什么澳大利亚干旱，面粉会涨价?<br />\r\n为什么美国使用生质柴油，会导致粮食出现缺口?<br />\r\n为什么智利铜矿闹罢工，全球铜价应声大涨?<br />\r\n为什么俄罗斯天然气停止供应乌克兰，快要断气的却是欧盟国家?<br />\r\n科学发展观读本，中国可持续发展必读书<br />\r\n石油多久会用完?我国海域有丰富能源可以取代石油吗?<br />\r\n水，这样用下去，还有多久会变成高价资源?<br />\r\n玉米、稻米、糖、大豆，都可以拿来做燃料，那我们吃什么?<br />\r\n价格这么贵，为什么还要叫基本金属?那稀有金属不就……<br />\r\n全球将步入资源为王的时代，储备资源就是储备财富<br />\r\n趋势观察家南方朔推荐\r\n		<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n		</div>\r\n	</div>\r\n	<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n		媒体推荐\r\n	</h3>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		有没有一种财富储备方式可以不受金融危机的掠夺，不仅可以在危机中保值，在未来还能有数倍的增值呢?有，那就是资源，尤其是珍贵稀缺资源的储备。<br />\r\n　　——时寒冰\r\n	</div>\r\n<br />\r\n</span>\r\n</p>', null, '2013-06-29 23:13:21', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('3mYVZz', 'qUfmYb', '2', '资源角逐:世界资源版图争夺战', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《资源角逐:世界资源版图争夺战》内容简介：欧洲人费尽心机设置连环陷阱，目的何在？国有矿企坐拥巨资，为何望“矿”兴叹？铁矿石谈判是“分庭抗争”还是“利益合谋”，“内鬼”利益藏身何处？</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">英美WTO起诉中国稀土，怀揣怎样底牌？</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">中国企业海外上市，如何泄露天机？</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">日本举国越洋屯田，意欲何为？</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《资源角逐:世界资源版图争夺战》作者以其亲身经历和见闻，讲述和剖析了围绕中国资源角逐的一场场阴谋、算计和角力。作者凭借广博的学识和多年的投资经历，全面透析了各国争夺资源版图的核心策略和手段，将许多不为人知的幕后故事清晰地呈现在读者面前。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">\r\n	<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		<li>\r\n			<b>出版社:</b>&nbsp;山西出版集团，山西人民出版社; 第1版 (2010年3月1日)\r\n		</li>\r\n		<li>\r\n			<b>平装:</b>&nbsp;304页\r\n		</li>\r\n		<li>\r\n			<b>语种：</b>&nbsp;简体中文\r\n		</li>\r\n		<li>\r\n			<b>开本:</b>&nbsp;16\r\n		</li>\r\n		<li>\r\n			<b>ISBN:</b>&nbsp;9787203067023\r\n		</li>\r\n		<li>\r\n			<b>条形码:</b>&nbsp;9787203067023\r\n		</li>\r\n		<li>\r\n			<b>商品尺寸:&nbsp;</b>23.8 x 16.8 x 3 cm\r\n		</li>\r\n		<li>\r\n			<b>商品重量:&nbsp;</b>662 g\r\n		</li>\r\n		<li>\r\n			<b>品牌:</b>&nbsp;山西人民出版社\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n	</p>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		《资源角逐:世界资源版图争夺战》：真实记录惊心动魄的阴谋、算计与角力。<br />\r\n世界资源版图争夺战，《非洲买矿记》一鸣惊人！中央政策研究室、国务院发展研究中心、中国社科院、北京大学、清华大学等广泛关注。<br />\r\n《资源角逐:世界资源版图争夺战》提示出世界资源角力的规则和策略。<br />\r\n资源是中国发展的瓶颈，也是中国未来竞争的基础。如何维持一个大国的资源版图，值得所有人思考。<br />\r\n阐释资源重商主义和大国资源经济学。\r\n		<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n		</div>\r\n	</div>\r\n	<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n		作者简介\r\n	</h3>\r\n	<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		张捷，网络笔名“谁是谁非任评说”，《绿叶》杂志专栏作家。张捷出自晚清时称“京东首富”的唐山张家，祖父为著名地质学家张文佑先生，外祖父为两弹一星功勋科学家赵九章先生，其家族在中国近代史上群星闪烁：晚清名士赵炳麟、署理湖广总督赵炳言、道光十八年状元钮保福、湖州名士钮承聪、民国要员戴季陶……与同时代的许多风云人物皆过从甚密，与清代大儒俞樾父子更是世交和互为师承关系，被称为传奇学术世家。<br />\r\n张捷幼年由于家庭受“文化大革命”冲击遍尝事态炎凉在陕西终南山下生活多年。获得全国奥数优胜，并以优异成绩考入当年录取分数最高的中国科学技术大学，先后成功地从事过核物理研究、半导体集成电路、互联网、金融投资、医疗服务、文化传播、律师、房地产、专栏作家等多个行业并深入接触到了各行业的核心。张捷曾投资资助了王云五、齐如山、沈从文、梁思成夫妇、罗荣渠等人的书籍出版，是《万象》杂志的运营投资者，个人经历也堪称传奇。<br />\r\n张捷以其家学传承和传奇经历为基础独立思考，近年来匿名或以笔名发表的许多文章在中国文化界、思想界广泛流传转载，引起极大反响，受到中央政策研究室、国务院发展研究中心、中国社科院、北京大学、清华大学、新华社、中央电视台等的广泛关注，曾接受《嘹望》周刊、《国家财经周刊》等媒体专访。\r\n	</div>\r\n	<p>\r\n		<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n	</p>\r\n<br />\r\n</span>\r\n</p>', null, '2013-06-29 23:22:54', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('Vb2Ujy', 'nEraQv', '2', '中国战略性新兴产业发展报告2013', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2013年1月1日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 《中国战略性新兴产业发展报告2013》是中国工程科技发展战略研究院面向社会公众、面向决策人员的研究报告。《中国战略性新兴产业发展报告2013》分析了我国战略性新兴产业发展的总体形势，总结了主要国家和地区新兴产业的发展情况、宏观战略和政策举措以及对我国的启示，着重围绕战略性新兴产业七个领域若干重点方向的发展现状、主要技术、战略布局与政策取向等进行了介绍，并结合北京、江苏、安徽、广东、深圳、合肥六省市战略性新兴产业的发展案例，具体论述了区域的发展情况、有益做法、主要问题与制约因素等。同时，《中国战略性新兴产业发展报告2013》重点梳理分析了现有政策，并提出未来战略性新兴产业发展的政策取向。&nbsp;\r\n</p>\r\n<p>\r\n	《中国战略性新兴产业发展报告2013》有助于社会公众了解中国战略性新兴产业发展的总体情况、各领域发展态势和政策走向，可供各级领导干部、有关决策部门和社会公众参考。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;科学出版社; 第1版 (2013年1月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=2013%20Report%20on%20the%20Development%20of%20China%27s%20Strategic%20Emerging%20Industries&search-alias=books\">2013 Report on the Development of China\'s Strategic Emerging Industries</a> \r\n	</li>\r\n	<li>\r\n		<b>精装:</b>&nbsp;464页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787030363176\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787030363176\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>26.2 x 19.2 x 3.2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>1.5 Kg\r\n	</li>\r\n</ul>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《中国战略性新兴产业发展报告(2013)》是中国工程科技发展战略研究院面向社会公众、面向决策人员的研究报告。《中国战略性新兴产业发展报告(2013)》有助于社会公众了解中国战略性新兴产业发展的总体情况、各领域发展态势和政策走向，可供各级领导干部、有关决策部门和社会公众参考。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	序一<br />\r\n序二<br />\r\n序三<br />\r\n前言<br />\r\n综合篇<br />\r\n第1章 国务院关于加快培育和发展战略性新兴产业的决定<br />\r\n第2章 “十二五”国家战略性新兴产业发展规划<br />\r\n第3章 “十二五”国家战略性新兴产业发展规划解读<br />\r\n第4章 战略性新兴产业总体发展形势<br />\r\n第5章 世界新兴产业发展概述<br />\r\n节能环保产业篇<br />\r\n第6章 与流程制造业有关的节能产业<br />\r\n第7章 环保产业<br />\r\n第8章 资源循环利用产业<br />\r\n专题 煤炭高效转化及近零排放利用产业培育与发展<br />\r\n新一代信息技术产业篇<br />\r\n第9章 集成电路产业<br />\r\n第10章 物联网产业<br />\r\n第11章 有机发光显示(OLED)产业<br />\r\n生物产业篇<br />\r\n第12章 生物医药产业<br />\r\n第13章 生物育种业<br />\r\n高端装备制造产业篇<br />\r\n第14章 民用航空制造业<br />\r\n第15章 卫星及应用产业<br />\r\n第16章 海洋装备产业<br />\r\n第17章 智能制造装备产业<br />\r\n新能源产业篇<br />\r\n第18章 风电产业<br />\r\n第19章 太阳能光伏产业<br />\r\n第20章 页岩油气产业<br />\r\n第21章 智能电网产业<br />\r\n新材料产业篇<br />\r\n第22章 功能材料产业<br />\r\n新能源汽车产业篇<br />\r\n第23章 新能源汽车产业<br />\r\n区域篇<br />\r\n第24章 区域发展综合分析<br />\r\n第25章 北京市战略性新兴产业发展情况<br />\r\n第26章 江苏省战略性新兴产业发展情况<br />\r\n第27章 安徽省战略性新兴产业发展情况<br />\r\n第28章 广东省战略性新兴产业发展情况<br />\r\n第29章 深圳市战略性新兴产业发展情况<br />\r\n第30章 合肥市战略性新兴产业发展情况<br />\r\n政策篇<br />\r\n第31章 培育和发展战略性新兴产业的政策回顾分析<br />\r\n第32章 培育和发展战略性新兴产业的政策取向<br />\r\n致谢<br />\r\n中国战略性新兴产业网简介\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:51:55', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('IvMzye', 'nEraQv', '2', '中国企业发展报告2013', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年12月1日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《中国企业发展报告(2013)》是国务院发展研究中心企业研究所集全所之力，在长期具有针对性的跟踪研究和积累的大量研究成果的基础上，通过精心组织、认真调研和充分讨论而形成的按年度连续出版的核心研究成果。《中国企业发展报告(2013)》坚持“用事实说话、向历史负责、对未来研判”的指导方针，立足于客观、具体、准确的数据和调查，力求能够较为全面、真实地反映过去一年来中国企业发展与改革的新情况、新特点、新问题、新挑战，能够较为深入、准确地分析即将来临的一年可能面临的新形势、新趋势、新机遇，以便为政府的政策制定和企业的发展战略提供参考。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中国发展出版社; 第1版 (2012年12月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=China%20Enterprise%20Development%20Report%202013&search-alias=books\">China Enterprise Development Report 2013</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;410页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787802348813\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787802348813\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24 x 18.4 x 2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>880 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中国发展出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《中国企业发展报告(2013)》由中国发展出版社出版。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	作者简介\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	国务院发展研究中心企业研究所是国务院发展研究中心直属的专门从事企业发展与改革政策研究及咨询的机构，长期致力于中国企业发展和改革领域中全局性、综合性、战略性、长期性、前瞻性以及热点和难点问题的理论、政策和实践研究，为党中央、国务院及有关政府部门提供政策建议，为企业及相关各界提供决策咨询。国务院发展研究中心企业研究所在政府和公共政策研究方面有着丰富的经验、资源和强大优势，在企业经济、产业经济及有关企业改革、发展战略与管理体制等政策、理论与应用方面有着深厚的研究积累。\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:52:07', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('EjYzAn', 'nEraQv', '2', '中国产业发展报告2012', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《中国产业发展报告2012》1998年末，为了及时、全面地掌握我国产业发展最新动态，国务院发展研究中心产业经济研究部会同二十多个国家有关部门组成课题组，开展“中国产业发展跟踪研究”。本书就是该项研究2011年度的成果之一。从1999年的第一份报告开始，《中国产业发展报告》已陆续出版9本，研究内容的覆盖面不断扩大，研究的深度与前瞻性不断加强，得到了方方面面读者的肯定和好评，是一本全面了解中国产业发展最新动态的重要参考资料。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《中国产业发展报告2012》近年来，随着我国产业结构调整升级不断深化，战略性新兴产业得到了前所未有的关注。本年度的报告中特别新增了战略性新兴产业板块，收录了8篇重要的研究成果，以满足读者对这一领域问题的研究需要。另外，为了提高传统产业研究的前瞻性和科学性，研究小组陆续建立了多个行业跟踪预测模型，并将部分预测成果在报告中发布，希望对读者的研究和决策提供更多有价值的参考信息。&nbsp;</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《中国产业发展报告2012》共分为五部分，主要内容包括：2011年宏观经济运行分析和2012年发展报告、2011年能源原材料行业运行情况和2012年走势分析、2011年制造业运行情况和2012年走势分析等。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中国发展出版社; 第1版 (2012年4月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=The%20Research%20Report%20on%20the%20Development%20of%20Chinese%20Industry(2012)&search-alias=books\">The Research Report on the Development of Chinese Industry(2012)</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;352页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787802347618, 7802347610\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787802347618\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24 x 18.2 x 2.2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>621 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中国发展出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《中国产业发展报告2012》国务院发展研究中心产业经济研究部的主要研究领域包括：第一，产业结构、产业组织、产业布局、产业政策、产业竞争力问题研究；第二，政府的行业监管体制问题研究；第三，关于促进产业结构优化升级的政策问题研究；第四，国民经济各产业的相互关系、协调发展及综合性对策研究。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	第一部分 2011年宏观经济运行分析和2012年发展展望<br />\r\n1 2011年中国经济运行的基本特点<br />\r\n1.1 2011年总体经济形势回顾<br />\r\n1.2 经济增速整体较快，增长趋势平稳回落<br />\r\n1.3 CPI高位稳步回落，宏观调控成效显著<br />\r\n1.4 房地产调控成果渐显，保障房取得成效<br />\r\n1.5 汽车产销增速下滑，社会消费增长小幅回落<br />\r\n1.6 进出口增长减速，贸易顺差明显缩减<br />\r\n1.7 农民收人增长快于城镇，城乡收人差距仍然较大<br />\r\n1.8 行业重组加快，产业集中度将提高<br />\r\n1.9 货币政策转向稳健，信贷规模适度增长<br />\r\n2 影晌2012年经济运行的主要因素<br />\r\n2.1 国际环境日趋复杂多变，外部压力将不断增大<br />\r\n2.2 国内环境有望改善<br />\r\n2.3 问题和压力<br />\r\n3 2012年中国经济前景展望<br />\r\n第二部分 2011年能源原材料行业运行情况和2012年走势分析<br />\r\n1 石油天然气工业<br />\r\n2 煤炭工业<br />\r\n3 电力行业<br />\r\n4 化学工业<br />\r\n5 钢铁行业<br />\r\n6 建材行业二<br />\r\n第三部分 2011年制造业运行情况和2012年走势分析<br />\r\n1 汽车行业<br />\r\n2 机械工业<br />\r\n3 家电制造业二<br />\r\n4 医药制造业<br />\r\n5 电子设备制造业二<br />\r\n第四部分 2011年服务业运行情况和2012年走势分析<br />\r\n1 IT服务业<br />\r\n2 房地产业<br />\r\n3 交通运输业<br />\r\n第五部分 战略性新兴产业<br />\r\n1 关注风电和太阳能发电新变化，适时调整规划和政策<br />\r\n2 我国新能源汽车发展技术路线的建议<br />\r\n3 环保产业<br />\r\n4 物联网产业<br />\r\n5 智能电网<br />\r\n6 生物产业<br />\r\n7 新材料产业<br />\r\n8 离端装备制造业\r\n</div>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:52:20', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('ieeiEz', 'nEraQv', '2', '中国与世界经济发展报告(2013版)', null, '<h1 class=\"parseasinTitle\" style=\"font-family:\'Microsoft Yahei\', PMingLiU, Verdana, Arial, Helvetica, sans-serif;font-size:1.5em;background-color:#FFFFFF;\">\r\n	<span id=\"btAsinTitle\" style=\"font-size:12px;\">中国与世界经济发展报告(2013版)/经济信息绿皮书</span> \r\n</h1>\r\n<p>\r\n	<span> </span>\r\n</p>\r\n<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\"> \r\n	<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n		<span class=\"byLinePipe\" style=\"color:#666666;\"> \r\n		<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n			<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年12月1日</span> \r\n		</div>\r\n		<div class=\"content\" style=\"margin:0.5em 0px 0em 25px;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n			<div id=\"outer_postBodyPS\">\r\n				<p>\r\n					杜平主编的这本《中国与世界经济发展报告(2013版)》由国家信息中心组织专家队伍编撰，对2013年国内外经济发展环境、宏观经济发展趋势、经济运行中的主要矛盾，产业经济和区域经济热点，宏观调控政策的取向进行了系统的分析预测，内容涵盖了十大宏观经济领域、六大重点行业、六大经济地区、四大世界经济体，并对国际贸易、国际货币、国际金融、国际大宗商品、国际油价的走势进行了分析和预测。目的是为各级政府部门和企业的管理决策、国内科研院所和大专院校开展经济形势分析预测和政策分析提供较为系统全面的参考资料。<br />\r\n国家信息中心专家认为，2012年，全球金融危机仍在继续发酵，欧洲经济出现二次衰退，新兴国家经济全面减速，日美经济也面临财政困境，复苏乏力。各国政府为缓解经济困难，相继采取了新一轮量化宽松的货币政策，但在尚未发现新的经济增长点、结构问题又没有根本解决的情况下，量化宽松政策很难使经济走出困境，反而可能引发新的通货膨胀预期，使世界经济陷入滞胀的局面。\r\n				</p>\r\n				<p>\r\n					《中国与世界经济发展报告(2013版)》认为，2013年中国经济增速将企稳回升，比2012年有所加快，宏观调控的政策取向仍将是稳增长、调结构和防通胀，结构调整力度将加大，增长质量将明显提高。根据权威国际组织的预测，2013年世界经济将总体出现回升，这对中国经济将是一个有利因素。但是，以美国为首的西方国家和部分新兴国家对我国出口商品采取的贸易保护措施有增无减，成为对我国经济的一个重要制约因素。综合各种因素预测，2013年中国经济将增长8%，投资、消费和净出口增速都将有小幅上升，居民消费价格上涨3%左右。\r\n				</p>\r\n				<p>\r\n					<br />\r\n				</p>\r\n《中国与世界经济发展报告(2013版)》建议，2013年应坚持稳增长的宏观调控政策总基调，继续实施积极的财政政策和稳健的货币政策，稳中求进，有针对性地加大扩大内需的政策力度，重视改革导向的供给政策，着力激发各类企业自主增长活力，高度重视发达国家新技术引发的产业升级新动向，加大对科技创新的政策和资金支持力度，大力推进结构调整以提高增长质量，着力改善民生以共享发展成果。\r\n				<p>\r\n					<br />\r\n				</p>\r\n			</div>\r\n		</div>\r\n</span> \r\n	</div>\r\n</span> \r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;社会科学文献出版社; 第1版 (2012年12月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Annual%20Report%20on%20Economic%20Development%20of%20China%20and%20the%20World(2013)&search-alias=books\">Annual Report on Economic Development of China and the World(2013)</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E7%BB%8F%E6%B5%8E%E4%BF%A1%E6%81%AF%E7%BB%BF%E7%9A%AE%E4%B9%A6&search-alias=books\">经济信息绿皮书</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;371页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;750974010X, 9787509740101\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787509740101\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.8 x 16.4 x 2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>640 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;社会科学文献出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span><br />\r\n</span> \r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	面对复杂的世界经济政治环境，杜平主编的这本《中国与世界经济发展报告(2013版)》重点围绕2013年国内外经济发展环境、宏观经济发展趋势及调控政策取向、产业经济和区域经济热点、经济运行中的主要矛盾等重点领域进行比较系统的分析预测，并提出可供参考的对策建议。<br />\r\n本书适读于：各级政府部门决策人员，科研机构研究人员，企事业单位领导，媒体记者，外国驻华商社及使领事馆工作人员，以及关注中国与世界经济发展的各界人士，各大图书馆、学术单位资料室收藏。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	作者简介\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	杜平，研究员，国家信息中心常务副主任兼国家电子政务外网管理中心副主任。\r\n</div>\r\n<p>\r\n	<span><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:52:31', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('faAFze', 'FzQ32i', '2', '2013年世界经济形势分析与预测', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年12月1日</span>\r\n</div>\r\n<p>\r\n	<p>\r\n		<p>\r\n			本书共分为: 总论 ; 国别与地区 ; 专题篇 ; 热点篇 ; 世界经济统计与预测几部分, 主要内容包括: 2012-2013年世界经济形势分析与展望、美国经济: 复苏与挑战等。\r\n		</p>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<p>\r\n			<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				<li>\r\n					<b>出版社:</b>&nbsp;社会科学文献出版社; 第1版 (2012年12月1日)\r\n				</li>\r\n				<li>\r\n					<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=World%20Economy%20Analysis%20and%20Forecast(2013)&amp;search-alias=books\">World Economy Analysis and Forecast(2013)</a>\r\n				</li>\r\n				<li>\r\n					<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=%E4%B8%96%E7%95%8C%E7%BB%8F%E6%B5%8E%E9%BB%84%E7%9A%AE%E4%B9%A6&amp;search-alias=books\">世界经济黄皮书</a>\r\n				</li>\r\n				<li>\r\n					<b>平装:</b>&nbsp;396页\r\n				</li>\r\n				<li>\r\n					<b>语种：</b>&nbsp;简体中文\r\n				</li>\r\n				<li>\r\n					<b>开本:</b>&nbsp;16\r\n				</li>\r\n				<li>\r\n					<b>ISBN:</b>&nbsp;9787509740583\r\n				</li>\r\n				<li>\r\n					<b>条形码:</b>&nbsp;9787509740583\r\n				</li>\r\n				<li>\r\n					<b>商品尺寸:&nbsp;</b>23.4 x 16.2 x 2.2 cm\r\n				</li>\r\n				<li>\r\n					<b>商品重量:&nbsp;</b>599 g\r\n				</li>\r\n				<li>\r\n					<b>品牌:</b>&nbsp;社会科学文献出版社\r\n				</li>\r\n			</ul>\r\n		</p>\r\n		<p>\r\n			<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				王洛林和张宇燕主编的《2013年世界经济形势分析与预测》内容介绍：世界经济增长速度在2012年整体放缓。发达经济体面临高失业和高债务等严重的经济和社会问题，传统宏观货币和财政政策空间几乎用尽，再加上人口老龄化和“二战”后婴儿潮大批进入退休年龄的影响，美国和日本经济低速增长，欧元区经济则出现负增长。受外需急剧下降以及国内经济调整的影响，一些新兴市场与发展中经济体放缓了经济增长步伐。全球贸易增速明显放缓和争端日益频繁，大宗商品价格逐步走低，股市受非常规政策影响非自然上涨，全球治理和区域一体化有亮点但进展缓慢。展望2013年世界经济，美国“财政悬崖”、欧洲债务危机久拖不决、流动性过剩、贸易保护主义和地区安全等诸多因素值得关注，全球经济的中低速增长将成为新的常态。\r\n				<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n				</div>\r\n			</div>\r\n			<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n				作者简介\r\n			</h3>\r\n			<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				张宇燕，教授，中国社会科学院亚太所党委书记、副所长，世界经济学会副会长、中国亚太学会副会长、全国美国经济学会副会长。曾在芬兰赫尔辛基联合国大学发展经济学研究所、美国马里兰大学经济系做访问学者。主要研究领域为国际政治经济学、制度经济学和公共选择理论。参与和主持多项国家和省部级社会科学研究项目，在《经济研究》等核心刊物发表数十篇学术论文，已出版多部学术专著，其中《经济发展与制度选择》一书获中国社会科学院第二届中青年优秀科研成果专著类一等奖。王洛林 男，1938年6月出生，湖北武昌人，1960年毕业于北京大学经济系。曾任厦门大学副校长，中国社会科学院常务副院长；现任中国社会科学院特邀顾问，全国日本经济学会会长，中国社会科学院研究生院教授、博士生导师。研究领域：国际贸易、国际投资、世界经济、宏观经济和金融等。主要著作有：《世界经济形势分析与预测》(主编)、《关于国有外贸企业转换经营机制的几个问题》(1995)、《日元贬值及其对亚洲经济的影响》(1999)、《日本的通货紧缩性经济危机》(2000)、《日本金融考察报告》(2001)等。\r\n			</div>\r\n			<p>\r\n				<br />\r\n			</p>\r\n		</p>\r\n	</p>\r\n</p>', null, '2013-06-29 23:46:25', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('UfMJVn', 'FzQ32i', '2', '2013年中国经济形势分析与预测', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年11月1日</span>\r\n</div>\r\n<p>\r\n	<p>\r\n		<p>\r\n			《2013经济蓝皮书-中国经济形势分析与预测》，本书从经济增长与产业发展、宏观政策、消费投资等方面对2012年中国宏观及微观经济层面形势，特别是全球金融危机及其对中国经济的影响进行了深入分析，可供关注中国经济问题的学者参考。\r\n		</p>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<p>\r\n			<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				<li>\r\n					<b>出版社:</b>&nbsp;社会科学文献出版社; 第1版 (2012年11月1日)\r\n				</li>\r\n				<li>\r\n					<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=Economy%20of%20China%20Analysis%20and%20Forecast(2013)&amp;search-alias=books\">Economy of China Analysis and Forecast(2013)</a>\r\n				</li>\r\n				<li>\r\n					<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=%E7%BB%8F%E6%B5%8E%E8%93%9D%E7%9A%AE%E4%B9%A6&amp;search-alias=books\">经济蓝皮书</a>\r\n				</li>\r\n				<li>\r\n					<b>平装:</b>&nbsp;406页\r\n				</li>\r\n				<li>\r\n					<b>语种：</b>&nbsp;简体中文\r\n				</li>\r\n				<li>\r\n					<b>开本:</b>&nbsp;16\r\n				</li>\r\n				<li>\r\n					<b>ISBN:</b>&nbsp;9787509740293, 7509740290\r\n				</li>\r\n				<li>\r\n					<b>条形码:</b>&nbsp;9787509740293\r\n				</li>\r\n				<li>\r\n					<b>商品尺寸:&nbsp;</b>23.8 x 16.4 x 2 cm\r\n				</li>\r\n				<li>\r\n					<b>商品重量:&nbsp;</b>662 g\r\n				</li>\r\n				<li>\r\n					<b>品牌:</b>&nbsp;社会科学文献出版社\r\n				</li>\r\n			</ul>\r\n			<p>\r\n				<br />\r\n			</p>\r\n			<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				陈佳贵和李扬主编的《2013年中国经济形势分析与预测》内容介绍：在世界经济复苏乏力、欧洲主权债务危机反复波动的国际大背景下，中国经济增速出现回落，预计2012年增长幅度为7.5%左右。前三季度回落幅度逐季收窄，经济运行整体企稳，农业生产形势良好，工业生产虽然有所放缓，但产业结构不断优化。依靠内需支撑，市场消费平稳，投资增长依然较快；受发达国家经济复苏乏力的影响，外需回落明显。另外，就业情况基本稳定、居民收入增长较快、物价涨幅回落，显示经济运行整体健康。<br />\r\n2013年国际环境仍然复杂严峻，国内经济运行中深层次问题尚待解决，但中国经济发展具有较多有利条件，有望进一步企稳回升，增长幅度区间将取决于国际大环境与内部深层次结构调整的进展。<br />\r\n稳增长是当前经济工作中的首要任务，同时需要处理好稳增长和调结构、转方式与深化改革间的关系。通过稳定投资增长、优化投资结构，扩大结构性减税力度、适当降低增值税标准税率等措施实现经济稳中求进。同时促进技术与制度创新，提高劳动力素质，培养经济长期增长潜力。\r\n				<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n				</div>\r\n			</div>\r\n			<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n				作者简介\r\n			</h3>\r\n			<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				陈佳贵，中国社会科学院经济学部企业社会责任研究中心理事长。1944年10月出生，男，汉族，四川省岳池人，中共党员，1988年中国社会科学院研究生院工业经济系毕业，博士。原中国社会科学院副院长，研究员，博士生导师，中国社会科学院主席团主席，中国社会科学院经济学部主任，学部委员，第十届、第十一届全国人大常委会委员，财经委员会委员；兼任中国企业管理研究会会长、中国工业经济联合总会副会长、国务院学位委员会学科评议组应用经济学科召集人、全国社科基金理论经济评审组副组长。学术专长为工业经济、企业管理。1991年被国务院学位委员会、国家教委授予“有突出贡献的中国博士”称号；1992年被人事部批准为“有突出贡献的中青年专家”；1993年始享受国务院颁发的政府特殊津贴。李扬 中国社会科学院副院长、研究员，长期从事中国经济相关研究，领域涉及货币、银行、金融市场等，专著《财政补贴经济分析》获1990年孙冶方经济科学奖著作奖。其他代表作还有《中国金融改革研究》、《金融全球化研究》等。\r\n			</div>\r\n		</p>\r\n	</p>\r\n</p>', null, '2013-06-29 23:47:44', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('6BZFZn', 'FzQ32i', '2', '解读中国经济', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《解读中国经济》是在首版《中国经济专题》的基础上修订而成的，新版结合最新的国际经济形势与中国经济新变化全面更新了相关数据和分析内容，并新增一篇关于“全球失衡、储备货币与全球治理”的附录。书中总结了中国与其他国家、地区经济发展和改革活动的经验，提出了一个经济发展和转型的一般理论，并以此理论分析中国在改革和发展过程中取得的各项成就，面临的主要经济、社会问题，探讨其原因和解决问题的办法。《解读中国经济》的最大特色就是用通俗的语言和生动的实例，系统地回顾了中国经济的发展历程与改革经验，深入浅出地讲解了中国经济发展的热点问题，重点阐述了自生能力理论和比较优势战略。</span><br />\r\n<br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">海报：</span><br />\r\n<br />\r\n<p>\r\n	<img src=\"http://g-ec4.images-amazon.com/images/G/28/BOOK-Catalog/annaww3/B0092KF86K_01_AMZN.jpg\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;北京大学出版社; 第1版 (2012年9月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Demystifying%20the%20Chinese%20Economy&search-alias=books\">Demystifying the Chinese Economy</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;315页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7301211104, 9787301211106\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787301211106\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24.5 x 17.4 x 1.8 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>499 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;北京大学出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	2013年中央国家机关“强素质作表率”读书活动评选推荐，《解读中国经济》 入选“最受中央国家机关干部欢迎的10本书”。<br />\r\n《解读中国经济》以宏大的历史视角展示了中国经济实力两千多年来的跌宕起伏和戏剧性复兴，解读了中国经济增长的源头以及未来增长的前景。对任何想要了解中国经济发展的人来说，这都是一部必读的著作，一部早就应该出现的重要著作！<br />\r\n中国在2030年甚至更早将再次成为世界最大的经济体？中国为什么没有出现科技革命？为什么说中国近代的政治和社会史就是中国知识分子的责任感和挫折感这两种心态相互交融所推动的过程？“东亚奇迹”现象出现的背后有什么成功经验？只有解决了国有企业的问题，向市场经济体制的过渡才能最终实现？中国的人均收入为什么在历史上长期保持不变？为什么中国在历史上一直是一个人口众多的国家？《解读中国经济》将给您答案！<br />\r\n站在中国顶级经济学家视角，看懂世界经济格局，看清中国经济走势。赫克曼、迈尔森、菲尔普斯等多位诺奖得主鼎力推荐！<br />\r\n《解读中国经济》首版曾荣获：<br />\r\n1.2009年度输出版优秀图书奖；<br />\r\n2.2009年度全行业优秀畅销品种；<br />\r\n3.第九届中国大学出版社图书奖优秀畅销书一等奖；<br />\r\n4.经典中国国际出版工程（英文版、德文版、泰文版、日文版）；\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	名人推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	没有人比林毅夫更了解中国经济，也没有人比他更有资格去描述这一经济体的根本运行机制。这本书是从一个独特的位置用一种独特的视角来看待中国奇迹。<br />\r\n——巴里•艾肯格林（Barry Eichengreen），加利福尼亚大学伯克利分校<br />\r\n林毅夫的《解读中国经济》是一部杰作。这本书在很多层面都取得了成功。它以宏大的历史视角展示了中国经济实力两千多年来的跌宕起伏和戏剧性复兴。它以具有分析性的资讯，解读了中国经济增长的源头以及未来增长的前景。林毅夫以他富有见地的比较优势战略（CAF）观点将中国注重实效的经济发展模式提升到了理论高度。这本书向很多传统的新古典经济理论的信条提出了挑战，并解释了在现实中照搬这些原理是如何对许多转型国家造成灾难性后果的。<br />\r\n——詹姆斯•赫克曼，芝加哥大学，2000年诺贝尔经济学奖获得者<br />\r\n这本书探讨了中国从一个贫困的发展中国家向一个现代经济增长的全球领导者进行巨大转变时的一些根本性问题。这些问题属于我们这一时代最为关键的问题。林毅夫是最有资格帮助我们理解这些问题的人。在书中他向我们提供了一个看待中国以及世界现代经济发展前提的全新而重要的视角。<br />\r\n——罗杰•迈尔森，芝加哥大学，2007年诺贝尔经济学奖获得者<br />\r\n这本书对中国过去的落后根源以及后来的超凡成功进行了清晰而富有见地的研究，对任何想要了解中国经济发展的人来说，这都是一部必读的著作。一部早就应该出现的重要著作！<br />\r\n——埃德蒙•费尔普斯，哥伦比亚大学，2006年诺贝尔经济学奖获得者<br />\r\n林毅夫代表着一种相对新兴的现象：他既是一位自信的中国学者，也是一位全球政策制定者。他现在是世界银行首席经济学家兼高级副行长。虽然获得过芝加哥大学博士学位，但他却是一位地道的中国人：专业、爱国，最重要的是务实。<br />\r\n——马丁•沃尔夫，《金融时报》（Financial Times）首席经济评论员<br />\r\n他（林毅夫）为他的国家的成功而感到骄傲，但同时也坦承它的不足。他在书中提出要纠正对中国过去的一些错误看法，并对未来提出新的见解。他还建议其他发展中国家学习中国在快速增长中摆脱贫困的经验。<br />\r\n——詹姆斯•普莱斯雷，彭博新闻社（Bloomberg News）评论员<br />\r\n经济理论的作用就像一张地图，地图不是真实世界本身，而是帮助我们了解周遭的环境以及下一步如果往前、往后、往右或者往左会遇到什么样的新景象，地图一定要有一定程度的抽象和简化，但如果把重要的地标忽略了或画错了，经常会造成人们行动的失误。<br />\r\n——林毅夫\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:22:19', '8', '1', '1');
INSERT INTO `tbl_library` VALUES ('myqeEf', 'nEraQv', '2', '中国低碳经济发展报告', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2013年5月1日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;蓝皮书聚焦中国的空气污染问题，从科学角度和国际视点解读雾霾现象，分析其危害，解释其成因，提出对策。书中指出，雾霾这样的环境危机事件也为我们敲响了警钟，成为绿色低碳发展的倒逼机制。此外书中还解读了十八大报告关于大力推进生态文明建设的精神和“十二五”规划中的绿色低碳发展战略，分解各地区节能减排指标，并提出完成指标的政策建议和落实措施。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;社会科学文献出版社; 第1版 (2013年5月1日)\r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E4%BD%8E%E7%A2%B3%E7%BB%8F%E6%B5%8E%E8%93%9D%E7%9A%AE%E4%B9%A6&search-alias=books\">低碳经济蓝皮书</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;220页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787509745465\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.4 x 16.4 x 1.2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>358 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;社会科学文献出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	Ⅰ&nbsp;总报告<br />\r\n1&nbsp;中国生态文明建设任重道远<br />\r\n<br />\r\nⅡ&nbsp;专题篇<br />\r\n2&nbsp;中国城市PM&nbsp;2.5的污染与治理<br />\r\n3&nbsp;PM&nbsp;2.5污染的治理路径与绿色低碳发展<br />\r\n4&nbsp;北京及周边区域颗粒物和细粒子排放源特征及其来源解析<br />\r\n5&nbsp;日本治理环境的经验及其对中国治理PM&nbsp;2.5问题的启示<br />\r\n<br />\r\nⅢ&nbsp;对策篇<br />\r\n6&nbsp;“十二五”时期节能目标的地区分解<br />\r\n7&nbsp;湖北省碳交易试点<br />\r\n8&nbsp;中国区域碳排放分异特征与低碳发展对策<br />\r\n9&nbsp;中国与欧盟在气候变化和低碳发展领域的合作<br />\r\n<br />\r\nⅣ&nbsp;国际篇<br />\r\n10&nbsp;日本的能源政策调整<br />\r\n11&nbsp;日本的能源政策与气候变化对策<br />\r\n12&nbsp;丰田市的智能城市建设<br />\r\n<br />\r\nⅤ&nbsp;附录<br />\r\n13&nbsp;附录1中国碳排放统计<br />\r\n14&nbsp;附录2世界碳排放统计\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:52:40', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('2eYzIj', 'nEraQv', '2', '中国投资发展报告', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2013年4月1日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;《投资蓝皮书：中国投资发展报告（2013版）》全面回顾和总结了2012年我国投资发展状况，分析了我国投资的宏观经济环境和政策背景。报告首次推出了“中国建投投资评级”，对直接股权投资市场、证券市场、房地产市场、理财市场等投资市场进行了分析和比较，对2013年投资市场走势进行了预测和展望。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;《投资蓝皮书：中国投资发展报告（2013版）》是中国建投集团在投资实践中对我国投资发展相关领域、市场、行业等深入研究和思考的成果。我们将秉承客观、严谨、科学的态度，在选题角度、研究深度和广度方面对投资领域的相关问题进行更为深入的研究和探索，为投资界及相关人士提供有价值的参考。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;社会科学文献出版社; 第1版 (2013年4月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Annual%20Report%20on%20The%20Development%20of%20China%27s%20Investment(2013)&search-alias=books\">Annual Report on The Development of China\'s Investment(2013)</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E6%8A%95%E8%B5%84%E8%93%9D%E7%9A%AE%E4%B9%A6&search-alias=books\">投资蓝皮书</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;534页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787509744536\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.4 x 16.4 x 2.6 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>1 Kg\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;社会科学文献出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《中国投资发展报告(2013版)/投资蓝皮书》编著者杨庆蔚。<br />\r\n本书是中国建投在投资实践中对中国投资发展各方面问题深入研究思考的成果，书中全面回顾和总结了2012年中国投资发展的状况，分析了中国投资发展的宏观经济和政策背景，并对直接股权投资、股票及债券市场、房地产市场、不良资产市场等有关市场和行业进行了梳理和分析，对2013年中国投资发展趋势进行了预测和展望。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	B.I&nbsp;总报告<br />\r\nB.1&nbsp;2012年中国投资发展回顾及2013年展望<br />\r\n一&nbsp;2012年中国投资发展回顾<br />\r\n二&nbsp;2012年各主要投资发展回顾<br />\r\n三&nbsp;2013年投资市场预测<br />\r\n附件&nbsp;中国建投投资评级说明<br />\r\nB；Ⅱ&nbsp;宏观环境篇<br />\r\nB.2&nbsp;2012年宏观经济环境分析与2013年展望<br />\r\nB.3&nbsp;2012年美国宏观经济形势分析与2013年展望<br />\r\nB.4中国城镇化战略及未来发展预测<br />\r\nB.5&nbsp;2012年中国固定资产投资分析与2013年展望<br />\r\nB&nbsp;Ⅲ&nbsp;市场形势篇<br />\r\nB.6&nbsp;2012年我国股权投资市场回顾与2013年展望<br />\r\nB.7&nbsp;2012年我国股票市场回顾与2013年展望&nbsp;贾，<br />\r\nB.8&nbsp;2012年我国债券市场回顾与2013年展望&nbsp;武<br />\r\nB.9&nbsp;2012年我国信托市场回顾与2013年展望&nbsp;曹<br />\r\nB.10&nbsp;2012年我国银行理财业务市场回顾与2013年展望<br />\r\nB.11&nbsp;2012年我国融资租赁市场回顾与2013年展望<br />\r\nB.12&nbsp;2012年我国房地产市场运行状况分析与2013年形势展望<br />\r\nB&nbsp;Ⅳ行业发展篇<br />\r\nB.13&nbsp;我国金融及相关服务业投资分析报告<br />\r\nB.14&nbsp;我国消费产业投资分析报告<br />\r\nB.15&nbsp;我国商业地产投资分析报告<br />\r\nB.16&nbsp;我国医疗机构投资分析报告<br />\r\nB.17&nbsp;我国出版传媒行业投资分析报告<br />\r\nB.18&nbsp;我国光伏行业投资分析报告<br />\r\nB.19&nbsp;我国医药制造业投资分析报告<br />\r\nB&nbsp;V&nbsp;投资实践篇<br />\r\nB.20&nbsp;我国投资市场法律环境报告<br />\r\nB.2l&nbsp;我国并购市场发展报告<br />\r\nB.22&nbsp;养老金投资管理分析报告<br />\r\nBⅥ&nbsp;附&nbsp;录<br />\r\nB.23&nbsp;2012年中国投资市场大事记\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:52:48', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('Vb63ua', 'nEraQv', '2', '中国金融发展报告(2013版)/金融蓝皮书', null, '<p>\r\n	中国金融发展报告(2013版)/金融蓝皮书\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年12月1日</span> \r\n</div>\r\n<div class=\"content\" style=\"margin:0.5em 0px 0em 25px;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<div id=\"outer_postBodyPS\">\r\n		<div id=\"postBodyPS\">\r\n			本书对2012年度中国金融发展和运行中的各方面主要情况进行概括和分析, 对所发生的一些主要金融事件进行研讨和评论。\r\n		</div>\r\n	</div>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;社会科学文献出版社; 第1版 (2012年12月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Annual%20Report%20on%20China%27s%20Financial%20Development(2013)&search-alias=books\">Annual Report on China\'s Financial Development(2013)</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E9%87%91%E8%9E%8D%E8%93%9D%E7%9A%AE%E4%B9%A6&search-alias=books\">金融蓝皮书</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;257页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787509740620\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787509740620\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.4 x 16.2 x 1.4 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>440 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;社会科学文献出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	李扬和王国刚主编的《中国金融发展报告（2013）》是中国社会科学院金融研究所组织编写的年度性研究报告，对2012年度中国金融发展与改革的各个方面进行了全面、系统的总结和分析，全书采取总分报告体例，简洁明快地展现了中国金融业在过去一年发展的全貌。总报告呈现了2012年中国金融发展的概貌，十一个分报告分别完整地介绍和总结了中国银行业、证券业、保险业、金融监管改革、货币市场、银行理财产品市场、债券市场、股票市场、期货市场、外汇市场和国际收支等各方面的发展、改革与变化。<br />\r\n本报告是系统阐述年度中国金融发展与改革的权威之作。中国社会科学院金融研究所对《中国金融发展报告》的写作持续了十多年的时间，具有丰富的写作经验，举全所之力、集全所智慧于一体。书中既有翔实的资料展示，又有深入的剖析和逻辑解构，是读者快速了解、管窥和掌握中国金融体系发展与改革现状的必要参考书。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	作者简介\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	李扬：1981、1984、1989年分别于安徽大学、复旦大学、中国人民大学获经济学学士、硕士、博士学位。1998～1999年，在美国哥伦比亚大学做高级访问学者。现任中国社会科学院金融研究所所长、金融研究中心主任；研究员，博士生导师；清华大学、北京大学、中国人民大学、复旦大学、南京大学、中国科技大学等大学兼职教授。第三任中国人民银行货币政策委员会委员。中国金融学会副会长、学术委员会委员；太平洋经济合作委员会(PECC)金融市场发展委员会委员；中国国际金融学会常务理事；中国城市金融学会常务理事；中国财政学会常务理事；中国科学院自然科学和社会科学交叉研究中心学术委会委员；若干省、市政府顾问。国家级有突出贡献中青年专家，国务院\"政府特殊津贴\"享受者。曾四次获得\"孙冶方经济科学\"著作奖和论文奖。王国刚，生于1955年，江苏无锡人。1988年毕业于中国人民大学，获经济学博士。研究员、博士生导师、注册会计师。现任中国社会科学院金融研究所所长，主要从事金融市场、公司金融和经济体制改革等问题研究，曾参与《公司法》、《证券法》、《信托法》等法律法规及有关政策的研讨。已出版著作近40部，发表论文600余篇，主持和参加过30多项国家级和国际合作课题的研究，获得10多项国家级、省部级的教学奖和科研奖，被十几个政府部门聘为经济顾问，40多家企业聘为投资顾问。\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-26 17:52:57', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('yEjEje', 'FzQ32i', '2', '第三次工业革命:新经济模式如何改变世界', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《第3次工业革命:新经济模式如何改变世界》中作者为我们描绘了一个宏伟的蓝图：数亿计的人们将在自己家里、办公室里、工厂里生产出自己的绿色能源，并在“能源互联网”上与大家分享，这就好像现在我们在网上发布、分享消息一样。能源民主化将从根本上重塑人际关系，它将影响我们如何做生意，如何管理社会，如何教育子女和如何生活。我们正处于第二次工业革命和石油世纪的最后阶段。这是一个令人难以接受的严峻现实，因为这一现实将迫使人类迅速过渡到一个全新的能源体制和工业模式。否则，人类文明就有消失的危险。作者敏锐地发现，历史上数次重大的经济革命都是在新的通讯技术和新的能源系统结合之际发生的。新的通讯技术和新的能源系统结合将再次出现——互联网技术和可再生能源将结合起来，将为第三次工业革命创造强大的新基础设施。第一次工业革命使19世纪的世界发生了翻天覆地的变化；第二次工业革命为20世纪的人们开创了新世界；第三次工业革命同样也将在21世纪从根本上改变人们的生活和工作。我们即将步入一个“后碳”时代。人类能否可持续发展，能否避免灾难性的气候变化，第三次工业革命将是未来的希望。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中信出版社; 第1版 (2012年6月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=The%20Third%20Industrial%20Revolution&search-alias=books\">The Third Industrial Revolution</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;312页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787508633121, 7508633121\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787508633121\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>22.8 x 16.8 x 2.2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>481 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中信出版股份有限公司\r\n	</li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《第3次工业革命:新经济模式如何改变世界》：《新华网》2012年6月5日头条新闻重点报道！<br />\r\n第三次工业革命改写人类发展进程，社会该怎么发展？经济该怎么突破？世界未来的趋势是什么？享誉全球的未来预测大师再次预言即将到来的社会革命，引领21世纪的新经济模式、影响全球未来30年的社会革命。我们的目标在哪里？我们的希望在哪里？我们的路该怎么走？<br />\r\n第一次工业革命造就了密集的城市核心区、拔地而起的工厂<br />\r\n第二次工业革命催生了城郊大片房地产业以及工业区的繁荣<br />\r\n第三次工业革命将会把每一栋楼房转变成住房和微型发电厂<br />\r\n如何转变经济发展方式？如何保持经济的可持续发展？<br />\r\n站在人类发展历史与未来的高度，面对数百年一遇的发展机遇，我们似乎找到了中国经济发展的希望——可持续发展是方向，转变经济发展方式是途径，而第三次工业革命就是我们的蓝图。<br />\r\n中国高层已经认识到，加快经济发展方式转变是中国经济领域的一场深刻变革，关系改革开放和社会主义现代化建设全局。转变经济发展方式已刻不容缓。而要转变经济发展方式，就必须在全球背景下，以第三次工业革命为契机，实施新兴科技和新兴产业发展战略。在这一点上，中国决策层的指向与全球的前瞻性思考形成了良好的默契。<br />\r\n一场跨越社会、跨越历史的产业革命即将爆发！《第三次工业革命》一书将为你提供全新而独到的视野，把握机遇，辉煌就在眼前。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	里夫金把21世纪两种不同的技术——互联网和可再生能源——联结在一起，为我们的未来描绘了一个新的、充满活力的经济前景。期望现在的经济重获新生、创造大量就业机会和为子孙后代创造可持续发展的未来，第三次工业革命提供了一个绝对必要的路线图。<br />\r\n——《赫芬顿邮报》传媒集团总裁、主编阿里安娜•赫芬顿<br />\r\n里夫金先生不仅明确地勾画了全球面临的挑战，也为商界领袖、政府和公众指明了前进的方向。<br />\r\n——思科董事长、总裁约翰•钱伯斯<br />\r\n杰里米•里夫金的创造性思维一直影响着决策者和公众。这本书展示了可再生能源与现代科技在向低碳经济过渡时将发挥重要作用。<br />\r\n——欧盟委员会主席若泽•曼努埃尔•巴罗佐<br />\r\n这是一部杰出的作品，作者里夫金是我们这个时代最重要的思想家之一……他提出了一个富有远见的新经济发展模式，这一模式能确保自然资源和生态系统的可持续性。<br />\r\n——政府间气候变化专门委员会主席拉金德拉•帕乔里<br />\r\n杰里米•里夫金的第三次工业革命模式是引领人类进入21世纪的杰出新经济模式。它是罗马长期经济发展计划的核心。它将为世界上每个要创造可持续发展和繁荣的城市描绘蓝图。<br />\r\n——意大利罗马市市长詹尼•阿雷曼诺<br />\r\n绝对发人深省的新经济模式……希望决策者和商界领袖行动起来，抓住这次机会，通过可持续发展改革和可再生能源分散布局推动社会经济的发展。<br />\r\n——飞利浦照明事业部首席执行官浦若迪<br />\r\n非常令人兴奋的改变全球能源系统的战略。这本书可以帮助制定解决15亿穷人社会和经济问题的方案，这些人得不到清洁、可靠和高效的能源服务。<br />\r\n——联合国工业发展组织总干事、联合国能源小组主席坎德赫•尤姆凯拉<br />\r\n杰里米•里夫金将严谨的思维与活泼、引人入胜的语言结合在一起，孕育出了这部适时而又重要的作品。它关注如何使能源来源多样化，以创造一个人们能安居乐业、我们所居住的星球也能承受的世界。<br />\r\n——世界可持续发展工商理事会主席比约恩•斯蒂格森<br />\r\n非常引人注目……许多专家关注怎样改变能源系统，却很少有人能像杰里米•里夫金这样提供一个全面的经济视角和社会发展蓝图。“能源互联网”将把世界经济增长提升到一个新的高度；同时，既能应对气候变化，又能增加能源的安全性……这实在是一本必读书。<br />\r\n——全球智能电网联合会主席圭多•巴特尔斯<br />\r\n真是后碳时代令人振奋的构想！杰里米•里夫金把绿色交通置于新的高科技的第三次工业革命框架内，重新界定了人类活动的概念。这很可能是人类交通的未来。<br />\r\n——国际清洁运输委员会主席艾伦•劳埃德<br />\r\n为了全球经济能过渡到更可持续的未来，杰里米•里夫金的第三次工业革命提出了一个视角全面、现实可行、技术可靠、市场驱动的模式。他富有远见的“分散式资本主义”模式在设计和建筑业的领袖们中间奏响了最强音。这些领袖们肩负着把第三次工业革命的理论与希望变成现实的重担。<br />\r\n——建筑业圆桌会议主席马克•卡索<br />\r\n博闻强识而又妙趣横生……伴随着传统政治思想的摒弃和资本主义的重新定义，杰里米•里夫金看到了第三次工业革命模式颠覆传统经济理论的可能性。这种全球社会的未来非常令人鼓舞。这是一本严肃思考者不应该错过的书。<br />\r\n——庞巴德运输集团乘客部总经理斯特凡•兰博德-麦桑<br />\r\n第三次工业革命给我们提供了令人兴奋的机会，它将为我们创造现在急需的就业机会、提高企业利润、突破技术瓶颈。我们应该牢牢地抓住它，虽然我们还有时间。<br />\r\n——未来论坛创始人、董事乔纳森•波立德<br />\r\n里夫金提供了一个如何转变经济范式的路线图。作为全球性房地产服务公司的总裁，我深信，把楼房作为“微型发电厂”的构想是有前途的。为了实现这一构想，我们需要迅速行动起来，并为第三次工业革命奠定基础。让我们开始吧！<br />\r\n——库什曼和韦克菲尔德全球经纪人董事长布鲁斯•莫斯勒\r\n</div>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:25:24', '2', '1', '1');
INSERT INTO `tbl_library` VALUES ('nqUzQf', 'FzQ32i', '2', '下一波世界趋势', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">发售日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年7月1日</span>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《下一波世界趋势》内容简介：盖马沃特向人们证明了世界绝不是我们一般人所想象的那样。他还解释了为什么进一步的一体化所获得的效果要远远超过全球化支持者所预料的。潘卡吉•盖马沃特所提出的观点绝对值得你深入思考，他通过系统而深入的论证，广泛的观察和案例分析表明，国与国之间文化的、政治的、地理的鸿沟仍然存在，地缘经济的差异仍在很大程度上影响全球战略。<br />\r\n接下来他又针对市场失灵――失业、环境恶化、经济波动，贸易和资本不平衡（反全球化者经常要提到的问题）――提出了自己的解决方案。《下一波世界趋势》以极强的信服力驱散了那种对全球化根深蒂固却又有失偏颇的假设。《下一波世界趋势》以大胆、泼辣的笔触解释了全球人们通过跨国一体化的新方案是能够实现共同繁荣的。盖马沃特的思想足以让任何人感到震撼――不论你是支持全球化还是反对全球化。<br />\r\n可以说，盖马沃特教授的“半全球化”视角为我们描绘出一幅更准确的全球化图景，这一视角虽然没有托马斯•弗里德曼的乐观，但却对中国企业的国际化更具指导性。<br />\r\n世界真的是这样吗？<br />\r\n2008年世界经济危机促使人们思考，如果没有全球化，美国次贷危机可以控制在一个更小的范围内。<br />\r\n全球化真的犹如洪水猛兽，会扩散市场失灵的效应么？\r\n</p>\r\n<p>\r\n	当今世界的全球化水平到底如何？是90%？50%？还是只有10%？如果只有可怜的10%，那我们需要畏惧全球化而实施保护主义么？相反，进一步全球化能给我们的GDP、就业带来什么好处呢？\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中信出版社; 第1版 (2012年7月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=World%203.0%3A%20Global%20Prosperity%20and%20How%20to%20Achieve%20It&search-alias=books\">World 3.0: Global Prosperity and How to Achieve It</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;320页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7508633725, 9787508633725\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787508633725\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>22.8 x 16.8 x 2.4 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>499 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中信出版股份有限公司\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《下一波世界趋势》编辑推荐：还记得当年吗？《世界是平的》一夜之间就风靡了全球。大家都在讨论：地球变小了，国界变没了，我们一下全都成了地球人！<br />\r\n但从今天看来，《世界是平的》好像在忽悠我们的智商，因为世界虽然在量变中，但并没有出现质的飞跃。你是否也被忽悠了呢？如果你想变得清醒一些。那么，我强烈建议你读一读《下一波世界趋势》这本书。《世界是平的》带给我们的就是一个虚幻的世界，《下一波世界趋势》则会带给你一个真实的现实世界。<br />\r\n阅读《下一波世界趋势》这本书，你会发现，原来全球化的进程只走了不到三分之一，原来国家之间的边界还是如此重要，原来全球主义完全是鬼话连篇，原来这个世界真的不是很公平……\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	我们现在还陷在世界2.0时代，在这个世界里，全球一体化的影响还是有限的。盖马沃特教授巧妙地提出了我们如何过渡到世界3.0时代，在这个世界里，开放会带来广泛的技术、文化和社会效益。知识的传播――通过人们、贸易或投资――将对全球增长起到非常大的作用。对于这一问题的讨论是非常有趣而及时的，应该得到我们的足够重视。<br />\r\n――素帕猜•巴尼巴滴 联合国贸易与发展会议秘书长<br />\r\n《下一波世界趋势》为我们思考全球化问题、市场失灵、市场一体化提供了一个综合性框架。盖马沃特给高级经理、商业人士和政府规划了非常有远见和实用价值的思路。他的开发全球化无限潜力进程中关于管理资本逆转和不平衡的观点意义非常重大。<br />\r\n――米歇尔•康德苏 国际货币基金组织前总裁<br />\r\n这本书应该得到广泛重视并阅读。它揭示了――基于盖马沃特作为享有盛誉的全球战略学家令人信服的客观分析――我们所生活的世界，充其量只能算半个全球化。接着又给我们讲述了对于我们个人、商业人士和政府，这个世界又意味着什么。<br />\r\n――拉丹•塔塔 塔塔集团董事会主席&nbsp;<br />\r\n这是一本在恰当的时间出版的恰当的书。自从大萧条之后最严重的经济危机发生以来，很多领导者都在寻求能够促使全球经济更稳定、更持续的发展之路。《下一波世界趋势》提供了达成这一目标的非常合理的战略。<br />\r\n――彼得•罗旭德 西门子公司总裁兼首席执行官<br />\r\n在这个全球化、经济强劲和社会发展不平衡时代，潘卡吉•玛格沃特给人们提供了一个印象深刻的和综合性的分析。他的书为我们锻造了一种思维模式，这种思维模式可以以可持续发展的思路重塑这个世界。<br />\r\n――穆罕默德•尤努斯 穷人的银行家、经济学家，2006年诺贝尔和平奖获得者\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:23:50', '6', '1', '1');
INSERT INTO `tbl_library` VALUES ('fMneYn', 'FzQ32i', '2', '中国经济增长,靠什么', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">发售日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年8月13日</span>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《中国经济增长,靠什么》内容简介：金融危机之后，批评中国经济的声音此起彼伏。有人怀疑，中国经济刺激计划最终加深了中国对出口和投资的依赖，并阻碍国内消费的增长。还有人怀疑，金融危机之后，诸如中国这样的低消费、高储蓄国家的增长模式是否时宜？世界经济复苏的力度可能不够强劲，出口不能像过去那样继续作为中国经济增长的主要来源。中国经济增长的必然归宿将是走向衰退。<br />\r\n但作者认为，中国针对经济问题的应对之策，不仅手段多样，而且经过精心策划。中国领导层已经认识到，对出口的严重依赖意味着全球经济衰退将使中国变得极其脆弱。中国经济刺激计划的效果令人吃惊，使中国成为最早开始从世界经济衰退中复苏的全球重要经济体。<br />\r\n另一方面，作者极其敏锐而客观地观察到，过去七八年的事实表明，温和的、对边缘修修剪剪的、循序渐进的经济改革不会引导中国经济从根本上实现再平衡。潜在的金融扭曲，对整个中国经济出现的严重资源错误配置起到了推波助澜的作用。\r\n</p>\r\n<p>\r\n	如果你对中国经济的未来感兴趣，如果你对涉及自己亲身利益的工资、就业、住房、金融甚至退休金感兴趣，那么，你一定不可错过这本客观分析中国经济的书——要知道，“在中国问题上，这本书的作者可以做任何人的导师。”\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中信出版社; 第1版 (2012年8月15日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Sustaining%20China%27s%20Economic%20Growth%20After%20the%20Global%20Financial%20Crisis&search-alias=books\">Sustaining China\'s Economic Growth After the Global Financial Crisis</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;236页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7508634039, 9787508634036\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787508634036\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>22.6 x 16.8 x 1.4 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>340 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中信出版股份有限公司\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《中国经济增长,靠什么》之所以起这么个书名，完全是有感于书的内容。试想，在美国经济萧条、欧洲债务缠身、日本经济萎靡不振等因素的多重打压下，中国经济明显开始放缓。出口增长很难持续、基础设施投资难以为继、国内消费提不起情绪……接下来，中国经济增长，到底靠什么。这就是《中国经济增长,靠什么》的主题。<br />\r\n放下你手中忙碌的工作，把一盏香茶，细细品味一本专司研究中国经济的老外写的一本书，你会发现，原来中国经济有那么多让你吃惊的东西，原来中国经济可以那么与众不同。<br />\r\n如果用一句经典台词来形容中国经济，我觉得“中国经济水深得很”非常合适。<br />\r\n中国经济谁能看得懂？国内作者写中国经济的书惯于集中皮毛，大有“头疼医头、脚疼医脚”的急躁习性。或大呼“中国经济即将崩溃”，或牢骚满腹，酷似其有经天纬地之才。在我看来，国外作者不受国内气候与惯常思维的影响，反而更有利于客观地评述中国经济。摆在读者你面前的这本《中国经济增长,靠什么》，就是一本极其客观评述中国经济的书。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	名人推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	本书是关注中国经济人士的必读之作。&nbsp;<br />\r\n——余永定&nbsp;<br />\r\n尼古拉斯•拉迪是美国极受尊敬的中国问题学者之一，因为他对中国经济进行了大量值得推崇的研究工作。他的新书《中国经济增长，靠什么》是关注中国经济人士的必读之作。中国政府近年来在重组失衡的经济和应对全球金融危机时采取了若干政策，就我的观察而言，在所有关于这些政策的转变和扭曲的著作中，本书内容最为全面详尽且行文结构最为合理。尼古拉斯•拉迪对中国经济刺激计划的辩解也许令某些人不快，但是他的阐释无疑将激发更加深刻的讨论。&nbsp;<br />\r\n——余永定 中国社会科学院世界经济与政治研究所所长，中国人民银行货币政策委员会前委员&nbsp;<br />\r\n中国问题对全球经济的重要意义无可匹敌，而尼古拉斯•拉迪作为中国经济政策最紧密的观察者地位无人可以取代。接下来我要说，本书值得任何关注世界经济未来的人阅读。&nbsp;<br />\r\n——劳伦斯•萨默斯 白宫国家经济委员会前主任，哈佛大学教授\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:23:41', '7', '1', '1');
INSERT INTO `tbl_library` VALUES ('yuaQba', 'FzQ32i', '2', '大转型:中国改革下一步', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">发售日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年8月2日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《大转型》内容简介：改革开放三十年带来了中国崛起，但也让政府统御市场的中国模式逐渐成型。<br />\r\n政府试图调控一切，但结果却是当前经济运行的种种怪现象：中国的广义货币泛滥成灾，但许多企业借不到钱，高利贷盛行；中国经济高速增长，但中国股市跌跌不休，投资人未能通过股市分享经济增长成果；在国民收入的三大构成部分中，政府财政收入所占比重越来越大，企业利润和居民收入占比逐渐减少，事实上的国进民退正在发生，等等。韦森教授以深厚的经济学修养，用通俗易懂的语言，梳理中国经济现状，探察各种乱象的根源，反思中国宏观经济运行机理。<br />\r\n说到政治体制改革，人们的第一反应是“一人一票”。韦森教授在书中以中国经济当前困境和西方的宪政发展史说明，民主首先是限权，尤其是限制政府的征税权。回看英国、法国和美国的民主历程，第一步无不是抗税。相比之下，中国的税务“利维坦”正在逐渐成型，在中国，政府可以征任何一种税而无须经任何人民代表同意，这种现状必须改变，管住政府的攫掠之手是民主的下一步。唯此，在国外买中国货比在国内买更便宜，吃个馒头还要交17%的税的荒唐现实才可能改变。2008年全球金融危机之后，“4万亿”仓促上马，时至今日，投资拉动经济增长模式已经弊端尽现。刺激经济增长下一步必须减税。<br />\r\n近年，政府一直提转变经济增长方式，但不首先转变政府的治理方式，经济方式就不可能转变。政府必须改变长期以来的凯恩斯主义经济政策，政府不能替人民花钱，政府如何花钱要受到人民代表的约束，要用预算民主约束政府花钱，限制将钱投向“铁、公、基”（铁路、公路、机场），必须把钱投向社保和民生。<br />\r\n世界经济发展有周期性，在第三次科技革命动力衰竭的情况下，本次的世界经济衰退将会持续相当长的时间。这给中国带来了巨大的挑战，政府投资熬过困难时期，等世界经济复苏再延续以前的模式，这种想法已经不现实了。中国经济的飞跃得益于市场化的红利，但现在正为扭曲的市场化所困——中国经济不该市场化的过度市场化，该市场化的。建立一个良序的市场经济秩序，是中国应对经济危机的当务之急，也是中国经济增长下一步的强大动力。\r\n</p>\r\n<p>\r\n	回溯至晚清，从历史的长时段看，中国人民寻求富强和建立一个现代国家的进程一直在进行。1978年后的改革开放是一座里程碑，但现在，这个伟大的社会转型进程又到了关键的时刻。中国的政治体制已到非改不行的时候了！\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中信出版社; 第1版 (2012年6月5日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=The%20Great%20Transformation%3Athe%20Current%20Situation%2CProblems%20and%20Feasible%20Cholices%20of%20China&search-alias=books\">The Great Transformation:the Current Situation,Problems and Feasible Cholices of China</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;335页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787508633008, 7508633008\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787508633008\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>22.6 x 16.8 x 2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>540 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中信出版股份有限公司\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《大转型》编辑推荐：最近，网上曾流传自由市场派“经济学家七剑下天山”之说，说“经济学家七剑下天山，剑指不合理制度，救生民于水火：莫问剑吴敬琏、由龙剑许小年、竞星剑张维迎、舍神剑茅于轼、青干剑陈志武、天瀑剑黄亚生、日月剑韦森”。但是，这些自由市场派经济学家们在网上经常挨骂。在这个自由派经济学家普遍挨骂的年代，韦森却是一个例外。他从来不删自己博客、财经网站上的回帖与留言，在民粹主义情绪肆意泛滥的网络世界里，他那些有理有据、“润物无声”的长文，得到了多数读者的认可。<br />\r\n韦森宣讲常识的方式，似乎也摆脱了中国自由派学者的普遍困境——一方面是来自大众的敌意和猜忌，另一方面是政府方面的疏远和冷漠。<br />\r\n——《南方人物周刊》\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	名人推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	古汉语之“经济”，乃“经国济世”，即今所谓政治者也。古希腊之economy,乃“家计”耳，初无涉国民也。及我历朝有为皇上谋“家计”汲民脂膏者，则谓之理财、聚敛之臣。泰西近世有为国民谋利益如斯密者，乃倡“政治经济学”，意为大众，非为皇家计也；迨皇权堙没、宪政功成，始不赘言政治矣。不意西学东渐后，宪政功未成，言政治不为皇家鼓吹则危，避政治纯言“经济”者欲不流为皇家聚敛之臣，盖亦难矣。幸有韦森等数君子岸然卓立，怀经国济世之志，倡公平正义之学，不特扬古华经济之佳意，更彰现代经济文明之真旨，聚敛之臣自不必读，而有志之士是必荐也。<br />\r\n——秦晖（清华大学历史学教授，著名史学家）<br />\r\n以前的改革等于经济改革，即使其他领域有改良，也都只能以经济发展的需要为起点，也以经济发展为终点。这么多年，韦森教授却不同，他的著作告诉我们：后发国家在发展初期即使回避制度改革也能走些里程，因为成熟的技术可以给它们提高如此的便利，但是，那种便利无法持续，必须的大转型最终是绕不过去的，其他国家没法绕过，中国也没法绕过。中国现在就处于这种绕不过去的时期。<br />\r\n——陈志武（耶鲁大学金融经济学教授、清华大学人文社会科学院长江教授）<br />\r\n古典的自由主义经济学家，如亚当•斯密，既有市场理性，又有道德意识。在当代中国经济学界中，韦森教授是少有的古典经济学的精神传人。他超越了经济学家的专业限制，从更广阔的历史与政治视野，思考当代中国改革的艰难转型，在人文与社会科学的跨学科领域，拥有广泛的知音与同道。我为上海有韦森这样的志同道合的学人而自豪。<br />\r\n——许纪霖（华东师范大学历史学教授，思勉人文高等研究院常务副院长）<br />\r\n在当下中国经济学家中，韦森是很独特的一位：他严谨的学养，他的历史和人文关怀，他的平实和独立，使他的中国经济研究既为制度的深层剖析，也是公共启蒙。我郑重推荐这本书。<br />\r\n——张力奋（英国《金融时报》副总编辑、FT中文网总编辑）\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:24:23', '4', '1', '1');
INSERT INTO `tbl_library` VALUES ('n2Ebi2', 'FzQ32i', '2', '中国2013:关键问题', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2013年1月1日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《中国2013:关键问题》内容简介：2013年，中国经济必将面临重大调整，公众有理由期待，面对新形势新问题，新领导班子将会锐意改革。在此关键时间点上，财新传媒与线装书局合作，继去年的《中国2012：寻找真实的成长》之后，推出《中国2013:关键问题》一书。因应着中国的复杂情况，《中国2013:关键问题》并没有急于给出结论，而着意于摆事实、讲道理，极少空洞的宏大叙事。虽然对国情、改革和“中国模式”等问题有基本的价值判断，但是这《中国2013:关键问题》仍旧保持了开放的态度。值得一提的是，吴敬琏、陈志武和许小年等作者，皆是一时之选，进一步保证了文章的品质与权威性。无论精读还是选读，不同需求的读者都会从中找到自己关注的焦点。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;线装书局; 第1版 (2013年1月1日)\r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;288页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787512007680\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787512007680\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24.2 x 16.8 x 2.4 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>458 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中南博集天卷\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《中国2013:关键问题》是中国国家智库十八大之后国势前瞻、中国最有话语权经济学家、学者，思考当下问题，预测中国未来五年走向、揭露增长受阻、民生告急、转型维艰、改革停滞不前的真相……；关键问题得不到有效解决，中国现代化进程将以失败告终，中国人再也过不上好日子！本书话题敏感，建言犀利见血，编者、编辑几易其稿，反复推敲作者观点和表述方式，使本书终于面世！《中国2013：关键问题》一书中有吴敬琏、许小年、陈志武、周其仁、夏斌、刘煜辉、韦森、俞可平、斯伟江、孙立平、何怀宏、贺卫方、陈有西、于建嵘……众多一流的、最有话语权的学者，都在各自的专业领域做出了判断和预测，覆盖经济整体、国民收入、司法改革、土地流转、税收体制等关系国计民生和每一个人命运的重大命题。其中，有忧思，有警告，有期待——面对当下新的政治周期和经济周期，我们也应该有所期待。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	名人推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	随着改革的深入，改革与反改革的力量交锋日益激烈。从某种意义上讲，此后的改革比改革初期更为艰难。改革初期的阻力主要来自意识形态，而此时则主要来自既得利益；改革初期的措施有帕累托改进的特性，而此时国有垄断企业和政府部门已经享受改革的成果，进一步改革会损害它们的利益。推动国有垄断企业和政治体制改革，意味着政府要对自身进行改革，改革由此进入更为艰难的攻坚战，进度明显放慢。&nbsp;<br />\r\n--国务院发展研究中心研究员 吴敬琏&nbsp;<br />\r\n在当前这种改革开放的状态下，无论采取怎样的宏观政策，我认为中国经济的增长都将无可挽回地继续下降。如果我们不能及时回到小平同志所确立的改革开放状态，而是一味空谈，拖延时间，经济和社会的问题不断积累，有可能超过我们脆弱的社会结构所能承受的范围。&nbsp;<br />\r\n--中欧国际工商学院经济学和金融学教授许小年&nbsp;<br />\r\n如果以农民的纯收入作为基数，2009年的财政税收约为6万亿元，等于13.8亿农民一年的纯收入。整个中国的农民数量还没有那么多，这更说明政府得到的钱是多得多么离谱。&nbsp;<br />\r\n如果征税权力不受限制的话，劳动收入占GDP的比重只会继续下降，这个局面将无法改变。&nbsp;<br />\r\n--耶鲁大学管理学院金融经济学教授、清华大学经济管理学院特聘教授 陈志武&nbsp;<br />\r\n政府本身，到底是服务还是管理，也是一个核心的问题。服务型的政府，是以“管理”为主，还是以“服务”为主？是代表部门利益，还是代表利益集团的利益，还是代表地方的利益，还是代表全社会的利益？现在，改革面对的问题，不仅仅是利益集团的问题，而且存在着国家利益被部门化、政府化、政党化的情况。这些下一步改革要解决的问题，在地方社会管理创新实践中已经开始触及了。&nbsp;<br />\r\n--清华大学公共管理学教授王名\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:24:09', '5', '1', '1');
INSERT INTO `tbl_library` VALUES ('aEB3Yf', 'FzQ32i', '2', '能源经济学', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2011年2月1日</span> \r\n</div>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;font-family:arial, helvetica, sans-serif;font-size:1em;\">魏一鸣</span>\r\n</p>\r\n<p>\r\n	《能源经济学》以能源经济与管理问题为主线，将能源战略与政策的研究纳入经济学研究框架，系统地介绍了能源需求与能源供给、能源市场、能源价格、能源效率、能源要素替代和能源贸易与能源金融等核心问题。在强调基础性和系统性的同时，《能源经济学》注重把握现代能源经济与管理问题的发展趋势，吸收了最新发展与研究成果，并注重深入浅出和与中国实践的结合，使《能源经济学》更适应当前高等院校经济与管理类人才培养的要求，特别是能源经济与管理领域高层次人才培养的要求。\r\n</p>\r\n<p>\r\n	《能源经济学》既可以作为能源经济与管理类专业的本科生教材，也可以作为高等院校师生、科研人员、企业管理人员及政府部门的公务人员的培训教材或自学参考材料。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;科学出版社; 第1版 (2011年2月1日)\r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E7%B2%BE%E5%93%81%E8%AF%BE%E7%A8%8B%E7%AB%8B%E4%BD%93%E5%8C%96%E6%95%99%E6%9D%90%E7%B3%BB%E5%88%97&search-alias=books\">精品课程立体化教材系列</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;206页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787030302885, 7030302885\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787030302885\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.6 x 16.6 x 1 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>281 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;科学出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《能源经济学》：精品课程立体化教材系列是根据教育部建设国家级精品课程的要求.由各学科领域全国知名教授领衔.精心编写而成。各门课程按照立体化教学的需要进行建设.努力构建出一个包括主干教材、多媒体教学课件、教学参考用书、学生辅导用书、习题案例集、教学录像、教学支持网站等丰富教学支持资源的立体化教学体系。选用该课程主干教材的教师可通过以下联系方式与出版社取得联系，以获得相关支持。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	前言<br />\r\n第1章 导论<br />\r\n1.1 能源经济学概述<br />\r\n1.2 能源经济学的研究对象和方法<br />\r\n1.3 能源经济学的形成与发展<br />\r\n1.4 能源经济学科与其他相关学科的联系<br />\r\n1.5 本章小结<br />\r\n第2章 能源需求<br />\r\n2.1 能源需求的基本概念<br />\r\n2.2 能源需求的主要影响因素<br />\r\n2.3 能源需求预测建模<br />\r\n2.4 本章小结<br />\r\n第3章 能源供给<br />\r\n3.1 能源供给的基本概念<br />\r\n3.2 能源供给的主要影响因素<br />\r\n3.3 能源供给预测<br />\r\n3.4 本章小结<br />\r\n第4章 能源市场<br />\r\n4.1 能源市场均衡分析<br />\r\n4.2 能源市场的非均衡分析<br />\r\n4.3 税收与能源配置<br />\r\n4.4 能源市场规制<br />\r\n4.5 本章小结<br />\r\n第5章 能源价格<br />\r\n5.1 能源定价理论<br />\r\n5.2 石油输出国组织与国际石油价格<br />\r\n5.3 能源价格对市场配置的调节<br />\r\n5.4 能源价格与一般价格水平<br />\r\n5.5 能源价格与经济增长<br />\r\n5.6 本章小结<br />\r\n第6章 能源效率<br />\r\n6.1 能源效率的内涵<br />\r\n6.2 能源效率的测度<br />\r\n6.3 能源效率的测度指标与方法<br />\r\n6.4 能源强度的分解<br />\r\n6.5 本章小结<br />\r\n第7章 能源要素替代<br />\r\n7.1 要素替代与能源需求<br />\r\n7.2 能源与其他要素的替代性<br />\r\n7.3 本章小结<br />\r\n第8章 国际能源贸易与能源金融<br />\r\n8.1 国际能源贸易<br />\r\n8.2 能源金融化与能源期货市场<br />\r\n8.3 能源期货市场的有效性<br />\r\n8.4 能源期货市场的价格风险管理功能<br />\r\n8.5 本章小结<br />\r\n参考文献\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:21:55', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('Nz2au2', 'FzQ32i', '2', '中国未来经济改革与发展路径', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2013年2月1日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《新浪•长安讲坛(第8辑):中国未来经济改革与发展路径》由吴敬琏等编。十八大召开以后，中国经济的发展面临新的前景，随着新一届领导班子的组建，关于我国未来经济的改革与发展方向的讨论也越来越多。《新浪•长安讲坛(第8辑):中国未来经济改革与发展路径》为中国经济五十人论坛系列从书，重点讨论新形势下中国经济的改革与发展的路径，集国内经济学大家的智慧，为我国经济发展顶层设计出谋划策。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中国经济出版社; 第1版 (2013年2月1日)\r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E4%B8%AD%E5%9B%BD%E7%BB%8F%E6%B5%8E50%E4%BA%BA%E8%AE%BA%E5%9D%9B%E4%B8%9B%E4%B9%A6&search-alias=books\">中国经济50人论坛丛书</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;418页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787513622073\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787513622073\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.6 x 17 x 3 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>640 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中国经济出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《新浪•长安讲坛(第8辑):中国未来经济改革与发展路径》由中国经济出版社出版。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	厚言&nbsp;<br />\r\n国际货币体系改革与人民币国际化&nbsp;<br />\r\n金融危机对传统经济理论的挑战&nbsp;<br />\r\n中国未来二十年：经济增长与社会发展&nbsp;<br />\r\n全球化与模式转型&nbsp;<br />\r\n国家资本主义不是必然能够避免金融危机&nbsp;<br />\r\n绿色经济与深化体制改革&nbsp;<br />\r\n关于中国金融战略的系统思考&nbsp;<br />\r\n当前经济形势与宏观政策&nbsp;<br />\r\n第三次工业革命需要什么样的教育&nbsp;<br />\r\n全球愤怒：金融危机的政治后遗症&nbsp;<br />\r\n目前保增长的重点应该放在哪里&nbsp;<br />\r\n从全球不平衡到全球债务危机：中国面对的挑战&nbsp;<br />\r\n经济走势和应对策略&nbsp;<br />\r\n怎样应对我们面临的挑战&nbsp;<br />\r\n通货膨胀与国民收入分配&nbsp;<br />\r\n制度企业家与中国的未来&nbsp;<br />\r\n发展小额信贷，促进普惠金融&nbsp;<br />\r\n中国必须通过的减速关&nbsp;<br />\r\n农民工市民化与公共服务制度创新&nbsp;<br />\r\n中国债券市场的最新发展&nbsp;<br />\r\n当前形势和中国企业的国际化&nbsp;<br />\r\n附录1 中国经济50人论坛简介&nbsp;<br />\r\n附录2 中国经济50人论坛成员名录&nbsp;<br />\r\n附录3 中国经济50人论坛企业家理事会成员名录\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:24:41', '3', '1', '1');
INSERT INTO `tbl_library` VALUES ('ZNryEf', 'FzQ32i', '2', '能源金融', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2011年7月1日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《能源金融》主要内容简介：金融市场动荡、石油价格上涨等问题不仅导致西方主要经济体经济明显降温，也拖累了新兴经济体和发展中经济体的发展，随着能源对政治经济战略意义的显现，作为一门新兴学科，能源金融（EneroyFinance）在世界金融秩序中的影响日渐突出，成为热点话题。《能源金融》遵循循序渐进、深入浅出的原则，在全面介绍国际能源市场现状和发展方向的基础上，重点对能源价格的形成机制进行深入阐述，并以此为切入点，系统地介绍了各类能源及其相关衍生金融产品的发展演化、市场结构、产品设计及在能源风险管理中的应用。《能源金融》内容广泛、资料翔实，不仅涵盖石油、天然气、煤炭和电力等传统能源领域，还涉及碳交易市场与碳金融、能源税与碳税、能源效率市场、新能源与可再生能源投融资等国际能源领域最新发展动向，除针对目前中国能源及其相关市场的发展现状，《能源金融》还系统性、前瞻性地提出了未来中国能源价格体系的演变和改革方向以及能源金融市场体系的构建和完善，《能源金融》可作为硕士研究生和博士研究生相关专业课程的教材，对国内能源界、金融界专业人士也有很好的借鉴意义。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;清华大学出版社; 第1版 (2011年7月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Energy%20Finance&search-alias=books\">Energy Finance</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;529页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787302257325, 7302257329\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787302257325\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>25.8 x 18.2 x 2.6 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>839 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;清华大学出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《能源金融》由清华大学出版社出版。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	作者简介\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	林伯强，男，1957年出生，福建漳州人。1982年获厦门大学货币银行学学士学位，1990年获美国加利福尼亚大学经济学博士学位。1993年进入亚洲开发银行，曾任亚洲开发银行(Asian Development Bank)主任能源经济学家。2006年6月至今，任厦门大学中国能源经济研究中心主任、金融系特聘教授及博士生导师。在亚行工作期间，主要负责能源领域的亚行对华项目。任多个重大能源和环保贷款项目和政策性与行业改革技术援助项目经理，管理和指导国内外咨询专家。了解中国国情以及能源和环保的实际情况，有丰富实践经验。近几年来，在《经济研究》、《金融研究》、《管理世界》等国内外一流经济学刊物的学术论文多达数十篇，并撰写了《现代能源经济学》、《2006中国能源发展报告》等著作。厦门大学经济学院金融学博士，厦门大学工商管理博士后流动站博士后，现于厦门大学中国能源经济研究中心从事能源经济等相关领域的教学与研究工作。主要研究方向为能源金融，_已在《金融研究》等多个国内杈威杂志发表学术论文，还参与了厦门大学中国能源经济研究中心主任林伯强教授主持的多项国家级、省部级课题，以及林伯强教授与英国战略基金、美国能源基金等国外著名研究机构合作开展的科研项目。\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:22:06', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('qMjQfa', 'nEjeUz', '2', '创新管理:技术变革、市场变革和组织变革的整合(第4版)', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年6月1日</span>\r\n</div>\r\n<p>\r\n	<p>\r\n		<p>\r\n			《创新管理:技术变革、市场变革和组织变革的整合(第4版)》的宗旨是为读者提供一种创新管理的综合方法，向读者讲授创新管理最新的知识和技能，帮助他们理解企业战略和运作层面的管理创新。具体来说，《创新管理:技术变革、市场变革和组织变革的整合(第4版)》的主题是：整合技术、市场和组织变革的管理，以改善企业的竞争力和组织的效率。\r\n		</p>\r\n		<p>\r\n			<br />\r\n		</p>\r\n		<p>\r\n			<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				<li>\r\n					<b>出版社:</b>&nbsp;中国人民大学出版社; 第1版 (2012年6月1日)\r\n				</li>\r\n				<li>\r\n					<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=Managing%20Innovation&amp;search-alias=books\">Managing Innovation</a>\r\n				</li>\r\n				<li>\r\n					<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=%E7%AE%A1%E7%90%86%E7%A7%91%E5%AD%A6%E4%B8%8E%E5%B7%A5%E7%A8%8B%E7%BB%8F%E5%85%B8%E8%AF%91%E4%B8%9B&amp;search-alias=books\">管理科学与工程经典译丛</a>\r\n				</li>\r\n				<li>\r\n					<b>平装:</b>&nbsp;480页\r\n				</li>\r\n				<li>\r\n					<b>语种：</b>&nbsp;简体中文, 英语\r\n				</li>\r\n				<li>\r\n					<b>开本:</b>&nbsp;16\r\n				</li>\r\n				<li>\r\n					<b>ISBN:</b>&nbsp;7300156576, 9787300156576\r\n				</li>\r\n				<li>\r\n					<b>条形码:</b>&nbsp;9787300156576\r\n				</li>\r\n				<li>\r\n					<b>商品尺寸:&nbsp;</b>25.8 x 18.4 x 2.2 cm\r\n				</li>\r\n				<li>\r\n					<b>商品重量:&nbsp;</b>599 g\r\n				</li>\r\n				<li>\r\n					<b>品牌:</b>&nbsp;中国人民大学出版社\r\n				</li>\r\n			</ul>\r\n			<p>\r\n				<br />\r\n			</p>\r\n			<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				《创新管理:技术变革、市场变革和组织变革的整合(第4版)》的宗旨是为读者提供一种创新管理的综合方法，向读者讲授创新管理最新的知识和技能，帮助他们理解企业战略和运作层面的管理创新。具体来说，《创新管理:技术变革、市场变革和组织变革的整合(第4版)》的主题是：整合技术、市场和组织变革的管理，以改善企业的竞争力和组织的效率。\r\n				<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n				</div>\r\n			</div>\r\n			<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n				名人推荐\r\n			</h3>\r\n			<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n				我确信这本书会成为技术和创新管理领域的经典著作。它对管理创新的理论前沿的介绍不仅综合性很强，而且论证严密完备。学生会受益于其清晰易懂的阐述和出色的教学支持，而学者们会从中得到有益于未来研究的启发性思想。&nbsp;<br />\r\n——Peter Augsdorfer 德国英戈尔斯塔特大学和法国格勒诺布尔管理学院公司战略与技术管理教授&nbsp;<br />\r\n这是对创新中最重要知识的综合，是这个领域的世界顶尖学者的著述。&nbsp;<br />\r\n——Clayton M.Christensen 哈佛商学院工商管理教授&nbsp;<br />\r\n创新的能力是竞争优势的重要来源，但对创新的管理充满风险。作者提供了清晰、系统的整合框架来引导学生和管理者去探索和了解这个复杂的领域。这本书紧跟知识管理、网络和新技术等当代重要的主题，并且结合了研究和实践两方面的例证材料。它可能是目前最全面的创新管理指南。&nbsp;<br />\r\n——Rob Goffce 伦敦商学院组织行为学教授&nbsp;<br />\r\n创新一直是一个挑战，在当今不断变化的时代更是如此。这本书有助于解释这种挑战的实质，并将相关问题置于当代的新环境下。今天的技术变化越来越快，将创新的多个方面整合起来，而不是仅仅把它当作一个技术问题，就是本书带给你的最真实的收获。&nbsp;<br />\r\n——C.John Brady 麦肯锡公司资深合伙人\r\n			</div>\r\n			<p>\r\n				<br />\r\n			</p>\r\n		</p>\r\n	</p>\r\n</p>', null, '2013-06-30 00:31:05', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('QZvii2', 'nEjeUz', '2', '创新管理:赢得持续竞争优势', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《创新管理:赢得持续竞争优势》基于战略—流程—资源—制度（文化）的分析框架，系统介绍了创新的内涵、特征及对提升国家和企业竞争力的重要性，创新管理的层次、模式、全球最佳创新公司的运作案例等。全书语言简洁、观点明确，通过对国际最佳创新公司的案例分析，以及对中国优秀创新企业（如海尔、华为、中集、方正等）的深刻剖析，帮助读者完成从知识到行动的有效转移。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;北京大学出版社; 第1版 (2009年5月1日)\r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=21%E4%B8%96%E7%BA%AA%E7%AE%A1%E7%90%86%E7%A7%91%E5%AD%A6%E4%B8%8E%E5%B7%A5%E7%A8%8B%E8%A7%84%E5%88%92%E6%95%99%E6%9D%90&search-alias=books\">21世纪管理科学与工程规划教材</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;403页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787301121061\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787301121061\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>22.8 x 16.8 x 1.8 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>499 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;北京大学出版社\r\n	</li>\r\n</ul>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《创新管理:赢得持续竞争优势》为21世纪管理科学与工程规划教材之一。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	作者简介\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	陈劲，浙江大学教授、博士生导师，国家杰出青年基金获得者，浙江大学求是特聘教授。现任浙江大学本科生院副院长、浙江大学竺可桢学院常务副院长、浙江大学科教发展战略研究中心（教育部战略研究基地）主任、浙江大学创新管理与持续竞争力研究中心（“985工程”国家哲学社会科学创新基地）常务副主任、中国工程院教育委员会委员、中国科学学与科技政策研究会副理事长、全国青年联合会委员等职务。近年来，在国内外学术期刊上发表论文100余篇，出版专著、译著10余部，主持国家级、省部级重大、重点科研项目多项，获得中国高校科技进步一等奖、浙江省哲学社科成果一等奖等省部级以上奖励多项。<br />\r\n郑刚，浙江大学管理学院副教授，管理学博士。现任浙江大学创新管理与持续竞争力研究中心办公室主任。近年来，在国内外学术期刊、国际学术会议上发表论文30余篇，出版专著、译著2部。主持国家自然科学基金、国家社科重大招标项目子课题等国家级项目2项，省部级项目多项。曾多次赴海尔集团、中集集团、万向集团、中控集团等国内数十家知名企业调研并提供管理咨询服务。主要研究方向：技术与创新管理、新产品开发与项目管理等。\r\n</div>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:56:07', '3', '1', '1');
INSERT INTO `tbl_library` VALUES ('E7v6fu', 'nEjeUz', '2', '创新与企业家精神(珍藏版)', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《创新与企业家精神(珍藏版)》内容简介：创新是企业家的标志，企业家社会是，德鲁克留给人类社会的一剂良药。激烈竞争、瞬息万变的市场和技术让人们深信不疑创新的重要性，但关键问题是，该如何进行创新。如果你懂得在哪里以及如何寻找创新机遇。你就能系统化地管理创新；如果你懂得运用创新的原则。你就能使创新发展为可行的事业。这就是德鲁克在《创新与企业家精神》中为我们揭示的重点。如何寻找创新机遇？将创意发展为可行的事业有何原则和禁忌？什么样的政策和措施才能使机构成功地孕育出企业家精神？具有企业家精神的机构如何组织和配备人员？如何成功地将一项创新引入市场，赢得市场？德鲁克在经典之作《创新与企业家精神》中，首次将实践创新与企业家精神视为所有企业和机构有组织、有目的、系统化的工作，并与我们共同探讨这些问题的答案。</span><br />\r\n<br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">海报：</span><br />\r\n<br />\r\n<p>\r\n	<img src=\"http://g-ec4.images-amazon.com/images/G/28/BOOK-Catalog/annaww/B002Q0X45K_01_AMZN.jpg\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;机械工业出版社; 第1版 (2009年9月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Innovation%20and%20Entrepreneurship&search-alias=books\">Innovation and Entrepreneurship</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E5%BE%B7%E9%B2%81%E5%85%8B%E7%AE%A1%E7%90%86%E7%BB%8F%E5%85%B8&search-alias=books\">德鲁克管理经典</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;232页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7111280652, 9787111280651\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787111280651\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24 x 16.6 x 2.4 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>458 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;机械工业出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《创新与企业家精神(珍藏版)》：<br />\r\n探讨企业家精神与创新实践的最佳经典著作<br />\r\n启蒙中国现代管理第一人<br />\r\n当今中国一代活跃着的组织最近的眉睫的问题，正是德鲁克向我们描述的如何管理的问题。<br />\r\n只有中国人才能建设中国，只有中国人才能发展中国。<br />\r\n——彼得·德鲁克<br />\r\n超越时空的德鲁克，管理者永恒的导师<br />\r\n他称自己是“社会生态学家”，他对社会学和经济学的影响深远，他的著作架起了从工业时代到知识时代的桥梁。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	我们需要的是一个企业家社会。在这个社会中，创新和企业家精神是一种平常、稳定和持续的活动。正如管理已经成为当代所有机构的特定器官，成为我们这个组织社会的整合器官一样，创新和企业家精神也应该成为我们社会、经济和组织维持生命活力的主要活动。这要求所有机构的管理者把创新与企业家精神作为企业和自己工作中的一种正常、不间断的日常行为和实践。本书的目的，就是提供完成这项工作所需的概念和工具。&nbsp;<br />\r\n　——彼得•德鲁克&nbsp;<br />\r\n经典经得起时间考验，值得一读再读，常读常新。它帮你理清思路，从任何新事变中发掘本质，找到历史渊源。&nbsp;<br />\r\n　——邵明路 彼得•德鲁克管理学院创办人 德鲁克百年诞辰全球纪念活动共同主席&nbsp;<br />\r\n创新是德鲁克管理思想的重要方面。如果说熊彼特系统地论述了创新的经济意义的话，那么德鲁克则系统地论述了创新的管理意义。我以为，如果德鲁克理论的上篇是管理的理论与实践，那么他的下篇就是创新的理论与实践。&nbsp;<br />\r\n　——诸大建 同济大学经济与管理学院教授 博士生导师 同济大学可持续发展与管理研究所所长&nbsp;<br />\r\n一本优秀的著作就是一座挖不尽的宝藏，可以陪伴人的终生。这样的著作一旦诞生，就已经独立于作者、独立于时代，属于每个读者自己。这样的书是永恒的、跨越时空的。 　　　　　　　　　　　　　　　&nbsp;<br />\r\n　——赵曙明 南京大学商学院院长 教授 博士生导师&nbsp;<br />\r\n全世界的管理者都应该感谢这个人，因为他贡献了毕生的精力，来理清我们社会中人的角色和组织机构的角色，我认为彼得•德鲁克比任何其他人都更有效地做到了这一点。&nbsp;<br />\r\n　——通用电气前首席执行官 杰克•韦尔奇&nbsp;<br />\r\n在所有的管理学书籍中，德鲁克的著作对我影响最深。&nbsp;<br />\r\n　——微软总裁 比尔•盖茨&nbsp;<br />\r\n德鲁克是我心中的英雄。他的著作和思想非常清晰，在那些狂热追求时髦的思想的人群中独树一帜。&nbsp;<br />\r\n　——英特尔主席 安迪•格鲁夫\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:56:20', '2', '1', '1');
INSERT INTO `tbl_library` VALUES ('Ijeiau', 'nEjeUz', '2', '21世纪的管理挑战', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<h1 class=\"parseasinTitle\" style=\"font-family:\'Microsoft Yahei\', PMingLiU, Verdana, Arial, Helvetica, sans-serif;font-size:1.5em;background-color:#FFFFFF;\">\r\n	<span id=\"btAsinTitle\">21世纪的管理挑战(珍藏版)&nbsp;<span style=\"font-size:16px;\">[平装]</span></span> \r\n</h1>\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\">~&nbsp;</span><a href=\"http://www.amazon.cn/s?ie=UTF8&field-author=%E5%BD%BC%E5%BE%97%C2%B7%E5%BE%B7%E9%B2%81%E5%85%8B(Drucker.P.F.)&search-alias=books\">彼得·德鲁克(Drucker.P.F.)</a><span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\">&nbsp;(作者),&nbsp;</span><a href=\"http://www.amazon.cn/s?ie=UTF8&field-author=%E6%9C%B1%E9%9B%81%E6%96%8C&search-alias=books\">朱雁斌</a><span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\">&nbsp;(译者)&nbsp;</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">我们生活在一个意义深远的转型期，变革空前而彻底。现在的管理学思想仍然沿用20世纪的那些基本假设，很少有人去注意它们是不是“事实”。但实际上，它们都已经过时了。如果用不正确的假设去制定战略，将不可避免地把组织引向不正确的轨道。《21世纪的管理挑战(珍藏版)》只涉及明天的“热点”问题，即关键性的、决定性的、生死攸关的和明天肯定会成为主要挑战的问题，将从以下6个方面深刻分析21世纪管理者面临的挑战：管理的新范式，战略一新的必然趋势，变革的引导者，信息挑战，知识工作者的生产率，自我管理。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;机械工业出版社; 第1版 (2009年9月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Management%20Challenges%20for%20the%2021st%20Century&search-alias=books\">Management Challenges for the 21st Century</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E5%BE%B7%E9%B2%81%E5%85%8B%E4%B8%96%E7%BA%AA%E7%B2%BE%E9%80%89&search-alias=books\">德鲁克世纪精选</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;176页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787111280606, 7111280601\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787111280606\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.8 x 16.6 x 1.8 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>340 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;机械工业出版社\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《21世纪的管理挑战(珍藏版)》：<br />\r\n社会转型期，管理决策者该如何应对？德鲁克经典著作中的“里程碑”解决如何应对于我们每天擦肩而过的趋势性变化。<br />\r\n启蒙中国现代管理第一人<br />\r\n当今中国一代活跃着的组织最近的眉睫的问题，正是德鲁克向我们描述的如何管理的问题。<br />\r\n只有中国人才能建设中国，只有中国人才能发展中国。<br />\r\n——彼得·德鲁克<br />\r\n超越时空的德鲁克，管理者永恒的导师<br />\r\n他称自己是“社会生态学家”，他对社会学和经济学的影响深远，他的著作架起了从工业时代到知识时代的桥梁。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	全世界的管理者都应该感谢这个人，因为他贡献了毕生的精力，来理清我们社会中人的角色和组织机构的角色，我认为彼得·德鲁克比任何其他人都更有效地做到了这一点。<br />\r\n　——通用电气前首席执行官 杰克·韦尔奇<br />\r\n在所有的管理学书籍中，德鲁克的著作对我影响最深。<br />\r\n　——微软总裁 比尔·盖茨<br />\r\n德鲁克是我心中的英雄。他的著作和思想非常清晰，在那些狂热追求时髦的思想的人群中独树一帜。<br />\r\n　——英特尔主席 安迪·格鲁夫<br />\r\n本书所讨论的问题，如新的社会、人口和经济现实，是政府无法顺利解决的问题。这些问题会对政治产生深远的影响，但它们都不是政治问题。它们不是自由市场经济可以从容应对的问题，也不是经济理论或经济政策可以处理的问题，只有管理层和知识工作者、专业人员或管理人员个人才能处理和解决这些问题。所有发达国家和所有新兴国家的国内政治活动家们一定会就这些问题展开广泛的争论。但是，这些问题的解决还得靠独立的组织、组织的管理层以及每一个知识工作者，特别是每一个管理人员。 　　　　　　　　　　　　　　　　　　　　　　　<br />\r\n　——彼得·德鲁克 　　<br />\r\n经典经得起时间考验，值得一读再读，常读常新。它帮你理清思路，从任何新事变中发掘本质，找到历史渊源。 　　　　　　　　　　　　<br />\r\n　——邵明路 彼得·德鲁克管理学院创办人 德鲁克百年诞辰全球纪念活动共同主席 　　<br />\r\n《21世纪的管理挑战》是我们打开未来的一把钥匙。 　　　　　　　　　　　　　　　　　<br />\r\n　——那国毅 德鲁克管理学教授 　　<br />\r\n德鲁克先生对于管理领域的贡献并不需要我们去做注释，但是对于中国的管理者来说，他的价值却难以估量。因为德鲁克先生，管理可以变得卓有成效；因为德鲁克先生，管理者释放了自己的价值。 　　　　　　　　　　　　　　　　　<br />\r\n　——陈春花 华南理工大学教授 博士生导师\r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>', null, '2013-06-30 00:54:46', '7', '1', '1');
INSERT INTO `tbl_library` VALUES ('32QnMf', 'nEjeUz', '2', '管理的实践', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《管理的实践(珍藏版)》是第一部从实际执行层面立论的管理学书籍。第一部兼顾管理现有企业和创新未来企业的著作。最棒的一本讲述“管理如何能够复制”的著作。《管理的实践(珍藏版)》的根本目的在于通过对管理原则、责任和实践的研究，探索如何建立有效的管理机制和制度。而衡量一种管理制度是否有效的标准就在于该制度能否将管理者个人特征的影响降到最低。贯穿全书的三条主线：管理企业；管理管理者；管理员工和工作；德鲁克的三个经典提问：我们的事业是什么，我们的事业将是什么，我们的事业究竟应该是什么。管理必须同时需要考虑三个方面的问题：成果和绩效，这是企业存在的目的，在企业内部共同工作的人所形成的组织，外在社会。也就是企业的社会责任。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;机械工业出版社; 第1版 (2009年9月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=The%20Practice%20of%20Management&search-alias=books\">The Practice of Management</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E5%BE%B7%E9%B2%81%E5%85%8B%E7%AE%A1%E7%90%86%E7%BB%8F%E5%85%B8&search-alias=books\">德鲁克管理经典</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;314页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7111280741, 9787111280743\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787111280743\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24 x 16.8 x 2.4 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>558 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;机械工业出版社\r\n	</li>\r\n</ul>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《管理的实践(珍藏版)》：<br />\r\n如果只读一本管理书籍，就读《管理的实践》！现代管理学大厦的根基 管理学诞生的标志<br />\r\n启蒙中国现代管理第一人<br />\r\n当今中国一代活跃着的组织最近的眉睫的问题，正是德鲁克向我们描述的如何管理的问题。<br />\r\n只有中国人才能建设中国，只有中国人才能发展中国。<br />\r\n——彼得·德鲁克<br />\r\n超越时空的德鲁克，管理者永恒的导师<br />\r\n他称自己是“社会生态学家”，他对社会学和经济学的影响深远，他的著作架起了从工业时代到知识时代的桥梁。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	全世界的管理者都应该感谢这个人，因为他贡献了毕生的精力，来理清我们社会中人的角色和组织机构的角色，我认为彼得·德鲁克比任何其他人都更有效地做到了这一点。<br />\r\n　——通用电气前首席执行官 杰克·韦尔奇<br />\r\n在所有的管理学书籍中，德鲁克的著作对我影响最深。<br />\r\n　——微软总裁 比尔·盖茨<br />\r\n德鲁克是我心中的英雄。他的著作和思想非常清晰，在那些狂热追求时髦的思想的人群中独树一帜。<br />\r\n　——英特尔主席 安迪·格鲁夫<br />\r\n《管理的实践》一书的出版使人们有可能学会如何去管理。在这之前，管理似乎只是少数天才能做的事，凡人是无法做到的。我坐下来花了些工夫，把管理变成了一门学科。<br />\r\n本书出版后，不但在美国一炮走红，在全球各地也都非常成功，包括欧洲、拉丁美洲，尤其在日本更是备受重视。日本人认为本书的观念奠定了他们经济成功和工业发展的基石。<br />\r\n　　——彼得·德鲁克<br />\r\n经典经得起时间考验，值得一读再读，常读常新。它帮你理清思路，从任何新事变中发掘本质，找到历史渊源。<br />\r\n　　——邵明路<br />\r\n彼得·德鲁克管理学院创办人德鲁克百年诞辰全球纪念活动共同主席<br />\r\n德鲁克先生对于管理领域的贡献并不需要我们去做注释，但是对于中国的管理者来说，他的价值却难以估量。因为德鲁克先生，管理可以变得卓有成效；因为德鲁克先生。管理者释放了自己的价值。<br />\r\n　　——陈春花<br />\r\n华南理工大学教授博士生导师<br />\r\n一本优秀的著作就是一座挖不尽的宝藏，可以陪伴人的终生。这样的著作一旦诞生。就已经独立于作者、独立于时代，属于每个读者自己。这样的书是永恒的、跨越时空的。<br />\r\n　　——赵曙明<br />\r\n南京大学商学院院长教授博士生导师\r\n</div>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:54:35', '8', '1', '1');
INSERT INTO `tbl_library` VALUES ('ZZFnYn', 'nEjeUz', '2', '影响力(经典版)', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《影响力(经典版)》就一直是最为畅销的图书。由于它的影响，劝说得以成为一门科学。无论你是普通人还是为某一产品或事业观点游说的人，这都是一本最基本的书，是你理解人们心理的基石。在《影响力》这本书中，心理学家罗伯特•B•西奥迪尼博士为我们解释了为什么有些人极具说服力，而我们总是容易上当受骗。隐藏在冲动地顺从他人行为背后的6大心理秘笈，正是这一切的根源。那些劝说高手们，总是熟练地运用它们，让我们就范。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">\r\n	<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n		<li>\r\n			<b>出版社:</b>&nbsp;万卷出版公司; 第1版(经典版) (2010年9月20日)\r\n		</li>\r\n		<li>\r\n			<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&amp;field-keywords=Influence%3A%20The%20Psychology%20of%20Persuasion%20(Collins%20Business%20Essentials)&amp;search-alias=books\">Influence: The Psychology of Persuasion (Collins Business Essentials)</a>\r\n		</li>\r\n		<li>\r\n			<b>平装:</b>&nbsp;286页\r\n		</li>\r\n		<li>\r\n			<b>语种：</b>&nbsp;简体中文\r\n		</li>\r\n		<li>\r\n			<b>开本:</b>&nbsp;16\r\n		</li>\r\n		<li>\r\n			<b>ISBN:</b>&nbsp;9787547012123, 7547012124\r\n		</li>\r\n		<li>\r\n			<b>条形码:</b>&nbsp;9787547012123\r\n		</li>\r\n		<li>\r\n			<b>商品尺寸:&nbsp;</b>22.6 x 16.8 x 2.6 cm\r\n		</li>\r\n		<li>\r\n			<b>商品重量:&nbsp;</b>458 g\r\n		</li>\r\n		<li>\r\n			<b>品牌:</b>&nbsp;湛庐文化\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n			《影响力(经典版)》具有以下特色：1.史上最强大、最震摄人心、最诡谲的心理学畅销书。自1986年出版以来，作为获得美国心理协会、美国心理学基金会年度大奖提名的西奥迪尼经典著作，《影响力》已经成为史上最强大、最震摄人心、最诡谲的心理学畅销书，24年来，它被翻译成26种文字广为传播，全球销量超过300万册。时至今日，《影响力》一书仍位列亚马逊总排行榜Top400之中，并雄踞消费者行为学和应用心理学类畅销书榜首。也正是在这个畅销版本的基础上，西奥迪尼才衍生出后来为斯坦福大学等多所名校所采用的教材版本。2.原汁原味，经典呈现。没有专家解读，没有每章导读，这是一本最原汁原味的《影响力》，醇厚甘甜，久而弥笃。阅读《影响力》仿佛是与西奥迪尼面对面，请细细聆听，在定格的经典中体味无孔不入的影响力。3.全新译本，不容错过。《影响力》由斩获国家级图书大奖“文津奖”的《牛奶可乐经济学》的译者担纲翻译，语言生动流畅，意趣盎然，西奥迪尼式的诡谲与雄辩惟妙惟肖、跃然纸上。\r\n			<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n			</div>\r\n		</div>\r\n		<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n			名人推荐\r\n		</h3>\r\n		<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n			《影响力》书评（一）：<br />\r\n无论比尔•盖茨随便说了些什么，整个世界都会洗耳恭听；无论巴菲特有什么些许暗示，华尔街的股市都会剧烈跌宕。大人物的影响力无处不在，甚至会形成一种类似迷信的权威，我们在别人的判断下改变着自己的判断，并且心悦诚服。<br />\r\n除了大人物，最容易影响我们的，是我们身边的那些推销员，或许你会被这个结论吓一跳，但是先让我们来看看罗伯特•西奥迪尼博士，这位亚利桑那州立大学心理学系的著名教授在自己这本既是营销学、又是心理学著作的《影响力》的开篇里所举的例子吧。<br />\r\n“……珠宝店里那些顾客的奇怪举动就是一个例子。在营业融错误地将绿松石珠宝的价格提高了一倍之后，他们将这些珠宝一抢而空。……这些顾客大多家境富裕，但对绿松石没有什么了解。在他们购买珠宝时，他们依靠的是‘昂贵=优质’这一基本原理。因此，这些一心想买到好珠宝的度假者，在绿松石珠宝的价格被提高之后，认为这些珠宝更有价值、更值得拥有。”<br />\r\n是那些富有的顾客过于愚蠢，还是在这一行为的背后另有缘由？罗伯特•西奥迪尼博士在研究这一现象之后做出结论：“这些人都是在‘一分钱一分货’的告诫中长大的，而且在生活中也多次体验到这一定律的正确性。过不了多久，他们就将这一定律转化成‘昂贵=优质’。这个公式在过去一直都很管用，因为通常来说，商品的价格都会随着价值的增加而提高，价格越高，质量就越好。”<br />\r\n睿智的罗伯特•西奥迪尼博士是在北卡罗莱那大学取得博士学位的，之后又在哥伦比亚大学进行过博士后研究，正是因为受到实验社会心理学方面的严格训练，《影响力》一书出版后，就一直是营销领域的畅销书，并被斯坦福大学等校用来作为教材。<br />\r\n人是如何影响他人，并且被他人影响的？我们每个人──其实都是在和协调、和购买打交道的人，影响力因此无处不在。据说，为了这本书，可爱的罗伯特•西奥迪尼博士耗费了几十年心血，上至高级政客、下至街头骗子，他都统统想办法与之打成一片，从而才有结论：尽管使人服从的策略五花八门，但都可以归结为8条基本的心理学原理：<br />\r\n1影响的武器；2互惠；3承诺和一致；4社会认同；5喜好；6权威；7短缺；8产即生效的影响力。<br />\r\n《影响力》曾获得美国心理学会、美国心理学基金会年度大奖提名。美国读者对其评论是Fascinaging-timeless-direct and to the point（迷人-永恒-切中要害），细想，你将不再盲从政治家的影响力而把选票投给自己并不真正支持的人，不再盲目听信经济学家的预言而把钱放在自己并不熟悉的股市和楼市上，最直接的，是你会不盲信直接敲门的推销员的花言巧语而买下一堆自己根本不需要的商品，那《影响力》的影响实在是迷人、永恒、切中要害的！<br />\r\n《影响力》书评（二）：<br />\r\n营销手法背后的心理学原理<br />\r\n作为一种曾经有效的营销手段，\"免费试用\"已经有着很长的历史了。一般的做法是把少量的有关商品提供潜在的顾客，并说明这样做的目的是让他们试一下看自己到底喜不喜欢这个商品。它的妙处在于，当推销商品的人看似真诚地要消费者了解他们的商品时，其实凭借真正的柔术方法，把礼物本身所固有的让人产生负债感的自然力量释放出来。最后结果是，我们会象征性（意思一下）地买一点，即使我们并不是特别喜欢&nbsp;<br />\r\n这种商品。这是商家利用了“互惠原理”，几乎每种营销手法的背后都有一则能指导人类行为的心理学原理，另如\"承诺和一致\"、\"社会认同\"、\"喜好\"、\"权威\"以及\"短缺\"等原理。&nbsp;<br />\r\n经济学假设人是理性的，那么营销学呢？&nbsp;<br />\r\n营销学也认为人是理性的，但是有限理性，而且感性、盲目时常取代理性。如果不是这样，商家们就不会几十年翻来覆去这么几招陈旧到庸俗的营销手段了。例如， “免费试用”。作为一种曾经有效的营销手段，“免费试用”已经有着很长的历史了。一般的做法是把少量的有关商品提供潜在的顾客，并说明这样做的目的是让他们试一下看自己到底喜不喜欢这个商品。它的妙处在于，当推销商品的人看似真诚地要消费者了解他们的商品时，其实凭借真正的柔术方法，把礼物本身所固有的让人产生负债感的自然力量释放出来。就像我们在超市购物，经常可以品尝到一小块糖果或一小片肉，同时我们也会发现，如果接受了那个满脸笑容、服务态度极好的工作人员递过来的免费品尝的食品后，我们就很难做到把牙签一扔，然后转身离去。最后结果是，我们会象征性（意思一下）地买一点，即使我们并不是特别喜欢这种商品。还有一种“免费试用”的策略。其通常做法是，一个营销员上门推销其公司产品，一开始他会极力证明他不是来卖东西的，而是来“占用一些你宝贵的时间，想听听你对该产品的意见和建议的”。然后他会提出，这产品最近在促销，你就是芸芸众生中比较幸运的一个，你有机会免费获得这份产品。当然，推销人员立刻会表示要收一点手续费（或其他一些杂费），但不管怎样，他会让你相信，今天你是捡到便宜了，竟然能以“超低价”得到这份礼物（事实上，这有语病，因为礼物是不能买的）。&nbsp;<br />\r\n对于这样拙劣的营销手段，为什么还有很多人上当、受骗？罗伯特•西奥迪尼，这位全球知名的影响力研究专家，他从人类心理学角度出发，用“互惠原理”进行了解释。他认为，人们总习惯于尽量以相同的方式报答他人为自己所做的一切（这也是一种文明的表现）。简单地说，就是对他人的某种行为，我们要以一种类似的行为去回报。如果人家给了我们某种好处，我们就应该以另外一种好处来报答他人的恩惠，而不能无动于衷，更不能以德报怨。相关的，中国有句俗话，吃了人家的嘴软，拿了人家的手短。于是，这一原理被商家掌握后，就有了“免费试用”等营销手法。尽管，很多时候我们也知道“天下没有免费的午餐”，但是既然有午餐说是免费的，试试总是无妨吧。抱着这样贪小便宜的心理，所谓理性的我们还是一个劲地往圈套里钻。&nbsp;<br />\r\n又比如，我们去商家购物，常会受到销售人员的肯定和称赞。其大致表达是“选这个，说明你很有品位”、“你很有眼光，这个款式是时下最流行的”、“一看你就知道这个款式适合你”……他们的意图很明显，促使顾客作出某个决定，或选择某种立场，按照心理学上“承诺和一致”的原理，人们一旦作出承诺，就会面对来自个人和外部的压力迫使其言行务必与承诺保持一致。罗伯特•西奥迪尼在《影响力》中提到一个例子。像宝洁和通用食品这样的大公司经常会发起征文比赛，就是要求参赛者写一份简短的个人声明，以“我为什么喜欢……”开头，接着便极力吹捧该公司的某种商品。这些公司会对参赛作品进行评选，获胜者可以得到丰厚的奖品。刚开始的时候，西奥迪尼实在不明白为什么会有公司愿意支付一笔不小的费用在这种无聊的比赛上，当他用“承诺和一致”的原理进行分析，商家的司马昭之心昭然若揭。这种比赛就是让尽可能多的人把他们对某种商品的喜爱之情记录下来。参赛者为了赢取不易到手又极具诱惑力的奖品，必须对这种商品大肆吹捧一番。所以，他们会挖空心思地找出该商品某种值得称赞的特性，并在他们的文章中尽情赞美。这样做的结果就是，成千上万的人都用了书面的形势为一种商品做了见证，让写作者本人最后都不知不觉地相信了自己所写的东西。&nbsp;<br />\r\n除了“互惠原理”、“承诺和一致”外，西奥迪尼还提到了“社会认同”、“喜好”、“权威”以及“短缺”等原理。根据西奥迪尼的观察，老道的营销手法虽然看起来五花八门，但归根到底可以归纳为6种基本类型，而每一种类型都受到一则能指导人类行为的基础心理学原理的控制。例如，社会认同原理认为，人们进行是非判断的标准之一就是看别人是怎么想的，尤其是当我们要决定什么是正确的行为时；喜好原理是说，人们总是愿意答应自己认识和喜爱的人（或有共同爱好、习性的人）提出的要求；权威所具有的强大力量会影响我们的行为，即使是具有独立思考能力的成年人也会为了服从权威的命令而作出一些完全丧失理智的事情来；“机会越少，价值越高”的短缺原理会对人们的行为造成全面的影响，害怕失去某种东西的想法往往比希望得到同等价值的想法对人们的激励作用更大。这些原理看起来都很简单，而且很多都是不言而喻的道理，但商家正是利用了这些，再配以小小的花招来进行营销。&nbsp;<br />\r\n让我们再举一个最常见的例子。根据社会认同原理，我们常常决定去做那些与我们相似的人所做的事情。这是很有道理的。因为，在大多数情况下，一个得到大家认同的行为都是可靠的、正确的。这是理性人的正常思维，但如果有厂家恰恰利用这一点，在推销产品（如牙膏、药物）的时候公布一些数据，还请了一些穿白大褂的人来推荐。（根据权威原理，专家的意见很容易俘获消费者的心，但穿白大褂的并不一定是医生，这是商家们通常采用的花招）想必，最后又会出现很多“理性的傻瓜”。无商不奸，无奸不商，西奥迪尼除了用心理学帮我们一一拆解营销诡计之外，还给我们预设了一个悖论——我们试图以理性抵抗欺骗，却往往被自以为的理性所蒙骗。<br />\r\n		</div>\r\n		<p>\r\n			<br />\r\n		</p>\r\n	</p>\r\n</span>\r\n</p>', null, '2013-06-30 00:40:28', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('Aj26z2', 'nEjeUz', '2', '自控力', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">发售日期:&nbsp;</span><span style=\"font-weight:bold;\">2012年8月21日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《自控力》内容简介：自控力唯一权威版本，450000读者热捧，告别拖延症、月光族、熬夜族、减肥控、淘宝控、电脑控、手机控、微博控、购物狂、工作狂、注意力涣散、抽烟、酗酒等。 作为一名健康心理学家，凯利•麦格尼格尔博士的工作就是帮助人们管理压力，并在生活中做出积极的改变。多年来，通过观察学生们是如何控制选择的，她意识到，人们关于自控的很多看法实际上妨碍了我们取得成功。例如，把自控力当作一种美德，可能会让初衷良好的目标脱离正轨。所以，麦格尼格尔要求她的学生了解影响自控的生理学基础、心理陷阱和各种社会因素。麦格尼格尔吸收了心理学、神经学和经济学等学科的最新洞见，为斯坦福大学继续教育项目开设了一门叫做“意志力科学”的课程，参与过这门课程的人称其能够“改变一生”。这门课程就是《自控力》一书的基础。本书为读者提供了清晰的框架，讲述了什么是自控力，自控力如何发生作用，以及为何自控力如此重要。<br />\r\n海报：\r\n</p>\r\n<p>\r\n	<img src=\"http://g-ec4.images-amazon.com/images/G/28/BOOK-Catalog/annaww1/B008AGHPM2_01_AMZN.jpg\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;印刷工业出版社; 第1版 (2012年7月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=The%20Willpower%20Instinct%3AHow%20Self-control%20Works%2CWhy%20it%20Matters%2Cand%20What%20You%20Can%20do%20to%20Get%20More%20of%20It&search-alias=books\">The Willpower Instinct:How Self-control Works,Why it Matters,and What You Can do to Get More of It</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;272页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;7514205039, 9787514205039\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787514205039\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.4 x 16.6 x 1.8 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>440 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;北京磨铁\r\n	</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《自控力》为斯坦福大学最受欢迎的心理学课程。只需10周，成功掌握自己的时间和生活。提高自控力的最有效途径，在于弄清自己如何失控、为何失控。<br />\r\n如果你总拖到最后一分钟才开始工作；总是月光，信用卡透支；想放松一下，却熬夜上网；一直想减肥，总是挫败；那么《自控力》就是专门为你而写的。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	名人推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	对于想了解如何更有效完成目标的人来说，这本书意义重大。麦格尼格尔清晰分解了繁多的相关科学研究及其应用。她还提出，认识到意志力的局限性对培养自控力至关重要。<br />\r\n——杰弗里•M•施瓦兹医生（Jeffrey M. Schwartz, M.D.）<br />\r\n与人合著《你不是你的大脑》（YOU ARE NOT YOUR BRAIN）<br />\r\n著有畅销书《脑锁》（BRAIN LOCK）<br />\r\n真是一本解放思想的书！凯利•麦格尼格尔解释了意志力的科学本质，粉碎了那些我们信以为真的神话。全书建立在鼓舞人心的事实上，而非压迫性的荒谬言论上。强大的意志力终于是每个人触手可及的了。<br />\r\n——杰夫•科尔文（Geoff Colvin）<br />\r\n著有《被高估的天赋：区分世界级大师和普通人的真实因素》（TALENT IS OVERRATED: WHAT REALLY SEPARATES WORLD-CLASS PERFORMERS FROM EVERYBODY ELSE）\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:43:25', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('aMnQj2', 'nEjeUz', '2', '成果管理(珍藏版)', null, '<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">每一个企业都需要思考和应对《成果管理(珍藏版)》提出和回答的一系列问题：企业的现实是什么？企业的成果区在哪里？应该采取什么行动？企业现状是什么，未来的状况应该是什么？《成果管理(珍藏版)》首次把战略一词应用到商业和管理中，并开创了对商业企业经济绩效的研究。迄今为止，大部分战略管理书籍阐述的问题几乎都源于《成果管理(珍藏版)》。书中分析了“企业的现实”——外部环境的基本规律和常见特点，探讨企业在这些“现实”面前如何摆正自己的位置，从而将它们转化为创造出绩效和成果的机会。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;机械工业出版社; 第1版 (2009年9月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Managing%20for%20Results&search-alias=books\">Managing for Results</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E5%BE%B7%E9%B2%81%E5%85%8B%E7%AE%A1%E7%90%86%E7%BB%8F%E5%85%B8&search-alias=books\">德鲁克管理经典</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;266页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787111280729\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787111280729\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>24 x 16.6 x 2.6 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>499 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;机械工业出版社\r\n	</li>\r\n</ul>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《成果管理(珍藏版)》：<br />\r\n《著有成效的管理者》最佳伴读<br />\r\n《管理的实践》姊妹篇<br />\r\n启蒙中国现代管理第一人<br />\r\n当今中国一代活跃着的组织最近的眉睫的问题，正是德鲁克向我们描述的如何管理的问题。<br />\r\n只有中国人才能建设中国，只有中国人才能发展中国。<br />\r\n——彼得·德鲁克<br />\r\n超越时空的德鲁克，管理者永恒的导师<br />\r\n他称自己是“社会生态学家”，他对社会学和经济学的影响深远，他的著作架起了从工业时代到知识时代的桥梁。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	媒体推荐\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	全世界的管理者都应该感谢这个人，因为他贡献了毕生的精力，来理清我们社会中人的角色和组织机构的角色，我认为彼得·德鲁克比任何其他人都更有效地做到了这一点。<br />\r\n　——通用电气前首席执行官 杰克·韦尔奇<br />\r\n在所有的管理学书籍中，德鲁克的著作对我影响最深。<br />\r\n　——微软总裁 比尔·盖茨<br />\r\n德鲁克是我心中的英雄。他的著作和思想非常清晰，在那些狂热追求时髦的思想的人群中独树一帜。<br />\r\n　——英特尔主席 安迪·格鲁夫<br />\r\n《成果管理》是第一部涉及企业战略的书。 它告诉我们如何分析环境和如何在这个环境中摆正企业的位置——在这方面，本书仍旧是独树一帜的。 它是试图在管理今天的企业与创造企业的明天之间取得平衡的第一本书——在这方面，它基本上仍旧是绝无仅有的。 在开创研究商业企业经济绩效的学科上，本书迈出了第一步。 在系统说明管理商业组织的管理者的经济任务方面，本书是“第一个吃螃蟹的人”。&nbsp;<br />\r\n　　——彼得·德鲁克&nbsp;<br />\r\n经典经得起时间考验，值得一读再读，常读常新。它帮你理清思路，从任何新事变中发掘本质，找到历史渊源。<br />\r\n　　——邵明路 彼得·德鲁克管理学院创办人 德鲁克百年诞辰全球纪念活动共同主席&nbsp;<br />\r\n德鲁克先生对于管理领域的贡献并不需要我们去做注释，但是对于中国的管理者来说，他的价值却难以估量。因为德鲁克先生，管理可以变得卓有成效；因为德鲁克先生，管理者释放了自己的价值。&nbsp;<br />\r\n　　——陈春花 华南理工大学教授 博士生导师 一本优秀的著作就是一座挖不尽的宝藏，可以陪伴人的终生。这样的著作一旦诞生，就已经独立于作者、独立于时代，属于每个读者自己。这样的书是永恒的、跨越时空的。&nbsp;<br />\r\n　　——赵曙明 南京大学商学院院长 教授 博士生导师\r\n</div>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:55:44', '5', '1', '1');
INSERT INTO `tbl_library` VALUES ('qmIVnu', 'nEjeUz', '2', '企业风险管理', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">&nbsp; &nbsp; 风险管理的研究和实践在我国刚刚起步，无论是风险管理的理论体系，还是企业的风险管理机制都还很不完善。本书对风险管理机制提纲挈领式的介绍，将对我国企业风险管理机制的建立和完善起到重要的引导作用。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">　 本书通过案例研究的方式，介绍了一种有效的风险管理机制。书中阐述了风险管理的基本概念，以及在整个企业范围内实施风险管理的基本原理，并对当前风险管理的技术进行了简要的概括。此外，作者对从实施这一机制的企业实践中总结出的一些经验和教训进行了分析，并分别对5个重要行业中具有代表性的公司进行了深度研究。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">　 本书是在有关科研项目深入研究的基础上撰写的，故在体例和行文上具备了较为鲜明的特色：书中所提出的观点客观而深刻，不仅具有极强的说服力，而且具有较强的可操作性；全书采取了案例主导型的体例，其内容较少涉及专业性过强的术语，即便对于初学者来说，读起来也并不吃力。相信那些形象生动的案例分析会使人读起来饶有兴味。</span><br />\r\n<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">　 无论对于有志从事风险管理实践和研究的企业管理与研究人员，还是对风险管理感兴趣的初学者，本书都是一本有助于他们深入了解现代企业风险管理机制的好书。</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">　 （财务总监国际联合会是由来自世界各地15 000多位高级财务管理人员组成的非营利组织，财务总监研究基金会是附属于该组织的一个非营利机构。这个基金会始建于1944年，为本书的研究提供了经济资助。）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;中国人民大学出版社; 第1版 (2004年1月1日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Making%20Enterprise%20Risk%20Management%20Pay%20off%3A%20How%20Leading%20Companies%20Implement%20Risk%20Management&search-alias=books\">Making Enterprise Risk Management Pay off: How Leading Companies Implement Risk Management</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=CFO%E7%AE%A1%E7%90%86%E6%96%B0%E7%9F%A5%E8%AF%91%E4%B8%9B&search-alias=books\">CFO管理新知译丛</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;246页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;32\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787300056623, 7300056628\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787300056623\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>20.8 x 14.6 x 1.6 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>281 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;中国人民大学出版社\r\n	</li>\r\n</ul>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<div class=\"bbeditor\">\r\n		<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n			<p>\r\n				本书特色：<br />\r\n分别对美国5个重要行业中的公司：金融服务方面的大通曼哈顿银行，制造业中的杜邦公司，信息技术领域的微软公司等，在整个企业范围内实施风险管理的实践进行了深度的研究和分析，从世界级企业的风险管理实践中得出经验与教训。<br />\r\n阐述了在整个企业范围内实施一体化风险管理的概念与理论。<br />\r\n评价了财务总监在企业风险管理中后发制人扮演的角色以及应起到的作用。<br />\r\n一些新领域中出现的风险，以及企业应该如何对它们进行正确的评估和预防。<br />\r\n介绍了场景分析，收益/损失曲线分析、对风险进行度量的风险收益法，以及一些新的有效度量方法。\r\n			</p>\r\n			<p>\r\n				<span style=\"color:#CC6600;font-size:12px;\"><br />\r\n</span> \r\n			</p>\r\n			<p>\r\n				<span style=\"color:#CC6600;font-size:12px;\">作者简介</span> \r\n			</p>\r\n			<p>\r\n				<br />\r\n			</p>\r\n		</div>\r\n		<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n			<div class=\"bbauthor\">\r\n				托马斯·L·巴顿，凯恩琳和理查德·基普会计学教授，北佛罗里达大学会计协会KPMG研究会的会员，他还是一名注册会计师，在佛罗里达大学获得会计学博士学位。与人共同完成了1998年的财务主管研究报告：《公共管理：创造了一种新的所有权》。他的文章《一种新机制的诞生——公开管理创造了一种新的所有权》获得了利布兰德银奖。巴顿博士是“合资企业收益分配对公司影响最小化理论”的创造者。此外，人们还普遍认为他是对“高效创造活动”开展管理控制的专家。&nbsp;<br />\r\n威廉·G·申克，弗吉尼亚大学麦克英塔尔商学院的教授。1977-1992年间他担任麦克英塔商学院院长，是财务会计标准委员会的技术顾问和项目主管。他与托马斯·L·巴顿合著了《一种新机制的诞生——管理创造了一种新的所有权》。&nbsp;<br />\r\n保罗·L·沃克，弗叶尼亚大学麦克塔尔商学院的会计学副教授，他的研究兴趣包括审计市场、审计失效问题、审计员的独立性，以及审计制度、风险和费用。他曾就任于安永咨询公司审计部门、毕怪威国际会计公司税务部门、得克萨斯在达拉斯的InterFirst银行债券清算和经营部门、内部审计和数据流程管理部门。&nbsp;\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>', null, '2013-06-30 00:55:53', '4', '1', '1');
INSERT INTO `tbl_library` VALUES ('e6zm6n', 'nEjeUz', '2', '精细化管理持续改善', null, '<div class=\"buying\" style=\"padding:0.25em 0em;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"byLinePipe\" style=\"color:#666666;\">出版日期:&nbsp;</span><span style=\"font-weight:bold;\">2013年1月30日</span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	《精细化管理持续改善》是一本关于精细化管理的书籍，全书共分四个部分，第一部分重点阐释了精细化管理的基本功能和来龙去脉，第二部分论述了企业管理持续改善的理论基础——精细化管理的ORTCC模型和工作核心，第三部分阐述了企业管理持续改善的实施方式、实施工具和基本步骤，第四部分论述了企业开展精细化管理，实现管理持续改善的五大模块。阅读《精细化管理持续改善》，将能系统地了解精细化管理的理论和方法，掌握持续改善的途径和方式，对管理工作的开展大有裨益。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;北京理工大学出版社; 第1版 (2013年1月30日)\r\n	</li>\r\n	<li>\r\n		<b>外文书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=Continuous%20Improvement%20of%20Lean%20Management&search-alias=books\">Continuous Improvement of Lean Management</a> \r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E5%8D%9A%E5%A3%AB%E5%BE%B7%E7%B2%BE%E7%BB%86%E5%8C%96%E7%AE%A1%E7%90%86%E7%B3%BB%E5%88%97%E4%B8%9B%E4%B9%A6&search-alias=books\">博士德精细化管理系列丛书</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;290页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;16\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787564071165\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787564071165\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>23.8 x 17 x 2 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>481 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;北京博士德文化发展有限公司\r\n	</li>\r\n</ul>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《精细化管理持续改善》编辑推荐：精细化管理是企事业单位管理不可逾越的管理阶段，人民空军提升日常管理水平的经典指南。从管理的实践出发，建立对管理的认识逻辑，有效地针对企业自身问题设计解决方案，进行问题处理，标本兼治，从根本上解决问题，真正实现管理结果的稳定和良好。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	作者简介\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	吴宏彪，北京博士德管理顾问有限公司资深顾问，北京大学、清华大学、空军工程大学、浙江大学、厦门大学、南京大学、中国人民大学、中国农业大学、华中科技大学等大学EMBA班、总裁班课程特聘教授。为军委空军司令部、政治部、装备部、后勤部，航空兵部队、导弹部队，武警湖北消防总队，海军总医院等部队和卫生部，中国电力联合会，南京市委，山西晋城监狱，国家电网公司华北公司，中国电力投资公司，大庆油田，辽河油田，中国移动，中国联通，湖北烟草，中国建设银行北京分行，中国银行北京分行，中国工商银行浙江省分行，中国农业银行，招商银行等大型企业和机构进行专业课程讲授。著有：《向军队学管理》《核心价值观》《销售学的诡计》《掌控力》《精细化管理》（与汪中求合著）。<br />\r\n赵辉，北京博士德文化发展有限公司总编，北京博士德管理顾问有限公司运营总监、讲师，从事公司管理20年，主管出版、运营等事务，带领团队出版经管类图书数百种。多年来，在工作中推行精细化管理，致力于打造朝气蓬勃且具有竞争力的团队，对精细化管理和领导力、执行力有独到的见解。主要课程：《精细化管理》 《精细化领导能力持续改善》《精细化执行力持续改善》；主要著作：《正能量修习术》。\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:55:30', '6', '1', '1');
INSERT INTO `tbl_library` VALUES ('ZzQFju', 'nEjeUz', '2', '现代企业管理', null, '<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">《现代企业管理》是我国高等院校管理学院、商学院和相关系科的一门核心课程。“企业管理”是现代管理学中的一个极为重要内容，与管理学的其他分支一样，随着现代社会经济生活的发展和我国企业实践的创新也在不断变化之中，《现代企业管理》的第一版使用至今已十余年，这次再版，修改的基点放在了更好地反映企业管理教学的新要求上。</span><br />\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\">第二版除对第一版的原有内容做了结构上的调整、修改之外，还根据教学中反馈的读者信息，增添了“企业存在的社会和经济目的”、“企业的道德责任”、“企业决策的伦理分析”、“管理创新的新发展”等内容，期望在今后的教学实践中更适应广大师生的需要。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b>出版社:</b>&nbsp;复旦大学出版社; 第2版 (2009年7月1日)\r\n	</li>\r\n	<li>\r\n		<b>丛书名:</b>&nbsp;<a href=\"http://www.amazon.cn/s?_encoding=UTF8&field-keywords=%E5%A4%A7%E5%AD%A6%E7%AE%A1%E7%90%86%E7%B1%BB%E6%95%99%E6%9D%90%E4%B8%9B%E4%B9%A6&search-alias=books\">大学管理类教材丛书</a> \r\n	</li>\r\n	<li>\r\n		<b>平装:</b>&nbsp;605页\r\n	</li>\r\n	<li>\r\n		<b>语种：</b>&nbsp;简体中文\r\n	</li>\r\n	<li>\r\n		<b>开本:</b>&nbsp;32\r\n	</li>\r\n	<li>\r\n		<b>ISBN:</b>&nbsp;9787309056976\r\n	</li>\r\n	<li>\r\n		<b>条形码:</b>&nbsp;9787309056976\r\n	</li>\r\n	<li>\r\n		<b>商品尺寸:&nbsp;</b>20 x 13.8 x 2.6 cm\r\n	</li>\r\n	<li>\r\n		<b>商品重量:&nbsp;</b>558 g\r\n	</li>\r\n	<li>\r\n		<b>品牌:</b>&nbsp;复旦大学出版社\r\n	</li>\r\n</ul>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	《现代企业管理由复旦大学出版社策划并编辑出版。<br />\r\n《现代企业管理在复旦大学、南京大学、上海交通大学、浙江大学、苏州大学、江西财经大学等校相关院系多年教学实践的基础上，邀请各学科的博士生导师领衔，采取确立主干课程，不断使用修订再版；及时补进新知识、新课程的“滚动开放”形式组织选题。《现代企业管理出版后，经二百余所大专院校近年来本科及工商管理硕士(MBA)班的使用，已成为广大师生的首选教材，受到了普遍的欢迎。\r\n	<div class=\"emptyClear\" style=\"font-size:0px;\">\r\n	</div>\r\n</div>\r\n<h3 class=\"productDescriptionSource\" style=\"color:#CC6600;font-size:12px;font-weight:normal;font-family:verdana, arial, helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	目录\r\n</h3>\r\n<div class=\"productDescriptionWrapper\" style=\"margin:0px 0px 1em;font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	第一章 现代企业制度<br />\r\n第一节 企业的概念与特征<br />\r\n第二节 企业的形成与性质<br />\r\n第三节 企业制度的演变<br />\r\n第四节 企业制度的创新:建立现代企业制度<br />\r\n第二章 现代企业组织<br />\r\n第一节 现代企业组织的成因<br />\r\n第二节 现代企业的组织模式和特征<br />\r\n第三节 现代企业的组织结构和形式<br />\r\n第四节 现代企业组织的关系<br />\r\n第五节 现代企业组织创新<br />\r\n第三章 现代企业类型<br />\r\n第一节 农业企业<br />\r\n第二节 制造企业<br />\r\n第三节 流通企业<br />\r\n第四节 金融企业&nbsp;<br />\r\n第五节 交通运输企业<br />\r\n第六节 邮电通讯企业<br />\r\n第七节 建筑安装企业<br />\r\n第八节 房地产企业<br />\r\n第四章 现代企业伦理<br />\r\n第一节 企业道德责任<br />\r\n第二节 企业伦理概述<br />\r\n第三节 企业伦理准则<br />\r\n第四节 管理活动中的伦理关系<br />\r\n第五节 企业决策的伦理分析<br />\r\n第五章 现代企业资源<br />\r\n第一节 现代企业资源概述<br />\r\n第二节 人力资源<br />\r\n第三节 物力资源<br />\r\n第四节 资金资源<br />\r\n第五节 知识资源<br />\r\n第六节 时间资源<br />\r\n第六章 现代企业发展<br />\r\n第一节 企业发展的动力和约束<br />\r\n第二节 企业发展的目标和战略<br />\r\n第三节 企业发展的机会和风险<br />\r\n第七章 现代企业运作<br />\r\n第一节 现代企业运作概述<br />\r\n第二节 现代企业运作的过程<br />\r\n第三节 现代企业运作的步骤<br />\r\n第八章 现代企业竞争<br />\r\n第一节 企业竞争概述<br />\r\n第二节 影响企业竞争的因素分析<br />\r\n第三节 现代企业竞争战略的制定<br />\r\n第四节 企业竞争优势的构建<br />\r\n第五节 市场营销竞争策略<br />\r\n第九章 现代企业领导<br />\r\n第一节 领导概述<br />\r\n第二节 领导者的品质<br />\r\n第三节 领导风格<br />\r\n第四节 二元关系论<br />\r\n第五节 冲突与解决<br />\r\n第六节 激励理论及其应用<br />\r\n第十章 现代企业沟通<br />\r\n第一节 企业管理沟通概述<br />\r\n第二节 企业内部雇员沟通<br />\r\n第三节 企业的非正式组织沟通<br />\r\n第四节 企业的外部沟通<br />\r\n第十一章 现代企业控制<br />\r\n第一节 现代企业控制概述<br />\r\n第二节 现代企业控制原理<br />\r\n第三节 现代企业控制分类<br />\r\n第四节 现代企业控制技术<br />\r\n第十二章 现代企业创新<br />\r\n第一节 创新的涵义和创新的体系<br />\r\n第二节 创新机会的来源管理<br />\r\n第三节 现代企业的管理创新<br />\r\n第四节 现代企业创新主体———企业家<br />\r\n第十三章 现代企业文化<br />\r\n第一节 现代企业文化概述<br />\r\n第二节 现代企业文化的内容与功能<br />\r\n第三节 现代企业文化的构筑与建设<br />\r\n第四节 现代企业文化的诊断与重塑\r\n</div>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', null, '2013-06-30 00:54:24', '9', '1', '1');
INSERT INTO `tbl_library` VALUES ('V7Bze2', 'myIJBz', '2', '科技项目管理', null, '<h1 class=\"parseasinTitle\" style=\"font-family:\'Microsoft Yahei\', PMingLiU, Verdana, Arial, Helvetica, sans-serif;font-size:1.5em;background-color:#FFFFFF;\">\r\n	<span id=\"btAsinTitle\"><span style=\"font-size:12px;\">科技项目管理&nbsp;</span><span style=\"font-size:12px;\">[平装]</span></span> \r\n</h1>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\">~&nbsp;</span><a href=\"http://www.amazon.cn/s?ie=UTF8&field-author=%E9%99%88%E7%9C%81%E5%B9%B3&search-alias=books\"><span style=\"font-size:12px;\">陈省平</span></a><span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\">&nbsp;(作者),&nbsp;</span><a href=\"http://www.amazon.cn/s?ie=UTF8&field-author=%E6%9D%8E%E5%AD%90%E5%92%8C&search-alias=books\"><span style=\"font-size:12px;\">李子和</span></a><span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\">&nbsp;(作者),&nbsp;</span><a href=\"http://www.amazon.cn/s?ie=UTF8&field-author=%E5%88%98%E6%B6%9B&search-alias=books\"><span style=\"font-size:12px;\">刘涛</span></a><span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\">&nbsp;(作者)&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\"> </span>\r\n</p>\r\n<ul style=\"font-family:verdana, arial, helvetica, sans-serif;font-size:small;background-color:#FFFFFF;\">\r\n	<li>\r\n		<b><span style=\"font-size:12px;\">出版社:</span></b><span style=\"font-size:12px;\">&nbsp;中山大学; 第1版 (2007年1月1日)</span>\r\n	</li>\r\n	<li>\r\n		<b><span style=\"font-size:12px;\">平装:</span></b><span style=\"font-size:12px;\">&nbsp;368页</span>\r\n	</li>\r\n	<li>\r\n		<b><span style=\"font-size:12px;\">开本:</span></b><span style=\"font-size:12px;\">&nbsp;0开</span>\r\n	</li>\r\n	<li>\r\n		<b><span style=\"font-size:12px;\">ISBN:</span></b><span style=\"font-size:12px;\">&nbsp;7306024744</span>\r\n	</li>\r\n	<li>\r\n		<b><span style=\"font-size:12px;\">条形码:</span></b><span style=\"font-size:12px;\">&nbsp;9787306024749</span>\r\n	</li>\r\n	<li>\r\n		<b><span style=\"font-size:12px;\">商品尺寸:&nbsp;</span></b><span style=\"font-size:12px;\">20.2 x 13.8 x 2.2 cm</span>\r\n	</li>\r\n	<li>\r\n		<b><span style=\"font-size:12px;\">商品重量:&nbsp;</span></b><span style=\"font-size:12px;\">399 g</span>\r\n	</li>\r\n</ul>\r\n<span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第一章 科技项目管理概述&nbsp;</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:14px;line-height:20px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第一节 项目与科技项目&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第二节 项目管理与科技项目管理&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第三节 国家科技计划与重大科技项目&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第四节 WT0对科技项目管理的影响</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:14px;line-height:20px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第二章 科技项目实施现状分析&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:14px;line-height:20px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第一节 国家863项目实施现状&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第二节 高校实施国家863项目的现状分析&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第三节 广东地区实施国家863项目的现状分析</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:14px;line-height:20px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第三章 科技项目中的集成与联合&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:14px;line-height:20px;background-color:#FFFFFF;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvetica, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">第一节 国内外“大科学”研究现状&nbsp;</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:verdana, arial, helvetica, sans-serif;line-height:normal;background-color:#FFFFFF;font-size:12px;\"><span style=\"color:#333333;font-family:Arial, \'Helvetica Neue\', Helvet