INSERT INTO `tbl_library` VALUES ('ErYr6j', 'QBvYBb', '2', '科研总院—[重点课题]【2013年】集团公司页岩气产业发展前景深化研究', null, '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><strong>&nbsp; &nbsp; 研究内容：</strong></span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">解读国家出台的《页岩气发展规划（</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2011-2015</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">年）》、《页岩气产业政策》等相关政策性文件，分析国内外页岩气开发现状，提出加快集团公司页岩气产业发展的措施建议。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><strong>&nbsp; &nbsp; 承担部门（单位）：</strong></span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">科研总院、政法部</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\"><strong>&nbsp; &nbsp; 配合部门（单位）：</strong></span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">煤炭部、工程部、华电工程，贵州、湖南、江西等页岩气开发公司</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>', null, '2013-08-07 10:12:13', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('7JNr2e', 'QBvYBb', '2', '科研总院—【2013年】集团系统科研经费筹集及科技成果产业化研究', null, '<p>\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp; &nbsp; 集团系统科研经费筹集及科技成果产业化研究（科研总院承担）</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 一般课题\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-07 10:12:23', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('ZveuMn', 'QBvYBb', '2', '科研总院—【2013年】集团公司实施国际化战略过程中的有关问题研究', null, '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp; &nbsp; <strong>研究内容：</strong></span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">总结集团公司成立以来实施国际化战略的基本情况，学习借鉴其他央企实施国际化战略的成功经验，重点对集团公司境外项目的管控方式、组织架构、融资渠道、发展方式、风险防控等方面进行深入研究，提出相关对策和建议。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp; &nbsp;<strong> 承担部门：</strong></span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">政法部、国际部</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">&nbsp; &nbsp;<strong> 配合单位：</strong></span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">香港公司、华电工程、运营公司、科研总院</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp; &nbsp; 参与课题\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>', null, '2013-08-07 10:12:32', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('JV3emm', 'bARVRb', '2', '科研总院—【2012年】关于科研总院如何在集团公司科研创新体系中发挥重要作用的探讨', null, '<p>\r\n	<span style=\"font-size:12px;\">&nbsp;</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\"> &nbsp; 该课题荣获华电集团公司2012年度政策研究优秀奖。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\">&nbsp; &nbsp;&nbsp;</span><a href=\"/userfiles/file/20130701/20130701012335_64.pdf\" target=\"_blank\"><span style=\"font-size:12px;\">关于科研总院如何在集团公司科研创新体系中发挥重要作用的探讨</span></a><span style=\"font-size:12px;\">&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-07 10:11:45', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('7rqIJz', 'buUJBr', '2', '中华人民共和国特种设备安全法', null, '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;&nbsp;<a href=\"/userfiles/file/20130701/20130701173502_806.doc\" target=\"_blank\">中华人民共和国特种设备安全法</a>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-07-01 17:35:11', '0', '1', '1');
INSERT INTO `tbl_library` VALUES ('bUFFBn', 'qM7bYr', '2', '2013.07 - 规模储能技术的现状与挑战', null, '<p>\r\n	<span style=\"line-height:2;font-family:SimSun;\">2013年</span><span style=\"line-height:2;font-family:SimSun;\">7月26日</span><span style=\"line-height:2;font-family:SimSun;\">在集团总部举办。</span><span style=\"line-height:2;font-family:SimSun;\">讲座题目为\"规模储能技术的现状与挑战---储能和深冷（液化空气）储能\"，主讲人为国家\"千人计划\"专家、中科院过程所-英国利兹大学储能科学与技术联合研究中心主任丁玉龙教授。\"科技每月一讲\"由集团科技环保部主办，科研总院组织协办。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;line-height:2;\">丁玉龙教授从成熟度、经济性、使用寿命和效率等方面对当前各种储能技术进行了比较，并重点介绍了储热与深冷（液化空气）储能的原理、研发现状及示范应用情况。讲座对增进集团公司系统对储能技术的了解起到了积极的作用。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-04 13:25:13', '7', '1', '1');
