INSERT INTO `tbl_library` VALUES ('z2eMZn', 'qM7bYr', '2', '2013.08 - 中国城镇化趋势与能源发展', null, '<p>\r\n	&nbsp; &nbsp; 2013年8月22日在集团总部举办。本次科技讲座邀请了中国社会科学院城市发展与环境所副所长、研究员、教授魏后凯主讲\"中国城镇化趋势与能源发展\"，旨在增进公司系统对新型城镇化进程及政策方面的了解，促进公司系统对能源企业在新型城镇化进程中的机遇与挑战的思考和认识，拓宽集团相关领域管理、生产、科研人员的眼界和思路。\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 魏后凯介绍了中国城镇化的进程、特点以及未来趋势和重点发展区域，中国快速城镇化对能源消费（总量、结构）和碳排放的影响和要求，与参会代表就水电移民与城镇化有机结合等问题进行了交流。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', null, '2013-08-27 16:05:10', '8', '1', '1');
