INSERT INTO `tbl_post` VALUES ('2qqiei', '集团公司部署安全生产工作', 'vENrau', 'NjQrmi', '2013-07-03 13:51:15', '', null, '<p>\r\n	<span style=\"color:#333333;font-family:SimSun;line-height:2;background-color:#FEFEFE;\">会议有关情况请阅</span><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><a href=\"http://www.chd.com.cn/\"><span style=\"color:#0000FF;font-family:SimSun;line-height:2;\">http://www.chd.com.cn</span></a></span><span style=\"color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:SimSun;line-height:2;\">&nbsp;&nbsp;2013</span><span style=\"font-family:SimSun;line-height:2;\">年</span><span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">月</span><span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">日要闻快递。附件为会议通报的集团公司上半年安全生产及事故情况，请各部门阅知，并在各部门工作中落实集团各项要求。</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:SimSun;line-height:2;background-color:#FEFEFE;\"><span style=\"font-family:SimSun;line-height:2;\"><span style=\"color:#333333;font-family:SimSun;line-height:2;background-color:#FEFEFE;\">特此通知。</span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:宋体;\"><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:宋体;\"><span style=\"font-family:SimSun;line-height:2;\">&nbsp; &nbsp; </span><span style=\"color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:SimSun;line-height:2;\">&nbsp;附件：1、</span><a href=\"/userfiles/file/20130703/20130703130224_558.rar\" target=\"_blank\"><span style=\"font-family:SimSun;line-height:2;\">2013年1-6月份火电产业安全生产情况通报</span></a></span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:宋体;\"><span style=\"font-family:SimSun;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2、</span><a href=\"/userfiles/file/20130703/20130703130545_930.rar\" target=\"_blank\"><span style=\"font-family:SimSun;line-height:2;\">2013年1-6月份水电与新能源产业安全生产情况通报</span></a><span style=\"font-family:SimSun;line-height:2;\"></span><span style=\"font-family:SimSun;line-height:2;\"></span><span style=\"font-family:SimSun;line-height:2;\"></span><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:宋体;\"><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:SimSun;line-height:2;background-color:#FEFEFE;\"><span style=\"font-family:SimSun;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;综合管理部</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:SimSun;line-height:2;background-color:#FEFEFE;\"><span style=\"font-family:SimSun;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2013年7月3日</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:9pt;color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:宋体;\"><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:9pt;color:#333333;font-family:\'sans serif\';line-height:20px;background-color:#FEFEFE;\"><span style=\"font-family:宋体;\"><br />\r\n</span></span>\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('2Ufy6n', '包头公司“五步走”力促安全月活动出实效', 'vENrau', 'NjQrmi', '2013-06-03 17:10:48', '', null, '<p style=\"font-family:songti;font-size:medium;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:19px;\"><span style=\"font-family:仿宋_GB2312;\"><span style=\"font-size:18px;\">&nbsp; &nbsp; 进入6月份以来，在第十一个“安全生产月”活动中，包头公司严格遵循“科学发展、安全发展，关注生命、确保安全”的主题精神，分步推进，层层深入，通过“五步走”扎实开展安全生产月活动，促进了公司安全生产工作的持续稳定发展，为年度“零违章、零缺陷、零非停、零事故”的安全生产目标顺利实现奠定了坚实基础。</span><br />\r\n</span></span>\r\n	<p>\r\n		<span style=\"font-size:19px;\"><span style=\"font-family:仿宋_GB2312;\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-weight:bold;font-size:18px;\">&nbsp;</span><span style=\"font-weight:bold;font-size:18px;\">第一步</span><span style=\"font-weight:bold;font-size:18px;\">：安全月活动启动。</span><span style=\"font-size:18px;\">包头公司高度重视本次安全月活动，专门成立了以党政领导为组长的“安全月”活动领导小组，制定了“安全月”活动实施方案，在公司第四届公司“员工节”开幕式上，全面启动了“安全生产月”活动，宣读了安全月倡议书，公司领导和全体员工一同在“科学发展，安全发展，关注生命、确保安全”主题横幅上签名，将安全理念植入员工心中。</span></span></span>\r\n	</p>\r\n<span style=\"font-family:仿宋_GB2312;\"></span>\r\n</p>\r\n<p class=\"p0\" style=\"font-family:songti;font-size:medium;background-color:#FFFFFF;text-align:justify;\">\r\n	<span style=\"font-size:19px;\"><span style=\"font-family:仿宋_GB2312;\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-weight:bold;font-size:18px;\">第二步</span><span style=\"font-weight:bold;font-size:18px;\">：</span><span style=\"font-weight:bold;font-size:18px;\">安全警示教育。</span><span style=\"font-size:18px;\">包头公司通过公司网页、生产现场布置安全宣传条幅、张贴安全宣传画板等安全警示教育目视化方法，大力营造“人人事事保安全”的良好氛围。同时，公司通过组织开展《发电企业作业环境本质安全管理规定》和《“两票”管理规定》内容专题培训，落实“回头看”工作，将培训内容贯彻于现场安全治理和检修作业危险点分析预控过程中，切实提高工作票危险点分析的管控能力和保障能力。&nbsp;&nbsp;&nbsp;</span></span></span><span style=\"font-family:仿宋_GB2312;\"></span>\r\n</p>\r\n<p class=\"p0\" style=\"font-family:songti;font-size:medium;background-color:#FFFFFF;text-align:justify;\">\r\n	<span style=\"font-size:19px;\"><span style=\"font-family:仿宋_GB2312;\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-weight:bold;font-size:18px;\">第三步</span><span style=\"font-weight:bold;font-size:18px;\">：</span><span style=\"font-weight:bold;font-size:18px;\">安全文化宣传。</span><span style=\"font-size:18px;\">公司积极利用安全学习集中组织对新颁国标《电力（业）安全工作规程》、《电力生产事故调查规程》等安全规程制度进行了宣贯学习。同时，公司通过开展安全文化书画、摄影和文学作品征集，“青年安全生产示范岗”，参加“科学发展安全发展知识竞赛”和“全国危险化学品安全法规知识竞赛”等活动有力推动了安全文化建设。</span></span></span><span style=\"font-family:仿宋_GB2312;\"></span>\r\n</p>\r\n<p class=\"p0\" style=\"font-family:songti;font-size:medium;background-color:#FFFFFF;text-indent:10pt;text-align:justify;\">\r\n	<span style=\"font-size:19px;\"><span style=\"font-family:仿宋_GB2312;\"><span style=\"font-size:18px;\">&nbsp;</span><span style=\"font-weight:bold;font-size:18px;\">&nbsp;</span><span style=\"font-weight:bold;font-size:18px;\">第四步</span><span style=\"font-weight:bold;font-size:18px;\">：</span><span style=\"font-weight:bold;font-size:18px;\">应急预案演练。</span><span style=\"font-size:18px;\">公司为保证安全生产工作到位做实，对2012年防汛工作进行了再部署再落实。通过具体做好五项准备工作（即：思想准备、预案准备、力量准备、物资准备和技术准备），开展了防汛综合应急演练。确保安全不留死角。</span></span></span><span style=\"font-family:仿宋_GB2312;\"></span>\r\n</p>\r\n<p class=\"p0\" style=\"font-family:songti;font-size:medium;background-color:#FFFFFF;text-indent:10pt;text-align:justify;\">\r\n	<span style=\"font-size:19px;\"><span style=\"font-family:仿宋_GB2312;\"><span style=\"font-weight:bold;font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-weight:bold;font-size:18px;\">第五步</span><span style=\"font-weight:bold;font-size:18px;\">：安全月与重点工作结合。</span><span style=\"font-size:18px;\">一是坚持与迎峰度夏相结合。作为华电集团在蒙西电网的重点电力生产企业之一，包头公司通过加大运行管理、电量管理和燃料管理，切实做好“迎峰度夏”工作，确保在夏季用电大负荷关键时刻能“发得出、顶得上”。二是坚持与设备治理相结合。公司大力开展隐患排查，通过重大危险源治理、25项反措专项工作排查各项设备问题，并积极贯彻落实设备“零缺陷”行动，做到小缺陷不过天、大缺陷不过夜，力争使消缺率达到100%，提高了设备健康水平。</span></span></span>\r\n</p>\r\n<p class=\"p0\" style=\"font-family:songti;font-size:medium;background-color:#FFFFFF;text-indent:10pt;text-align:justify;\">\r\n	<span style=\"font-size:19px;\"><span style=\"font-family:仿宋_GB2312;\"><br />\r\n</span></span>\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('2umuY3', '火电厂液氨罐区安全管理暂行规定（试行）', 'vENrau', 'NjQrmi', '2013-06-09 10:40:04', '', null, '<a href=\"/userfiles/file/20130609/20130609103037_457.doc\" target=\"_blank\">火电厂液氨罐区安全管理暂行规定（试行）</a>', '1');
INSERT INTO `tbl_post` VALUES ('3E7r6n', '关于加强科学施救提高生产安全事故灾难应急救援水平的指导意见', 'vENrau', 'NjQrmi', '2013-06-07 14:38:29', '', null, '<a href=\"/userfiles/file/20130609/20130609143846_42.pdf\" target=\"_blank\">关于加强科学施救提高生产安全事故灾难应急救援水平的指导意见</a>', '1');
INSERT INTO `tbl_post` VALUES ('3eYjUr', '关于开展2013年“安全生产月”活动的通知', 'vENrau', 'NjQrmi', '2013-05-30 10:27:25', '', null, '<p>\r\n	各部门、单位：\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;&nbsp;为贯彻落实党中央、国务院和集团公司关于安全生产工作的部署，深入开展2013年“安全生产月”活动，按照集团公司《关于开展2013年“安全生产月”活动的通知》（中国华电安[2013]286号）要求，请各部门、单位：\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 1、组织员工学习《电力（业）安全工作规程》（科研总院小图书馆有备，也可根据需要自行购置）；\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 2、利用工间或业余时间浏览学习交通安全、消防安全、火灾避险、应急逃生等安全宣传挂图（设置在前台大厅右侧，每周将更新一个安全主题）；\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 3、自觉加强安全宣传，开展各类安全学习和讨论，并鼓励本部门员工积极向集团安全文化征文报送稿件（稿件报送请联系综合管理部）；\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 4、遵守规范的工作流程，杜绝各类违章操作，加强日常安全意识，尊重和珍爱生命。\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;&nbsp;（各部门、单位和人员下班、出差或长时间离开工位，请自觉断开电脑、打印机、复印机、饮水机等电源插座，锁紧门窗等，杜绝安全隐患，防止不安全事件发生，谢谢！）\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 另外，综合管理部将于6月组织一次2013年安全生产警示教育片观看活动，具体时间将另行通知。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;综合管理部\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;2013年5月30日\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('3uieq2', '电厂发变组大修安全管理制度', 'vENrau', 'NjQrmi', '2013-06-14 10:10:47', '', null, '<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">一、组织机构与职责</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、安全监督组</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">组长：</span><span style=\"font-family:SimSun;line-height:2;\">....</span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">成员：</span><span style=\"font-family:SimSun;line-height:2;\">.......................</span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、职责</span><span style=\"font-family:SimSun;line-height:2;\">:</span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">负责大修现场安全监察工作</span><span style=\"font-family:SimSun;line-height:2;\">,</span><span style=\"font-family:SimSun;line-height:2;\">认真监督执行大修安全管理制度、消防制度，深入现场</span><span style=\"font-family:SimSun;line-height:2;\">,</span><span style=\"font-family:SimSun;line-height:2;\">认真检查各工作面的安全措施执行情况；采取有效手段，制止“三违”现象，及时发现处理安全隐患</span><span style=\"font-family:SimSun;line-height:2;\">,</span><span style=\"font-family:SimSun;line-height:2;\">做到安全文明施工。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">二、大修安全措施（详见大修方案）</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">三、安全管理规定</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">为了确保</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修安全，根据电业安全管理规定，结合现场实际情况，特制定本规定。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、参加</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修的各部门，必须严格执行电业安全工作有关规定，服从大修指挥部的安全管理。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、各参修部门根据本部门工作任务制定相应的安全措施，并认真组织实施，落实到人。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、厂部、车间、班组</span><span style=\"font-family:SimSun;line-height:2;\">(</span><span style=\"font-family:SimSun;line-height:2;\">工作面</span><span style=\"font-family:SimSun;line-height:2;\">)</span><span style=\"font-family:SimSun;line-height:2;\">三级安全监察人员，戴“安全监察”标志，现场监督。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、认真检查大修现场各工作面的安全情况，以铁的手腕制止“三违”现象发生，及时完善大修现场安全措施，消除不安全隐患，确保大修施工安全。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、对大修安全重要控制点制定相应的控制措施。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6</span><span style=\"font-family:SimSun;line-height:2;\">、各班开好班前班后会，认真做好安全“五同时”工作，各工作面人员必须清楚工作任务和安全措施情况，做到心中有数，确保人身和保设备安全。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">、交叉作业时，各工作面应互相协调，工作中做到“三不伤害”，防止不安全现象出现。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">8</span><span style=\"font-family:SimSun;line-height:2;\">、所有参加大修人员按规定着装，凡着装不符合规定者，不得进入大修现场。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">9</span><span style=\"font-family:SimSun;line-height:2;\">、为确保大修安全，参观学习人员一律不得进入大修现场。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">10</span><span style=\"font-family:SimSun;line-height:2;\">、起重作业人员必须持证上岗，专人指挥起吊重物时，严禁工作人员在重物下行走停留。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">11</span><span style=\"font-family:SimSun;line-height:2;\">、高空作业人员必须系安全带，传递工具零件必须使用工具袋吊送，严禁抛甩。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">12</span><span style=\"font-family:SimSun;line-height:2;\">、认真执行安全生产重奖重罚制度，对大修中安全工作的好人好事进行表扬和奖励，对“三违”现象实行处罚。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">四、安全奖罚规定</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">为加强</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修现场安全管理，认真落实各级人员安全责任制，确保大修顺利进行，按照《厂经济责任制考核规定》，结合大修实际情况，特制定本规定。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">（一）、在大修安全施工中表现突出的下列情况给予表扬和奖励</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、在大修中工作认真负责，严格执行规章制度，及时发现不安全隐患，采取有效措施排除陷患，避免发生人身</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、工作认真负责，严格执行规章制度，及时发现设备隐患，采取得力措施排除设备重大隐患，避免发生设备</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">者。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、在发生</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">异常时，积极组织处理，措施得力和在处理中不顾个人安危，积极参与，表现突出者。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、认真开展反“三违”活动，积极举报和制止反“三违”行为表现突出者。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、立足本职工作，为</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修安全作出贡献者。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">（二）、凡有下列情形之一者，视情节给予批评教育和经济处罚</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、在工作中未严格执行规章制度，安全措施不力，发生人身伤亡</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">。按《厂经济责任制》处罚。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、在大修中未严格执行规章制度，安全措施不力，发生设备</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">，按《厂经济责任制》处罚。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、对大修中发生的事故和异常隐瞒不报、谎报，无故延误报告或毁坏</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">现场，阻挠调查或者给调查处理设置障碍者。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、在大修中对违章、违法、乱纪者，视情节给予批评、罚款、停工检查三种处理。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">（三）、奖惩管理</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、在大修工作中作出突出贡献有关人员，填写特殊贡献申报表，由车间</span><span style=\"font-family:SimSun;line-height:2;\">(</span><span style=\"font-family:SimSun;line-height:2;\">公司</span><span style=\"font-family:SimSun;line-height:2;\">)</span><span style=\"font-family:SimSun;line-height:2;\">签署意见报安全监察部，经大修指挥部讨论决定给予表扬奖励。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、在大修中末严格执行规章制度或措施不力引发的</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">和异常，各部门必须及时上报安监部，经大修指挥部讨论决定给予批评教育或经济处罚。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">五、大修消防管理制度</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">为认真贯彻执行消防法，确保</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修顺利进行，特制订如下规定：</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、各参加大修部门指定专人负责大修期间消防管理工作。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、各工作面及时消除各种易燃易爆物资，做到工完料尽场地清。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、经常检查现场消防器材完好性，及时调整补充器材。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、大修现场按消防规定，严格控制易燃易爆物品，严禁超量存放。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、在主机、主变、油库、油槽等重要防火部位需进行明火作业时，严格执行动火管理制度，经厂消防部门批准后方可开工。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6</span><span style=\"font-family:SimSun;line-height:2;\">、各大修单位动用消防器材后，必须向厂消防部门报告，并及时给予补充。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">、其它部位进行明火作业时，必须做好防火措施。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">8</span><span style=\"font-family:SimSun;line-height:2;\">、加强施工人员防火教育，提高施工人员消防意识，防止火灾</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">发生。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">六、安全用电管理规定</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">为了贯彻电业安全工作规定，防止人身触电事态发生，确保</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修顺利进行，特制定本规定：</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、本次机组大修用电，由检修值班</span><span style=\"font-family:SimSun;line-height:2;\">电工</span><span style=\"font-family:SimSun;line-height:2;\">负责安排接线，任何部门、个人不得私自乱拉乱接电源。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、需用电部门事先提出用电要求，由值班电工妥善安排，用电部门工作结束必须通知值班</span><span style=\"font-family:SimSun;line-height:2;\">电工</span><span style=\"font-family:SimSun;line-height:2;\">拆除电源。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、大修临时用电必须使用装有漏电保护装置的配电箱配电以保安全。</span><span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、便携、手持、移动等电动工具必须接漏电保护器，绝缘良好并接地可靠，符合要求，否则不得使用。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、在蜗壳、尾水管、金属压力部器内移动工具照明电源电压不得超过</span><span style=\"font-family:SimSun;line-height:2;\">12V</span><span style=\"font-family:SimSun;line-height:2;\">，以保安全。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6</span><span style=\"font-family:SimSun;line-height:2;\">、临时用电线路一律不得超负荷运行，并按额定量选用熔丝。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">、值班</span><span style=\"font-family:SimSun;line-height:2;\">电工</span><span style=\"font-family:SimSun;line-height:2;\">必须认真巡视检查大修临时用电设备，发现问题及时处理，以确保大修用电安全。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">8</span><span style=\"font-family:SimSun;line-height:2;\">、需要在运行设备上接电源时，必须与运行人员联系，办理有关手续，方可接电。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">七、临时工安全管理</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、凡大修使用临时工应符合地方政府相关规定并证明持有四证</span><span style=\"font-family:SimSun;line-height:2;\">(</span><span style=\"font-family:SimSun;line-height:2;\">劳务证、身份证、暂住证、计划生育证</span><span style=\"font-family:SimSun;line-height:2;\">)</span><span style=\"font-family:SimSun;line-height:2;\">并建立临时用工档案。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、临时工的安全管理纳入用工部门安全管理，实行“谁用工、谁主管、谁负责”。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、用工部门必须与临时工签订书面劳务合同。合同中必须明确规定双方的权利、义务与责任。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、临时工必须经过厂部、公司</span><span style=\"font-family:SimSun;line-height:2;\">(</span><span style=\"font-family:SimSun;line-height:2;\">车间</span><span style=\"font-family:SimSun;line-height:2;\">)</span><span style=\"font-family:SimSun;line-height:2;\">、班组工作面的安全教育，方可上工。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、凡从事特种作业的临时工，必须持有劳动部门签发的有效特种人员操作证，否则禁止上岗作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6</span><span style=\"font-family:SimSun;line-height:2;\">、临时工参加大修现场工作必须由用工部门派专人负责管理，交待工作任务、现场安全措施及注意事项，负责现场作业，安全监护。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">、临时工应自觉遵守大修现场的纪律和规定，主动接受安全管理，严格执行安全</span><span style=\"font-family:SimSun;line-height:2;\">操作规程</span><span style=\"font-family:SimSun;line-height:2;\">，严禁蛮干。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">8</span><span style=\"font-family:SimSun;line-height:2;\">、临时工工作中发生“三违”现象，两次规劝不改或在现场无理取闹，影响大修工作，指挥部有权令其退出大修现场，不再录用。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">9</span><span style=\"font-family:SimSun;line-height:2;\">、发电机风洞打开后，应该派人</span><span style=\"font-family:SimSun;line-height:2;\">24</span><span style=\"font-family:SimSun;line-height:2;\">小时值班。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">八、安全文明施工规定</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、大修人员进入大修现场，应自觉遵守大修现场的各项规章制度，做好本职工作，不准串岗。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、大修现场严禁嘻笑打闹，不准干扰妨碍他人正常作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、大修期间不安排参观，特殊情况经指挥部批准由专人带领。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、大修工具器具、物件、配件和拆卸解体的设备、设施等，每日收工时，按规定要求，妥善放整齐，工作现场进行打扫整理，保持现场不乱。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、增强职工自我保护意识，施工中的废油、废料、废物品，不准乱扔乱倒，应按要求集中放置，并安排专人及时清理；保持大修现场整洁。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6</span><span style=\"font-family:SimSun;line-height:2;\">、对有毒、有害、易燃废弃物，必须严格按有关规定进行回收处理。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">、大修人员必须严格按规定着装，佩戴合格安全用具。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">8</span><span style=\"font-family:SimSun;line-height:2;\">、发电机风洞门打开后应设专人值班，工作人员下班时应将门关闭。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">9</span><span style=\"font-family:SimSun;line-height:2;\">、不得将盒饭及食品带入厂房。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">九、危险预控点管理规定</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组扩大性大修项目多，工程难度大，工期长，技术复杂，在大修现场的安全管理中突出抓好危险预控点的管理最为重要。为确保大修顺利进行，做到该修必修，修必修好，努力实现</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修后一次并网发电成功，特制订本规定。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">（一）、危险预控点划分的原则与依据</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、在</span><span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">号发变组大修起重要作用和占有重要位置，有可能影响大修的重要环节、项目、施工的重要部位、阶段；危及国家财产及职工生命安全的范围内。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、危险预控点的划分，是依据历次大修中</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">易发作业部位，同时参照生技部设备分类原则和工程项目的总费用、工期、技术复杂难度以及作业环境等因素来确定的。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">（二）、危险预控点</span><span style=\"font-family:SimSun;line-height:2;\">(</span><span style=\"font-family:SimSun;line-height:2;\">部位</span><span style=\"font-family:SimSun;line-height:2;\">)</span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、搭设与拆卸检修平台。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、转轮排油、内部检修、叶片密封检修作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、汽蚀处理作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、围带更换，水导油槽、端面密封、顶盖排水系统检修作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、推力油槽检修作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6</span><span style=\"font-family:SimSun;line-height:2;\">、</span><span style=\"font-family:SimSun;line-height:2;\">1B</span><span style=\"font-family:SimSun;line-height:2;\">停送电措施。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">、发变组大修前后电气试验。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">8</span><span style=\"font-family:SimSun;line-height:2;\">、</span><span style=\"font-family:SimSun;line-height:2;\">11B</span><span style=\"font-family:SimSun;line-height:2;\">吊罩检查、修理作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">9</span><span style=\"font-family:SimSun;line-height:2;\">、吊拆主变套管作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">10</span><span style=\"font-family:SimSun;line-height:2;\">、主变做试验时拆接引线。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">12</span><span style=\"font-family:SimSun;line-height:2;\">、吊装磁极。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">13</span><span style=\"font-family:SimSun;line-height:2;\">、定子检查及处理。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">14</span><span style=\"font-family:SimSun;line-height:2;\">、发电机盖板打开后应设固定遮栏。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">15</span><span style=\"font-family:SimSun;line-height:2;\">、进水口及尾水门盖板打开后应设固定遮栏。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">16</span><span style=\"font-family:SimSun;line-height:2;\">、检修排水泵应工作正常。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">17</span><span style=\"font-family:SimSun;line-height:2;\">、油槽内电焊、油漆等作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">18</span><span style=\"font-family:SimSun;line-height:2;\">、蜗壳内作业。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">29</span><span style=\"font-family:SimSun;line-height:2;\">、</span><span style=\"font-family:SimSun;line-height:2;\">GIS</span><span style=\"font-family:SimSun;line-height:2;\">室传动等保护试验</span><span style=\"font-family:SimSun;line-height:2;\">.</span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">20</span><span style=\"font-family:SimSun;line-height:2;\">、断开压油装置电源。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">21</span><span style=\"font-family:SimSun;line-height:2;\">、油槽渗漏试验。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">22</span><span style=\"font-family:SimSun;line-height:2;\">、导叶套筒拆装</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">23</span><span style=\"font-family:SimSun;line-height:2;\">、主机、主变、油槽等部位动火。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">24</span><span style=\"font-family:SimSun;line-height:2;\">、水车室调速器操作油管拆卸吊装。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">25</span><span style=\"font-family:SimSun;line-height:2;\">、控制环吊装。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">26</span><span style=\"font-family:SimSun;line-height:2;\">、导叶密封条更换。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">27</span><span style=\"font-family:SimSun;line-height:2;\">、真空破坏阀吊装。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">28</span><span style=\"font-family:SimSun;line-height:2;\">、机组检修后第一次启动。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">29</span><span style=\"font-family:SimSun;line-height:2;\">、定子线圈、铁芯清扫使用清洗剂</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">30</span><span style=\"font-family:SimSun;line-height:2;\">、工作人员上下尾水管楼梯。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">（三）、危险预控点作业施工的管理要求</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1</span><span style=\"font-family:SimSun;line-height:2;\">、各参修部门行政主要领导应将危险预控点的施工管理放在突出位置，组织参修班组，重点确定，结合各工作面的实际情况，参照万安水电厂危险点及其控制措施，制定危险预控点的防范措施，报安监部备查。并将危险预控点措施落到实处，确保如期完成。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2</span><span style=\"font-family:SimSun;line-height:2;\">、在危险预控点上作业施工负责人，在施工前应召开专门会议，布置工程施工安全措施及要求，工作进度、施工工艺方面等内容和要求，真正做到五同时。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3</span><span style=\"font-family:SimSun;line-height:2;\">、大修安全监督组在整个大修过程中实行全过程监督，特别是对危险预控点上作业，按照防范措施要求施行重点监督。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4</span><span style=\"font-family:SimSun;line-height:2;\">、在危险预控点上作业工作面，应设置现场安全监督员，把好现场安全关。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5</span><span style=\"font-family:SimSun;line-height:2;\">、危险预控点上施工作业面，应配备有经验的相应技术等级的工人。</span><span style=\"font-family:SimSun;line-height:2;\">6</span><span style=\"font-family:SimSun;line-height:2;\">、危险预控点应配备适量的消防器材，严格遵守动火规定。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7</span><span style=\"font-family:SimSun;line-height:2;\">、进入风洞、发电机定子、推力、水导油槽、主变线圈检查、工作，应穿专用工作服，不得携带杂物，所带工具应进行登记。工作结束后，应进行清点。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">8</span><span style=\"font-family:SimSun;line-height:2;\">、 \r\n机组压油装置电源必须在关闭</span><span style=\"font-family:SimSun;line-height:2;\">1101</span><span style=\"font-family:SimSun;line-height:2;\">阀后切除，以防跑油。</span><span></span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">9</span><span style=\"font-family:SimSun;line-height:2;\">、 \r\n保护传动必须在一次设备工作完毕经试验合格后请示生产副厂长或总工同意后，由工作负责人统一指挥；</span><span style=\"font-family:SimSun;line-height:2;\">GIS</span><span style=\"font-family:SimSun;line-height:2;\">室间隔的传动试验必须检查各间隔的气压、油压正常，接地刀闸拉开，接地线撤除后才可进行。</span> \r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<br />\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('3umYRr', '关于汇总上报2013年上半年合同清理情况的通知', 'vyEzUb', 'NjQrmi', '2013-07-04 10:30:11', '', null, '<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	总院各部门、各单位：\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	根据集团公司中国华电法[2013]387号文件要求，请将你部门、单位主办签订的合同情况于7月20日前汇送到综合管理部，以便统一报送集团公司。具体要求参见附件中集团通知。\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	联系人：孔令莹\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	电邮：<a href=\"mailto:lingying-kong@chdi.ac.cn\">lingying-kong@chdi.ac.cn</a> \r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	电话：5196 6891\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	附件：1、&nbsp;<a href=\"/userfiles/file/20130704/20130704103244_872.gw\" target=\"_blank\">关于上报2013年上半年合同清理情况的通知</a> \r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、<a href=\"/userfiles/file/20130704/20130704103518_604.doc\" target=\"_blank\">公司系统2013年上半年合同清理情况统计表</a> \r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 综合管理部\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2013年7月4日\r\n</p>\r\n<p style=\"text-align:left;text-indent:30pt;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>&nbsp;\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('6BZJjm', '安全文化与人性文化', 'vENrau', 'NjQrmi', '2013-06-11 10:32:35', '', null, '<p>\r\n	<span style=\"line-height:2;font-size:12px;\">&nbsp; 安全生产，对于我们来说已经不是一个陌生的名词了。近年来，人性化管理逐步扩大，各企业对安全生产有了清晰的认识。怎样开展好安全生产工作，如何有效地将安全生产的各项措施落到实处，杜绝各种安全</span><span style=\"line-height:2;font-size:12px;\">事故</span><span style=\"line-height:2;font-size:12px;\">的发生，是企业中每一个管理人员的一项重要工作内容和课题。安全生产，对于企业来讲，我觉得应该从以下几方面开展工作：</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-size:12px;\">&nbsp; 一、抓好安全生产过程中的人性化管理，必须强化制度与执行相统一。安全生产过程中的遵章守纪和人性化管理完全是一致的，两者之间丝毫不存在实质上的冲突。“严格”两字，重在一个“格”字，就是各种规章制度只要你在执行过程中不出这个“格”，不管如何的严肃认真都是值得充分肯定的。“严是爱，松是害”这句话，是对严格管理与人性化管理之间的关系最恰当的诠释。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-size:12px;\">&nbsp; 二、强化员工自我安全意识。经过对各项</span><span style=\"line-height:2;font-size:12px;\">工伤</span><span style=\"line-height:2;font-size:12px;\">事故</span><span style=\"line-height:2;font-size:12px;\">的调查发现，80％的</span><span style=\"line-height:2;font-size:12px;\">工伤</span><span style=\"line-height:2;font-size:12px;\">事故</span><span style=\"line-height:2;font-size:12px;\">都是因为员工在生产过程中未按照机器设备操作流程进行操作或粗心大意造成的。针对这种情况，各部门管理人员应该</span><span style=\"line-height:2;font-size:12px;\">培训</span><span style=\"line-height:2;font-size:12px;\">、强调并进行监督，并不断地宣传、形成人人关</span><span style=\"line-height:2;font-size:12px;\">注安</span><span style=\"line-height:2;font-size:12px;\">全的意识，从而减少人为安全</span><span style=\"line-height:2;font-size:12px;\">事故</span><span style=\"line-height:2;font-size:12px;\">的发生。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-size:12px;\">　三、加强新员工的</span><span style=\"line-height:2;font-size:12px;\">安全培训</span><span style=\"line-height:2;font-size:12px;\">。新员工在进入企业之后，对于各种机器设备的操作或注意事项都不熟悉，是各种</span><span style=\"line-height:2;font-size:12px;\">工伤</span><span style=\"line-height:2;font-size:12px;\">事故</span><span style=\"line-height:2;font-size:12px;\">的高发人群。针对此情况，企业应对新员工进行上岗前</span><span style=\"line-height:2;font-size:12px;\">培训</span><span style=\"line-height:2;font-size:12px;\">，由各部门的管理干部、技术人员对他们进行各种设备的操作注意事项</span><span style=\"line-height:2;font-size:12px;\">培训</span><span style=\"line-height:2;font-size:12px;\">等，一定要待考核合格后再上岗。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-size:12px;\">　四、安全生产的人性化与生产工艺和工作环境有着不可脱离的关系。在生产工艺的过程中应充分利用人体生理工程学原理，对于出现</span><span style=\"line-height:2;font-size:12px;\">工伤</span><span style=\"line-height:2;font-size:12px;\">的作业点或存在安全隐患的地方，企业应有专人负责并进行分析，对于生产工艺存在安全隐患的地方应该进行改进，同时结合ISO质量管理体系和7S管理工具等进行规范和监督，这样可大大降低安全</span><span style=\"line-height:2;font-size:12px;\">事故</span><span style=\"line-height:2;font-size:12px;\">的发生。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-size:12px;\">　五、安全文化的建立同时也需要与提高员工素质相结合。人员素质的高低直接关系生产过程能否顺利进行、制度能否顺利执行的问题。高素质的员工队伍，不仅业务技术一流，而且要具有高度的纪律和遵章守纪的观念，只有这样，安全文化与人性文化才有它的发展根基。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1');
