INSERT INTO `tbl_post` VALUES ('6n6FZ3', '危险化学品重大危险源监督管理暂行规定', 'vENrau', 'NjQrmi', '2013-06-08 10:12:37', '', null, '<a href=\"/userfiles/file/20130609/20130609102258_168.doc\" target=\"_blank\">国家安监总局40号令《危险化学品重大危险源监督管理暂行规定》</a>', '1');
INSERT INTO `tbl_post` VALUES ('6RjiQv', '集团公司部署安全生产工作', 'biymau', 'NjQrmi', '2013-07-03 13:18:41', '', null, '<p style=\"text-align:left;\" class=\"p0\" align=\"left\">\r\n	<span style=\"font-family:\'sans serif\';font-size:9pt;\"><span style=\"font-family:\'sans serif\';font-size:9pt;\">会议有关情况请阅</span><span><a href=\"http://www.chd.com.cn/\"><span style=\"font-family:\'sans serif\';color:#0000ff;font-size:9pt;text-decoration:underline;\">http://www.chd.com.cn</span></a></span><span style=\"font-family:\'sans serif\';font-size:9pt;\">&nbsp;&nbsp;2013<span>年</span><span>7</span><span>月</span><span>3</span><span>日要闻快递。附件为会议通报的集团公司上半年安全生产及事故情况，请各部门阅知，并在各部门工作中落实集团各项要求。</span></span><span style=\"font-family:\'sans serif\';font-size:9pt;\"></span> </span> \r\n</p>\r\n<p style=\"text-align:left;\" class=\"p0\">\r\n	<span style=\"font-family:\'sans serif\';font-size:9pt;\">特此通知。</span> \r\n</p>\r\n<p style=\"text-align:left;\" class=\"p0\">\r\n	<span style=\"font-family:\'sans serif\';font-size:9pt;\"></span><span style=\"font-family:\'sans serif\';font-size:9pt;\"></span>&nbsp;\r\n</p>\r\n<p style=\"text-align:left;\" class=\"p0\">\r\n	<span style=\"font-family:\'sans serif\';font-size:9pt;\">&nbsp;&nbsp;&nbsp;&nbsp;附件：1、<a href=\"userfiles/file/20130703/20130703130224_558.rar\" target=\"_blank\">2013年1-6月份火电产业安全生产情况通报</a></span> \r\n</p>\r\n<p style=\"text-align:left;\" class=\"p0\">\r\n	<span style=\"font-family:\'sans serif\';font-size:9pt;\"></span><span style=\"font-family:\'sans serif\';font-size:9pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2、<a href=\"userfiles/file/20130703/20130703130545_930.rar\" target=\"_blank\">2013年1-6月份水电与新能源产业安全生产情况通报</a></span> \r\n</p>\r\n<p style=\"text-align:left;\" class=\"p0\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\" class=\"p0\" align=\"justify\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;综合管理部\r\n</p>\r\n<p style=\"text-align:left;\" class=\"p0\" align=\"justify\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2013年7月3日\r\n</p>', '1');
