INSERT INTO `tbl_post` VALUES ('7B3aQb', '转发国务院安委会安全生产大检查工作实施方案等文件的通知', 'vENrau', 'NjQrmi', '2013-07-11 12:10:05', '', null, '<p>\r\n	<a href=\"/userfiles/file/20130711/20130711121022_81.gw\" target=\"_blank\"><a href=\"/userfiles/file/20130712/20130712095942_817.gw\" target=\"_blank\">转发国务院安委会安全生产大检查工作实施方案等文件的通知</a></a> \r\n</p>\r\n<p>\r\n	<br />\r\n&nbsp;\r\n</p>\r\n<p>\r\n	<a href=\"/userfiles/file/20130712/20130712100046_954.pdf\" target=\"_blank\"><a href=\"/userfiles/file/20130712/20130712100046_954.pdf\" target=\"_blank\">附件1：《国家能源局关于贯彻落实国务院安委会安全生产大检查工作实施方案 进一步做好电力行业安全生产大检查工作的通知》（国能电安【2013】251号）</a></a>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<a href=\"/userfiles/file/20130712/20130712100416_567.pdf\" target=\"_blank\">附件2：《国务院安委会关于印发国务院安委会安全生产大检查工作实施方案的通知》（安委明电[2013]2号）</a>\r\n</p>\r\n<p>\r\n	<a href=\"/userfiles/file/20130711/20130711121547_104.pdf\" target=\"_blank\"></a>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1');
