INSERT INTO `tbl_post` VALUES ('AVzUNz', '关于召开页岩气勘探开发的地球物理解决方案视频讲座的通知', 'qIFB7r', 'Uvuyqy', '2013-06-16 22:14:04', '', null, '<p>\r\n	各部门、单位：\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; 我院承办的集团科技每月一讲活动，六月份正常举办，欢迎大家到现场学习，现将有关信息通知如下：\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; <strong>讲座题目</strong>：页岩气勘探开发的地球物理解决方案\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; <strong>时&nbsp;&nbsp; &nbsp;间</strong>：2013年6月18日 上午9:00-11:00\r\n</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp; 地&nbsp;&nbsp;&nbsp; 点</strong>：集团总部B306会议室\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; <strong>主 讲 人</strong>：中国工程院彭苏萍院士\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>主要</strong><strong>内容</strong>：对页岩气勘探开发过程中的钻前阶段预测、完井阶段评价、压裂阶段监测等方面的技术和方法进行介绍。\r\n</p>', '1');
