INSERT INTO `tbl_post` VALUES ('buYNn2', '请各部门、单位及时更新本部门内网系统信息', 'vENrau', 'NjQrmi', '2013-05-29 14:31:09', '', null, '<p>\r\n	各部门、单位：\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 科研总院内网系统于5月29日已进行试运行，请各部门、单位尽快安排一名部门管理员对本部门内网系统进行信息更新和管理，部门领导（或负责人）对本部门内网所发信息进行审批。如有需要向总院系统通知公告的信息，请部门领导或分管院领导审核后，联系综合管理部在“公司通知公告”中进行发布，谢谢！\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	联系人：张锋 &nbsp; &nbsp; &nbsp;联系电话：010-5196 6662\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 综合管理部\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2013年5月29日\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1');
