INSERT INTO `tbl_post` VALUES ('eEj6Bb', '关于召开规模储能技术的现状与挑战视频讲座的通知', 'qIFB7r', 'Uvuyqy', '2013-07-23 09:18:47', '', null, '<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">各部门、单位：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>&nbsp;\r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\">由集团科环部主办，我院承办的集团科技“每月一讲”活动，七月份正常举办，欢迎大家到现场学习，现将有关信息通知如下，集团通知正文请见附件：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span>&nbsp;\r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">讲座题目：规模储能技术的现状与挑战——储热和深冷（液化空气）储能</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">时&nbsp;&nbsp;&nbsp;间：2013年7月26日&nbsp;上午9:00-11:00</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">地&nbsp;&nbsp;&nbsp;点：集团总部B306会议室</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">主 讲 人：国家“千人计划”专家、中科院过程所-英国利兹大学储能科学与技术联合研究中心主任</span><span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">丁玉龙教授</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span><span style=\"line-height:2;\">&nbsp;</span><span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">主要内容：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> <span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">介绍储能技术的发展现状，比较国内外规模化储能技术的差距，总结当前储能科学与技术的挑战。然后聚焦储热和深冷（液化空气）储能技术，涵括以下几方面：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">（1）聚焦储热/冷的原因</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">（2）储热和深冷储能的物理机理</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">（3）深冷储能&nbsp;-&nbsp;包括原理、使用范围、发展历史、效率比较、改进措施和应用举例（高调节比调峰电厂和高能效CO2捕集）</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">（4）储热&nbsp;-&nbsp;规模储热技术的研究现状，包括材料、单元和器件、系统、应用（太阳能热发电、核电调峰、分布式能源系统和风电弃风等）</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size:18px;\">附件</span><span style=\"font-size:18px;\">：1.</span><a href=\"/userfiles/file/20130723/20130723092918_45.gw\" target=\"_blank\"><span style=\"font-size:18px;\">关于召开规模储能技术的现状与挑战视频讲座的通知</span></a><span style=\"font-size:18px;\">&nbsp; </span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:24px;\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style=\"font-size:18px;\">2.</span></span><a href=\"/userfiles/file/20130723/20130723093013_440.doc\" target=\"_blank\"><span style=\"font-size:18px;\">专家简介</span></a> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	&nbsp;\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('eeQFba', '关于开展2013年科研总院新员工培训的补充通知', 'YFzIFn', 'AzIZr2', '2013-08-15 13:22:27', '', null, '<p>\r\n	总院各部门：\r\n</p>\r\n<p>\r\n	原定于2013年8月21日上午9:30在总院会议室开展的关于2013年新员工培训的讲座，现改为2013年8月23日（周五）进行，其他时间和地点不变。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	另：若有2012年入职的员工有需求参加新员工培训讲座，可主动报名。\r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;\">联系人： </span><span style=\"background-color:#FFFFFF;\">王潇彤 &nbsp; &nbsp;电话：51968660&nbsp;&nbsp;&nbsp;邮箱：xiaotong-wang@chdi.ac.cn</span>\r\n</p>\r\n<p>\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('ENfmAr', '施工现场消防安全责任制度', 'vENrau', 'NjQrmi', '2013-06-13 09:08:54', '', null, '<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">1</span></span><span style=\"font-family:SimSun;line-height:2;\">．防火制度的建立</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(1)</span></span><span style=\"font-family:SimSun;line-height:2;\">施工现场都要建立、健全防火检查制度。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(2)</span></span><span style=\"font-family:SimSun;line-height:2;\">建立义务消防队，人数不少于施工总人员的</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">10</span></span><span style=\"font-family:SimSun;line-height:2;\">％。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(3)</span></span><span style=\"font-family:SimSun;line-height:2;\">建立动用明火审批制度，按规定划分级别审批手续完善，并有监护措施。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">2</span></span><span style=\"font-family:SimSun;line-height:2;\">．消防器材的配备</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(1)</span></span><span style=\"font-family:SimSun;line-height:2;\">临时搭设的建筑物区域内，每</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">100m2</span></span><span style=\"font-family:SimSun;line-height:2;\">配备</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">2</span></span><span style=\"font-family:SimSun;line-height:2;\">只</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">10L</span></span><span style=\"font-family:SimSun;line-height:2;\">灭火器。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(2)</span></span><span style=\"font-family:SimSun;line-height:2;\">大型临时设施总面积超过</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">1200m2</span></span><span style=\"font-family:SimSun;line-height:2;\">，应备有专供消防用的积水桶</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(</span></span><span style=\"font-family:SimSun;line-height:2;\">池</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">)</span></span><span style=\"font-family:SimSun;line-height:2;\">、黄砂池等设施，上述设施周围不得堆放物品。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(3)</span></span><span style=\"font-family:SimSun;line-height:2;\">临时木工间、油漆间和木、机具间等每</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">25m2</span></span><span style=\"font-family:SimSun;line-height:2;\">配备一只种类合适的灭火器，油库危险品仓库应配备足够数量、种类合适的灭火器。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(4)24m</span></span><span style=\"font-family:SimSun;line-height:2;\">高度以上高层建筑施工现场，应设置具有足够扬程的高压水泵或其他防火设备和设施。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">3</span></span><span style=\"font-family:SimSun;line-height:2;\">．施工现场的防火要求</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(1)</span></span><span style=\"font-family:SimSun;line-height:2;\">各单位在编制施工组织设计时，施工总平面图、施工方法和施工技术均要符合消防安全要求。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(2)</span></span><span style=\"font-family:SimSun;line-height:2;\">施工现场应明确划分用火作业、易燃可燃材料堆场、仓库、易燃废品集中站和生活区等区域。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(3)</span></span><span style=\"font-family:SimSun;line-height:2;\">施工现场夜间应有照明设备，保持消防车通道畅通无阻，并要安排力量加强值班巡逻。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(4)</span></span><span style=\"font-family:SimSun;line-height:2;\">施工作业期间需搭设临时性建筑时，必须经施工企业技术负责人批准，施工结束应及时拆除。但不得在高压架空下面搭设临时性建筑物或堆放可燃物品。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(5)</span></span><span style=\"font-family:SimSun;line-height:2;\">施工现场应配备足够的消防器材，指定专人维护、管理、定期更新，保证完整好用。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(6)</span></span><span style=\"font-family:SimSun;line-height:2;\">在土建施工时，应先将消防器材和设施配备好，有条件的，应敷设好室外消防水管和消防栓。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(7)</span></span><span style=\"font-family:SimSun;line-height:2;\">焊、割作业点与氧气瓶、电石桶和乙炔发生器等危险物品的距离不得少于</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">l0m</span></span><span style=\"font-family:SimSun;line-height:2;\">，与易燃易爆物品的距离不得少于</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">30m</span></span><span style=\"font-family:SimSun;line-height:2;\">；如达不到上述要求的，应执行动火审批制度，并采取有效的安全隔离措施。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(8)</span></span><span style=\"font-family:SimSun;line-height:2;\">乙炔发生器和氧气瓶的存放之间距离不得小于</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">2m</span></span><span style=\"font-family:SimSun;line-height:2;\">，使用时，二者的距离不得小于</span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">5m</span></span><span style=\"font-family:SimSun;line-height:2;\">。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(9)</span></span><span style=\"font-family:SimSun;line-height:2;\">氧气瓶、乙炔发生器等焊割设备上的安全附件应完整有效，否则不准使用。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(10)</span></span><span style=\"font-family:SimSun;line-height:2;\">施工现场的焊、割作用，必须符合防火要求，严格执行“十不烧”规定。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(11)</span></span><span style=\"font-family:SimSun;line-height:2;\">冬期施工采用保温加热措施时，应符合以下要求：</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">1)</span></span><span style=\"font-family:SimSun;line-height:2;\">采用电热器加温，应设电压调整器控制电压，导线应绝缘良好，连接牢固，并在现场设置多处测量点。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">2)</span></span><span style=\"font-family:SimSun;line-height:2;\">采用锯末生石灰蓄热，应选择安全配方比，并经工程技术人员同意后方可使用。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">3)</span></span><span style=\"font-family:SimSun;line-height:2;\">采用保温或加热措施前，应进行安全教育，施工过程中，应安排专人巡逻检查，发现隐患及时处理。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">(12)</span></span><span style=\"font-family:SimSun;line-height:2;\">施工现场的动火作业，必须执行审批制度。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">1)</span></span><span style=\"font-family:SimSun;line-height:2;\">一级动火作业由所在单位行政负责人填写动火申请表，编制安全技术措施方案，报公司保卫部门及消防部门审查批准后，方可动火。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">2)</span></span><span style=\"font-family:SimSun;line-height:2;\">二级动火作业由所在工地、车间的负责人填写动火申请表，编制安全技术措施方案，报本单位主管部门审查批准后，方可动火。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">3)</span></span><span style=\"font-family:SimSun;line-height:2;\">三级动火作业由所在班组填写动火申请表，经工地、车间负责人及主管人员审查批准后，方可动火。</span><span></span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><span style=\"font-family:SimSun;line-height:2;\"><span style=\"font-family:SimSun;line-height:2;\">4)</span></span><span style=\"font-family:SimSun;line-height:2;\">古建筑和重要文物单位等场所动火作业，按一级动火手续上报审批。</span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span><br />\r\n</span>\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('ERFFZv', '关于2013年“端午节”放假和值班安排的通知', 'biymau', 'NjQrmi', '2013-06-03 10:40:20', '', null, '<div class=\"Section0\">\r\n	<p class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:12pt;\"></span> \r\n	</p>\r\n	<p class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">各部门、</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">各单位：</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;根据国务院办公厅规定和公司工作安排，2013年“端午节”放假三天，具体日期为6月10日(星期一)至12日(星期三)放假调休，共3天，6月8日（星期六）、9日（星期日）上班。现就端午节期间有关工作要求通知如下：</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;1、节日期间确保安全，办公区重要文件资料要妥善存放，关闭全部电器电源，关好门窗</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">；</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;2、除本部员工外，其它人员一律不得进入办公区；本部员工进入办公室须在楼下一层门卫处出示证件并登记</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">；</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;3、</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">为确保交通安全，坚决杜绝酒后、超速、超载、疲劳驾驶等严重交通违法行为。如有特殊情况需开车到外地办公事，应请示主管领导批准</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">。</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:30pt;\" class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">祝公司全体员工节日快乐！&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:15pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:21pt;\" class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:10.5pt;\"></span> \r\n	</p>\r\n	<p class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">附件：2013年</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">端午节</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">放假值班表&nbsp;</span> \r\n	</p>\r\n	<p class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>&nbsp;\r\n	</p>\r\n	<p class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">综合管理部&nbsp;</span> \r\n	</p>\r\n	<p class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">2013年6月3日</span> \r\n	</p>\r\n	<p class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span><span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>&nbsp;\r\n	</p>\r\n	<div style=\"text-align:left;\">\r\n		附件：<a href=\"/userfiles/file/20130603/20130603173246_796.doc\" target=\"_blank\">2013年端午节放假值班表</a> \r\n	</div>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:left;\" class=\"p0\">\r\n		<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span> \r\n	</p>\r\n</div>', '1');
INSERT INTO `tbl_post` VALUES ('EVbaY3', '电力安全工作规程(热力和机械部分)', 'vENrau', 'NjQrmi', '2013-06-14 13:09:40', '', null, '<a href=\"/userfiles/file/20130614/20130614131011_795.doc\" target=\"_blank\">电业安全工作规程(热力和机械部分)GB 26164.1</a>', '1');
INSERT INTO `tbl_post` VALUES ('Eviaii', '车辆交通安全管理制度', 'vENrau', 'NjQrmi', '2013-06-13 09:05:05', '', null, '<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1 </span></b><b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">安全教育及奖惩</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1.1  公司每年应组织一次车队全体职工参加的交通安全教育培训与考试。此项工作由主管安全的副总经理负责，工程部配合。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1.2  对新入队的驾驶员，必须进行时间不少于三天的交通及施工安全教育，了解本企业施工情况和道路运输特点，掌握一般安全知识（施工安全、起重及机械安全等），熟悉所驾驶车辆的机械性能和安全性能，经考试合格后方可分配工作。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1.3  特种车辆驾驶员及驻班的货车驾驶员，必须选择工作经验丰富，责任心强，持有相关证件三年以上的人员担任，同时，必须接受岗前安全教育，考核合格后方能上岗作业。</span><b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2 </span></b><b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">交通安全的一般规定</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.1  汽车驾驶员必须经过专业训练，经有关部门考试合格，发给执照后，方可独立驾驶车辆，严禁无证驾驶。开车前严禁饮酒，行车、加油时，不准吸烟、饮食和闲谈，驾驶室不准超额坐人。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.2行车前必须检查刹车、方向盘、喇叭、照明灯、信号灯等主要装置是否齐全完好，严禁带病出车。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.3  汽车在起步、调头、倒车、拐弯、过十字路口时，应“一慢二看三通过”，会车时，要做到礼让三先（先让、先慢、先行）。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.4  进入公司大院内，各单位生产区域内的车辆必须服从有关部门的管理，车速限制在</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">10</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">㎞／</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">h</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">以内。不准随意鸣号，不准乱停放。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.5  机动车载物，不准超过行驶证上核定的载重量。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.6  大型货运汽车载物，高度从地面起不准超过四米，宽度不准超出车厢，长度前端不准超过车身，后端不准超出车厢二米，超出部分不准触地。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.7  大型货运汽车挂车载物，高度从地面起不准超过三米，宽度不准超出车厢，长度前端不准超过车身，后端不准超出车厢一米。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.8  载重量在一千公斤以上的小型货运汽车载物，高度从地面起不准超过二点五米，宽度不准超出车厢，长度前端不准超出车身，后端不准超出车厢一米。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.9  载重量不满一千公斤的小型货运汽车、小型拖拉机挂车、后三轮车载物，高度从地面起不准超过二米，宽度不准超出车厢，长度前端不准超出车身，后端不准超出车厢五十厘米。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.10  严禁超限装运（超长、超宽、超高），特殊情况需运输应事先到交通管理部门办理通行手续，装载物品要捆绑稳固，载货汽车不准搭乘无关人员。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.11  货运汽车挂车、拖拉机挂车、半挂车、平板车、起重车、自动倾卸车不准载人。但拖拉机挂车和设有安全保险或乘车装置的半挂车、平板车、起重车、自动倾卸车，经车辆管理机关核准，可以附载押运或装卸人员一至五人。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.12  货车载人时，严禁超过核定人数，并应先到有关部门办理手续，载物高度超过车厢栏板时，货物上不准乘人。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.13  货车进入高速公路时，除驾驶室和车厢经核准设有的固定座位外，其它任何部位不准载人。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.14  货车开动时，应待人员上下稳定，才能关门起步，严禁抢上跳下，脚踏板和保险杠严禁站人，起步时要查看周围有无人员和障碍物。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.15  机动车辆停车时要选择适当地点，不准乱停乱放，停车后应将钥匙取下，拉紧手刹车制动器，熄火挂档。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.16  凡装载易燃易爆物品的车辆严禁进入公司大院内停放。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.17  车辆进入高速公路应当配备故障车警告标志牌；规定安装安全带的车辆，其驾驶员和前排乘车人必须系安全带。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2.18  机动车辆在高速公路上行驶时，乘车人不准站立，不准向车外抛洒物品。</span><b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3 </span></b><b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">安全行车注意事项</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.1  一般平直道路行车</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.1.1保持适当的行车间距，避免追尾撞车</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">发生。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.1.2控制好车速，在可见距离范围内，遇到情况随时能把车停住，以防万一。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.1.3遵守右侧通行规则。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.1.4注意观察其它车辆行人动态，设法避免在混合交通道路上发生冲突点和交织点。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2  会车</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.1会车时，保持适当的侧向距离。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.2会车时，前方有障碍物的车辆必须主动让无障碍物的车辆先行，避免争道行驶。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.3会车时，如遇有超越的车辆，应主动让车，使对方通过，防止形成会车和超越车构成三角形式。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.4在山地转弯处会车，必须严格遵守右侧通行的规则。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.5在泥泞、光滑路面上会车时，应降低车速且严禁使用制动、猛打方向、猛加油、猛减速等易造成侧滑的操作。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.6在视线不清的情况下会车，要减慢车速，开小灯行驶，主动注意避让；夜间会车，在距对方</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">150</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">米时，要变远光灯为近光灯，当距离</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">50</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">米左右时，要互闭大灯而用小灯，不准用防雾灯；在会车中，目光要避开光亮，而应移向黑暗处，避免会车造成目眩现象。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.7尽量避免在陡坡上会车，一旦会车时，下坡车要给上坡车让路。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.2.8拖带挂车的汽车会车时，应加大侧向间距，尽量避开转弯时会车。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.3  超车</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.3.1做好超车准备，在保证安全的情况下，前车示意允许后超越，被超越车发现超车信号，应主动靠右，适当减速，做到安全礼让。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.3.2掌握好超车距离，不要给被超越车感到突然。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.3.3避免迎面来车时交会超车。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.3.4严禁超越正在超车的车辆，避免形成双重超车。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.3.5在干线上行驶，左侧有支线时不能超车，右侧道路会合点处也不能超车。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.4  坡道行车</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.4.1认真检查车辆的技术状况，必须保持制动器、转向装置、轮胎等完好的技术状态。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.4.2控制好车速，特别要掌握好下坡行驶速度。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.4.3注意防止溜车。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.4.4汽车行驶在光滑路面的坡道时，应加大行车间距。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.4.5汽车上坡时，前边有车应该适当延长车距。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3.4.6通过傍山险路时，要特别注意观察，尽量靠近峭壁一侧行车，要注意车上人员、物质、蓬杆等不要与山石、树木刮碰，如遇到会车，尽量靠近峭壁一侧相会，以免临近山谷边沿，容易发生危险。</span><b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4 </span></b><b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">的预防</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4.1  违章是事故的前因，</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">是违章的后果。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4.2  加强车辆管理，提高车辆完好率。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4.2.1车队严格监督车辆完好情况和安全运行情况。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4.2.2完善车辆使用、保养、修理等规章制度。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4.2.3严格车辆检查制度，除定期组织检查外，平时应坚持出车前检查。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4.2.4提高驾驶员的素质，定期组织驾驶员参加培训学习，提高驾驶员年度审验工作质量，使年度审验达到预期目的。</span><b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5 </span></b><b><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">的处理</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.1  交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">的定性</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">一般交通事故：指一次</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">造成重伤</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人至</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人（或者轻伤</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人以上的群伤），或者直接经济损失在</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">万元至</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">万元的</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.1.2重大交通事故：指一次</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">造成死亡</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人至</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人（或者重伤</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人以上</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">10</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人以下的群伤），或者直接经济损失在</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">万元以上</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">6</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">万元以下的</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.1.3特大交通事故：指一次</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">造成死亡</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人以上（或者重伤</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">11</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人以上；或者死亡</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人，同时重伤</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">6</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人以上；或者死亡</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人，同时重伤</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">人以上的群伤），或者直接经济损失</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">6</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">万元以上的</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.2  交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">的上报</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.2.1发生交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">的车辆必须立即停车，当事人必须保护现场，抢救伤者和财产（必须移动时应标明位置），并迅速报告公安机关或者执勤交通警察，听候处理。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.2.2当事人发生一般交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">后，必须及时向队里报告，并同时向公司安全监察部、车队汇报。（在交通警察部门结案后，上报裁定资料）</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.2.3发生重大及以上交通事故，</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">单位必须在</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">24</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">小时内，用电话或者传真快速向公司安全监察部门、车队报告，次日上报事故的简单概况（事故发生的时间、地点、经过、人员伤亡或者车辆受损等情况）。在交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">结案后</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">15</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">天内上报交通</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">调查报告书及有关资料。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5.2.4发生交通事故，车队必须立即派人前往</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">现场协助调查处理。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">发生有人员伤亡的重大交通事故，公司安全监察部接到报告后，派人协助</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">事故</span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">调。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<br />\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('F3UR3y', '项目工程安全管理办法', 'vENrau', 'NjQrmi', '2013-06-14 10:00:22', '', null, '<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（一）组织机构</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">1、建立安全生产文明施工领导小组，由项目经理任组长，技术负责人、安全员为副组长，施工员、材料员等其他（根据需要）为组员，要明确施工，明确岗位责任，奖罚兑现。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2、根据施工组织设计和公司要求，结合工地实际情况，制定工地安全生产、文明施工制度的奖罚办法。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">3、凡安全防护措施不齐备，存在严重隐患。或管理人员盲目指挥，安全无保证一时，安全员有权令其停工，坚持一票否决制。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二）工程安全生产基本要求</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">1、贯彻“安全第一，预防为主”方针，管生产必须管安全，做到安全工作与生产任务同时计划、同时布置、同时检查、同时总结、同时评比，安全防护措施要提前施工，经验收合格后交付使用。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2、加强安全生产的宣传教育，工人报到后，按在册人数开会宣传有关法规、规章制度，由项目经理与每个人签定《安全生产合同书》，未经教育培训和签定合同书的人员不准上岗作业。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">3、每个分部分项工程，按工种由项目经理或施工员，向班组做安全交底，交底要求具体、明确，要有正式的书面记录和签字。特殊工种，如架子工、塔机工、</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">电工</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">等分别按安全技术标准规程做专项技术交底，形成书面文字，签字后交付作业。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">4、每月安全领导小组作一次工地的安全检查，平时，针对存在的问题，及时分片或分组开会教育，令其整改。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">5、每天安全员和班组长向班组安全讲话，检查安全准备工作，紧跟生产制度，进行监护，对违章作业，及时纠正，对冒险作业，坚决制止。其教育或均需记录签字，以示慎重敲响警钟。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">6、严格“三件宝“的使用，上班不戴安全帽的高空作业不系安全带的一次罚款5-30元，不听劝阻的，停止其作业。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">安全网间隔一层搭设，必须牢固可靠，符合规范要求。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">7、严禁酒后上班，不准打赤脚、穿拖鞋、硬底鞋上班。不准高处向下扔物品，违规罚款5-30元。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">8、“四口”，坑、井、易燃易爆场所，电源装置和危险地带，要有护栏，夜间设红灯示警。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">9、特殊工种，如</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">电工</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">、架子工、焊工，起重工、机械工等必须持证上岗，无证人员不准进行操作。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">10、机械设备有漏电保护装置和接零接地，施工电线架空搭设，不准线拖在地上，开关、插座集中在配电箱内，有防雨装置由</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">电工</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">专职管理。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">11、经常对机械设备进行检查维护保养，每天上班试运行运转后才能进入施工操作，严禁塔机等机械设备带病作业。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">12、易发生触电</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">事故</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">地方和移动照明，使用36伏低压行灯。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">13、搅拌台、钢筋制作场、木工制作场，必须在牢固棚架下操作。磨石机、水泵操作和</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">电工</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">等，穿绝缘鞋、戴绝缘手套作业。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">14、木工房、库房、工棚、厨房等临时设施，配套足够的灭火器材，挂有醒目的警示标志，严禁吸烟。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">15、暑期施工，有相应的十滴水，清凉油等，防暑药品，爆晒地点，应有遮荫措施。对粉尘，有毒气体有保护措施。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">16、若发生安全</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">事故</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">，除积极挽救伤员和上报外，必须按“三不放过”原则，查明原因，明确责任，切实整改。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（三）项目工程安全管理基础资料</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">安全档案资料是安全基础工作之一，也是检查考核落实安全生产责任制的治疗和反映工作情况的重要依据，同时是为安全管理工作提供分析、研究、掌握安全动态，以便改进工作，达到预测、预报、预防</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">事故</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">的目的，必须及时作好作全，真实反映情况。要作好的治疗主要有：</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（一）工地的安全组织机构及岗位责任制；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（二）安全生产、文明施工的各项制度；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（三）安全生产宣传教育、培训情况；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（四）安全技术资料（计划、措施）、交底、检查验收；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（五）班组安全活动情况（包括违章作业的教育、处理）；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">6、组安全工种人员上岗证复印件（编汇成册，注明上、离岗时间和原因）；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">7、奖罚资料；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">8、伤亡</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">事故</span><span style=\"font-size:12px;font-family:SimSun;line-height:2;\">档案；</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">9、有关文件、会议记录。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">（四）安全检查</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">1、按公司每年开展的项目，逐一检查。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">2、对安全管理基础资料逐一核实，审查。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">3、作出评定、提出处理意见和整改意见。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-size:12px;font-family:SimSun;line-height:2;\">4、复查整改情况。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<br />\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('FBBFNb', '消防监督检查规定', 'vENrau', 'NjQrmi', '2013-06-09 14:49:19', '', null, '<a href=\"/userfiles/file/20130609/20130609144930_486.pdf\" target=\"_blank\">消防监督检查规定（公安部107号令）</a><br />', '1');
INSERT INTO `tbl_post` VALUES ('femQVv', '中国华电集团科学技术研究总院科技项目管理办法', 'qIFB7r', 'Uvuyqy', '2012-08-21 15:58:03', '', null, '<p>\r\n	<a href=\"/userfiles/file/20130716/20130716154709_40.doc\" target=\"_blank\">关于印发《中国华电集团科学技术研究总院科技项目管理办法》的通知</a> \r\n</p>\r\n<a href=\"/userfiles/file/20130716/20130716154946_886.doc\" target=\"_blank\">中国华电集团科学技术研究总院科技项目管理办法</a> \r\n<p>\r\n	&nbsp;\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('FFbmqe', '中国华电集团公司火电风电企业反恐怖防范指导意见（试行）', 'vENrau', 'NjQrmi', '2013-06-03 15:45:11', '', null, '<a href=\"/userfiles/file/20130609/20130609154532_970.doc\" target=\"_blank\">中国华电集团公司火电风电企业反恐怖防范指导意见（试行）</a>', '1');
INSERT INTO `tbl_post` VALUES ('FRj6Z3', '西塞山公司春检抓短板、栓“牛鼻子”', 'vENrau', 'NjQrmi', '2013-06-05 17:13:14', '', null, '<p style=\"font-family:songti;font-size:medium;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:28px;font-family:FangSong_GB2312;\">&nbsp; &nbsp; 3</span><span style=\"font-size:18px;line-height:28px;font-family:FangSong_GB2312;\">月13日</span><span style=\"font-size:18px;line-height:28px;font-family:FangSong_GB2312;\">上午，西塞山公司组织召开春检动员大会，</span><span style=\"font-size:18px;line-height:28px;font-family:FangSong_GB2312;\">目的是使全体干部员工进一步增强做好安全工作的责任感和紧迫感，强化落实安全责任的执行力和专注力。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">&nbsp; &nbsp; 会上，安生部宣读了关于春检活动安排。副总经理兼总工程师黎亚兵从人员思想状况、设备状况和管理状况三个方面剖析了公司目前存在的主要问题，要求各部门、外委单位必须保持清醒的头脑，严格按照上级有关春检工作的部署，结合季节特点及部门工作实际，查找安全生产存在的短板，采取有效措施深挖隐患、消除隐患，保证春检取得实效。他指出：春检应坚持“三重”抓推进，即坚持重监管、重排查、重整改，坚持“四个严防”保实效，即严防骄傲自满，严防责任松懈，严防监管缺失，严防违章反弹，重点全面推进安全生产十项工作。</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">一是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">积极推进安评整改的收官工作</span></span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">，确保主要问题整改率大于80％，一般问题整改率为100％；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">二是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">积极推进安全生产标准化上台阶工作</span></span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">，规范安全</span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">生产行为，控制事故风险；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">三是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">强化液氨灌区重大危险源的全过程管控工作；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">四是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">吸取有关事故教训，深化</span></span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">作业环境本质安全工作</span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">，现场作业保证各项安全防护措施执行到位、检查到位、监督到位；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">五是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">加快推进</span></span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">“无泄漏工厂”创建工作</span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">，3月份力争完成#3机组“无泄漏机组”验收；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">六是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">按照湖北公司的部署，有效推进</span></span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">安全生产示范化工作</span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">七是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">建立反违章长效机制，</span></span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">狠抓危险点的预控与分析，坚持“不安全不工作”原则，</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">现场作业严格</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">“五想五不干”，即一想安全风险，不清楚不干，二想安全措施，不完善不干，三想安全工具，未配备不干，四想安全环境，不合格不干，五想安全技能，不具备不干，</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">防止发生人身事故</span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">八是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">保证隐患排查治理工作常态化</span></span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">，从细微入手、从预控着手，防范细小的隐患引发“蝴蝶效应”，杜绝安全生产的龙卷风；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">九是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">狠抓外委工程安全管理工作，重点</span></span><span style=\"font-size:14pt;\"><span style=\"font-size:18px;font-family:FangSong_GB2312;\">确保交叉作业、高处作业、受限空间内作业、起重作业等高危作业安全可控、在控；</span><b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">十是</span></b><span style=\"font-size:18px;font-family:FangSong_GB2312;\">牢固树立“大安全”管理理念，抓好技术监督、掺煤配烧，防止“四管爆漏”引起机组“非停”，同时抓好</span></span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">交通、消防责任制的落实。</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">黎总生动地将安全管理比作一头倔强的牛，要求春检就是要剑指安全管理上的短板，栓住短板这个“牛鼻子”，引导安全管理向更好的方向迈进。<br />\r\n</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">&nbsp; &nbsp; 总经理钱勇指出，春检要</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">抓好“两个突出”，突出基础管理和突出补强薄弱环节，严把“三道关口”，即把好预防关、把好自查关、把好奖惩关，扎扎实实落实黎总布置的十项重点工作。钱总要求</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">安生部引进交通管理中的红、黄灯规则，策划好</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">“</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">周亮灯</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">”</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">安全风险预控机制，把春检中发现的问题及危险点标识为</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">“</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">黄灯</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">”</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">，将每周出现的重大隐患或问题标识为</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">“</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">红灯</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">”</span><span style=\"font-size:18px;font-family:FangSong_GB2312;\">，对被亮黄、红灯的问题逐一督办，克服以往安全检查的盲目性，使安全管理有限的人力、物力、财力得到充分发挥，最大限度地发挥春检活动的作用，为公司的生产经营和发展创造一个良好的安全环境。<br />\r\n</span><span style=\"font-size:18px;line-height:28px;font-family:FangSong_GB2312;\">&nbsp; &nbsp;&nbsp;各部门主任、外委单位经理会上均做了表态发言，表示</span><span style=\"font-size:18px;line-height:28px;font-family:FangSong_GB2312;\">将</span><span style=\"font-size:18px;line-height:28px;font-family:FangSong_GB2312;\">以积极饱满的工作热情、扎实严谨的工作作风，全面有序推进春检工作。</span>\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('fUnEne', '中华人民共和国道路交通安全法实施条例', 'vENrau', 'NjQrmi', '2013-06-03 09:01:32', '', null, '<a href=\"/userfiles/file/20130609/20130609142602_602.pdf\" target=\"_blank\">中华人民共和国道路交通安全法实施条例</a>', '1');
INSERT INTO `tbl_post` VALUES ('fuQFRn', '关于2013年“端午节”放假和值班安排的通知', 'vyEzUb', 'NjQrmi', '2013-06-03 10:49:02', '', null, '<p class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">各部门、</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">各单位：</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;根据国务院办公厅规定和公司工作安排，2013年“端午节”放假三天，具体日期为6月10日(星期一)至12日(星期三)放假调休，共3天，6月8日（星期六）、9日（星期日）上班。现就端午节期间有关工作要求通知如下：</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;1、节日期间确保安全，办公区重要文件资料要妥善存放，关闭全部电器电源，关好门窗</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">；</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;2、除本部员工外，其它人员一律不得进入办公区；本部员工进入办公室须在楼下一层门卫处出示证件并登记</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">；</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"><br />\r\n</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;3、</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">为确保交通安全，坚决杜绝酒后、超速、超载、疲劳驾驶等严重交通违法行为。如有特殊情况需开车到外地办公事，应请示主管领导批准</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">。</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';background:#ffffff;font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:30pt;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:16px;\">祝公司全体员工节日快乐！&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:15pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';color:#000000;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">附件：2013年</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">端午节</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">放假值班表&nbsp;</span> \r\n</p>\r\n<p class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>&nbsp;\r\n</p>\r\n<p class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">综合管理部&nbsp;</span> \r\n</p>\r\n<p class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:\'仿宋_GB2312\';font-size:16px;\">2013年6月3日</span> \r\n</p>\r\n<p class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span><span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>&nbsp;<a href=\"/userfiles/file/20130603/20130603173246_796.doc\" target=\"_blank\"> 附件：2013年端午节放假值班表</a>\r\n</p>\r\n<p style=\"text-align:right;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>\r\n</p>\r\n<p style=\"text-align:right;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>\r\n</p>\r\n<p style=\"text-align:right;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:15pt;\"></span>\r\n</p>', '1');
