INSERT INTO `tbl_post` VALUES ('J7bQna', '关于科研总院内网系统试运行的通知', 'vENrau', 'NjQrmi', '2013-05-29 09:46:51', '', null, '<p>\r\n	各部门、单位：\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; 为了积极响应集团公司号召，提高企业信息化水平，科研总院内网系统，经过近两个月的策划、筹备和建设，现已基本建成，并于5月29日进行试运行，请各部门、单位积极组织本部门员工进行测试、运行，发现问题或有好的建议，请及时和综合管理部联系，谢谢！\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; 联系人：张锋 &nbsp; &nbsp; &nbsp; 联系电话：010-5196 6662\r\n</p>\r\n<div class=\"__kindeditor_paste__\">\r\n	<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n		&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 综合管理部\r\n	</p>\r\n	<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n		&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2013年5月29日\r\n	</p>\r\n</div>', '1');
INSERT INTO `tbl_post` VALUES ('jAFjai', '关于组织观看2013年安全生产警示教育片的通知', 'vENrau', 'NjQrmi', '2013-06-05 15:05:01', '', null, '<p>\r\n	各部门、单位：\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 为积极响应集团公司<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\">2013年“安全生产月”活动的要求，提高总院系统干部员工的安全生产意识，将于6月7日（周五）组织观看<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\">2013年安全生产警示教育片——《生产安全事故典型案例盘点（2013版）》（时长约1.5小时），请各部门、单位全体人员安排好工作前来观看，并鼓励大家进行安全大讨论，书写观后感或安全宣传材料向集团公司报送，谢谢！</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\">&nbsp; &nbsp; 时间：6月7日（周五）下午14:00</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\">&nbsp; &nbsp; 地点：总院本部会议室（中海地产广场东塔11层）</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\">&nbsp; &nbsp; 人员：总院全体干部员工</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;综合管理部</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\"><span style=\"color:#333333;font-family:宋体;line-height:20px;background-color:#FEFEFE;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2013年6月5日</span></span> \r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('JNfM7v', '关于已购买Adobe Acrobat软件的通知', 'qIFB7r', 'Uvuyqy', '2013-08-20 09:33:05', '', null, '<p style=\"text-align:left;text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">总院各部门、各单位：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">为做好科研总院软件正版化工作，满足大家使用需求，现购置了一套Adobe&nbsp;Acrobat&nbsp;XI专业版（单机版），已安装在小图书馆公用电脑上，欢迎大家使用。</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">Adobe&nbsp;Acrobat&nbsp;XI专业版新增了将PDF文件转换成PowerPoint或Excel文档、轻松编辑PDF文件的文本和图片、多个文件合并成一个PDF等功能，改进了将Word、Excel、PowerPoint转化成PDF文件等功能，详细功能请查看以下链接：</span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"><a href=\"http://www.adobe.com/cn/products/acrobatpro/features._sl_id-contentfilter_sl_featuredisplaytypes_sl_all.html\"><span style=\"font-size:24px;\">http://www.adobe.com/cn/products/acrobatpro/features._sl_id-<span style=\"line-height:2;font-size:24px;\">contentfilter_sl_featuredisplaytypes_sl_all.html</span></span></a></span> \r\n</p>\r\n<p style=\"text-align:left;text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span> \r\n</p>\r\n<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"> \r\n<p style=\"text-align:center;text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;科研项目管理部</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\" align=\"right\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">二零一三年八月二十日</span> \r\n</p>\r\n</span>', '1');
