INSERT INTO `tbl_post` VALUES ('niA73q', '关于印发公司《财务支出和报销管理办法（试行）》的通知', 'QZNRFn', 'ZfuaUv', '2013-08-28 10:40:04', '2012年1月11日发布的关于印发公司《财务支出和报销管理办法（试行）》的通知及附件。', null, '<p>\r\n	<a href=\"/userfiles/file/20130828/20130828104743_4.doc\" target=\"_blank\">关于印发公司《财务支出和报销管理办法（试行）》的通知</a>\r\n</p>\r\n<p>\r\n	<a href=\"/userfiles/file/20130828/20130828104822_408.doc\" target=\"_blank\">附件：公司财务支出和报销管理办法（试行）</a>\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('NVfUnq', '企业安全文化管理的几个手段', 'vENrau', 'NjQrmi', '2013-06-13 10:50:29', '', null, '<p>\r\n	<span style=\"line-height:2;\">&nbsp; &nbsp; &nbsp;一：是要坚持不断提高员工整体素质</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;\">&nbsp; &nbsp; 人是企业财富的创造者，是企业发展的的动力和源泉。只有高素质的人才、高质量的管理、切合企业实际的经营战略，才能在激烈的市场竞争中立于不败之地。因此，企业安全文化建设，要在提高人的素质上下功夫。近几年来，企业发生的各类安全</span><span style=\"line-height:2;\">事故</span><span style=\"line-height:2;\">，大多数是员工处于侥幸、盲目、习惯性违章造成的。这就需要从思想上、心态上去宣传、教育、引导,使员工树立正确的安全价值观，这是一个微妙而缓慢的心理过程，需要我们做艰苦细致的教育工作。提高员工安全文化素质的最根本途径就是根据企业的特点，进行安全知识和技能教育、安全文化教育，以创造和建立保护员工身心安全的安全文化氛围为首要条件。同时，加强安全宣传，向员工灌输“以人为本，安全第一”、“安全就是效益、安全创造效益”、“行为源于认识，预防胜于处罚，责任重于泰山”、“安全不是为了别人，而是为了你自己”等安全观，树立“不作没有把握的事”的安全理念，增强员工的安全意识，形成人人重视安全，人人为安全尽责的良好氛围。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;\">&nbsp; &nbsp; 二：是要坚持开展丰富多彩的安全文化活动</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;\">&nbsp; &nbsp; 企业要增强凝聚力，当然要靠经营上的高效益和职工生活水平的提高，但心灵的认可、感情的交融、共同的价值取向也必不可少。开展丰富多彩的安全文化活动，是增强员工凝聚力，培养安全意识的一种好形式。因此，要广泛地开展认同性活动、娱乐活动、激励性活动、教育活动;张贴安全标语、提合理化建议;举办安全论文研讨、安全知识竞赛、安全演讲、</span><span style=\"line-height:2;\">事故</span><span style=\"line-height:2;\">安全展览;建立光荣台、违章人员曝光台;评选最佳班组、先进个人;开展安全竞赛活动，实行安全考核，一票否决制。通过各种活动方式向员工灌输和渗透企业安全观，取得广大员工的认同。对开展的\"安全生产年\"、\"百日安全无</span><span style=\"line-height:2;\">事故</span><span style=\"line-height:2;\">\"、\"创建平安企业\"等一系列活动，都要与实际相结合，其活动最根本的落脚点都要放在基层车间和班组，只有基层认真的按照活动要求结合自身实际，制定切实可行的实施方案，扎扎实实的开展，不走过场才会收到实效，才能使安全文化建设更加尽善尽美。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;\">&nbsp; &nbsp; 三：是要坚持树立大安全观</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;\">&nbsp; &nbsp; 企业发生</span><span style=\"line-height:2;\">事故</span><span style=\"line-height:2;\">，绝大部分是职工的安全意识淡薄造成的，因此，以预防人的不安全行为生产为目的，从安全文化的角度要求人们建立安全新观念。比如上级组织安全检查是帮助下级查处安全隐患，预防</span><span style=\"line-height:2;\">事故</span><span style=\"line-height:2;\">，这本是好事，可是下级往往是百般应付，恐怕查出什么问题，就是真的查出问题也总是想通过走关系，大事化小、小事化了。又如安监人员巡视现场本应该是安全生产的\"保护神\"，可是现场管理者和操作人员利用\"你来我停，你走我干\"的游击战术来对付安监人员。还有，本来\"我要安全\"是员工本能的内在需要，可现在却变成了管理者强迫被管理者必须完成的一项硬性指标……上述的错误观念一日不除，正确的安全理念就树立不起来，安全文化建设就永远是空中楼阁。我们应利用一切宣传媒介和手段，有效地传播、教育和影响公众，建立大安全观，通过宣传教育途径，使人人都具有科学的安全观、职业伦理道德、安全行为规范，掌握自救、互救应急的防护技术。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('NZ3UVv', '关于科研总院内网系统试运行的通知', 'vENrau', 'NjQrmi', '2013-05-29 17:33:01', '', null, '<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	各部门、单位：\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; 为了积极响应集团公司号召，提高企业信息化水平，科研总院内网系统，经过近两个月的策划、筹备和建设，现已基本建成，并于5月29日进行试运行，请各部门、单位积极组织本部门员工进行测试、运行，发现问题或有好的建议，请及时和综合管理部联系，谢谢！\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; 联系人：张锋 &nbsp; &nbsp; &nbsp;联系电话：010-5196 6662\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 综合管理部\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2013年5月29日\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('NzMJBr', '生产安全事故应急预案管理办法', 'vENrau', 'NjQrmi', '2013-06-04 09:43:27', '', null, '<a href=\"/userfiles/file/20130609/20130609104354_852.doc\" target=\"_blank\">生产安全事故应急预案管理办法-国家安监总局令第17号</a>', '1');
INSERT INTO `tbl_post` VALUES ('QfyuEv', '安全施工责任制度', 'vENrau', 'NjQrmi', '2013-06-14 10:28:11', '', null, '<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">1 </span></b><b><span style=\"font-family:SimSun;line-height:2;\">公司安全委员会职责</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1.1  安全委员会是公司安全管理权力机构；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1.2 \r\n 审定公司年度的安全工作目标计划。定期和不定期的召开会议，研究解决安全施工（生产）中的重大事项和问题；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">1.3 \r\n 审批重伤及以上的</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">报告书，对责任者作出处理决定。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">2 </span></b><b><span style=\"font-family:SimSun;line-height:2;\">公司领导的安全职责</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.1 \r\n公司主要负责人安全职责</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.1.1　建立、健全本单位安全生产责任制；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.1.2组织制定本单位安全生产规章制度和</span><span style=\"font-family:SimSun;line-height:2;\">操作规程</span><span style=\"font-family:SimSun;line-height:2;\">；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.1.3保证本单位安全生产投入的有效实施；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.1.4督促、检查本单位的安全生产工作，及时消除生产安全</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">隐患；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.1.5组织制定并实施本单位的生产安全</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">应急救援</span><span style=\"font-family:SimSun;line-height:2;\">预案</span><span style=\"font-family:SimSun;line-height:2;\">；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.1.6及时、如实报告生产安全</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">2.2 \r\n 公司其他负责人的安全职责</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">公司其他负责人在各自职责范围内，协助公司主要负责人搞好安全生产管理工作。</span><span> </span>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">3 </span></b><b><span style=\"font-family:SimSun;line-height:2;\">公司职能部门的安全职责</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3.1 \r\n 职能部门负责人按照本部门的职责，组织有关工作人员做好安全生产责任制的落实，对本部门职责范围的安全生产工作负责；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">3.2 \r\n 职能部门工作人员在本人职责范围内做好安全生产工作。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">4 </span></b><b><span style=\"font-family:SimSun;line-height:2;\">施工单位（项目）负责人安全职责</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.1　施工单位主要负责人（项目部经理）安全职责</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.1.1建立、健全本单位（项目）安全生产责任制；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.1.2组织实施公司、分公司或项目部制订的安全生产规章制度和</span><span style=\"font-family:SimSun;line-height:2;\">操作规程</span><span style=\"font-family:SimSun;line-height:2;\">；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.1.3保证本单位（项目）安全生产投入的有效实施；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.1.4督促、检查本单位（项目）的安全生产工作，及时消除生产安全</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">隐患；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.1.5组织制定并实施本单位（项目）的生产安全</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">应急救援</span><span style=\"font-family:SimSun;line-height:2;\">预案</span><span style=\"font-family:SimSun;line-height:2;\">；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.1.6及时、如实报告生产安全</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">4.2 \r\n 施工单位其他负责人（项目部副经理、技术负责人）安全职责</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">施工单位其他负责人（项目部副经理）在各自职责范围内，协助本单位（项目）主要负责人搞好安全生产管理工作。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b> </b>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">5 </span></b><b><span style=\"font-family:SimSun;line-height:2;\">施工单位（项目）职能科室的安全职责</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5.1 \r\n 施工单位（项目）职能科室负责人按照本部门的职责，组织有关工作人员做好安全生产责任制的落实，对本部门职责范围的安全生产工作负责；</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">5.2 \r\n 施工单位（项目）职能科室工作人员在本人职责范围内做好安全生产工作。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18.05pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:SimSun;line-height:2;\">6 </span></b><b><span style=\"font-family:SimSun;line-height:2;\">施工班组人员安全职责</span><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1 \r\n 施工班组长安全职责</span><b><span></span></b>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1.1对本班组人员在施工过程中的安全和健康负直接管理责任。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1.2负责组织学习与执行有关安全施工的规定、规程和措施，并向本班组人员如实告知作业场所和工作岗位存在的危险因素、防范措施以及</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">应急措施。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1.3负责组织每周一次的现场安全检查及每周一次的安全日活动，及时总结与布置安全工作，并做好各项安全活动记录。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1.4　根据本队（班）人员情况，合理分配工作，工作中督促和检查施工人员正确使用劳保用品和工器具，及时纠正违章作业。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1.5在工程项目开工前，负责组织本队（班）参加施工的人员接受安全交底并签字。对未签字的人员，不得安排参加该项目的施工。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1.6负责解决施工（生产）中存在的问题，凡不能解决的隐患应及时向单位领导汇报，采取措施。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.1.7组织本班组人员分析</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">原因，吸取教训，及时改进安全工作。 </span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.2  施工班组技术员安全职责</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.2.1负责本队（班）安全技术工作。协助队（班）长组织本队（班）人员学习与执行上级有关安全施工的规程、规定和安全施工措施。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.2.2做好一般施工项目的安全施工措施的编制和安全施工作业票的填写以及交底工作，并监督检查措施的执行情况。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.2.3协助队（班）长进行本队（班）施工场所的安全检查和施工项目开工前安全施工条件的检查。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.2.4参加本队（班）的事故（包括未遂</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">）调查分析。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.3 \r\n 施工人员安全职责</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.3.1认真学习并自觉执行安全施工的有关规定、规程和措施，服从管理，不违章作业。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.3.2正确佩戴使用、维护和保管好工器具和劳动防护用品、用具，并在使用前进行认真检查。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.3.3特种作业及特殊工艺作业必须取证上岗。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7.3.4工作前应对工作现场进行检查，做好安全措施，以确保不伤害自己，不伤害他人，不被他人伤害。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.3.5接受安全施工（生产）教育和培训，掌握本职工作所需的安全施工（生产）知识，提高安全施工（生产）技能，增强</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">预防和应急处理能力。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.3.6对无安全施工措施和未经安全施工交底的施工项目，有权拒绝施工并可越级上告。有权制止他人违章；有权拒绝违章指挥。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">6.3.7发现直接危及人身安全的紧急情况时，有权停止作业或者在采取可能的应急措施后撤离作业现场。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;line-height:2;\">7.3.8发生人身事故时应立即抢救伤者，保护事故现场并及时向上级报告，调查事故时必须如实反映情况，分析</span><span style=\"font-family:SimSun;line-height:2;\">事故</span><span style=\"font-family:SimSun;line-height:2;\">时应积极提出改进意见和防范措施。</span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<br />\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('qiEzAj', '企业安全生产培训', 'vENrau', 'NjQrmi', '2013-06-13 13:21:50', '', null, '<a href=\"/userfiles/file/20130613/20130613132237_526.ppt\" target=\"_blank\"><a href=\"/userfiles/file/20130613/20130613132237_526.ppt\" target=\"_blank\">企业安全生产培训ppt</a></a>', '1');
INSERT INTO `tbl_post` VALUES ('QJR3Mj', '关于科研总院安全管理系统开通试运行的通知', 'vENrau', 'NjQrmi', '2013-06-05 11:28:34', '', null, '<p>\r\n	各部门、单位：\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 为进一步加强安全管理、提高安全意识，树立”安全责任重于泰山“的理念，科研总院安全管理系统经过近一个月的策划、筹备和建设，现已开通试运行，链接入口在总院内网首页左侧“总院快捷入口”下方，请各部门、单位试用并提出宝贵意见，同时请组织本部门员工进行安全法规、行业规章及安全制度的学习。\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 提醒：进入相关项目建设、施工、大修、改造等现场时，请遵守项目单位、施工单位、业主单位的有关安全规章制度，遵守安全法规，确保安全无事故。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 综合管理部\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2013年6月5日\r\n</p>', '1');
