INSERT INTO `tbl_post` VALUES ('YFfau2', '关于召开中国城镇化的战略选择视频讲座的通知', 'qIFB7r', 'Uvuyqy', '2013-08-20 09:46:46', '', null, '<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">各部门、单位：</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span><span style=\"line-height:2;\">&nbsp;</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"><span style=\"line-height:2;font-size:18px;\">由集团科环部主办，我院承办的集团科技“每月一讲”活动，八月份正常举办，欢迎大家到现场学习，现将有关信息通知如下，</span><span style=\"line-height:2;\"><span style=\"font-size:18px;\">集团通知正文请见附件</span>：</span></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">讲座题目：中国城镇化的战略选择</span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">时&nbsp;&nbsp;&nbsp;间：2013年8月22日 下午14:00-16:00</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">地&nbsp;&nbsp;&nbsp;点：集团总部B306会议室</span><span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\"><span style=\"line-height:2;font-size:24px;\"><span style=\"font-size:18px;\">主&nbsp;讲 人 ：魏后凯教授</span>，</span><span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">中国社会科学院城市发展与环境研究所副所长、研究员</span></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\">主要内容：中国城镇化的进程、评价和战略转型，构建科学合理的城市化格局，能源企业在新型城镇化进程中的战略选择。</span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\"><span style=\"line-height:2;font-size:18px;\">附件：1.</span><a href=\"http://office.chdi.ac.cn/userfiles/file/20130820/20130820095228_751.gw\" target=\"_blank\"><span style=\"line-height:2;font-size:18px;\">关于召开中国城镇化的战略选择视频讲座的通知</span></a></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\"><span style=\"line-height:2;font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;2.</span><a href=\"http://office.chdi.ac.cn/userfiles/file/20130820/20130820095402_640.doc\" target=\"_blank\"><span style=\"line-height:2;font-size:18px;\">专家简介</span></a></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:16pt;\"><span style=\"line-height:2;font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"line-height:2;font-size:18px;\">科研项目管理部</span></span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\" align=\"right\">\r\n	<span style=\"line-height:2;font-family:\'仿宋_GB2312\';font-size:18px;\">二零一三年八月二十日</span> \r\n</p>\r\n<p style=\"text-indent:2em;\" class=\"p0\">\r\n	<span style=\"font-family:\'仿宋_GB2312\';font-size:16pt;\"></span><span style=\"line-height:2;\">&nbsp;</span> \r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('yInIRn', '女工特殊保护管理制度', 'vENrau', 'NjQrmi', '2013-06-13 11:00:20', '', null, '<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">1 \r\n不得在女职工怀孕期、产期、哺乳期降低其基本工资，或者解除劳动合同。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">2 \r\n禁止安排女职工从事国家规定的第四级体力劳动强度的劳动和其他女职工禁忌从事的劳动。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">3 \r\n女职工在月经期间，所在单位不得安排其从事高空、低温、冷水和国家规定的第三级体力劳动强度的劳动。</span><span><br />\r\n</span>\r\n	<p>\r\n		<span style=\"line-height:2;\"><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">4 </span></span><span style=\"font-family:SimSun;font-size:12px;\"><span style=\"line-height:2;\">女职工在怀孕期间，所在单位不得安排其从事国家规定的第三级体力劳动强度的劳动和孕期禁忌从事的劳动，不得在正常劳动日以外延长劳动时间，对不能胜任原劳动的，应当根据医务部门的证明，予以减轻劳动量或者安排其他劳动。</span><br />\r\n</span><span style=\"font-family:SimSun;font-size:12px;\"><span style=\"line-height:2;\">　　怀孕七个月以上（含七个月）的女职工，一般不得安排其从事夜班劳动，在劳动时间内应安排一定的休息时间。</span><br />\r\n</span><span style=\"font-family:SimSun;font-size:12px;\"><span style=\"line-height:2;\">　　怀孕的女职工，在劳动时间内进行产前检查，应当算作劳动时间。</span><br />\r\n</span><span style=\"line-height:2;\"><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">5 </span></span><span style=\"font-family:SimSun;font-size:12px;line-height:2;\">女职工在哺乳期内，所在单位不得安排其从事国家规定的第三级体力劳动强度的劳动和哺乳期禁忌从事的劳动，不得延长其劳动时间，一般不得安排其从事夜班劳动。</span>\r\n	</p>\r\n<span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">6 \r\n女职工因生理特点禁忌从事劳动的范围由劳动部规定。</span><span></span>\r\n</p>\r\n<p style=\"text-indent:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">7 \r\n女职工违反国家有关计划生育规定的，其劳动保护应当按照国家有关计划生育规定办理，不适用本规定。</span><span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:SimSun;font-size:12px;line-height:2;\">&nbsp; &nbsp; 8 各级工会、妇联组织负责对本规定的执行进行监督检查。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1');
INSERT INTO `tbl_post` VALUES ('yiuMni', '关于开展2013年“安全生产月”活动的通知', 'vENrau', 'NjQrmi', '2013-05-30 09:14:38', '', null, '<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	各部门、单位：\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; 为贯彻落实党中央、国务院和集团公司关于安全生产工作的部署，深入开展2013年“安全生产月”活动，<span>按照集团公司《关于开展2013年“安全生产月”活动的通知》（中国华电安[2013]286号）要求，请各部门、单位：</span>\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<span>&nbsp; &nbsp; 1、</span>组织员工学习《电力（业）安全工作规程》（科研总院小图书馆有备，也可根据需要自行购置）；\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; 2、利用工间或业余时间浏览学习交通安全、消防安全、火灾避险、应急逃生等安全宣传挂图（设置在前台大厅右侧，每周将更新一个安全主题）；\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; 3、自觉加强安全宣传，开展各类安全学习和讨论，并鼓励本部门员工积极向集团安全文化征文报送稿件（稿件报送请联系综合管理部）；\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; 4、<span>遵守规范的工作流程</span>，杜绝各类违章操作，<span>加强日常安全意识</span><span>，</span>尊重和珍爱生命。\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp;&nbsp;（各部门、单位和人员下班、出差或长时间离开工位，请自觉断开电脑、打印机、复印机、饮水机等电源插座，锁紧门窗等，杜绝安全隐患，防止不安全事件发生，谢谢！）\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; 另外，综合管理部将于6月组织一次2013年安全生产警示教育片观看活动，具体时间将另行通知。\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;综合管理部\r\n</p>\r\n<p style=\"color:#333333;font-family:宋体;text-indent:24px;background-color:#FEFEFE;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;2013年5月30日\r\n</p>', '1');
